import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// ===== PERFORMANCE OPTIMIZATION: LAZY LOADING =====
const LazyMap = React.lazy(() => 
  import('./components/LazyMap').catch(() => ({ default: () => <div>Map Loading...</div> }))
);

const LazyChart = React.lazy(() => 
  import('./components/LazyChart').catch(() => ({ default: () => <div>Chart Loading...</div> }))
);

// ===== CUSTOM HOOKS (MOVED OUTSIDE COMPONENT) =====
const useDefaultLocation = () => {
  return useMemo(() => ({
    lat: 10.178322301561957,
    lng: 76.43089144291672,
    accuracy: 50,
    source: 'default'
  }), []);
};

const useFallbackData = () => {
  return useMemo(() => ({
    interpolated_aqi: 85,
    nearest_sensor_info: {
      current_aqi: 85,
      current_station_name: 'ASIET Campus Station',
      distance_to_nearest: 0.5,
      current_sensor_data: {
        pm25: 25.5, pm10: 42.3, so2: 15.8, no2: 37.2,
        co: 1.2, o3: 45.6, nh3: 12.4, temp: 28, hum: 65, pre: 1013
      }
    },
    forecast_data: {
      nearest_station: [
        { day: 'Today', pm25_max: 30.2, pm10_max: 37.0, so2_max: 2.4, no2_max: 40.1, co_max: 1.1, o3_max: 3.7, nh3_max: 92.7 },
        { day: 'Tomorrow', pm25_max: 24.1, pm10_max: 34.2, so2_max: 2.4, no2_max: 45.2, co_max: 1.2, o3_max: 3.2, nh3_max: 100.0 },
        { day: 'Day 3', pm25_max: 24.3, pm10_max: 30.3, so2_max: 2.2, no2_max: 42.1, co_max: 1.2, o3_max: 3.5, nh3_max: 100.0 },
        { day: 'Day 4', pm25_max: 19.7, pm10_max: 23.4, so2_max: 2.3, no2_max: 38.5, co_max: 1.2, o3_max: 3.7, nh3_max: 100.0 }
      ]
    },
    sensor_locations: {
      'lora-v1': { lat: 10.178322301561957, lng: 76.43089144291672, name: 'Station 1 (ASIET Campus)' },
      'loradev2': { lat: 10.17095090340159, lng: 76.42962876824544, name: 'Station 2 (Mattoor Junction)' }
    },
    idw_status: 'location_required',
    timestamp: new Date().toISOString()
  }), []);
};

function Dashboard() {
  // ===== USE HOOKS AT TOP LEVEL =====
  const defaultLocation = useDefaultLocation();
  const fallbackData = useFallbackData();
  const navigate = useNavigate();

  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('detecting');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dataSource, setDataSource] = useState('fallback'); // 'fallback', 'api', 'interpolated'

  // ===== REFS =====
  const mapRef = useRef(null);
  const chartRef = useRef(null);
  const locationTimeoutRef = useRef(null);
  const dataFetchTimeoutRef = useRef(null);

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // ===== IMMEDIATE INITIALIZATION =====
  useEffect(() => {
    console.log('âš¡ DASHBOARD: Initializing with username:', username);
    
    // Set immediate fallback data for instant UI
    setDashboardData(fallbackData);
    setNearestStation({
      name: 'ASIET Campus Station',
      distance: 0.5,
      aqi: 85
    });
    
    // Start location detection and API calls
    startLocationDetection();
    
    return () => {
      if (locationTimeoutRef.current) clearTimeout(locationTimeoutRef.current);
      if (dataFetchTimeoutRef.current) clearTimeout(dataFetchTimeoutRef.current);
    };
  }, [username, fallbackData]);

  // ===== LOCATION DETECTION =====
  const startLocationDetection = useCallback(() => {
    setLocationStatus('detecting');
    
    if (!navigator.geolocation) {
      console.log('ðŸ“ Geolocation not supported, using default location');
      handleLocationFallback();
      return;
    }

    // Set timeout for location detection
    locationTimeoutRef.current = setTimeout(() => {
      console.log('ðŸ“ Location detection timeout, using default');
      handleLocationFallback();
    }, 10000);

    navigator.geolocation.getCurrentPosition(
      (position) => {
        clearTimeout(locationTimeoutRef.current);
        
        const locationData = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          accuracy: position.coords.accuracy,
          source: 'gps'
        };

        console.log('âœ… GPS location obtained:', locationData);
        setUserLocation(locationData);
        setLocationStatus('found');
        
        // Save location to server
        saveLocationToServer(locationData);
        
        // Fetch dashboard data with location
        fetchDashboardData(locationData);
      },
      (error) => {
        clearTimeout(locationTimeoutRef.current);
        console.log('âŒ Location error:', error.message);
        handleLocationFallback();
      },
      {
        enableHighAccuracy: true,
        timeout: 8000,
        maximumAge: 300000 // 5 minutes
      }
    );
  }, []);

  const handleLocationFallback = useCallback(() => {
    console.log('ðŸ“ Using default location');
    setUserLocation(defaultLocation);
    setLocationStatus('default');
    
    // Fetch dashboard data without location
    fetchDashboardData(null);
  }, [defaultLocation]);

  // ===== SAVE LOCATION TO SERVER =====
  const saveLocationToServer = useCallback(async (locationData) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/save-user-location/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          latitude: locationData.lat,
          longitude: locationData.lng,
          accuracy: locationData.accuracy,
          source: locationData.source
        })
      });

      if (response.ok) {
        console.log('âœ… Location saved to server');
      } else {
        console.log('âš ï¸ Failed to save location to server');
      }
    } catch (error) {
      console.log('âš ï¸ Error saving location to server:', error);
    }
  }, [username, API_BASE_URL]);

  // ===== FETCH DASHBOARD DATA =====
  const fetchDashboardData = useCallback(async (locationData = null) => {
    try {
      setLoading(true);
      setError(null);
      
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      
      if (locationData) {
        url.searchParams.append('lat', locationData.lat);
        url.searchParams.append('lng', locationData.lng);
      }

      console.log('ðŸ”„ Fetching dashboard data from:', url.toString());

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 'Content-Type': 'application/json' }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('âœ… Dashboard data received:', data);

      // Process the response data
      processDashboardResponse(data, locationData);
      
    } catch (error) {
      console.error('âŒ Dashboard API error:', error);
      setError(error.message);
      setDataSource('fallback');
      
      // Continue with fallback data
      setLoading(false);
    }
  }, [username, API_BASE_URL]);

  // ===== PROCESS DASHBOARD RESPONSE =====
  const processDashboardResponse = useCallback((data, locationData) => {
    try {
      // Determine data source based on API response
      let source = 'api';
      let currentAQI = data.interpolated_aqi || data.nearest_sensor_info?.current_aqi || 85;
      let currentValues = {};
      let stationName = 'ASIET Campus Station';
      let distance = 0.5;

      // Check if we have IDW interpolated values
      if (data.idw_interpolated_values && Object.keys(data.idw_interpolated_values).length > 0) {
        source = 'interpolated';
        currentValues = data.idw_interpolated_values;
        stationName = 'Your Location (Smart Analysis)';
        distance = 0;
        currentAQI = data.interpolated_aqi || currentAQI;
        
        console.log('ðŸŽ¯ Using IDW interpolated values');
      } else if (data.nearest_sensor_info) {
        // Use nearest station data
        currentValues = data.nearest_sensor_info.current_sensor_data || {};
        stationName = data.nearest_sensor_info.current_station_name || 'ASIET Campus Station';
        distance = data.nearest_sensor_info.distance_to_nearest || 0.5;
        currentAQI = data.nearest_sensor_info.current_aqi || currentAQI;
        
        console.log('ðŸ“¡ Using nearest station data');
      }

      // Update dashboard data
      const updatedData = {
        ...data,
        // Ensure we have current values for the UI
        current_sensor_data: currentValues,
        interpolated_aqi: currentAQI,
        // Keep forecast data structure
        forecast_data: data.forecast_data || fallbackData.forecast_data,
        // Keep sensor locations
        sensor_locations: data.sensor_locations || fallbackData.sensor_locations,
        // Update IDW status
        idw_status: data.idw_status || 'location_required',
        // Add weather data
        weather_data: {
          temp: currentValues.temp || 28,
          hum: currentValues.hum || 65,
          pre: currentValues.pre || 1013
        }
      };

      setDashboardData(updatedData);
      setDataSource(source);
      
      // Update nearest station info
      setNearestStation({
        name: stationName,
        distance: distance,
        aqi: currentAQI
      });

      setLoading(false);
      
      console.log(`âœ… Dashboard updated with ${source} data`);
      
    } catch (error) {
      console.error('âŒ Error processing dashboard response:', error);
      setError('Failed to process dashboard data');
      setLoading(false);
    }
  }, [fallbackData]);

  // ===== COMPUTED VALUES =====
  const currentValues = useMemo(() => {
    if (!dashboardData) return fallbackData.nearest_sensor_info.current_sensor_data;
    
    // Priority: IDW interpolated > nearest sensor > fallback
    return dashboardData.idw_interpolated_values || 
           dashboardData.current_sensor_data ||
           dashboardData.nearest_sensor_info?.current_sensor_data || 
           fallbackData.nearest_sensor_info.current_sensor_data;
  }, [dashboardData, fallbackData]);

  const currentAQI = useMemo(() => {
    if (!dashboardData) return fallbackData.interpolated_aqi;
    
    return dashboardData.interpolated_aqi || 
           dashboardData.nearest_sensor_info?.current_aqi || 
           fallbackData.interpolated_aqi;
  }, [dashboardData, fallbackData]);

  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  const weatherData = useMemo(() => {
    if (!dashboardData) return { temp: 28, hum: 65, pre: 1013 };
    
    return dashboardData.weather_data || {
      temp: currentValues.temp || 28,
      hum: currentValues.hum || 65,
      pre: currentValues.pre || 1013
    };
  }, [dashboardData, currentValues]);

  // ===== MEMOIZED COMPONENTS =====
  const metricIcons = useMemo(() => ({
    pm25: 'ðŸŒ±', pm10: 'ðŸƒ', so2: 'ðŸ­', no2: 'ðŸ’¨',
    co: 'â˜ï¸', o3: 'â˜€ï¸', nh3: 'âš—ï¸'
  }), []);

  const formatValue = useCallback((value, metric) => {
    const numValue = parseFloat(value) || 0;
    return metric === 'co' ? numValue.toFixed(1) : Math.round(numValue);
  }, []);

  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []);

  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  
  const handleLogout = useCallback(() => {
    localStorage.clear();
    navigate('/login');
  }, [navigate]);

  const scrollToMap = useCallback(() => {
    document.getElementById('map-section')?.scrollIntoView({ 
      behavior: 'smooth', 
      block: 'start' 
    });
  }, []);

  // ===== LOCATION STATUS COMPONENT =====
  const LocationStatus = useMemo(() => {
    const getLocationDisplay = () => {
      switch (locationStatus) {
        case 'detecting':
          return <span style={{ color: '#f59e0b' }}>ðŸ“ Detecting your location...</span>;
        case 'found':
          return (
            <span style={{ color: '#10b981' }}>
              ðŸ“ GPS Location (Â±{Math.round(userLocation?.accuracy || 0)}m)
              {nearestStation && ` â†’ ${nearestStation.name} (${nearestStation.distance?.toFixed(1)}km)`}
            </span>
          );
        case 'default':
          return <span style={{ color: '#6b7280' }}>ðŸ“ Using default area (Enable location for personalized data)</span>;
        default:
          return <span>ðŸ“ {nearestStation?.name || 'ASIET Campus Area'}</span>;
      }
    };

    return <div className="location-info">{getLocationDisplay()}</div>;
  }, [locationStatus, userLocation, nearestStation]);

  // ===== DATA SOURCE INDICATOR =====
  const DataSourceIndicator = useMemo(() => {
    const getSourceInfo = () => {
      switch (dataSource) {
        case 'interpolated':
          return {
            icon: 'ðŸŽ¯',
            text: 'Smart Analysis (IDW Interpolated)',
            color: '#10b981',
            description: 'Personalized data based on your location'
          };
        case 'api':
          return {
            icon: 'ðŸ“¡',
            text: 'Nearest Station Data',
            color: '#3b82f6',
            description: 'Live data from closest monitoring station'
          };
        case 'fallback':
          return {
            icon: 'ðŸ”„',
            text: 'Fallback Data',
            color: '#f59e0b',
            description: 'Using cached data due to connection issues'
          };
        default:
          return {
            icon: 'ðŸ“Š',
            text: 'Loading...',
            color: '#6b7280',
            description: 'Fetching latest data'
          };
      }
    };

    const source = getSourceInfo();
    
    return (
      <div className="data-source-indicator" style={{ 
        background: `${source.color}20`,
        border: `1px solid ${source.color}40`,
        borderRadius: '8px',
        padding: '8px 12px',
        marginTop: '8px',
        fontSize: '0.875rem'
      }}>
        <span style={{ color: source.color, fontWeight: '600' }}>
          {source.icon} {source.text}
        </span>
        <div style={{ color: '#6b7280', fontSize: '0.8rem', marginTop: '2px' }}>
          {source.description}
        </div>
      </div>
    );
  }, [dataSource]);

  // ===== METRIC CARDS =====
  const MetricCards = useMemo(() => {
    return Object.entries(currentValues).slice(0, 7).map(([key, value]) => (
      <div key={key} className="metric-card">
        <div className="metric-icon">{metricIcons[key] || 'ðŸ“Š'}</div>
        <div className="metric-value">{formatValue(value, key)}</div>
        <div className="metric-label">{key.toUpperCase()}</div>
        <div className="metric-unit">{key === 'co' ? 'mg/mÂ³' : 'Âµg/mÂ³'}</div>
        {dataSource === 'interpolated' && (
          <div className="metric-badge" style={{ 
            background: '#10b981', color: 'white', fontSize: '0.7rem', 
            padding: '2px 6px', borderRadius: '8px', 
            position: 'absolute', top: '8px', right: '8px' 
          }}>
            ðŸŽ¯ Smart
          </div>
        )}
      </div>
    ));
  }, [currentValues, metricIcons, formatValue, dataSource]);

  // ===== FORECAST TABLE =====
  const ForecastTable = useMemo(() => {
    const forecastData = dashboardData?.forecast_data?.nearest_station || 
                        fallbackData.forecast_data.nearest_station;
    
    return (
      <table className="forecast-table">
        <thead>
          <tr>
            <th>ðŸ“… Day</th>
            <th>ðŸ“ˆ Max Value</th>
          </tr>
        </thead>
        <tbody>
          {forecastData.map((item, index) => (
            <tr key={index}>
              <td>{item.day}</td>
              <td>{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }, [dashboardData, selectedParameter, formatValue, fallbackData]);

  // ===== RENDER =====
  return (
    <div className="dashboard-page">
      {/* Real-time Status */}
      <div className="realtime-status">
        ðŸ”´ LIVE â€¢ Updates every 30s â€¢ {dataSource === 'interpolated' ? 'Smart Analysis' : 
                                        dataSource === 'api' ? 'Live Data' : 'Cached Data'}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" style={{ marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>â˜°</div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">ðŸ  Home</a></li>
            <li><a href="/forecast" className="nav-link">ðŸ“ˆ Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">ðŸ“‹ Health Update</a></li>
            <li><a href="/health-report" className="nav-link">ðŸ“„ Health Report</a></li>
            <li><a href="/add-family" className="nav-link">ðŸ‘¥ Add Family</a></li>
            <li className="user-info">ðŸ‘¤ <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">ðŸšª Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        â„¹ï¸ <span>Your Location AQI: {Math.round(currentAQI)} - {aqiStatus.status}
        {nearestStation && ` â€¢ ${nearestStation.name} ${nearestStation.distance > 0 ? `(${nearestStation.distance?.toFixed(1)}km)` : ''}`}</span>
      </div>

      {/* Error Banner */}
      {error && (
        <div className="error-banner" style={{ background: '#fef2f2', border: '1px solid #fecaca', color: '#dc2626', padding: '12px', borderRadius: '8px', margin: '12px' }}>
          âš ï¸ {error} - Using fallback data
        </div>
      )}

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">ðŸ‘¤</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                {LocationStatus}
                {DataSourceIndicator}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
              </div>
              <div className="aqi-status">
                {dataSource === 'interpolated' ? 'Smart Analysis AQI' : 
                 dataSource === 'api' ? 'Live Station AQI' : 'Cached AQI'}
              </div>
              <div className="aqi-badge">
                {nearestStation?.name || 'Your Location'}
              </div>
              <div className="last-updated">
                Last updated: {new Date().toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Sensor Notification */}
        <div className="sensor-notification">
          ðŸ“¡ <strong>{nearestStation?.name || 'ASIET Campus Station'}</strong> - 
          {dataSource === 'interpolated' ? 
            'Smart analysis based on your exact location using multiple sensors.' :
            locationStatus === 'found' ? 
              `You are ${nearestStation?.distance?.toFixed(1) || '0.5'}km from the monitoring station.` :
              'You are viewing data from our closest monitoring station.'
          }
          <a href="#map-section" onClick={scrollToMap}> View on map</a>
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            ðŸŒ¬ï¸ Current Air Quality at Your Location
            {dataSource === 'interpolated' && <small> (Smart Analysis)</small>}
            {dataSource === 'api' && <small> (Live Data)</small>}
            {dataSource === 'fallback' && <small> (Cached Data)</small>}
          </h2>
          <div className="metrics-grid">
            {MetricCards}
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div id="map-section" className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ—ºï¸ Interactive Sensor Map</h3>
            </div>
            <div className="map-container">
              <React.Suspense fallback={
                <div style={{ height: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8fafc' }}>
                  <div>ðŸ—ºï¸ Loading map...</div>
                </div>
              }>
                <LazyMap 
                  userLocation={userLocation} 
                  dashboardData={dashboardData}
                  currentAQI={currentAQI}
                />
              </React.Suspense>
            </div>
            <div style={{ marginTop: '12px', textAlign: 'center', fontSize: '0.875rem', color: '#6b7280' }}>
              ðŸ“ {userLocation?.source === 'gps' ? 'Blue' : 'Gray'} marker: Your location â€¢ Tower markers: Monitoring stations
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ“Š 4-Day Air Quality Forecast</h3>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <React.Suspense fallback={
                <div style={{ height: '200px', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8fafc' }}>
                  <div>ðŸ“Š Loading chart...</div>
                </div>
              }>
                <LazyChart 
                  dashboardData={dashboardData}
                  selectedParameter={selectedParameter}
                  fallbackData={fallbackData}
                />
              </React.Suspense>
              
              <div className="forecast-table-container">
                {ForecastTable}
              </div>
            </div>
          </div>
        </div>

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">ðŸŒ¤ï¸ Weather Conditions</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">ðŸŒ¡ï¸</div>
              <div className="weather-value">{weatherData.temp}Â°C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ’§</div>
              <div className="weather-value">{weatherData.hum}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ“</div>
              <div className="weather-value">{weatherData.pre} hPa</div>
              <div className="weather-label">Pressure</div>
            </div>
          </div>
        </div>

        {/* System Status */}
        <div className="disclaimer">
          <h3>âš¡ System Status & Data Source</h3>
          <p>
            <strong>ðŸ“Š Current Data Source:</strong> 
            <span style={{ color: aqiStatus.color, fontWeight: 600 }}>
              {dataSource === 'interpolated' ? ' SMART ANALYSIS (IDW)' : 
               dataSource === 'api' ? ' LIVE DATA' : ' CACHED DATA'}
            </span>
          </p>
          <p>
            <strong>ðŸ“ Location:</strong> 
            {locationStatus === 'found' ? 
              `GPS Active (Â±${Math.round(userLocation?.accuracy || 0)}m)` : 
              'Default Area (Enable location for personalized data)'
            }
          </p>
          <p>
            <strong>ðŸ”„ Update Status:</strong> 
            {loading ? 'Fetching latest data...' : 'Data current'}
            {error && <span style={{ color: '#dc2626' }}> - Connection issues detected</span>}
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala - Smart Air Quality Monitoring</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
