import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/HealthReport.css';

const LazyChart = React.lazy(() => import('./LazyChart'));

// --- Helper Functions (same as Dashboard.js) ---
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const getAQIColor = (aqi) => {
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 150) return '#ef4444';
    return '#7c2d12';
};

const getAQIStatus = (aqi) => {
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    return 'Hazardous';
};

// Function to get user-friendly station names
const getFriendlyStationName = (stationName) => {
    if (!stationName) return 'Local Monitoring Station';
    
    // Remove technical terms and make user-friendly
    const cleanName = stationName
        .replace(/lora|LoRa|LORA/gi, '')
        .replace(/v1|v2|V1|V2/gi, '')
        .replace(/dev|DEV|development/gi, '')
        .replace(/node|NODE/gi, '')
        .replace(/sensor|SENSOR/gi, '')
        .replace(/station|STATION/gi, '')
        .replace(/[_-]+/g, ' ')
        .replace(/\s+/g, ' ')
        .trim();
    
    // If name becomes too short or empty, provide a generic name
    if (cleanName.length < 3) {
        return 'Local Air Quality Monitor';
    }
    
    // Capitalize properly
    return cleanName
        .toLowerCase()
        .split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ') + ' Area';
};

// Health Recommendations based on AQI and Health Risk Level
const getHealthRecommendations = (aqi, healthRiskLevel) => {
    const baseRecommendations = {
        good: {
            general: [
                'ðŸŒŸ Perfect air quality - enjoy all outdoor activities',
                'ðŸš¶â€â™‚ï¸ Great time for outdoor exercise and sports',
                'ðŸªŸ Open windows to let fresh air in',
                'ðŸ‘¶ Safe conditions for children to play outside'
            ],
            sensitive: [
                'âœ… Excellent conditions for people with respiratory issues',
                'ðŸƒâ€â™€ï¸ No restrictions on outdoor activities',
                'ðŸ’¨ Breathe easy - air quality is optimal'
            ]
        },
        moderate: {
            general: [
                'âš ï¸ Air quality is acceptable for most people',
                'ðŸƒâ€â™‚ï¸ Reduce prolonged outdoor exertion if sensitive',
                'ðŸ˜· Consider wearing a mask during heavy traffic hours',
                'ðŸŒ… Best outdoor times: early morning and late evening'
            ],
            sensitive: [
                'âš ï¸ Limit prolonged outdoor activities',
                'ðŸ˜· Wear N95 mask when going outside',
                'ðŸ  Stay indoors during peak pollution hours',
                'ðŸ’Š Keep rescue medications readily available'
            ]
        },
        unhealthy: {
            general: [
                'ðŸš¨ Avoid prolonged outdoor activities',
                'ðŸ  Stay indoors as much as possible',
                'ðŸ˜· Wear N95 mask if you must go outside',
                'ðŸªŸ Keep windows and doors closed'
            ],
            sensitive: [
                'ðŸš¨ URGENT: Stay indoors immediately',
                'ðŸ¥ Seek medical attention if experiencing symptoms',
                'ðŸ˜· Mandatory N95 mask for any outdoor exposure',
                'ðŸ“ž Contact doctor if breathing difficulties occur'
            ]
        },
        hazardous: {
            general: [
                'ðŸš¨ EMERGENCY: Avoid all outdoor activities',
                'ðŸ  Remain indoors with air purification',
                'ðŸ˜· N95 mask mandatory for any exposure',
                'ðŸ¥ Seek immediate medical help for symptoms'
            ],
            sensitive: [
                'ðŸš¨ HEALTH EMERGENCY: Immediate indoor shelter',
                'ðŸ¥ Seek emergency medical attention',
                'ðŸ“ž Call emergency services if severe symptoms',
                'ðŸš— Consider relocation to cleaner air area'
            ]
        }
    };

    // Determine category
    let category = 'good';
    if (aqi > 150) category = 'hazardous';
    else if (aqi > 100) category = 'unhealthy'; 
    else if (aqi > 50) category = 'moderate';

    // Determine if user is sensitive based on health risk level
    const isSensitive = healthRiskLevel === 'High' || healthRiskLevel === 'Critical';
    
    return {
        recommendations: isSensitive ? baseRecommendations[category].sensitive : baseRecommendations[category].general,
        isSensitive,
        category,
        isEmergency: category === 'hazardous' || (category === 'unhealthy' && isSensitive)
    };
};

// Emergency contacts based on AQI level
const getEmergencyContacts = (aqi) => {
    if (aqi <= 100) return null;
    
    return {
        primary: {
            name: 'Kerala Pollution Control Board',
            number: '0471-2418566',
            description: '24/7 Air Quality Emergency Hotline'
        },
        medical: {
            name: 'Medical Emergency',
            number: '108',
            description: 'Immediate medical assistance'
        },
        poison: aqi > 150 ? {
            name: 'Poison Control',
            number: '1066',
            description: 'Pollution-related health emergencies'
        } : null
    };
};

function HealthReport() {
    const { user } = useAuth();
    const [username] = useState(user?.name || null);
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [currentTime, setCurrentTime] = useState(new Date());
    
    // --- State for location and interpolation (same as Dashboard) ---
    const [userLocation, setUserLocation] = useState(null);
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [currentDataInfo, setCurrentDataInfo] = useState(null);

    const navigate = useNavigate();
