import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/Dashboard.css';

// It is best practice to put these in their own files
const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

// --- Helper Functions ---
const getAQIColor = (aqi) => {
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 150) return '#ef4444';
    return '#7c2d12';
};
const getAQIStatus = (aqi) => {
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    return 'Hazardous';
};
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

function Dashboard() {
    const { user, logout } = useAuth();
    const [username] = useState(user?.name || 'User');
    const [selectedParameter, setSelectedParameter] = useState('pm25');
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationName, setUserLocationName] = useState('Detecting location...');
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const fetchDashboardData = useCallback(async () => {
        setLoading(true);
        try {
            const url = `${API_BASE_URL}/api/dashboard/?username=${username}`;
            const response = await fetch(url);
            if (!response.ok) {
                const errData = await response.json();
                if (errData.redirect_to) navigate(errData.redirect_to);
                throw new Error(errData.error);
            }
            const data = await response.json();
            setDashboardData(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        fetchDashboardData();
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
                (err) => console.warn("Geolocation disabled.")
            );
        }
    }, [fetchDashboardData]);

    useEffect(() => {
        if (userLocation && dashboardData?.stations) {
            const stations = Object.entries(dashboardData.stations);
            let nearestDist = Infinity, nearestId = null;
            stations.forEach(([id, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
            });
            setNearestStation({ id: nearestId, distance: nearestDist });
            
            let weightedSum = 0, weightSum = 0;
            stations.forEach(([, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
                weightedSum += (station.highest_sub_index || 0) * weight;
                weightSum += weight;
            });
            if (weightSum > 0) setInterpolatedData({ aqi: Math.round(weightedSum / weightSum) });
        }
    }, [userLocation, dashboardData]);

    const displayAqi = interpolatedData?.aqi || Object.values(dashboardData?.stations || {})[0]?.highest_sub_index || 0;

    const alertInfo = useMemo(() => {
        const aqi = displayAqi;
        if (aqi <= 50) return { className: 'good', message: 'Air quality is good. Enjoy your day!' };
        if (aqi <= 100) return { className: 'moderate', message: 'Air quality is moderate. Sensitive individuals should limit outdoor exertion.' };
        return { className: 'unhealthy', message: 'Air quality is unhealthy. It is advisable to reduce outdoor activities.' };
    }, [displayAqi]);

    const handleLogout = useCallback(() => {
        logout();
        navigate('/');
    }, [logout, navigate]);

    if (loading) return <div className="panel-loader"><div className="loading-spinner"></div><h2>Loading Dashboard...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;
    if (!dashboardData) return <div>No data available.</div>;

    const { health_data, stations, forecasts } = dashboardData;
    const forecastForNearest = nearestStation ? forecasts[nearestStation.id] : null;
    const currentValues = nearestStation ? stations[nearestStation.id]?.averages : Object.values(stations)[0]?.averages;
    const pollutants = [
        { key: 'pm25', name: 'PM2.5' }, { key: 'pm10', name: 'PM10' }, { key: 'so2', name: 'SO₂' },
        { key: 'no2', name: 'NO₂' }, { key: 'co', name: 'CO' }, { key: 'o3', name: 'O₃' }, { key: 'nh3', name: 'NH₃' }
    ];

    return (
        <div className="dashboard-page">
            {/* --- NAVBAR ADDED --- */}
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" />
                        AirAware
                    </Link>
                    <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}><i className="fas fa-bars"></i></div>
                    <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                        <li><Link to="/" className="nav-link">Home</Link></li>
                        <li><Link to="/map" className="nav-link">Live Map</Link></li>
                        {user ? (
                        <>
                            <li><Link to="/dashboard" className="nav-link nav-button active">Dashboard</Link></li>
                            <li className="nav-link user-greeting">Hello, {user.name}</li>
                            <li><button onClick={handleLogout} className="nav-link login-btn nav-button"><i className="fas fa-sign-out-alt"></i> Logout</button></li>
                        </>
                        ) : (
                        <li><Link to="/login" className="nav-link login-btn"><i className="fas fa-sign-in-alt"></i> Login</Link></li>
                        )}
                    </ul>
                </div>
            </nav>

            <div className="main-container">
                <div className={`alert-banner ${alertInfo.className}`}>
                    <i className="fas fa-info-circle"></i>
                    <span>{alertInfo.message}</span>
                </div>
                <div className="welcome-header">
                    <div className="welcome-text">
                        <h1>Welcome, {username}!</h1>
                        <p>{userLocationName}</p>
                    </div>
                </div>

                {displayAqi !== undefined && nearestStation && (
                    <div className="user-location-panel">
                        <div className="user-location-header"><i className="fas fa-street-view"></i><span>AQI at Your Location</span></div>
                        <div className="user-location-body">
                            <div className="user-aqi-value" style={{color: getAQIColor(displayAqi)}}>{displayAqi}</div>
                            <div className="user-location-details">
                                <span>Interpolated Value</span><br />
                                <span>Nearest: {nearestStation.name} ({nearestStation.distance} km)</span>
                            </div>
                        </div>
                    </div>
                )}
                
                {/* --- CURRENT POLLUTANT VALUES ADDED --- */}
                <div className="metrics-section">
                    <h2 className="section-title">Current Pollutant Levels (at Your Location)</h2>
                    <div className="metrics-grid">
                        {pollutants.map(p => (
                            <div className="metric-card" key={p.key}>
                                <div className="metric-label">{p.name}</div>
                                <div className="metric-value">
                                    {(currentValues?.[p.key]?.toFixed(2)) ?? 'N/A'}
                                    <span className="metric-unit"> µg/m³</span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="dashboard-grid">
                    <div className="dashboard-card">
                        <h3>Sensor Map</h3>
                        <Suspense fallback={<div className="panel-loader">Loading Map...</div>}>
                            <LazyMap userLocation={userLocation} stations={stations} />
                        </Suspense>
                    </div>
                    <div className="dashboard-card">
                        <h3>4-Day Forecast</h3>
                        <Suspense fallback={<div className="panel-loader">Loading Chart...</div>}>
                            <LazyChart forecastData={forecastForNearest} selectedParameter={selectedParameter} />
                        </Suspense>
                    </div>
                </div>
                
                <div className="health-section">
                    <div className="health-content">
                        <h3>Your Health Status</h3>
                        <p>Risk Level: <span style={{color: health_data.risk_level === 'Low' ? '#10b981' : '#ef4444'}}>{health_data.risk_level}</span></p>
                        <h4>Recommendations:</h4>
                        <ul>
                            {health_data.recommendations.map((rec, index) => <li key={index}>{rec}</li>)}
                        </ul>
                    </div>
                    <div className="health-advisory">
                        <h3>General Health Advisory</h3>
                        <p>People with asthma, heart disease, older adults, and young children are more susceptible to air pollution.</p>
                    </div>
                </div>
            </div>

            {/* --- FOOTER ADDED --- */}
            <footer className="footer" id="contact">
                <div className="footer-container">
                    <div className="footer-grid">
                        <div className="footer-section"><h3>About AirAware</h3><p className="footer-text">Real-time air quality monitoring solutions for a healthier tomorrow.</p></div>
                        <div className="footer-section"><h3>Quick Links</h3><Link to="/" className="footer-link">Home</Link><Link to="/map" className="footer-link">Live Map</Link></div>
                        <div className="footer-section"><h3>Contact</h3><p className="footer-text">ASIET AI & IoT Lab<br/>Kalady, Ernakulam</p></div>
                    </div>
                    <div className="footer-bottom"><p>&copy; 2025 AirAware. All rights reserved.</p></div>
                </div>
            </footer>
        </div>
    );
}

export default Dashboard;
