import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';
import './css/Dashboard.css'; // Make sure you have this CSS file

ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler
);

// --- HELPER COMPONENTS (LazyMap and LazyChart) ---

const LazyMap = ({ userLocation, stations }) => {
    const mapRef = useRef(null);
    const mapInstanceRef = useRef(null);
    const markersRef = useRef({});
    const userMarkerRef = useRef(null);

    const getAQIColor = (aqi) => {
        if (aqi === null || aqi === undefined) return '#6b7280';
        if (aqi <= 50) return '#10b981';
        if (aqi <= 100) return '#f59e0b';
        if (aqi <= 200) return '#ef4444';
        return '#7c2d12';
    };
    const createAqiIcon = (aqi, name) => {
        const color = getAQIColor(aqi);
        const iconHtml = `<div style="background-color: ${color};" class="aqi-marker-icon"><div class="aqi-marker-icon-inner">${Math.round(aqi) || 'N/A'}</div></div><div class="aqi-marker-label">${name}</div>`;
        return window.L.divIcon({ html: iconHtml, className: 'custom-div-icon', iconSize: [40, 40], iconAnchor: [20, 40] });
    };

    useEffect(() => {
        if (mapRef.current && !mapInstanceRef.current) {
            const map = window.L.map(mapRef.current, { zoomControl: false }).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
            window.L.control.zoom({ position: 'topleft' }).addTo(map);
            mapInstanceRef.current = map;
        }
    }, []);

    useEffect(() => {
        const map = mapInstanceRef.current;
        if (!map || !stations) return;

        Object.values(markersRef.current).forEach(marker => marker.remove());
        markersRef.current = {};

        Object.entries(stations).forEach(([id, station]) => {
            const { lat, lng, name } = station.station_info;
            const marker = window.L.marker([lat, lng], { icon: createAqiIcon(station.highest_sub_index, name) }).addTo(map);
            markersRef.current[id] = marker;
        });

    }, [stations]);
    
    useEffect(() => {
        const map = mapInstanceRef.current;
        if (map && userLocation) {
            map.setView([userLocation.lat, userLocation.lng], 14);
            if (userMarkerRef.current) {
                userMarkerRef.current.setLatLng([userLocation.lat, userLocation.lng]);
            } else {
                userMarkerRef.current = window.L.marker([userLocation.lat, userLocation.lng]).addTo(map);
            }
        }
    }, [userLocation]);

    return <div ref={mapRef} style={{ height: '100%', width: '100%', borderRadius: '12px' }}></div>;
};

const LazyChart = ({ forecastData, selectedParameter }) => {
    const chartData = useMemo(() => {
        if (!forecastData || forecastData.length === 0) return { labels: [], datasets: [] };
        const labels = forecastData.map(d => new Date(d.day).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
        const dataPoints = forecastData.map(d => d[`${selectedParameter}_max`]);
        return {
            labels,
            datasets: [{
                label: selectedParameter.toUpperCase(),
                data: dataPoints,
                borderColor: '#3b82f6',
                backgroundColor: 'rgba(59, 130, 246, 0.2)',
                fill: true,
                tension: 0.4,
            }]
        };
    }, [forecastData, selectedParameter]);

    const chartOptions = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: { legend: { display: false } },
        scales: { x: { ticks: { color: '#6b7280' } }, y: { ticks: { color: '#6b7280' } } }
    };

    if (!forecastData || forecastData.length === 0) {
        return <div className="panel-loader"><p>No forecast data available.</p></div>;
    }

    return <Line options={chartOptions} data={chartData} />;
};


// --- Main Dashboard Component ---
function Dashboard() {
  const [username] = useState(() => (JSON.parse(localStorage.getItem('user') || '{}')).name || 'User');
  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [interpolatedData, setInterpolatedData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };
  
  const fetchDashboardData = useCallback(async () => {
    setIsLoading(true);
    try {
      const url = `${API_BASE_URL}/api/dashboard/?username=${username}`;
      const response = await fetch(url);
      if (!response.ok) {
        const errData = await response.json();
        if (errData.redirect_to) navigate(errData.redirect_to);
        throw new Error(errData.error || 'Failed to fetch dashboard data');
      }
      const data = await response.json();
      setDashboardData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  }, [username, navigate, API_BASE_URL]);

  useEffect(() => {
    fetchDashboardData();
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        (err) => console.warn("Geolocation is disabled or failed.")
      );
    }
  }, [fetchDashboardData]);

  useEffect(() => {
    if (userLocation && dashboardData?.stations) {
      const stations = Object.entries(dashboardData.stations);
      let nearestDist = Infinity, nearestId = null;

      stations.forEach(([id, station]) => {
        const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
        if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
      });
      setNearestStation({ id: nearestId, distance: nearestDist });
      
      let weightedSum = 0, weightSum = 0;
      stations.forEach(([, station]) => {
        const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
        const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
        weightedSum += (station.highest_sub_index || 0) * weight;
        weightSum += weight;
      });
      if (weightSum > 0) {
        setInterpolatedData({ aqi: Math.round(weightedSum / weightSum) });
      }
    }
  }, [userLocation, dashboardData]);

  const displayData = useMemo(() => {
    if (!dashboardData) return null;
    return {
      aqi: interpolatedData?.aqi,
      nearest: nearestStation ? {
        name: dashboardData.stations[nearestStation.id]?.station_info.name,
        distance: nearestStation.distance.toFixed(1)
      } : null
    };
  }, [dashboardData, nearestStation, interpolatedData]);

  if (isLoading) return <div className="panel-loader"><div className="loading-spinner"></div><h2>Loading Dashboard...</h2></div>;
  if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;
  if (!dashboardData) return <div>No data available.</div>;

  const { health_data, stations, forecasts } = dashboardData;
  const forecastForNearest = nearestStation ? forecasts[nearestStation.id] : null;

  return (
    <div className="dashboard-page">
      <nav className="navbar">{/* ... Your Navbar JSX ... */}</nav>
      <div className="main-container">
        <div className="welcome-header">
          <h1>Welcome, {username}!</h1>
          <p>Your health risk is currently <span style={{color: health_data.risk_level === 'Low' ? '#10b981' : '#ef4444', fontWeight: 'bold'}}>{health_data.risk_level}</span> (Score: {health_data.score})</p>
        </div>

        {displayData?.aqi !== undefined && displayData.nearest && (
          <div className="user-location-panel">
            <div className="user-location-header">
              <i className="fas fa-street-view"></i>
              <span>AQI at Your Location</span>
            </div>
            <div className="user-location-body">
              <div className="user-aqi-value">{displayData.aqi}</div>
              <div className="user-location-details">
                <span>Interpolated Value</span><br />
                <span>Nearest: {displayData.nearest.name} ({displayData.nearest.distance} km)</span>
              </div>
            </div>
          </div>
        )}
        
        <div className="dashboard-grid">
          <div className="dashboard-card">
            <h3>Sensor Map</h3>
            <Suspense fallback={<div className="panel-loader">Loading Map...</div>}>
              <LazyMap userLocation={userLocation} stations={stations} />
            </Suspense>
          </div>
          <div className="dashboard-card">
            <h3>4-Day Forecast</h3>
            <select className="parameter-selector" value={selectedParameter} onChange={(e) => setSelectedParameter(e.target.value)}>
                <option value="pm25">PM2.5</option>
                <option value="pm10">PM10</option>
                <option value="so2">SO</option>
            </select>
            <Suspense fallback={<div className="panel-loader">Loading Chart...</div>}>
              <LazyChart forecastData={forecastForNearest} selectedParameter={selectedParameter} />
            </Suspense>
          </div>
        </div>
        
        
        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title"> Weather Conditions</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon"></div>
              <div className="weather-value">{formatValue(currentValues.temp || 28, 'temp')}C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon"></div>
              <div className="weather-value">{formatValue(currentValues.hum || 65, 'hum')}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon"></div>
              <div className="weather-value">{formatValue(currentValues.pre || 1013, 'pre')} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
            </div>
          </div>
        </div>

        {/* System Status */}
        <div className="system-status">
          <h3> System Status</h3>
          <div className="status-grid">
            <div className="status-item">
              <div className="status-icon"></div>
              <div className="status-content">
                <div className="status-title">Performance</div>
                <div className="status-value">Optimized</div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon"></div>
              <div className="status-content">
                <div className="status-title">Location</div>
                <div className="status-value">
                  {locationStatus === 'gps_detected' ? 'GPS Active' : 
                   locationStatus === 'ip_detected' ? 'IP Located' : 
                   locationStatus === 'detecting' ? 'Detecting...' : 'Default'}
                </div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon"></div>
              <div className="status-content">
                <div className="status-title">Data Type</div>
                <div className="status-value">
                  {dataType === 'interpolated' ? 'Smart Analysis' : 'Nearest Station'}
                </div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon"></div>
              <div className="status-content">
                <div className="status-title">Data Source</div>
                <div className="status-value">
                  {dataSource === 'live' ? 'Live Data' :
                  
                   dataSource === 'cached' ? 'Cached Data' : 'Loading...'}
                </div>
              </div>
            </div>
          </div>
          
          <div className="system-description">
            <p>
              <strong> Production Ready:</strong> This dashboard uses progressive loading 
              to ensure instant access. Location detection runs in the background while you 
              view real air quality data.
            </p>
            <p>
              <strong> Smart Analysis:</strong> When you're within 1km of monitoring stations, 
              we calculate personalized air quality values for your exact location using 
              advanced interpolation techniques.
            </p>
            <p>
              <strong> Data Transparency:</strong> We clearly indicate whether you're viewing 
              live sensor data, sample data, or cached information. Enable location access 
              for the most accurate readings.
            </p>
          </div>
        </div>

        {/* Help Section */}
        <div className="help-section">
          <h3> Understanding Your Data</h3>
          <div className="help-grid">
            <div className="help-item">
              <div className="help-icon"></div>
              <div className="help-content">
                <h4>Smart Interpolated Values</h4>
                <p>When you're within 1km of monitoring stations, we show personalized air quality calculated for your exact location.</p>
              </div>
            </div>
            <div className="help-item">
              <div className="help-icon"></div>
              <div className="help-content">
                <h4>Nearest Station Data</h4>
                <p>When you're beyond 1km, we show data from the closest monitoring station with distance indicated.</p>
              </div>
            </div>
            <div className="help-item">
              <div className="help-icon"></div>
              <div className="help-content">
                <h4>Live Data</h4>
                <p>Real-time readings from our sensor network, updated every 30 seconds for the most current information.</p>
              </div>
            </div>
            
          </div>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h3> Quick Actions</h3>
          <div className="quick-actions-grid">
            <button 
              onClick={handleEnableLocation}
              className="quick-action-btn"
              disabled={locationStatus === 'gps_detected'}
            >
              <div className="quick-action-icon"></div>
              <div className="quick-action-text">
                <div className="quick-action-title">Enable Location</div>
                <div className="quick-action-desc">Get personalized air quality data</div>
              </div>
            </button>
            
            <button 
              onClick={handleRefreshData}
              className="quick-action-btn"
            >
              <div className="quick-action-icon"></div>
              <div className="quick-action-text">
                <div className="quick-action-title">Refresh Data</div>
                <div className="quick-action-desc">Get latest air quality readings</div>
              </div>
            </button>
            
            <button 
              onClick={() => document.getElementById('map-section')?.scrollIntoView({ behavior: 'smooth' })}
              className="quick-action-btn"
            >
              <div className="quick-action-icon"></div>
              <div className="quick-action-text">
                <div className="quick-action-title">View Map</div>
                <div className="quick-action-desc">See sensor locations and coverage</div>
              </div>
            </button>
            
            <button 
              onClick={() => window.open('/health-assessment', '_self')}
              className="quick-action-btn"
            >
              <div className="quick-action-icon"></div>
              <div className="quick-action-text">
                <div className="quick-action-title">Health Assessment</div>
                <div className="quick-action-desc">Update your health profile</div>
              </div>
            </button>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>AirAware Kerala</h4>
              <p>Smart Air Quality Monitoring System</p>
              <p>Real-time data  Personalized insights  Health-focused</p>
            </div>
            <div className="footer-section">
              <h4>Features</h4>
              <ul>
                <li>Real-time air quality monitoring</li>
                <li>Smart location-based analysis</li>
                <li>4-day weather forecasting</li>
                <li>Health risk assessment</li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Data Sources</h4>
              <ul>
                <li>Station 1 (ASIET Campus)</li>
                <li>Station 2 (Mattoor Junction)</li>
                <li>Advanced interpolation algorithms</li>
                <li>Weather integration</li>
              </ul>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala - Smart Air Quality Monitoring System</p>
            <p>Developed for real-world deployment  Production-ready  User-friendly</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
