import React, { useState, useEffect, useCallback, useMemo, Suspense, useRef } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler
} from 'chart.js';
import './css/Dashboard.css';

ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler
);

// --- HELPER FUNCTIONS ---
const getIPLocation = async () => {
    try {
        const response = await fetch('https://ipapi.co/json/');
        if (!response.ok) return null;
        const data = await response.json();
        return { lat: data.latitude, lng: data.longitude, city: data.city, region: data.region };
    } catch (error) {
        console.warn('IP location fetch failed:', error);
        return null;
    }
};
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};
const getAQIColor = (aqi) => {
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 150) return '#ef4444';
    return '#7c2d12';
};
const getAQIStatus = (aqi) => {
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    return 'Hazardous';
};

// --- HELPER COMPONENTS (It's best practice to put these in their own files) ---
const LazyMap = ({ userLocation, stations }) => {
    const mapRef = useRef(null);
    const mapInstanceRef = useRef(null);
    const markersRef = useRef({});
    const userMarkerRef = useRef(null);

    const createAqiIcon = (aqi) => {
        const color = getAQIColor(aqi);
        const iconHtml = `<div style="background-color: ${color};" class="aqi-marker-icon"><div class="aqi-marker-icon-inner">${Math.round(aqi) || 'N/A'}</div></div>`;
        return window.L.divIcon({ html: iconHtml, className: 'custom-div-icon', iconSize: [40, 40], iconAnchor: [20, 40] });
    };

    useEffect(() => {
        if (mapRef.current && !mapInstanceRef.current) {
            const map = window.L.map(mapRef.current, { zoomControl: false }).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
            window.L.control.zoom({ position: 'topleft' }).addTo(map);
            mapInstanceRef.current = map;
        }
    }, []);

    useEffect(() => {
        const map = mapInstanceRef.current;
        if (!map || !stations) return;
        Object.values(markersRef.current).forEach(marker => marker.remove());
        markersRef.current = {};
        Object.entries(stations).forEach(([id, station]) => {
            const { lat, lng } = station.station_info;
            const marker = window.L.marker([lat, lng], { icon: createAqiIcon(station.highest_sub_index) }).addTo(map);
            markersRef.current[id] = marker;
        });
    }, [stations]);
    
    useEffect(() => {
        const map = mapInstanceRef.current;
        if (map && userLocation) {
            map.setView([userLocation.lat, userLocation.lng], 14);
            if (userMarkerRef.current) {
                userMarkerRef.current.setLatLng([userLocation.lat, userLocation.lng]);
            } else {
                userMarkerRef.current = window.L.marker([userLocation.lat, userLocation.lng]).addTo(map);
            }
        }
    }, [userLocation]);

    return <div ref={mapRef} style={{ height: '100%', width: '100%', borderRadius: '12px' }}></div>;
};

const LazyChart = ({ forecastData, selectedParameter }) => {
    const chartData = useMemo(() => {
        if (!forecastData || forecastData.length === 0) return { labels: [], datasets: [] };
        const labels = forecastData.map(d => new Date(d.day).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
        const dataPoints = forecastData.map(d => d[`${selectedParameter}_max`]);
        return {
            labels,
            datasets: [{
                label: selectedParameter.toUpperCase(),
                data: dataPoints,
                borderColor: '#4f46e5',
                backgroundColor: 'rgba(79, 70, 229, 0.1)',
                fill: true,
                tension: 0.4,
            }]
        };
    }, [forecastData, selectedParameter]);
    const chartOptions = {
        responsive: true, maintainAspectRatio: false,
        plugins: { legend: { display: false } },
        scales: { x: { ticks: { color: '#64748b' } }, y: { ticks: { color: '#64748b' } } }
    };
    if (!forecastData || forecastData.length === 0) {
        return <div className="panel-loader"><p>No forecast data available.</p></div>;
    }
    return <Line options={chartOptions} data={chartData} />;
};

// --- Main Dashboard Component ---
function Dashboard() {
    const { user, logout } = useAuth();
    const [username] = useState(user?.name || 'User');
    const [selectedParameter, setSelectedParameter] = useState('pm25');
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationName, setUserLocationName] = useState('Detecting location...');
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';
    
    const startSmartLocationDetection = useCallback(async () => {
        setLoading(true);
        let locationName = "Default Location";
        const ipLocation = await getIPLocation();
        if (ipLocation) {
            setUserLocation(ipLocation);
            setUserLocationName(`${ipLocation.city}, ${ipLocation.region} (Approximate)`);
        }
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (pos) => {
                    const gpsLocation = { lat: pos.coords.latitude, lng: pos.coords.longitude };
                    setUserLocation(gpsLocation);
                    setUserLocationName("Your Precise Location");
                },
                () => { if (!ipLocation) setUserLocationName("Could not detect location"); }
            );
        }
        try {
            const url = `${API_BASE_URL}/api/dashboard/?username=${username}`;
            const response = await fetch(url);
            if (!response.ok) {
                const errData = await response.json();
                if (errData.redirect_to) navigate(errData.redirect_to);
                throw new Error(errData.error);
            }
            const data = await response.json();
            setDashboardData(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        startSmartLocationDetection();
    }, [startSmartLocationDetection]);

    useEffect(() => {
        if (userLocation && dashboardData?.stations) {
            const stations = Object.entries(dashboardData.stations);
            let nearestDist = Infinity, nearestId = null;

            stations.forEach(([id, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
            });
            setNearestStation({ id: nearestId, distance: nearestDist });
            
            let weightedSum = 0, weightSum = 0;
            stations.forEach(([, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
                weightedSum += (station.highest_sub_index || 0) * weight;
                weightSum += weight;
            });
            if (weightSum > 0) {
                setInterpolatedData({ aqi: Math.round(weightedSum / weightSum) });
            }
        }
    }, [userLocation, dashboardData]);

    const displayAqi = interpolatedData?.aqi || Object.values(dashboardData?.stations || {})[0]?.highest_sub_index || 0;
    const alertInfo = useMemo(() => {
        if (displayAqi <= 50) return { className: 'good', message: 'Air quality is good. Enjoy your day!' };
        if (displayAqi <= 100) return { className: 'moderate', message: 'Air quality is moderate. Sensitive individuals should take precautions.' };
        return { className: 'unhealthy', message: 'Air quality is unhealthy. It is advisable to reduce outdoor activities.' };
    }, [displayAqi]);

    if (loading) return <div className="panel-loader"><div className="loading-spinner"></div><h2>Loading Dashboard...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;
    if (!dashboardData) return <div>No data available.</div>;

    const { health_data, stations, forecasts } = dashboardData;
    const forecastForNearest = nearestStation ? forecasts[nearestStation.id] : null;
    const currentValues = nearestStation ? stations[nearestStation.id]?.averages : Object.values(stations)[0]?.averages;
    const pollutants = [
        { key: 'pm25', name: 'PM2.5' }, { key: 'pm10', name: 'PM10' }, { key: 'so2', name: 'SO₂' },
        { key: 'no2', name: 'NO₂' }, { key: 'co', name: 'CO' }, { key: 'o3', name: 'O₃' }, { key: 'nh3', name: 'NH₃' }
    ];

    return (
        <div className="dashboard-page">
            <nav className="navbar">{/* --- Your Navbar JSX --- */}</nav>
            <div className="main-container">
                <div className={`alert-banner ${alertInfo.className}`}>
                    <i className="fas fa-info-circle"></i>
                    <span>{alertInfo.message}</span>
                </div>
                <div className="welcome-header">
                    <div className="welcome-text">
                        <h1>Welcome, {username}!</h1>
                        <p>{userLocationName}</p>
                    </div>
                    <div className="aqi-overview">
                        <div className="aqi-value" style={{ color: getAQIColor(displayAqi) }}>{Math.round(displayAqi)}</div>
                        <div className="aqi-status">{getAQIStatus(displayAqi)}</div>
                    </div>
                </div>

                {/* ... other sections ... */}
                
                <div className="dashboard-grid">
                    <div className="dashboard-card">
                        <h3>Sensor Map</h3>
                        <Suspense fallback={<div className="panel-loader">Loading Map...</div>}>
                            <LazyMap userLocation={userLocation} stations={stations} />
                        </Suspense>
                    </div>
                    <div className="dashboard-card">
                        <h3>4-Day Forecast (Nearest Station)</h3>
                        {/* --- FIX: Parameter selector is now used --- */}
                        <select className="parameter-selector" value={selectedParameter} onChange={(e) => setSelectedParameter(e.target.value)}>
                            <option value="pm25">PM2.5</option>
                            <option value="pm10">PM10</option>
                            <option value="so2">SO₂</option>
                        </select>
                        <Suspense fallback={<div className="panel-loader">Loading Chart...</div>}>
                            <LazyChart forecastData={forecastForNearest} selectedParameter={selectedParameter} />
                        </Suspense>
                    </div>
                </div>
                
                <div className="health-section">
                    <div className="health-content">
                        <h3>Your Health Status</h3>
                        <p>Risk Level: <span style={{color: health_data.risk_level === 'Low' ? '#10b981' : '#ef4444', fontWeight: 'bold'}}>{health_data.risk_level}</span> (Score: {health_data.score})</p>
                        <h4>Key Recommendations:</h4>
                        <ul>
                            {health_data.recommendations.slice(0, 3).map((rec, index) => <li key={index}>{rec}</li>)}
                        </ul>
                    </div>
                    <div className="health-advisory">
                        <h3>General Health Advisory</h3>
                        <p>People with asthma, heart disease, or other respiratory conditions should follow health advisories closely and limit exposure during high AQI periods.</p>
                    </div>
                </div>
            </div>
            <footer className="footer">
                <div className="footer-container">
                    <div className="footer-grid">
                        <div className="footer-section"><h3>About AirAware</h3><p>Real-time air quality monitoring for a healthier Kerala.</p></div>
                        <div className="footer-section"><h3>Quick Links</h3><Link to="/" className="footer-link">Home</Link><Link to="/map" className="footer-link">Live Map</Link></div>
                        <div className="footer-section"><h3>Contact</h3><p>ASIET AI & IoT Lab</p></div>
                    </div>
                    <div className="footer-bottom"><p>&copy; 2025 AirAware. All rights reserved.</p></div>
                </div>
            </footer>
        </div>
    );
}

export default Dashboard;
