import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/FamilyPage.css'; // Create this new CSS file

function FamilyPage() {
    const { user, logout } = useAuth();
    const [username] = useState(user?.name || null);
    const [familyMembers, setFamilyMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    
    // Form state
    const [newName, setNewName] = useState('');
    const [newAge, setNewAge] = useState('');
    const [newRelationship, setNewRelationship] = useState('');
    const [formError, setFormError] = useState('');

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const fetchFamilyMembers = useCallback(async () => {
        if (!username) {
            navigate('/login');
            return;
        }
        setIsLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/api/family-members/?username=${username}`);
            if (!response.ok) throw new Error('Failed to fetch family members.');
            const data = await response.json();
            setFamilyMembers(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        fetchFamilyMembers();
    }, [fetchFamilyMembers]);

    const handleAddMember = async (e) => {
        e.preventDefault();
        setFormError('');
        if (!newName || !newAge || !newRelationship) {
            setFormError('All fields are required.');
            return;
        }
        try {
            const response = await fetch(`${API_BASE_URL}/api/family-members/`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    username: username,
                    name: newName,
                    age: newAge,
                    relationship: newRelationship
                })
            });
            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.error || 'Failed to add member.');
            }
            const newMember = await response.json();
            setFamilyMembers(prev => [...prev, newMember]);
            setIsModalOpen(false);
            setNewName(''); setNewAge(''); setNewRelationship('');
        } catch (err) {
            setFormError(err.message);
        }
    };

    if (isLoading) return <div className="panel-loader"><h2>Loading Family Members...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;

    return (
        <div className="family-page">
            <nav className="navbar">{/* --- Your Navbar JSX Here --- */}</nav>
            <div className="container">
                <div className="dashboard">
                    <section className="profile-card">
                        <div className="profile-avatar">{username ? username.charAt(0) : 'U'}</div>
                        <h2 className="profile-name">{username}</h2>
                        <div className="profile-stats">
                            <div className="stat">
                                <div className="stat-number">{familyMembers.length}</div>
                                <div className="stat-label">Family Members</div>
                            </div>
                        </div>
                    </section>
                    <section className="members-section">
                        <div className="section-header">
                            <h2 className="section-title">Family Members</h2>
                            <button className="add-btn" onClick={() => setIsModalOpen(true)}>
                                <span>+</span> Add Member
                            </button>
                        </div>
                        {familyMembers.length > 0 ? (
                            <div className="members-grid">
                                {familyMembers.map(member => (
                                    <div className="member-card" key={member.id}>
                                        <div className="member-avatar">{member.name.charAt(0)}</div>
                                        <div className="member-info">
                                            <div className="member-name">{member.name}</div>
                                            <div className="member-relation">{member.relationship}</div>
                                            <div className="member-age">{member.age} years old</div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="no-members">
                                <p>No family members added yet. Click "Add Member" to get started.</p>
                            </div>
                        )}
                    </section>
                </div>
            </div>

            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h3 className="modal-title">Add Family Member</h3>
                            <button className="close-btn" onClick={() => setIsModalOpen(false)}>&times;</button>
                        </div>
                        <form onSubmit={handleAddMember}>
                            {formError && <div className="form-error">{formError}</div>}
                            <div className="form-group">
                                <label htmlFor="name">Full Name</label>
                                <input type="text" value={newName} onChange={(e) => setNewName(e.target.value)} placeholder="Enter full name" />
                            </div>
                            <div className="form-group">
                                <label htmlFor="age">Age</label>
                                <input type="number" value={newAge} onChange={(e) => setNewAge(e.target.value)} placeholder="Enter age" />
                            </div>
                            <div className="form-group">
                                <label>Relationship</label>
                                <select value={newRelationship} onChange={(e) => setNewRelationship(e.target.value)}>
                                    <option value="">Select Relationship</option>
                                    <option value="Father">Father</option>
                                    <option value="Mother">Mother</option>
                                    <option value="Sibling">Sibling</option>
                                    <option value="Spouse">Spouse</option>
                                    <option value="Child">Child</option>
                                    <option value="Other">Other</option>
                                </select>
                            </div>
                            <button type="submit" className="btn">Add Member</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default FamilyPage;
