import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/FamilyPage.css';
import logoImage from '../assets/aqi.webp'; 

function FamilyPage() {
    const { user, logout } = useAuth();
    const [username] = useState(() => {
        try {
            const userData = JSON.parse(localStorage.getItem('user') || '{}');
            return userData.name || user?.name || null;
        } catch {
            return user?.name || null;
        }
    });
    
    const [familyMembers, setFamilyMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingMember, setEditingMember] = useState(null);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    
    // Unified form state for both adding and editing
    const [formState, setFormState] = useState({ name: '', age: '', relationship: '' });
    const [formError, setFormError] = useState('');
    const [formLoading, setFormLoading] = useState(false);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
    ? 'https://airaware-app-gcw7.onrender.com' 
    : 'http://localhost:8000';

    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

    const fetchFamilyMembers = useCallback(async () => {
        if (!username) {
            navigate('/login');
            return;
        }
        setIsLoading(true);
        setError(null);
        
        try {
            const response = await fetch(`${API_BASE_URL}/api/family-members/?username=${username}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: Failed to fetch family members`);
            }
            
            const data = await response.json();
            console.log('‚úÖ Family members fetched:', data);
            setFamilyMembers(data);
        } catch (err) {
            console.error('‚ùå Error fetching family members:', err);
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        const handleResize = () => {
            setIsMobileView(window.innerWidth <= 768);
        };
        window.addEventListener('resize', handleResize);
        return () => {
            window.removeEventListener('resize', handleResize);
        };
        }, []);
    useEffect(() => {
        fetchFamilyMembers();
    }, [fetchFamilyMembers]);

    // Event Handlers
    const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
    
    const handleLogout = useCallback(() => {
        localStorage.clear();
        navigate('/login');
    }, [navigate]);

    const handleAddMemberClick = () => {
        setEditingMember(null);
        setFormState({ name: '', age: '', relationship: '' });
        setFormError('');
        setIsModalOpen(true);
    };

    const handleEditMemberClick = (member) => {
        setEditingMember(member);
        setFormState({ 
            name: member.name, 
            age: member.age.toString(), 
            relationship: member.relationship 
        });
        setFormError('');
        setIsModalOpen(true);
    };

    const handleDeleteMember = async (memberId, memberName) => {
        if (window.confirm(`Are you sure you want to delete ${memberName} from your family list? This action cannot be undone.`)) {
            try {
                const response = await fetch(`${API_BASE_URL}/api/family-members/${memberId}/`, { 
                    method: 'DELETE',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                
                if (!response.ok) {
                    throw new Error('Failed to delete member');
                }
                
                setFamilyMembers(prev => prev.filter(m => m.id !== memberId));
                console.log('‚úÖ Family member deleted successfully');
            } catch (err) {
                console.error('‚ùå Error deleting member:', err);
                alert("Failed to delete member. Please try again.");
            }
        }
    };
    
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        setFormError('');
        setFormLoading(true);

        // Validation
        if (!formState.name.trim()) {
            setFormError('Name is required');
            setFormLoading(false);
            return;
        }
        
        if (!formState.age || parseInt(formState.age) < 0 || parseInt(formState.age) > 150) {
            setFormError('Please enter a valid age (0-150)');
            setFormLoading(false);
            return;
        }
        
        if (!formState.relationship) {
            setFormError('Please select a relationship');
            setFormLoading(false);
            return;
        }

        const url = editingMember 
            ? `${API_BASE_URL}/api/family-members/update/${editingMember.id}/`
            : `${API_BASE_URL}/api/family-members/`;
        
        const method = editingMember ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                headers: { 
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({ 
                    ...formState, 
                    age: parseInt(formState.age),
                    username 
                })
            });

            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.error || 'An unknown error occurred.');
            }

            const updatedMember = await response.json();

            if (editingMember) {
                setFamilyMembers(prev => prev.map(m => m.id === updatedMember.id ? updatedMember : m));
                console.log('‚úÖ Family member updated successfully');
            } else {
                setFamilyMembers(prev => [...prev, updatedMember]);
                console.log('‚úÖ Family member added successfully');
            }
            
            setIsModalOpen(false);
        } catch (err) {
            console.error('‚ùå Error saving family member:', err);
            setFormError(err.message);
        } finally {
            setFormLoading(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormState(prev => ({ ...prev, [name]: value }));
        // Clear error when user starts typing
        if (formError) setFormError('');
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
        setFormError('');
        setFormState({ name: '', age: '', relationship: '' });
        setEditingMember(null);
    };

    const getRelationshipIcon = (relationship) => {
        const icons = {
            'Father': 'üë®',
            'Mother': 'üë©',
            'Sibling': 'üë´',
            'Spouse': 'üíë',
            'Child': 'üë∂',
            'Grandparent': 'üë¥',
            'Other': 'üë§'
        };
        return icons[relationship] || 'üë§';
    };

    const getAgeCategory = (age) => {
        if (age < 13) return { category: 'Child', color: '#10b981', icon: 'üë∂' };
        if (age < 20) return { category: 'Teen', color: '#f59e0b', icon: 'üë¶' };
        if (age < 60) return { category: 'Adult', color: '#3b82f6', icon: 'üë®' };
        return { category: 'Senior', color: '#8b5cf6', icon: 'üë¥' };
    };

    if (isLoading) {
        return (
            <div className="family-page">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <h2>üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Loading Your Family...</h2>
                    <p>Fetching family member information</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="family-page">
                <div className="error-container">
                    <div className="error-icon">‚ö†Ô∏è</div>
                    <h2>Unable to Load Family Data</h2>
                    <p className="error-message">{error}</p>
                    <div className="error-actions">
                        <button onClick={fetchFamilyMembers} className="retry-btn">üîÑ Try Again</button>
                        <button onClick={() => navigate('/dashboard')} className="back-btn">üè† Back to Dashboard</button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="family-page">
            {/* Real-time Status */}
            <div className="realtime-status">
                üë®‚Äçüë©‚Äçüëß‚Äçüë¶ FAMILY MANAGEMENT ‚Ä¢ {familyMembers.length} Members ‚Ä¢ Health Monitoring Active
            </div>

            {/* Navigation */}
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                                {/* 2. USE THE IMPORTED VARIABLE */}
                                <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
                                AirAware
                              </Link>

                    <div className="menu-toggle" onClick={toggleMenu}>‚ò∞</div>

                    <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                        <li><Link to="/dashboard" className="nav-link">üè† Dashboard</Link></li>
                        
                        <li><Link to="/health-assessment" className="nav-link">üìã Health Update</Link></li>
                        <li><Link to="/health-report" className="nav-link">üìÑ Health Report</Link></li>
                        <li><Link to="/add-family" className="nav-link active">üë• Add Family</Link></li>
                        <li className="user-info">üë§ <span>{username}</span></li>
                        <li>
                            <button onClick={handleLogout} className="nav-link login-btn">üö™ Logout</button>
                        </li>
                    </ul>
                </div>
            </nav>

            {/* Main Container */}
            <div className="main-container">
                {/* Page Header */}
                <div className="page-header">
                    <div className="header-content">
                        <div className="header-text">
                            <h1>üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Family Health Management</h1>
                            <p className="subtitle">
                                Manage your family's health profiles and monitor air quality impact on each member
                            </p>
                            
                        </div>
                        <div className="header-actions">
                            <button className="add-member-btn primary" onClick={handleAddMemberClick}>
                                <span className="btn-icon">‚ûï</span>
                                <span>Add Family Member</span>
                            </button>
                            
                        </div>
                    </div>
                </div>

                {/* Dashboard Grid */}
                <div className="dashboard-grid">
                    {/* Profile Overview Card */}
                    <div className="dashboard-card profile-overview">
                        <div className="card-header">
                            <h3>üë§ Your Profile</h3>
                        </div>
                        <div className="profile-content">
                            <div className="profile-avatar-large">
                                {username ? username.charAt(0).toUpperCase() : 'U'}
                            </div>
                            <div className="profile-details">
                                <h2 className="profile-name">{username}</h2>
                                <div className="profile-role">Family Administrator</div>
                                <div className="profile-stats">
                                    <div className="profile-stat">
                                        <div className="stat-icon">üë•</div>
                                        <div className="stat-content">
                                            <div className="stat-value">{familyMembers.length}</div>
                                            <div className="stat-label">Total Members</div>
                                        </div>
                                    </div>
                                    <div className="profile-stat">
                                        <div className="stat-icon">üè•</div>
                                        <div className="stat-content">
                                            <div className="stat-value">
                                                {familyMembers.filter(m => m.age < 18 || m.age > 65).length}
                                            </div>
                                            <div className="stat-label">High Risk</div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Family Overview Card */}
                    <div className="dashboard-card family-overview">
                        <div className="card-header">
                            <h3>üìä Family Health Overview</h3>
                        </div>
                        <div className="overview-stats">
                            <div className="overview-stat">
                                <div className="stat-circle children">
                                    <div className="stat-number">
                                        {familyMembers.filter(m => m.age < 18).length}
                                    </div>
                                </div>
                                <div className="stat-info">
                                    <div className="stat-title">Children</div>
                                    <div className="stat-desc">Under 18 years</div>
                                </div>
                            </div>
                            <div className="overview-stat">
                                <div className="stat-circle adults">
                                    <div className="stat-number">
                                        {familyMembers.filter(m => m.age >= 18 && m.age < 65).length}
                                    </div>
                                </div>
                                <div className="stat-info">
                                    <div className="stat-title">Adults</div>
                                    <div className="stat-desc">18-64 years</div>
                                </div>
                            </div>
                            <div className="overview-stat">
                                <div className="stat-circle seniors">
                                    <div className="stat-number">
                                        {familyMembers.filter(m => m.age >= 65).length}
                                    </div>
                                </div>
                                <div className="stat-info">
                                    <div className="stat-title">Seniors</div>
                                    <div className="stat-desc">65+ years</div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Family Members Section */}
                <div className="members-section">
                    <div className="section-header">
                        <h2 className="section-title">üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Family Members</h2>
                        <div className="section-actions">
                            
                        </div>
                    </div>

                    {familyMembers.length > 0 ? (
                        <div className="members-grid">
                            {familyMembers.map(member => {
                                const ageCategory = getAgeCategory(member.age);
                                return (
                                    <div className="member-card" key={member.id}>
                                        <div className="member-card-header">
                                            <div className="member-avatar">
                                                {getRelationshipIcon(member.relationship)}
                                            </div>
                                            <div className="member-status online"></div>
                                        </div>
                                        <div className="member-info">
                                            <h3 className="member-name">{member.name}</h3>
                                            <div className="member-details">
                                                <div className="detail-item">
                                                    <span className="detail-icon">üë§</span>
                                                    <span className="detail-text">{member.relationship}</span>
                                                </div>
                                                <div className="detail-item">
                                                    <span className="detail-icon">{ageCategory.icon}</span>
                                                    <span className="detail-text">{member.age} years ‚Ä¢ {ageCategory.category}</span>
                                                </div>
                                                <div className="detail-item">
                                                    <span className="detail-icon">üè•</span>
                                                    <span className={`health-status ${(member.age < 18 || member.age > 65) ? 'high-risk' : 'normal'}`}>
                                                        {(member.age < 18 || member.age > 65) ? 'High Risk' : 'Normal Risk'}
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="member-actions">
                                            <Link 
                                                to={`/health-assessment?username=${member.name}`} 
                                                className="action-btn health-btn"
                                                title="View Health Assessment"
                                            >
                                                <span className="btn-icon">üè•</span>
                                                <span>Health Info</span>
                                            </Link>
                                            <button 
                                                className="action-btn edit-btn" 
                                                onClick={() => handleEditMemberClick(member)}
                                                title="Edit Member Details"
                                            >
                                                <span className="btn-icon">‚úèÔ∏è</span>
                                                <span>Edit</span>
                                            </button>
                                            <button 
                                                className="action-btn delete-btn" 
                                                onClick={() => handleDeleteMember(member.id, member.name)}
                                                title="Remove Member"
                                            >
                                                <span className="btn-icon">üóëÔ∏è</span>
                                                <span>Delete</span>
                                            </button>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    ) : (
                        <div className="no-members">
                            <div className="no-members-icon">üë®‚Äçüë©‚Äçüëß‚Äçüë¶</div>
                            <h3>No Family Members Added Yet</h3>
                            <p>Start building your family health profile by adding family members</p>
                            <button className="add-first-member-btn" onClick={handleAddMemberClick}>
                                <span className="btn-icon">‚ûï</span>
                                <span>Add Your First Family Member</span>
                            </button>
                        </div>
                    )}
                </div>

                {/* Health Tips Section */}
                <div className="health-tips-section">
                    <h2 className="section-title">üí° Family Health Tips</h2>
                    <div className="tips-grid">
                        <div className="tip-card">
                            <div className="tip-icon">üë∂</div>
                            <div className="tip-content">
                                <h4>Children & Air Quality</h4>
                                <p>Children are more vulnerable to air pollution. Monitor AQI closely and limit outdoor activities when levels are high.</p>
                            </div>
                        </div>
                        <div className="tip-card">
                            <div className="tip-icon">üë¥</div>
                            <div className="tip-content">
                                <h4>Senior Care</h4>
                                <p>Elderly family members should avoid outdoor activities when AQI exceeds 100 and use air purifiers indoors.</p>
                            </div>
                        </div>
                        <div className="tip-card">
                            <div className="tip-icon">üè•</div>
                            <div className="tip-content">
                                <h4>Health Monitoring</h4>
                                <p>Regular health assessments help track how air quality affects each family member differently.</p>
                            </div>
                        </div>
                        <div className="tip-card">
                            <div className="tip-icon">üè†</div>
                            <div className="tip-content">
                                <h4>Indoor Air Quality</h4>
                                <p>Keep indoor plants, use air purifiers, and ensure good ventilation to protect your family's health.</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Enhanced Modal */}
            {isModalOpen && (
                <div className="modal-overlay" onClick={handleModalClose}>
                    <div className="modal" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <div className="modal-title-section">
                                <h3 className="modal-title">
                                    {editingMember ? '‚úèÔ∏è Edit Family Member' : '‚ûï Add Family Member'}
                                </h3>
                                <p className="modal-subtitle">
                                    {editingMember 
                                        ? 'Update the information for this family member'
                                        : 'Add a new family member to monitor their health'
                                    }
                                </p>
                            </div>
                            <button className="close-btn" onClick={handleModalClose}>‚úï</button>
                        </div>
                        
                        <form onSubmit={handleFormSubmit} className="modal-form">
                            {formError && (
                                <div className="form-error">
                                    <span className="error-icon">‚ö†Ô∏è</span>
                                    <span>{formError}</span>
                                </div>
                            )}
                            
                            <div className="form-group">
                                <label htmlFor="name">
                                    <span className="label-icon">üë§</span>
                                    Full Name *
                                </label>
                                <input 
                                    type="text" 
                                    name="name" 
                                    value={formState.name} 
                                    onChange={handleInputChange} 
                                    placeholder="Enter full name" 
                                    required 
                                    className="form-input"
                                />
                            </div>
                            
                            <div className="form-group">
                                <label htmlFor="age">
                                    <span className="label-icon">üéÇ</span>
                                    Age *
                                </label>
                                <input 
                                    type="number" 
                                    name="age" 
                                    value={formState.age} 
                                    onChange={handleInputChange} 
                                    placeholder="Enter age" 
                                    min="0" 
                                    max="150"
                                    required 
                                    className="form-input"
                                />
                            </div>
                            
                            <div className="form-group">
                                <label htmlFor="relationship">
                                    <span className="label-icon">üë®‚Äçüë©‚Äçüëß‚Äçüë¶</span>
                                    Relationship *
                                </label>
                                <select 
                                    name="relationship" 
                                    value={formState.relationship} 
                                    onChange={handleInputChange} 
                                    required 
                                    className="form-select"
                                >
                                    <option value="">Select Relationship</option>
                                    <option value="Father">üë® Father</option>
                                    <option value="Mother">üë© Mother</option>
                                    <option value="Sibling">üë´ Sibling</option>
                                    <option value="Spouse">üíë Spouse</option>
                                    <option value="Child">üë∂ Child</option>
                                    <option value="Grandparent">üë¥ Grandparent</option>
                                    <option value="Other">üë§ Other</option>
                                </select>
                            </div>
                            
                            <div className="form-actions">
                                <button 
                                    type="button" 
                                    className="btn-cancel" 
                                    onClick={handleModalClose}
                                    disabled={formLoading}
                                >
                                    Cancel
                                </button>
                                <button 
                                    type="submit" 
                                    className="btn-submit" 
                                    disabled={formLoading}
                                >
                                    {formLoading ? (
                                        <>
                                            <div className="btn-spinner"></div>
                                            <span>Saving...</span>
                                        </>
                                    ) : (
                                        <>
                                            <span className="btn-icon">
                                                {editingMember ? 'üíæ' : '‚ûï'}
                                            </span>
                                            <span>
                                                {editingMember ? 'Save Changes' : 'Add Member'}
                                            </span>
                                        </>
                                    )}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Footer */}
            <footer className="footer">
                <div className="footer-container">
                    <div className="footer-content">
                        <div className="footer-section">
                            <h4>AirAware Kerala</h4>
                            <p>Family Health Management System</p>
                            <p>Real-time air quality monitoring ‚Ä¢ Family health tracking ‚Ä¢ Government approved</p>
                            <div className="social-links">
                                <a href="#" className="social-link">üìò</a>
                                <a href="#" className="social-link">üê¶</a>
                                <a href="#" className="social-link">üíº</a>
                                <a href="#" className="social-link">üì∑</a>
                            </div>
                        </div>
                        <div className="footer-section">
                            <h4>Quick Links</h4>
                            <ul>
                                <li><Link to="/dashboard">üè† Dashboard</Link></li>
                              
                                <li><Link to="/health-assessment">üìã Health Assessment</Link></li>
                                <li><Link to="/health-report">üìÑ Health Report</Link></li>
                            </ul>
                        </div>
                        <div className="footer-section">
                            <h4>Family Health Features</h4>
                            <ul>
                                <li>Individual health assessments</li>
                                <li>Age-specific recommendations</li>
                                <li>Vulnerable group monitoring</li>
                                <li>Real-time air quality alerts</li>
                            </ul>
                        </div>
                        <div className="footer-section">
                            <h4>Support & Contact</h4>
                            <p>
                                Adi Shankara Institute of Engineering and Technology<br/>
                                Kalady 683574, Ernakulam<br/>
                                Kerala, India
                            </p>
                            <p>
                                <strong>Emergency:</strong> 0471-2418566<br/>
                                <strong>Email:</strong> aiiot@adishankara.ac.in<br/>
                                <strong>Phone:</strong> 0484 246 3825
                            </p>
                        </div>
                    </div>
                    <div className="footer-bottom">
                        <p>&copy; 2025 AirAware Kerala - Complete Family Health Monitoring</p>
                        <p>Protecting every family member ‚Ä¢ Powered by real sensor data ‚Ä¢ Government standards</p>
                    </div>
                </div>
            </footer>
        </div>
    );
}

export default FamilyPage;
