// Fixed App.js
import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import Login from './components/Login';
import Signup from './components/Signup';
import HealthAssessment from './components/HealthAssessment';
import Dashboard from './components/Dashboard';
import MapPage from './components/MapPage';
import HealthReport from './components/HealthReport';
import FamilyPage from './components/FamilyPage';
import AdminDashboard from './components/AdminDashboard';
import AdminLogin from './components/AdminLogin';

// Fast Session Manager - Optimized for speed
const SessionManager = {
  SESSION_DURATION: 60 * 60 * 1000, // 1 hour

  isLoggedIn: () => {
    try {
      const user = localStorage.getItem('user');
      const loginTime = localStorage.getItem('loginTime');

      if (!user || !loginTime) return false;

      const sessionAge = Date.now() - parseInt(loginTime);
      if (sessionAge > SessionManager.SESSION_DURATION) {
        SessionManager.logout();
        return false;
      }

      return true;
    } catch {
      return false;
    }
  },

  getCurrentUser: () => {
    if (!SessionManager.isLoggedIn()) return null;

    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch {
      SessionManager.logout();
      return null;
    }
  },

  login: (userData) => {
    try {
      localStorage.setItem('user', JSON.stringify(userData));
      localStorage.setItem('loginTime', Date.now().toString());
      SessionManager.setAutoLogout();
    } catch (error) {
      console.error('Login failed:', error);
    }
  },

  logout: () => {
    try {
      localStorage.removeItem('user');
      localStorage.removeItem('loginTime');
      localStorage.removeItem('userLocation');
    } catch (error) {
      console.error('Logout error:', error);
    }

    if (window.sessionTimeout) {
      clearTimeout(window.sessionTimeout);
    }

    // Only redirect if not already on home page
    if (window.location.pathname !== '/') {
      window.location.href = '/';
    }
  },

  setAutoLogout: () => {
    if (window.sessionTimeout) {
      clearTimeout(window.sessionTimeout);
    }

    window.sessionTimeout = setTimeout(() => {
      alert('Session expired. Please login again.');
      SessionManager.logout();
    }, SessionManager.SESSION_DURATION);
  }
};

// Auth Context
const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  if (!SessionManager.isLoggedIn()) {
    return <Navigate to="/login" replace />;
  }
  return children;
};

// Loading Component
const LoadingScreen = () => {
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backgroundColor: '#f8fafc',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        width: '40px',
        height: '40px',
        border: '3px solid #e5e7eb',
        borderTop: '3px solid #3b82f6',
        borderRadius: '50%',
        animation: 'spin 1s linear infinite',
        marginBottom: '16px'
      }}></div>
      <p style={{ color: '#6b7280', fontSize: '14px' }}>Loading AirAware...</p>
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fast initialization with error handling
    const initializeAuth = () => {
      try {
        const currentUser = SessionManager.getCurrentUser();
        setUser(currentUser);

        if (currentUser) {
          SessionManager.setAutoLogout();
        }
      } catch (error) {
        console.error('Auth initialization error:', error);
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    // Use setTimeout to prevent blocking
    const timer = setTimeout(initializeAuth, 100);

    // Lightweight activity listener
    const handleActivity = () => {
      if (SessionManager.isLoggedIn()) {
        SessionManager.setAutoLogout();
      }
    };

    // Throttled activity detection
    let activityTimer;
    const throttledActivity = () => {
      if (activityTimer) return;
      activityTimer = setTimeout(() => {
        handleActivity();
        activityTimer = null;
      }, 30000); // Check every 30 seconds
    };

    document.addEventListener('click', throttledActivity, { passive: true });
    document.addEventListener('keydown', throttledActivity, { passive: true });

    return () => {
      clearTimeout(timer);
      document.removeEventListener('click', throttledActivity);
      document.removeEventListener('keydown', throttledActivity);
      if (activityTimer) clearTimeout(activityTimer);
    };
  }, []);

  const authValue = {
    user,
    login: (userData) => {
      try {
        SessionManager.login(userData);
        setUser(userData);
      } catch (error) {
        console.error('Login error:', error);
      }
    },
    logout: () => {
      try {
        SessionManager.logout();
        setUser(null);
      } catch (error) {
        console.error('Logout error:', error);
      }
    },
    isLoggedIn: () => SessionManager.isLoggedIn()
  };

  // Show loading screen while initializing
  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <AuthContext.Provider value={authValue}>
      <Router>
        <div className="App">
          <Routes>
            <Route path="/" element={<HomePage />} />

            {/* Authentication Routes */}
            <Route 
              path="/login" 
              element={user ? <Navigate to="/dashboard" replace /> : <Login />} 
            />
            <Route 
              path="/signup" 
              element={user ? <Navigate to="/dashboard" replace /> : <Signup />} 
            />

            {/* Protected Routes */}
            <Route 
              path="/health-assessment" 
              element={
                <ProtectedRoute>
                  <HealthAssessment />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/dashboard" 
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } 
            />
            <Route path="/health-report" element={<ProtectedRoute><HealthReport /></ProtectedRoute>} />

            {/* Public Map Route - No login required */}
            <Route path="/map" element={<MapPage />} />

            {/* Catch all route */}
            <Route path="*" element={<Navigate to="/" replace />} />
            <Route path="/admin-dashboard" element={<ProtectedRoute><AdminDashboard /></ProtectedRoute>} />
            <Route path="/add-family" element={<ProtectedRoute><FamilyPage /></ProtectedRoute>} />

          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
