import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { useAuth } from '../App';
import './css/MapPage.css';

// Helper function to get AQI color
const getAQIColor = (aqi) => {
  if (aqi === null || aqi === undefined) return '#6b7280';
  if (aqi <= 50) return '#10b981';
  if (aqi <= 100) return '#f59e0b';
  if (aqi <= 150) return '#ef4444';
  if (aqi <= 200) return '#dc2626';
  if (aqi <= 300) return '#7c2d12';
  return '#4c1d95';
};

// REAL-WORLD FALLBACK DATA - This ensures your app works even when backend is down
const FALLBACK_STATIONS = {
  'lora-v1': {
    id: 'lora-v1',
    station_info: {
      name: 'Station 1 (ASIET Campus)',
      lat: 10.178322,
      lng: 76.430891
    },
    highest_sub_index: 85,
    aqi_status: 'MODERATE',
    averages: {
      pm25: 42,
      pm10: 78,
      so2: 12,
      no2: 28,
      co: 1.2,
      o3: 65,
      nh3: 15
    },
    forecast_data: [
      { day: 'Tomorrow', pm25_max: 45, pm10_max: 80, so2_max: 15, no2_max: 30, co_max: 1.5, o3_max: 70, nh3_max: 18 },
      { day: 'Wed, Jul 24', pm25_max: 38, pm10_max: 72, so2_max: 12, no2_max: 25, co_max: 1.1, o3_max: 62, nh3_max: 14 },
      { day: 'Thu, Jul 25', pm25_max: 52, pm10_max: 95, so2_max: 18, no2_max: 35, co_max: 1.8, o3_max: 78, nh3_max: 22 },
      { day: 'Fri, Jul 26', pm25_max: 41, pm10_max: 76, so2_max: 14, no2_max: 28, co_max: 1.3, o3_max: 68, nh3_max: 16 }
    ],
    latest_item: {
      date: '2025-07-19',
      time: '14:30:00',
      received_at: '2025-07-19 14:30:00'
    }
  },
  'loradev2': {
    id: 'loradev2',
    station_info: {
      name: 'Station 2 (Mattoor Junction)',
      lat: 10.170950,
      lng: 76.429628
    },
    highest_sub_index: 102,
    aqi_status: 'UNHEALTHY',
    averages: {
      pm25: 58,
      pm10: 95,
      so2: 18,
      no2: 35,
      co: 1.8,
      o3: 78,
      nh3: 22
    },
    forecast_data: [
      { day: 'Tomorrow', pm25_max: 60, pm10_max: 98, so2_max: 20, no2_max: 38, co_max: 2.0, o3_max: 82, nh3_max: 25 },
      { day: 'Wed, Jul 24', pm25_max: 55, pm10_max: 88, so2_max: 16, no2_max: 32, co_max: 1.6, o3_max: 75, nh3_max: 20 },
      { day: 'Thu, Jul 25', pm25_max: 65, pm10_max: 105, so2_max: 22, no2_max: 40, co_max: 2.2, o3_max: 85, nh3_max: 28 },
      { day: 'Fri, Jul 26', pm25_max: 52, pm10_max: 92, so2_max: 17, no2_max: 33, co_max: 1.7, o3_max: 76, nh3_max: 21 }
    ],
    latest_item: {
      date: '2025-07-19',
      time: '14:30:00',
      received_at: '2025-07-19 14:30:00'
    }
  }
};

// Enhanced Navbar Component
const MapNavbar = ({ user, logout, onMenuToggle, isMenuOpen }) => {
  const navigate = useNavigate();

  const handleLogout = React.useCallback(() => {
    logout();
    navigate('/');
  }, [logout, navigate]);

  const handleDashboardNavigation = React.useCallback(() => {
    if (user) {
      navigate('/dashboard');
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  return (
    <nav className="map-navbar">
      <div className="navbar-content">
        <Link to="/" className="navbar-brand">
          <img 
            src="/aqi.webp" 
            alt="AQM Logo" 
            loading="lazy" 
            width="32" 
            height="32"
          />
          <span>AirAware</span>
        </Link>

        <div className="menu-toggle" onClick={onMenuToggle}>
          <i className="fas fa-bars"></i>
        </div>

        <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
          <li><Link to="/" className="nav-link">Home</Link></li>
          <li><Link to="/map" className="nav-link active">Live Map</Link></li>
          
          {user ? (
            <>
              <li>
                <button onClick={handleDashboardNavigation} className="nav-link nav-button">
                  <i className="fas fa-chart-bar"></i> Dashboard
                </button>
              </li>
              <li className="nav-link user-greeting">
                <i className="fas fa-user-circle"></i> {user.name}
              </li>
              <li>
                <button onClick={handleLogout} className="nav-link logout-btn nav-button">
                  <i className="fas fa-sign-out-alt"></i> Logout
                </button>
              </li>
            </>
          ) : (
            <li><Link to="/login" className="nav-link login-btn">
              <i className="fas fa-sign-in-alt"></i> Login
            </Link></li>
          )}
        </ul>
      </div>
    </nav>
  );
};

// Enhanced Loading Component with Progress
const MapLoadingOverlay = ({ progress, message }) => (
  <div className="map-loading-overlay">
    <div className="loading-content">
      <div className="loading-spinner-enhanced">
        <div className="spinner-ring"></div>
        <div className="spinner-ring"></div>
        <div className="spinner-ring"></div>
      </div>
      <div className="loading-text">
        <h3>Loading Air Quality Data</h3>
        <p>{message || 'Fetching real-time monitoring stations...'}</p>
        <div className="loading-progress">
          <div className="progress-bar" style={{ width: `${progress}%` }}></div>
        </div>
        <small>{progress}% Complete</small>
      </div>
    </div>
  </div>
);

// Enhanced Error Component with Offline Mode
const ErrorDisplay = ({ error, onRetry, onOfflineMode, isUsingFallback }) => (
  <div className="error-display">
    <div className="error-content">
      <i className={`fas ${isUsingFallback ? 'fa-wifi' : 'fa-exclamation-triangle'} error-icon`}></i>
      <h3>{isUsingFallback ? 'Offline Mode' : 'Connection Error'}</h3>
      <p>{isUsingFallback ? 'Using cached data. Some information may not be current.' : error}</p>
      <div className="error-actions">
        {!isUsingFallback && (
          <>
            <button onClick={onRetry} className="retry-button">
              <i className="fas fa-refresh"></i> Retry Connection
            </button>
            <button onClick={onOfflineMode} className="offline-button">
              <i className="fas fa-wifi"></i> Use Offline Mode
            </button>
          </>
        )}
        {isUsingFallback && (
          <button onClick={onRetry} className="retry-button">
            <i className="fas fa-cloud"></i> Reconnect
          </button>
        )}
      </div>
    </div>
  </div>
);

// Connection Status Component
const ConnectionStatus = ({ isOnline, isUsingFallback, lastUpdate }) => (
  <div className={`connection-status ${isOnline ? 'online' : 'offline'}`}>
    <div className="status-indicator">
      <i className={`fas ${isOnline ? 'fa-wifi' : 'fa-wifi-slash'}`}></i>
      <span>{isOnline ? 'Live Data' : (isUsingFallback ? 'Offline Mode' : 'Connecting...')}</span>
    </div>
    {lastUpdate && (
      <small>Last updated: {lastUpdate}</small>
    )}
  </div>
);

// Main Component
const MapPage = () => {
  const [map, setMap] = React.useState(null);
  const [stations, setStations] = React.useState({});
  const [selectedStationId, setSelectedStationId] = React.useState('lora-v1');
  const [isLoading, setIsLoading] = React.useState(true);
  const [error, setError] = React.useState(null);
  const [selectedParameter, setSelectedParameter] = React.useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);
  const [retryCount, setRetryCount] = React.useState(0);
  const [isUsingFallback, setIsUsingFallback] = React.useState(false);
  const [loadingProgress, setLoadingProgress] = React.useState(0);
  const [loadingMessage, setLoadingMessage] = React.useState('Initializing...');
  const [lastUpdate, setLastUpdate] = React.useState(null);
  const [isOnline, setIsOnline] = React.useState(navigator.onLine);

  const mapRef = React.useRef(null);
  const markersRef = React.useRef({});
  const leafletLoadedRef = React.useRef(false);
  const { user, logout } = useAuth();
  
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Monitor online status
  React.useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Enhanced Leaflet loading
  const loadLeaflet = React.useCallback(() => {
    return new Promise((resolve, reject) => {
      if (window.L && leafletLoadedRef.current) {
        resolve(window.L);
        return;
      }

      if (leafletLoadedRef.current) {
        const checkLoaded = setInterval(() => {
          if (window.L) {
            clearInterval(checkLoaded);
            resolve(window.L);
          }
        }, 100);
        setTimeout(() => {
          clearInterval(checkLoaded);
          reject(new Error("Leaflet loading timeout"));
        }, 10000);
        return;
      }

      leafletLoadedRef.current = true;
      setLoadingMessage('Loading map library...');
      setLoadingProgress(20);

      const link = document.createElement('link');
      link.rel = "stylesheet";
      link.href = "https://unpkg.com/leaflet@1.7.1/dist/leaflet.css";
      document.head.appendChild(link);

      const script = document.createElement('script');
      script.src = "https://unpkg.com/leaflet@1.7.1/dist/leaflet.js";
      script.async = true;
      document.head.appendChild(script);

      script.onload = () => {
        setLoadingProgress(50);
        resolve(window.L);
      };
      script.onerror = () => {
        leafletLoadedRef.current = false;
        reject(new Error("Failed to load map library"));
      };
    });
  }, []);

  // Initialize map
  React.useEffect(() => {
    let isMounted = true;

    const initialize = async () => {
      try {
        setLoadingMessage('Initializing map...');
        setLoadingProgress(30);

        const L = await loadLeaflet();
        
        if (isMounted && mapRef.current && !mapRef.current._leaflet_id) {
          setLoadingProgress(60);
          setLoadingMessage('Setting up map view...');
          
          const mapInstance = L.map(mapRef.current, {
            preferCanvas: true,
            zoomControl: true,
            attributionControl: false
          }).setView([10.176, 76.430], 15);
          
          L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            maxZoom: 18,
            attribution: '© OpenStreetMap contributors'
          }).addTo(mapInstance);
          
          setMap(mapInstance);
          setLoadingProgress(80);
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setIsLoading(false);
        }
      }
    };

    initialize();
    
    return () => { 
      isMounted = false; 
    };
  }, [loadLeaflet]);

  // Enhanced data fetching with smart fallback
  React.useEffect(() => {
    if (!map) return;

    let isMounted = true;
    let timeoutId;

    const fetchData = async () => {
      try {
        setError(null);
        setLoadingMessage('Fetching sensor data...');
        setLoadingProgress(85);

        // Create abort controller for timeout
        const controller = new AbortController();
        timeoutId = setTimeout(() => controller.abort(), 5000); // 5 second timeout

        console.log(`Attempting to fetch from: ${API_BASE_URL}/api/map/`);

        const response = await fetch(`${API_BASE_URL}/api/map/`, {
          signal: controller.signal,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          mode: 'cors'
        });

        clearTimeout(timeoutId);

        if (!response.ok) {
          throw new Error(`Server responded with ${response.status}: ${response.statusText}`);
        }
        
        const data = await response.json();
        console.log('API Response:', data);
        
        if (data && data.stations && isMounted) {
          setStations(data.stations);
          setIsLoading(false);
          setIsUsingFallback(false);
          setLoadingProgress(100);
          setLastUpdate(new Date().toLocaleTimeString());
          setRetryCount(0);
          
          // Store successful data in localStorage for future fallback
          try {
            localStorage.setItem('airaware_last_data', JSON.stringify({
              stations: data.stations,
              timestamp: Date.now()
            }));
          } catch (e) {
            console.warn('Could not save to localStorage:', e);
          }
        } else {
          throw new Error('Invalid data structure received from server');
        }
      } catch (err) {
        clearTimeout(timeoutId);
        console.error('Fetch error:', err);
        
        if (isMounted) {
          if (err.name === 'AbortError') {
            setError('Connection timeout - server took too long to respond');
          } else if (err.message.includes('Failed to fetch')) {
            setError('Cannot connect to server. Please check if the server is running and accessible.');
          } else {
            setError(err.message);
          }

          // Try to use cached data from localStorage
          try {
            const cachedData = localStorage.getItem('airaware_last_data');
            if (cachedData) {
              const parsed = JSON.parse(cachedData);
              const cacheAge = Date.now() - parsed.timestamp;
              
              if (cacheAge < 24 * 60 * 60 * 1000) { // Less than 24 hours old
                console.log('Using cached data from localStorage');
                setStations(parsed.stations);
                setIsUsingFallback(true);
                setIsLoading(false);
                setLoadingProgress(100);
                setLastUpdate(new Date(parsed.timestamp).toLocaleTimeString());
                return;
              }
            }
          } catch (e) {
            console.warn('Could not load cached data:', e);
          }

          // If no cached data, use built-in fallback
          console.log('Using built-in fallback data');
          setStations(FALLBACK_STATIONS);
          setIsUsingFallback(true);
          setIsLoading(false);
          setLoadingProgress(100);
          setLastUpdate('Offline data');
        }
      }
    };

    fetchData();
    
    // Set up polling only if online and not using fallback
    let interval;
    if (isOnline && !isUsingFallback) {
      interval = setInterval(() => {
        if (!isUsingFallback) {
          fetchData();
        }
      }, 60000); // 1 minute
    }

    return () => {
      isMounted = false;
      if (interval) clearInterval(interval);
      if (timeoutId) clearTimeout(timeoutId);
    };
  }, [map, API_BASE_URL, retryCount, isOnline, isUsingFallback]);

  // Enhanced marker updates
  React.useEffect(() => {
    if (!map || Object.keys(stations).length === 0) return;

    const updateMarkers = () => {
      Object.entries(stations).forEach(([id, station]) => {
        if (!station?.station_info?.lat || !station?.station_info?.lng) return;

        const { lat, lng } = station.station_info;
        const aqi = station.highest_sub_index;
        const status = station.aqi_status || 'Unknown';
        
        const popupContent = `
          <div class="marker-popup">
            <h4>${station.station_info.name}</h4>
            <p><strong>AQI:</strong> <span style="color: ${getAQIColor(aqi)}">${aqi ? Math.round(aqi) : 'N/A'}</span></p>
            <p><strong>Status:</strong> ${status}</p>
            <small>Click to view details</small>
            ${isUsingFallback ? '<br><small style="color: #f59e0b;">⚠️ Offline data</small>' : ''}
          </div>
        `;

        if (markersRef.current[id]) {
          markersRef.current[id]
            .setLatLng([lat, lng])
            .setPopupContent(popupContent)
            .setStyle({ fillColor: getAQIColor(aqi) });
        } else {
          const marker = window.L.circleMarker([lat, lng], {
            radius: 12,
            fillColor: getAQIColor(aqi),
            color: '#ffffff',
            weight: 2,
            opacity: 1,
            fillOpacity: 0.9
          }).addTo(map);
          
          marker.bindPopup(popupContent);
          marker.on('click', () => setSelectedStationId(id));
          markersRef.current[id] = marker;
        }
      });
    };

    const timeoutId = setTimeout(updateMarkers, 100);
    return () => clearTimeout(timeoutId);
  }, [map, stations, isUsingFallback]);

  // Event handlers
  const handleRetry = React.useCallback(() => {
    setRetryCount(prev => prev + 1);
    setIsLoading(true);
    setError(null);
    setIsUsingFallback(false);
    setLoadingProgress(0);
  }, []);

  const handleOfflineMode = React.useCallback(() => {
    setStations(FALLBACK_STATIONS);
    setIsUsingFallback(true);
    setIsLoading(false);
    setError(null);
    setLoadingProgress(100);
    setLastUpdate('Offline data');
  }, []);

  const toggleMenu = React.useCallback(() => {
    setIsMenuOpen(prev => !prev);
  }, []);

  // Memoized chart data
  const forecastChartData = React.useMemo(() => ({
    labels: stations[selectedStationId]?.forecast_data?.map(d => d.day) || [],
    datasets: [{
      label: `${selectedParameter.toUpperCase()} Forecast`,
      data: stations[selectedStationId]?.forecast_data?.map(d => d[`${selectedParameter}_max`]) || [],
      borderColor: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      tension: 0.4,
      pointBackgroundColor: '#3b82f6',
      pointBorderColor: '#ffffff',
      pointBorderWidth: 2,
      pointRadius: 4
    }]
  }), [stations, selectedStationId, selectedParameter]);

  const parameterButtons = React.useMemo(() => [
    { key: 'pm25', label: 'PM2.5', icon: 'fas fa-smog' },
    { key: 'pm10', label: 'PM10', icon: 'fas fa-cloud' },
    { key: 'nh3', label: 'NH3', icon: 'fas fa-flask' },
    { key: 'so2', label: 'SO2', icon: 'fas fa-atom' },
    { key: 'no2', label: 'NO2', icon: 'fas fa-industry' },
    { key: 'co', label: 'CO', icon: 'fas fa-car' },
    { key: 'o3', label: 'O3', icon: 'fas fa-sun' }
  ], []);

  const selectedStationData = stations[selectedStationId];

  return (
    <div className="map-page-enhanced">
      <MapNavbar 
        user={user} 
        logout={logout} 
        onMenuToggle={toggleMenu} 
        isMenuOpen={isMenuOpen} 
      />
      
      <ConnectionStatus 
        isOnline={isOnline}
        isUsingFallback={isUsingFallback}
        lastUpdate={lastUpdate}
      />
      
      <div className="map-container-enhanced">
        <div id="map" ref={mapRef} className="map-element"></div>
        
        {isLoading && (
          <MapLoadingOverlay 
            progress={loadingProgress} 
            message={loadingMessage}
          />
        )}
        
        {error && !isLoading && !isUsingFallback && (
          <ErrorDisplay 
            error={error} 
            onRetry={handleRetry} 
            onOfflineMode={handleOfflineMode}
            isUsingFallback={false}
          />
        )}
        
        <div className="map-controls">
          <button 
            className="map-control-btn"
            onClick={() => map && map.setView([10.176, 76.430], 15)}
            title="Reset View"
          >
            <i className="fas fa-home"></i>
          </button>
          {isUsingFallback && (
            <button 
              className="map-control-btn reconnect-btn"
              onClick={handleRetry}
              title="Try to Reconnect"
            >
              <i className="fas fa-cloud"></i>
            </button>
          )}
        </div>
      </div>

      <div className={`details-panel-enhanced ${selectedStationData ? 'open' : ''}`}>
        <div className="panel-header-enhanced">
          <h2 className="panel-title">
            <i className="fas fa-map-marker-alt"></i>
            Monitoring Stations
          </h2>
          
          <div className="station-selector-enhanced">
            {isLoading ? (
              <div className="station-skeleton">
                <div className="skeleton-item"></div>
                <div className="skeleton-item"></div>
              </div>
            ) : (
              Object.keys(stations).map(stationId => (
                <button
                  key={stationId}
                  className={`station-btn-enhanced ${selectedStationId === stationId ? 'active' : ''}`}
                  onClick={() => setSelectedStationId(stationId)}
                >
                  <div className="station-info">
                    <span className="station-name">
                      {stations[stationId]?.station_info?.name || stationId}
                    </span>
                    <span className="station-aqi" style={{ 
                      color: getAQIColor(stations[stationId]?.highest_sub_index) 
                    }}>
                      AQI: {Math.round(stations[stationId]?.highest_sub_index) || 'N/A'}
                    </span>
                  </div>
                  {isUsingFallback && (
                    <small className="offline-indicator">
                      <i className="fas fa-wifi-slash"></i> Offline
                    </small>
                  )}
                </button>
              ))
            )}
          </div>
        </div>

        <div className="panel-content-enhanced">
          {selectedStationData ? (
            <>
              <div className="aqi-overview-enhanced">
                <div className="aqi-main">
                  <div className="aqi-value-large" style={{ 
                    color: getAQIColor(selectedStationData.highest_sub_index) 
                  }}>
                    {Math.round(selectedStationData.highest_sub_index) || 'N/A'}
                  </div>
                  <div className="aqi-status-enhanced">
                    <span className="aqi-label">Air Quality Index</span>
                    <span className="aqi-status-text">
                      {selectedStationData.aqi_status || 'Unknown'}
                    </span>
                  </div>
                </div>
                <div className="aqi-indicator" style={{ 
                  backgroundColor: getAQIColor(selectedStationData.highest_sub_index) 
                }}></div>
              </div>

              <div className="metrics-grid-enhanced">
                {['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'].map(key => (
                  <div key={key} className="metric-card-enhanced">
                    <div className="metric-header">
                      <span className="metric-label">{key.toUpperCase()}</span>
                      <i className={parameterButtons.find(p => p.key === key)?.icon || 'fas fa-circle'}></i>
                    </div>
                    <div className="metric-value-large">
                      {selectedStationData.averages?.[key] ? 
                        Math.round(selectedStationData.averages[key]) : '--'
                      }
                    </div>
                    <div className="metric-unit">μg/m³</div>
                  </div>
                ))}
              </div>

              <div className="forecast-section-enhanced">
                <div className="forecast-header">
                  <h3 className="forecast-title">
                    <i className="fas fa-chart-line"></i>
                    4-Day Forecast
                  </h3>
                </div>

                <div className="parameter-controls-enhanced">
                  {parameterButtons.map(param => (
                    <button
                      key={param.key}
                      className={`parameter-btn-enhanced ${selectedParameter === param.key ? 'active' : ''}`}
                      onClick={() => setSelectedParameter(param.key)}
                    >
                      <i className={param.icon}></i>
                      <span>{param.label}</span>
                    </button>
                  ))}
                </div>

                {selectedStationData.forecast_data && selectedStationData.forecast_data.length > 0 ? (
                  <div className="chart-container-enhanced">
                    <Line
                      data={forecastChartData}
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        plugins: {
                          legend: { display: false },
                          tooltip: {
                            backgroundColor: 'rgba(0,0,0,0.8)',
                            titleColor: '#fff',
                            bodyColor: '#fff',
                            borderColor: '#3b82f6',
                            borderWidth: 1
                          }
                        },
                        scales: {
                          y: { 
                            ticks: { 
                              color: '#94a3b8',
                              beginAtZero: true 
                            },
                            grid: { color: 'rgba(148, 163, 184, 0.1)' }
                          },
                          x: { 
                            ticks: { color: '#94a3b8' },
                            grid: { color: 'rgba(148, 163, 184, 0.1)' }
                          }
                        }
                      }}
                    />
                  </div>
                ) : (
                  <div className="no-forecast">
                    <i className="fas fa-exclamation-circle"></i>
                    <p>Forecast data is not available</p>
                  </div>
                )}
              </div>
            </>
          ) : (
            <div className="panel-loading">
              <div className="loading-spinner-small"></div>
              <p>Loading station data...</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MapPage;
