// Enhanced Dashboard.js - Updated to handle location names and proper data display

import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// ===== PERFORMANCE OPTIMIZATION: LAZY LOADING =====
const LazyMap = React.lazy(() => 
  import('./LazyMap').catch(() => ({ default: () => <div>Map Loading...</div> }))
);

const LazyChart = React.lazy(() => 
  import('./components/LazyChart').catch(() => ({ default: () => <div>Chart Loading...</div> }))
);

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('detecting');
  const [dataType, setDataType] = useState('station'); // 'interpolated' or 'station'
  const [loading, setLoading] = useState(false);

  // ===== REFS =====
  const mapRef = useRef(null);
  const chartRef = useRef(null);
  const locationTimeoutRef = useRef(null);
  const dataFetchTimeoutRef = useRef(null);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // ===== ENHANCED FALLBACK DATA =====
  const fallbackData = useMemo(() => ({
    current_data: {
      current_aqi: 85,
      current_station_name: 'ASIET Campus Station',
      current_sensor_data: {
        pm25: 25.5, pm10: 42.3, so2: 15.8, no2: 37.2,
        co: 1.2, o3: 45.6, nh3: 12.4, temp: 28, hum: 65, pre: 1013
      },
      distance_to_nearest: 0.5,
      is_within_1km: false,
      data_type: 'station'
    },
    forecast_data: {
      nearest_station: [
        { day: 'Today', pm25_max: 30.2, pm10_max: 37.0, so2_max: 2.4, no2_max: 40.1, co_max: 1.1, o3_max: 3.7, nh3_max: 92.7 },
        { day: 'Tomorrow', pm25_max: 24.1, pm10_max: 34.2, so2_max: 2.4, no2_max: 45.2, co_max: 1.2, o3_max: 3.2, nh3_max: 100.0 },
        { day: 'Day 3', pm25_max: 24.3, pm10_max: 30.3, so2_max: 2.2, no2_max: 42.1, co_max: 1.2, o3_max: 3.5, nh3_max: 100.0 },
        { day: 'Day 4', pm25_max: 19.7, pm10_max: 23.4, so2_max: 2.3, no2_max: 38.5, co_max: 1.2, o3_max: 3.7, nh3_max: 100.0 }
      ]
    },
    sensor_locations: {
      'lora-v1': { lat: 10.178322301561957, lng: 76.43089144291672, name: 'ASIET Campus Station' },
      'loradev2': { lat: 10.17095090340159, lng: 76.42962876824544, name: 'Mattoor Junction Station' }
    },
    idw_status: 'inactive',
    user_location: null,
    timestamp: new Date().toISOString()
  }), []);

  // ===== INITIALIZE WITH FALLBACK DATA =====
  useEffect(() => {
    console.log('âš¡ ENHANCED DASHBOARD: Starting with fallback data');
    
    // Set fallback data immediately
    setDashboardData(fallbackData);
    setNearestStation({
      name: 'ASIET Campus Station',
      distance: 0.5,
      aqi: 85
    });
    setLocationStatus('detecting');
    setDataType('station');

    // Start background processes
    startBackgroundOptimizations();

    return () => {
      if (locationTimeoutRef.current) clearTimeout(locationTimeoutRef.current);
      if (dataFetchTimeoutRef.current) clearTimeout(dataFetchTimeoutRef.current);
    };
  }, []);

  // ===== BACKGROUND OPTIMIZATION PROCESSES =====
  const startBackgroundOptimizations = useCallback(() => {
    // Get user location first
    locationTimeoutRef.current = setTimeout(() => {
      getUserLocationBackground();
    }, 100);

    // Then fetch real data
    dataFetchTimeoutRef.current = setTimeout(() => {
      fetchRealDataBackground();
    }, 500);
  }, []);

  // ===== ENHANCED LOCATION DETECTION =====
  const getUserLocationBackground = useCallback(async () => {
    if (!navigator.geolocation) {
      console.log('ðŸ“ Geolocation not supported');
      setLocationStatus('not_supported');
      return;
    }

    try {
      setLocationStatus('detecting');
      console.log('ðŸ“ Getting user location...');
      
      const position = await new Promise((resolve, reject) => {
        const timeout = setTimeout(() => reject(new Error('Timeout')), 10000);
        navigator.geolocation.getCurrentPosition(
          (pos) => {
            clearTimeout(timeout);
            resolve(pos);
          },
          reject,
          { enableHighAccuracy: true, timeout: 8000, maximumAge: 300000 }
        );
      });

      const locationData = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: Date.now(),
        source: 'gps'
      };

      setUserLocation(locationData);
      setLocationStatus('found');
      console.log('âœ… Location obtained:', locationData);

      // Fetch data with location
      fetchRealDataBackground(locationData);

    } catch (error) {
      console.log('ðŸ“ Location detection failed:', error.message);
      setLocationStatus('denied');
      
      // Fetch data without location
      fetchRealDataBackground();
    }
  }, []);

  // ===== ENHANCED DATA FETCHING =====
  const fetchRealDataBackground = useCallback(async (locationData = null) => {
    try {
      setLoading(true);
      console.log('ðŸ”„ Fetching real dashboard data...');

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      
      if (locationData) {
        url.searchParams.append('lat', locationData.lat.toString());
        url.searchParams.append('lng', locationData.lng.toString());
      }

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 'Content-Type': 'application/json' }
      });

      clearTimeout(timeoutId);

      if (response.ok) {
        const data = await response.json();
        console.log('âœ… Real dashboard data received:', data);
        
        // Update all state with real data
        setDashboardData(data);
        
        // Update location information
        if (data.user_location) {
          setUserLocationName(data.user_location.location_name);
          console.log('ðŸ“ User location name:', data.user_location.display_name);
        }
        
        // Update nearest station info
        if (data.nearest_sensor_info) {
          setNearestStation({
            name: data.nearest_sensor_info.current_station_name,
            distance: data.nearest_sensor_info.distance_to_nearest,
            aqi: data.nearest_sensor_info.current_aqi
          });
        }
        
        // Update data type (interpolated or station)
        if (data.current_data) {
          setDataType(data.current_data.data_type);
          console.log('ðŸ“Š Data type:', data.current_data.data_type);
          console.log('ðŸ“ Within 1km:', data.current_data.is_within_1km);
        }

        console.log('âœ… Dashboard data updated successfully');
        
      } else {
        console.error('âŒ Failed to fetch dashboard data:', response.status);
        // Keep fallback data on error
      }
    } catch (error) {
      console.error('âŒ Error fetching dashboard data:', error);
      // Keep fallback data on error
    } finally {
      setLoading(false);
    }
  }, [username, API_BASE_URL]);

  // ===== MEMOIZED CALCULATIONS =====
  const currentValues = useMemo(() => {
    if (!dashboardData) return fallbackData.current_data.current_sensor_data;
    
    return dashboardData.current_data?.current_sensor_data || 
           dashboardData.nearest_sensor_info?.current_sensor_data || 
           fallbackData.current_data.current_sensor_data;
  }, [dashboardData, fallbackData]);

  const currentAQI = useMemo(() => {
    if (!dashboardData) return fallbackData.current_data.current_aqi;
    
    return dashboardData.current_data?.current_aqi || 
           dashboardData.nearest_sensor_info?.current_aqi || 
           fallbackData.current_data.current_aqi;
  }, [dashboardData, fallbackData]);

  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  // ===== ENHANCED LOCATION STATUS COMPONENT =====
  const LocationStatus = useMemo(() => {
    const getLocationDisplay = () => {
      switch (locationStatus) {
        case 'detecting':
          return <span style={{ color: '#f59e0b' }}>ðŸ“ Detecting location...</span>;
          
        case 'found':
          const locationName = userLocationName?.display_name || 'Your Location';
          const accuracy = userLocation?.accuracy ? `Â±${Math.round(userLocation.accuracy)}m` : '';
          const distance = nearestStation?.distance ? `${nearestStation.distance.toFixed(1)}km` : '';
          
          return (
            <span style={{ color: '#10b981' }}>
              ðŸ“ {locationName} {accuracy && `(${accuracy})`}
              {nearestStation && distance && (
                <span style={{ color: '#6b7280', fontSize: '0.9em' }}>
                  {' '} â†’ {nearestStation.name} ({distance})
                </span>
              )}
            </span>
          );
          
        case 'denied':
          return <span style={{ color: '#ef4444' }}>ðŸ“ Location access denied - Using default area</span>;
          
        case 'not_supported':
          return <span style={{ color: '#6b7280' }}>ðŸ“ Location not supported - Using default area</span>;
          
        default:
          return <span style={{ color: '#6b7280' }}>ðŸ“ {nearestStation?.name || 'ASIET Campus Area'}</span>;
      }
    };

    return <div className="location-info">{getLocationDisplay()}</div>;
  }, [locationStatus, userLocationName, userLocation, nearestStation]);

  // ===== DATA TYPE INDICATOR =====
  const DataTypeIndicator = useMemo(() => {
    const getDataTypeDisplay = () => {
      if (dataType === 'interpolated') {
        return (
          <div className="data-type-indicator interpolated">
            <span className="indicator-icon">ðŸŽ¯</span>
            <span className="indicator-text">Smart Interpolated Values</span>
            <span className="indicator-subtitle">Based on your precise location</span>
          </div>
        );
      } else {
        return (
          <div className="data-type-indicator station">
            <span className="indicator-icon">ðŸ“¡</span>
            <span className="indicator-text">Nearest Station Data</span>
            <span className="indicator-subtitle">
              {nearestStation?.distance ? 
                `${nearestStation.distance.toFixed(1)}km from ${nearestStation.name}` : 
                'From closest monitoring station'
              }
            </span>
          </div>
        );
      }
    };

    return getDataTypeDisplay();
  }, [dataType, nearestStation]);

  // ===== MEMOIZED METRIC ICONS =====
  const metricIcons = useMemo(() => ({
    pm25: 'ðŸŒ±', pm10: 'ðŸƒ', so2: 'ðŸ­', no2: 'ðŸ’¨',
    co: 'â˜ï¸', o3: 'â˜€ï¸', nh3: 'âš—ï¸'
  }), []);

  // ===== OPTIMIZED FORMAT FUNCTION =====
  const formatValue = useCallback((value, metric) => {
    const numValue = parseFloat(value) || 0;
    return metric === 'co' ? numValue.toFixed(1) : Math.round(numValue);
  }, []);

  // ===== MEMOIZED EVENT HANDLERS =====
  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []);

  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  
  const handleLogout = useCallback(() => {
    localStorage.clear();
    navigate('/login');
  }, [navigate]);

  const scrollToMap = useCallback(() => {
    document.getElementById('map-section')?.scrollIntoView({ 
      behavior: 'smooth', 
      block: 'start' 
    });
  }, []);

  // ===== ENHANCED METRIC CARDS =====
  const MetricCards = useMemo(() => {
    return Object.entries(currentValues).slice(0, 7).map(([key, value]) => (
      <div key={key} className="metric-card">
        <div className="metric-icon">{metricIcons[key] || 'ðŸ“Š'}</div>
        <div className="metric-value">{formatValue(value, key)}</div>
        <div className="metric-label">{key.toUpperCase()}</div>
        <div className="metric-unit">{key === 'co' ? 'mg/mÂ³' : 'Âµg/mÂ³'}</div>
        {dataType === 'interpolated' && (
          <div className="metric-badge interpolated-badge">
            ðŸŽ¯ Smart
          </div>
        )}
        {dataType === 'station' && (
          <div className="metric-badge station-badge">
            ðŸ“¡ Station
          </div>
        )}
      </div>
    ));
  }, [currentValues, metricIcons, formatValue, dataType]);

  // ===== ENHANCED FORECAST TABLE =====
  const ForecastTable = useMemo(() => {
    const forecastData = dashboardData?.forecast_data?.nearest_station || 
                        fallbackData.forecast_data.nearest_station;
    
    return (
      <table className="forecast-table">
        <thead>
          <tr>
            <th>ðŸ“… Day</th>
            <th>ðŸ“ˆ Max Value</th>
          </tr>
        </thead>
        <tbody>
          {forecastData.map((item, index) => (
            <tr key={index}>
              <td>{item.day}</td>
              <td>{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }, [dashboardData, selectedParameter, formatValue, fallbackData]);

  // ===== ENHANCED SENSOR NOTIFICATION =====
  const SensorNotification = useMemo(() => {
    const getNotificationContent = () => {
      if (dataType === 'interpolated') {
        return (
          <div className="sensor-notification interpolated">
            ðŸŽ¯ <strong>Smart Interpolated Data</strong> - 
            You are within 1km of monitoring stations. 
            We're showing personalized air quality values calculated specifically for your location at{' '}
            <strong>{userLocationName?.display_name || 'your current position'}</strong>.
            <a href="#map-section" onClick={scrollToMap}> View on map</a>
          </div>
        );
      } else {
        return (
          <div className="sensor-notification station">
            ðŸ“¡ <strong>Nearest Sensor: {nearestStation?.name || 'ASIET Campus Station'}</strong> - 
            {locationStatus === 'found' && nearestStation?.distance ? 
              `You are ${nearestStation.distance.toFixed(1)}km from the monitoring station at ${userLocationName?.display_name || 'your location'}.` :
              'You are viewing data from our closest monitoring station.'
            }
            <a href="#map-section" onClick={scrollToMap}> View on map</a>
          </div>
        );
      }
    };

    return getNotificationContent();
  }, [dataType, nearestStation, userLocationName, locationStatus, scrollToMap]);

  // ===== RENDER =====
  return (
    <div className="dashboard-page">
      {/* Real-time Status */}
      <div className="realtime-status">
        ðŸ”´ LIVE â€¢ Updates every 30s â€¢ {locationStatus === 'found' ? 'GPS Active' : 'Default Location'}
        {dataType === 'interpolated' && ' â€¢ Smart Analysis'}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" style={{ marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>â˜°</div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">ðŸ  Home</a></li>
            <li><a href="/forecast" className="nav-link">ðŸ“ˆ Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">ðŸ“‹ Health Update</a></li>
            <li><a href="/health-report" className="nav-link">ðŸ“„ Health Report</a></li>
            <li><a href="/add-family" className="nav-link">ðŸ‘¥ Add Family</a></li>
            <li className="user-info">ðŸ‘¤ <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">ðŸšª Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Enhanced Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        â„¹ï¸ <span>
          {userLocationName?.display_name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}
          {nearestStation && nearestStation.distance && (
            ` â€¢ Distance to station: ${nearestStation.distance.toFixed(1)}km`
          )}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Enhanced Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">ðŸ‘¤</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                {LocationStatus}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
              </div>
              <div className="aqi-status">
                {dataType === 'interpolated' ? 'Smart Analysis AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                {userLocationName?.display_name || nearestStation?.name || 'Your Location'}
              </div>
              <div className="last-updated">
                Last updated: {new Date().toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Data Type Indicator */}
        {DataTypeIndicator}

        {/* Enhanced Sensor Notification */}
        {SensorNotification}

        {/* Enhanced Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            ðŸŒ¬ï¸ Current Air Quality {userLocationName?.display_name ? `at ${userLocationName.display_name}` : 'at Your Location'}
            {dataType === 'interpolated' && <small> (Smart Analysis)</small>}
            {dataType === 'station' && <small> (Nearest Station)</small>}
          </h2>
          <div className="metrics-grid">
            {MetricCards}
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div id="map-section" className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ—ºï¸ Interactive Sensor Map</h3>
              {userLocationName && (
                <div className="location-display">
                  ðŸ“ {userLocationName.display_name}
                </div>
              )}
            </div>
            <div className="map-container">
              <React.Suspense fallback={
                <div style={{ height: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8fafc' }}>
                  <div>ðŸ—ºï¸ Loading map...</div>
                </div>
              }>
                <LazyMap 
                  userLocation={userLocation} 
                  dashboardData={dashboardData}
                  currentAQI={currentAQI}
                  dataType={dataType}
                />
              </React.Suspense>
            </div>
            <div style={{ marginTop: '12px', textAlign: 'center', fontSize: '0.875rem', color: '#6b7280' }}>
              ðŸ“ Blue marker: Your location â€¢ Tower markers: Monitoring stations
              {dataType === 'interpolated' && ' â€¢ ðŸŽ¯ Smart analysis active'}
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ“Š 4-Day Air Quality Forecast</h3>
              <div className="forecast-source">
                Data source: {nearestStation?.name || 'ASIET Campus Station'}
              </div>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <React.Suspense fallback={
                <div style={{ height: '200px', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8fafc' }}>
                  <div>ðŸ“Š Loading chart...</div>
                </div>
              }>
                <LazyChart 
                  dashboardData={dashboardData}
                  selectedParameter={selectedParameter}
                  fallbackData={fallbackData}
                />
              </React.Suspense>
              
              <div className="forecast-table-container">
                {ForecastTable}
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">ðŸŒ¤ï¸ Weather Conditions (Live Data)</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">ðŸŒ¡ï¸</div>
              <div className="weather-value">{currentValues.temp || '28'}Â°C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ’§</div>
              <div className="weather-value">{currentValues.hum || '65'}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ“</div>
              <div className="weather-value">{currentValues.pre || '1013'} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
            </div>
          </div>
        </div>

        {/* Enhanced Performance Status */}
        <div className="disclaimer">
          <h3>âš¡ Performance & System Status</h3>
          <p>
            <strong>ðŸš€ Smart Analysis:</strong> This dashboard uses advanced interpolation techniques 
            to provide personalized air quality data based on your exact location when you're within 1km of monitoring stations.
          </p>
          <p>
            <strong>ðŸ”„ System Status:</strong> 
            <span style={{ color: '#10b981', fontWeight: 600 }}> LIVE & OPTIMIZED</span> | 
            Location: {locationStatus === 'found' ? 
              `GPS Active (${userLocationName?.display_name || 'Located'})` : 
              'Default Area'
            } | 
            Data Type: {dataType === 'interpolated' ? 'Smart Analysis' : 'Nearest Station'} |
            {loading && <span style={{ color: '#f59e0b' }}> Updating...</span>}
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala - Smart Air Quality Monitoring</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
