import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/FamilyPage.css';
import logoImage from '../assets/aqi.webp'; 

function FamilyPage() {
    const { user, logout } = useAuth();
    const [username] = useState(() => {
        try {
            const userData = JSON.parse(localStorage.getItem('user') || '{}');
            return userData.name || user?.name || null;
        } catch {
            return user?.name || null;
        }
    });
    
    const [familyMembers, setFamilyMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingMember, setEditingMember] = useState(null);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    
    const [formState, setFormState] = useState({ name: '', age: '', relationship: '' });
    const [formError, setFormError] = useState('');
    const [formLoading, setFormLoading] = useState(false);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
    ? 'https://airaware-app-gcw7.onrender.com' 
    : 'http://localhost:8000';

    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

    const fetchFamilyMembers = useCallback(async () => {
        if (!username) {
            navigate('/login');
            return;
        }
        setIsLoading(true);
        setError(null);
        
        try {
            const response = await fetch(`${API_BASE_URL}/api/family-members/?username=${username}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: Failed to fetch family members`);
            }
            
            const data = await response.json();
            setFamilyMembers(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        const handleResize = () => setIsMobileView(window.innerWidth <= 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {
        fetchFamilyMembers();
    }, [fetchFamilyMembers]);

    // Event Handlers
    const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
    
    const handleLogout = useCallback(() => {
        localStorage.clear();
        navigate('/login');
    }, [navigate]);

    const handleAddMemberClick = () => {
        setEditingMember(null);
        setFormState({ name: '', age: '', relationship: '' });
        setFormError('');
        setIsModalOpen(true);
    };

    const handleEditMemberClick = (member) => {
        setEditingMember(member);
        setFormState({ 
            name: member.name, 
            age: member.age.toString(), 
            relationship: member.relationship 
        });
        setFormError('');
        setIsModalOpen(true);
    };

    // --- NEW FUNCTION TO HANDLE HEALTH INFO NAVIGATION ---
    const handleHealthInfoClick = (member) => {
        if (member.has_health_assessment) {
            // If they have a report, navigate to the report page for that member
            navigate(`/family-health-report/${member.id}`);
        } else {
            // If not, navigate to the assessment form for that member.
            // You will need to create this page, similar to your main HealthAssessment page.
            navigate(`/family-health-assessment/${member.id}`);
        }
    };

    const handleDeleteMember = async (memberId, memberName) => {
        if (window.confirm(`Are you sure you want to delete ${memberName} from your family list? This action cannot be undone.`)) {
            try {
                const response = await fetch(`${API_BASE_URL}/api/family-members/${memberId}/`, { 
                    method: 'DELETE',
                });
                
                if (!response.ok) throw new Error('Failed to delete member');
                
                setFamilyMembers(prev => prev.filter(m => m.id !== memberId));
            } catch (err) {
                alert("Failed to delete member. Please try again.");
            }
        }
    };
    
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        setFormError('');
        setFormLoading(true);

        if (!formState.name.trim() || !formState.age || parseInt(formState.age) < 0 || !formState.relationship) {
            setFormError('Please fill out all fields correctly.');
            setFormLoading(false);
            return;
        }

        const url = editingMember 
            ? `${API_BASE_URL}/api/family-members/update/${editingMember.id}/`
            : `${API_BASE_URL}/api/family-members/`;
        
        const method = editingMember ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ ...formState, age: parseInt(formState.age), username })
            });

            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.error || 'An unknown error occurred.');
            }

            const updatedMember = await response.json();

            if (editingMember) {
                setFamilyMembers(prev => prev.map(m => m.id === updatedMember.id ? updatedMember : m));
            } else {
                setFamilyMembers(prev => [...prev, updatedMember]);
            }
            
            setIsModalOpen(false);
        } catch (err) {
            setFormError(err.message);
        } finally {
            setFormLoading(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormState(prev => ({ ...prev, [name]: value }));
        if (formError) setFormError('');
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
        setFormError('');
        setFormState({ name: '', age: '', relationship: '' });
        setEditingMember(null);
    };

    const getRelationshipIcon = (relationship) => {
        const icons = {
            'Father': 'üë®', 'Mother': 'üë©', 'Sibling': 'üë´', 'Spouse': 'üíë',
            'Child': 'üë∂', 'Grandparent': 'üë¥', 'Other': 'üë§'
        };
        return icons[relationship] || 'üë§';
    };

    const getAgeCategory = (age) => {
        if (age < 13) return { category: 'Child', color: '#10b981', icon: 'üë∂' };
        if (age < 20) return { category: 'Teen', color: 'f59e0b', icon: 'üë¶' };
