// Progressive Loading Dashboard.js - Loads instantly, updates with GPS

import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// Lazy loading components
const LazyMap = React.lazy(() => 
  import('./LazyMap').catch(() => ({ default: () => <div>Map Loading...</div> }))
);

const LazyChart = React.lazy(() => 
  import('./LazyChart').catch(() => ({ default: () => <div>Chart Loading...</div> }))
);

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('not_started');
  const [dataType, setDataType] = useState('station');
  const [loading, setLoading] = useState(true);
  const [locationLoading, setLocationLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isUpdatingWithLocation, setIsUpdatingWithLocation] = useState(false);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // ===== PHASE 1: IMMEDIATE DASHBOARD LOAD =====
  const loadDashboardDefault = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ðŸš€ PHASE 1: Loading dashboard without location...');

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      // NO lat/lng parameters - backend will use default data

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000);

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 'Content-Type': 'application/json' }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('âœ… PHASE 1: Default dashboard data received');
      
      // Process default data
      processDashboardData(data);
      
      // Set default location status
      setLocationStatus('not_started');
      
    } catch (error) {
      console.error('âŒ PHASE 1: Error loading default dashboard:', error);
      setError(error.message);
      
      // Set minimal fallback data
      setDashboardData({
        nearest_sensor_info: {
          current_device_id: 'lora-v1',
          current_aqi: 50,
          current_station_name: 'Station 1 (ASIET Campus)',
          current_sensor_data: {
            pm25: 25, pm10: 42, so2: 8, no2: 40,
            co: 1.1, o3: 46, nh3: 93, temp: 28, hum: 65, pre: 1013
          },
          distance_to_nearest: null,
          is_within_1km: false,
          current_forecast_data: [
            { day: 'Today', pm25_max: 30, pm10_max: 37, so2_max: 8, no2_max: 40, co_max: 1.1, o3_max: 46, nh3_max: 93 },
            { day: 'Tomorrow', pm25_max: 24, pm10_max: 34, so2_max: 8, no2_max: 45, co_max: 1.2, o3_max: 43, nh3_max: 100 }
          ]
        },
        sensor_locations: {
          'lora-v1': { lat: 10.178322301561957, lng: 76.43089144291672, name: 'Station 1 (ASIET Campus)' },
          'loradev2': { lat: 10.17095090340159, lng: 76.42962876824544, name: 'Station 2 (Mattoor Junction)' }
        },
        idw_status: 'location_required',
        status: 'fallback'
      });
      
    } finally {
      setLoading(false);
    }
  }, [username, API_BASE_URL]);

  // ===== PHASE 2: BACKGROUND GPS DETECTION =====
  const startLocationDetection = useCallback(async () => {
    if (!navigator.geolocation) {
      console.log('ðŸ“ PHASE 2: Geolocation not supported');
      setLocationStatus('not_supported');
      return;
    }

    try {
      setLocationLoading(true);
      setLocationStatus('detecting');
      console.log('ðŸ“ PHASE 2: Starting background GPS detection...');
      
      const position = await new Promise((resolve, reject) => {
        const timeout = setTimeout(() => reject(new Error('GPS timeout')), 10000);
        navigator.geolocation.getCurrentPosition(
          (pos) => {
            clearTimeout(timeout);
            resolve(pos);
          },
          (error) => {
            clearTimeout(timeout);
            reject(error);
          },
          { enableHighAccuracy: true, timeout: 8000, maximumAge: 600000 }
        );
      });

      const locationData = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: Date.now(),
        source: 'gps'
      };

      setUserLocation(locationData);
      setLocationStatus('found');
      console.log('âœ… PHASE 2: GPS location obtained:', locationData);

      // Get location name
      await getLocationName(locationData.lat, locationData.lng);
      
      // PHASE 3: Update dashboard with location
      await updateDashboardWithLocation(locationData);

    } catch (error) {
      console.log('ðŸ“ PHASE 2: GPS detection failed:', error.message);
      setLocationStatus('denied');
      setLocationLoading(false);
    }
  }, []);

  // ===== PHASE 3: UPDATE WITH LOCATION DATA =====
  const updateDashboardWithLocation = useCallback(async (locationData) => {
    try {
      setIsUpdatingWithLocation(true);
      console.log('ðŸŽ¯ PHASE 3: Updating dashboard with location data...');

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      url.searchParams.append('lat', locationData.lat.toString());
      url.searchParams.append('lng', locationData.lng.toString());

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 'Content-Type': 'application/json' }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('âœ… PHASE 3: Location-specific data received');
      
      // Process location-specific data
      processDashboardData(data);
      
    } catch (error) {
      console.error('âŒ PHASE 3: Error updating with location:', error);
      // Don't show error - keep default data
    } finally {
      setIsUpdatingWithLocation(false);
      setLocationLoading(false);
    }
  }, [username, API_BASE_URL]);

  // ===== REVERSE GEOCODING =====
  const getLocationName = useCallback(async (lat, lng) => {
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`
      );
      
      if (response.ok) {
        const data = await response.json();
        const locationName = {
          display_name: data.display_name || `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
          city: data.address?.city || data.address?.town || data.address?.village,
          state: data.address?.state,
          country: data.address?.country
        };
        
        setUserLocationName(locationName);
        console.log('ðŸ“ Location name resolved:', locationName.display_name);
      }
    } catch (error) {
      console.log('âŒ Reverse geocoding failed:', error);
    }
  }, []);

  // ===== PROCESS DASHBOARD DATA =====
  const processDashboardData = useCallback((data) => {
    setDashboardData(data);
    
    // Extract nearest station info
    if (data.nearest_sensor_info) {
      setNearestStation({
        name: data.nearest_sensor_info.current_station_name || 'Station 1 (ASIET Campus)',
        distance: data.nearest_sensor_info.distance_to_nearest,
        aqi: data.nearest_sensor_info.current_aqi || 50
      });
    }

    // Set data type based on IDW status and location proximity
    if (data.idw_status === 'active' && data.idw_interpolated_values && 
        Object.keys(data.idw_interpolated_values).length > 0) {
      setDataType('interpolated');
    } else {
      setDataType('station');
    }

    console.log('ðŸ“Š Dashboard data processed:', {
      dataType: data.idw_status === 'active' ? 'interpolated' : 'station',
      nearestStation: data.nearest_sensor_info?.current_station_name,
      hasLocation: !!data.user_location
    });
  }, []);

  // ===== INITIALIZATION =====
  useEffect(() => {
    const initializeDashboard = async () => {
      console.log('âš¡ Initializing progressive dashboard...');
      
      // PHASE 1: Load dashboard immediately without location
      await loadDashboardDefault();
      
      // PHASE 2: Start GPS detection in background (non-blocking)
      setTimeout(() => {
        startLocationDetection();
      }, 1000); // Small delay to ensure dashboard loads first
    };

    initializeDashboard();
  }, [loadDashboardDefault, startLocationDetection]);

  // ===== MEMOIZED CALCULATIONS =====
  const currentValues = useMemo(() => {
    if (!dashboardData) return {};
    
    // Use IDW interpolated values if available
    if (dataType === 'interpolated' && dashboardData.idw_interpolated_values) {
      return dashboardData.idw_interpolated_values;
    }
    
    // Use nearest station data
    if (dashboardData.nearest_sensor_info && dashboardData.nearest_sensor_info.current_sensor_data) {
      return dashboardData.nearest_sensor_info.current_sensor_data;
    }
    
    return {};
  }, [dashboardData, dataType]);

  const currentAQI = useMemo(() => {
    if (!dashboardData) return 50;
    
    // Use interpolated AQI if available
    if (dataType === 'interpolated' && dashboardData.interpolated_aqi) {
      return dashboardData.interpolated_aqi;
    }
    
    // Use nearest station AQI
    if (dashboardData.nearest_sensor_info && dashboardData.nearest_sensor_info.current_aqi) {
      return dashboardData.nearest_sensor_info.current_aqi;
    }
    
    return 50;
  }, [dashboardData, dataType]);

  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  // ===== LOCATION STATUS DISPLAY =====
  const LocationStatus = useMemo(() => {
    const getLocationDisplay = () => {
      switch (locationStatus) {
        case 'not_started':
          return <span style={{ color: '#6b7280' }}>ðŸ“ Starting location detection...</span>;
          
        case 'detecting':
          return (
            <span style={{ color: '#f59e0b' }}>
              ðŸ“ Detecting location...
              {locationLoading && <span className="location-spinner">âŸ³</span>}
            </span>
          );
          
        case 'found':
          const locationName = userLocationName?.display_name || 'Your Location';
          const accuracy = userLocation?.accuracy ? `Â±${Math.round(userLocation.accuracy)}m` : '';
          const distance = nearestStation?.distance ? `${nearestStation.distance.toFixed(1)}km` : '';
          
          return (
            <span style={{ color: '#10b981' }}>
              ðŸ“ {locationName} {accuracy && `(${accuracy})`}
              {nearestStation && distance && (
                <span style={{ color: '#6b7280', fontSize: '0.9em' }}>
                  {' '} â†’ {nearestStation.name} ({distance})
                </span>
              )}
              {isUpdatingWithLocation && <span className="update-spinner">âŸ³</span>}
            </span>
          );
          
        case 'denied':
          return <span style={{ color: '#ef4444' }}>ðŸ“ Location access denied - Using default area</span>;
          
        case 'not_supported':
          return <span style={{ color: '#6b7280' }}>ðŸ“ Location not supported - Using default area</span>;
          
        default:
          return <span style={{ color: '#6b7280' }}>ðŸ“ {nearestStation?.name || 'Default Area'}</span>;
      }
    };

    return <div className="location-info">{getLocationDisplay()}</div>;
  }, [locationStatus, userLocationName, userLocation, nearestStation, locationLoading, isUpdatingWithLocation]);

  // ===== DATA TYPE INDICATOR =====
  const DataTypeIndicator = useMemo(() => {
    if (dataType === 'interpolated') {
      return (
        <div className="data-type-indicator interpolated">
          <span className="indicator-icon">ðŸŽ¯</span>
          <span className="indicator-text">Smart Interpolated Values</span>
          <span className="indicator-subtitle">Based on your precise location</span>
          {isUpdatingWithLocation && <span className="update-badge">Updating...</span>}
        </div>
      );
    } else {
      return (
        <div className="data-type-indicator station">
          <span className="indicator-icon">ðŸ“¡</span>
          <span className="indicator-text">Nearest Station Data</span>
          <span className="indicator-subtitle">
            {nearestStation?.distance ? 
              `${nearestStation.distance.toFixed(1)}km from ${nearestStation.name}` : 
              'From closest monitoring station'
            }
          </span>
          {isUpdatingWithLocation && <span className="update-badge">Updating...</span>}
        </div>
      );
    }
  }, [dataType, nearestStation, isUpdatingWithLocation]);

  // ===== SENSOR NOTIFICATION =====
  const SensorNotification = useMemo(() => {
    if (dataType === 'interpolated') {
      return (
        <div className="sensor-notification interpolated">
          ðŸŽ¯ <strong>Smart Interpolated Data</strong> - 
          You are within 1km of monitoring stations. 
          We're showing personalized air quality values calculated specifically for your location at{' '}
          <strong>{userLocationName?.display_name || 'your current position'}</strong>.
        </div>
      );
    } else {
      return (
        <div className="sensor-notification station">
          ðŸ“¡ <strong>Nearest Sensor: {nearestStation?.name || 'Station 1 (ASIET Campus)'}</strong> - 
          {locationStatus === 'found' && nearestStation?.distance ? 
            `You are ${nearestStation.distance.toFixed(1)}km from the monitoring station at ${userLocationName?.display_name || 'your location'}.` :
            'You are viewing data from our closest monitoring station.'
          }
        </div>
      );
    }
  }, [dataType, nearestStation, userLocationName, locationStatus]);

  // ===== METRIC CARDS =====
  const metricIcons = useMemo(() => ({
    pm25: 'ðŸŒ±', pm10: 'ðŸƒ', so2: 'ðŸ­', no2: 'ðŸ’¨',
    co: 'â˜ï¸', o3: 'â˜€ï¸', nh3: 'âš—ï¸'
  }), []);

  const formatValue = useCallback((value, metric) => {
    const numValue = parseFloat(value) || 0;
    return metric === 'co' ? numValue.toFixed(1) : Math.round(numValue);
  }, []);

  const MetricCards = useMemo(() => {
    const parameters = ['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'];
    
    return parameters.map((key) => {
      const value = currentValues[key] || 0;
      
      return (
        <div key={key} className="metric-card">
          <div className="metric-icon">{metricIcons[key] || 'ðŸ“Š'}</div>
          <div className="metric-value">{formatValue(value, key)}</div>
          <div className="metric-label">{key.toUpperCase()}</div>
          <div className="metric-unit">{key === 'co' ? 'mg/mÂ³' : 'Âµg/mÂ³'}</div>
          {dataType === 'interpolated' && (
            <div className="metric-badge interpolated-badge">
              ðŸŽ¯ Smart
            </div>
          )}
          {dataType === 'station' && (
            <div className="metric-badge station-badge">
              ðŸ“¡ Station
            </div>
          )}
          {isUpdatingWithLocation && (
            <div className="metric-updating">âŸ³</div>
          )}
        </div>
      );
    });
  }, [currentValues, metricIcons, formatValue, dataType, isUpdatingWithLocation]);

  // ===== FORECAST TABLE =====
  const ForecastTable = useMemo(() => {
    const forecastData = dashboardData?.forecast_data?.nearest_station || 
                        dashboardData?.nearest_sensor_info?.current_forecast_data || [];
    
    return (
      <table className="forecast-table">
        <thead>
          <tr>
            <th>ðŸ“… Day</th>
            <th>ðŸ“ˆ Max Value</th>
          </tr>
        </thead>
        <tbody>
          {forecastData.map((item, index) => (
            <tr key={index}>
              <td>{item.day}</td>
              <td>{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }, [dashboardData, selectedParameter, formatValue]);

  // ===== EVENT HANDLERS =====
  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []);

  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  
  const handleLogout = useCallback(() => {
    localStorage.clear();
    navigate('/login');
  }, [navigate]);

  const handleRetryLocation = useCallback(() => {
    setLocationStatus('not_started');
    setLocationLoading(false);
    setIsUpdatingWithLocation(false);
    setTimeout(() => {
      startLocationDetection();
    }, 500);
  }, [startLocationDetection]);

  // ===== LOADING STATE =====
  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading dashboard...</p>
          <small>Getting air quality data</small>
        </div>
      </div>
    );
  }

  // ===== ERROR STATE =====
  if (error && !dashboardData) {
    return (
      <div className="dashboard-page">
        <div className="error-container">
          <div className="error-message">
            <h2>âš ï¸ Connection Issue</h2>
            <p>We're having trouble connecting to our servers. Please try again.</p>
            <button 
              className="retry-button"
              onClick={() => window.location.reload()}
            >
              ðŸ”„ Retry
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===== MAIN RENDER =====
  return (
    <div className="dashboard-page">
      {/* Real-time Status */}
      <div className="realtime-status">
        ðŸ”´ LIVE â€¢ Updates every 30s â€¢ {locationStatus === 'found' ? 'GPS Active' : 'Default Data'}
        {dataType === 'interpolated' && ' â€¢ Smart Analysis'}
        {isUpdatingWithLocation && ' â€¢ Updating...'}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" style={{ marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>â˜°</div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">ðŸ  Home</a></li>
            <li><a href="/forecast" className="nav-link">ðŸ“ˆ Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">ðŸ“‹ Health Update</a></li>
            <li><a href="/health-report" className="nav-link">ðŸ“„ Health Report</a></li>
            <li><a href="/add-family" className="nav-link">ðŸ‘¥ Add Family</a></li>
            <li className="user-info">ðŸ‘¤ <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">ðŸšª Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        â„¹ï¸ <span>
          {userLocationName?.display_name || nearestStation?.name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}
          {nearestStation && nearestStation.distance && (
            ` â€¢ Distance to station: ${nearestStation.distance.toFixed(1)}km`
          )}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">ðŸ‘¤</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                {LocationStatus}
                {locationStatus === 'denied' && (
                  <button onClick={handleRetryLocation} className="retry-location-btn">
                    ðŸ”„ Retry Location
                  </button>
                )}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
                {isUpdatingWithLocation && <span className="updating-indicator">âŸ³</span>}
              </div>
              <div className="aqi-status">
                {dataType === 'interpolated' ? 'Smart Analysis AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                {userLocationName?.display_name || nearestStation?.name || 'Your Location'}
              </div>
              <div className="last-updated">
                Last updated: {new Date().toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Data Type Indicator */}
        {DataTypeIndicator}

        {/* Sensor Notification */}
        {SensorNotification}

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            ðŸŒ¬ï¸ Current Air Quality {userLocationName?.display_name ? `at ${userLocationName.display_name}` : 'in Your Area'}
            {dataType === 'interpolated' && <small> (Smart Analysis)</small>}
            {dataType === 'station' && <small> (Nearest Station)</small>}
          </h2>
          <div className="metrics-grid">
            {MetricCards}
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div id="map-section" className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ—ºï¸ Interactive Sensor Map</h3>
              {userLocationName && (
                <div className="location-display">
                  ðŸ“ {userLocationName.display_name}
                </div>
              )}
            </div>
            <div className="map-container">
              <React.Suspense fallback={
                <div style={{ height: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8fafc' }}>
                  <div>ðŸ—ºï¸ Loading map...</div>
                </div>
              }>
                <LazyMap 
                  userLocation={userLocation} 
                  dashboardData={dashboardData}
                  currentAQI={currentAQI}
                  dataType={dataType}
                />
              </React.Suspense>
            </div>
            <div style={{ marginTop: '12px', textAlign: 'center', fontSize: '0.875rem', color: '#6b7280' }}>
              ðŸ“ Blue marker: Your location â€¢ Tower markers: Monitoring stations
              {dataType === 'interpolated' && ' â€¢ ðŸŽ¯ Smart analysis active'}
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ“Š 4-Day Air Quality Forecast</h3>
              <div className="forecast-source">
                Data source: {nearestStation?.name || 'Station 1 (ASIET Campus)'}
              </div>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <React.Suspense fallback={
                <div style={{ height: '200px', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8fafc' }}>
                  <div>ðŸ“Š Loading chart...</div>
                </div>
              }>
                <LazyChart 
                  dashboardData={dashboardData}
                  selectedParameter={selectedParameter}
                />
              </React.Suspense>
              
              <div className="forecast-table-container">
                {ForecastTable}
              </div>
            </div>
          </div>
        </div>

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">ðŸŒ¤ï¸ Weather Conditions (Live Data)</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">ðŸŒ¡ï¸</div>
              <div className="weather-value">{currentValues.temp || '28'}Â°C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ’§</div>
              <div className="weather-value">{currentValues.hum || '65'}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ“</div>
              <div className="weather-value">{currentValues.pre || '1013'} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
            </div>
          </div>
        </div>

        {/* System Status */}
        <div className="disclaimer">
          <h3>âš¡ System Status</h3>
          <p>
            <strong>ðŸš€ Progressive Loading:</strong> Dashboard loads instantly with default data, then updates with your location for personalized air quality information.
          </p>
          <p>
            <strong>ðŸ”„ System Status:</strong> 
            <span style={{ color: '#10b981', fontWeight: 600 }}> LIVE & OPTIMIZED</span> | 
            Location: {locationStatus === 'found' ? 
              `GPS Active (${userLocationName?.display_name || 'Located'})` : 
              locationStatus === 'detecting' ? 'Detecting...' : 'Default Area'
            } | 
            Data Type: {dataType === 'interpolated' ? 'Smart Analysis' : 'Nearest Station'} |
            {isUpdatingWithLocation && <span style={{ color: '#f59e0b' }}> Updating with location...</span>}
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala - Smart Air Quality Monitoring</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
