import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// Lazy loading components with fallbacks
const LazyMap = React.lazy(() => 
  import('./LazyMap').catch(() => ({ 
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">üó∫Ô∏è</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() => 
  import('./LazyChart').catch(() => ({ 
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">üìä</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// Utility Functions
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + 
           Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
           Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// IP-based location service
const getIPLocation = async () => {
  try {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 5000);
    
    const response = await fetch('https://ipapi.co/json/', { 
      signal: controller.signal,
      headers: {
        'Accept': 'application/json',
        'User-Agent': 'AirAware/1.0'
      }
    });
    
    clearTimeout(timeoutId);
    
    if (!response.ok) throw new Error(`HTTP ${response.status}`);
    
    const data = await response.json();
    
    if (!data.latitude || !data.longitude) {
      throw new Error('Invalid location data');
    }
    
    return {
      lat: parseFloat(data.latitude),
      lng: parseFloat(data.longitude),
      city: data.city || 'Unknown City',
      region: data.region || 'Unknown Region',
      country: data.country || 'Unknown Country',
      accuracy: 10000,
      source: 'ip'
    };
  } catch (error) {
    console.log('IP location failed:', error);
    return null;
  }
};

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [interpolatedData, setInterpolatedData] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [dataType, setDataType] = useState('station');
  const [dataSource, setDataSource] = useState('loading');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
  const [healthData, setHealthData] = useState(null);

  const navigate = useNavigate();
  
  const API_BASE_URL = (() => {
    try {
      return process.env.REACT_APP_API_URL || 'http://localhost:8000';
    } catch (e) {
      return 'http://localhost:8000';
    }
  })();
  
  const locationTimeoutRef = useRef(null);
  const abortControllerRef = useRef(null);

  // ===== INSTANT DASHBOARD LOAD WITH ENHANCED SAMPLE DATA =====
  const loadDashboardInstantly = useCallback(async () => {
    console.log('üöÄ Loading dashboard instantly...');
    setDataSource('sample');
    setLocationStatus('detecting');
    
    const sampleData = {
      stations: {
        'lora-v1': {
          station_info: {
            name: 'Station 1 (ASIET Campus)',
            lat: 10.178322301561957,
            lng: 76.43089144291672
          },
          averages: {
            pm25: 25, pm10: 42, so2: 8, no2: 40,
