  // ===== ENHANCED GPS LOCATION WITH BETTER ERROR HANDLING =====
  const getGPSLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation not supported'));
        return;
      }

      console.log('üéØ Starting GPS location request...');

      const timeout = setTimeout(() => {
        console.log('‚è∞ GPS timeout after 10 seconds');
        reject(new Error('GPS timeout'));
      }, 10000);

      navigator.geolocation.getCurrentPosition(
        (position) => {
          clearTimeout(timeout);
          console.log('‚úÖ GPS position received:', position);
          
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy,
            source: 'gps',
            timestamp: Date.now()
          };
          
          // Validate coordinates for Kerala/India region
          if (location.lat < 8.0 || location.lat > 37.0 || location.lng < 68.0 || location.lng > 97.0) {
            console.warn('‚ö†Ô∏è GPS coordinates seem outside India region:', location);
          }
          
          // Check if coordinates are reasonable
          if (isNaN(location.lat) || isNaN(location.lng)) {
            reject(new Error('Invalid GPS coordinates'));
            return;
          }
          
          console.log(`üìç GPS Location: ${location.lat.toFixed(6)}, ${location.lng.toFixed(6)} (¬±${Math.round(location.accuracy)}m)`);
          resolve(location);
        },
        (error) => {
          clearTimeout(timeout);
          console.log('‚ùå GPS error:', error);
          
          let errorMessage = 'GPS location failed';
          switch(error.code) {
            case error.PERMISSION_DENIED:
              errorMessage = 'GPS permission denied by user';
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage = 'GPS position unavailable';
              break;
            case error.TIMEOUT:
              errorMessage = 'GPS request timeout';
              break;
          }
          
          reject(new Error(errorMessage));
        },
        { 
          enableHighAccuracy: true, 
          timeout: 10000, 
          maximumAge: 60000  // Accept cached position up to 1 minute old
        }
      );
    });
  }, []);

  // ===== ENHANCED REVERSE GEOCODING WITH BETTER FALLBACK =====
  const getLocationName = useCallback(async (lat, lng) => {
    try {
      console.log(`üîç Getting location name for: ${lat.toFixed(6)}, ${lng.toFixed(6)}`);
      
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 8000);
      
      // Try OpenStreetMap Nominatim first (more reliable for India)
      const nominatimUrl = `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1&zoom=18`;
      
      try {
        const response = await fetch(nominatimUrl, {
          signal: controller.signal,
          headers: {
            'Accept': 'application/json',
            'User-Agent': 'AirAware Dashboard/1.0'
          }
        });
        
        clearTimeout(timeoutId);
        
        if (response.ok) {
          const data = await response.json();
          console.log('üåç Nominatim response:', data);
          
          if (data && data.address) {
            const addr = data.address;
            
            // Extract detailed location information
            const village = addr.village || addr.hamlet;
            const town = addr.town || addr.city;
            const suburb = addr.suburb || addr.neighbourhood;
            const county = addr.county || addr.state_district;
            const state = addr.state;
            const country = addr.country;
            
            // Build a meaningful display name
            let displayParts = [];
            
            // Add most specific location first
            if (village) displayParts.push(village);
            else if (suburb) displayParts.push(suburb);
            else if (town) displayParts.push(town);
            
            // Add broader location
            if (county && county !== town) displayParts.push(county);
            if (state) displayParts.push(state);
            
            const displayName = displayParts.length > 0 ? displayParts.join(', ') : `${lat.toFixed(4)}, ${lng.toFixed(4)}`;
            
            const locationName = {
              display_name: displayName,
              city: town || village || suburb || 'Unknown City',
              state: state || 'Kerala',
              country: country || 'India',
              coordinates: `${lat.toFixed(6)}, ${lng.toFixed(6)}`,
              accuracy: 'GPS',
              full_address: data.display_name
            };
            
            console.log('‚úÖ Location resolved:', locationName);
            setUserLocationName(locationName);
            return;
          }
        }
      } catch (nominatimError) {
        console.log('‚ö†Ô∏è Nominatim failed, trying alternative...');
      }
      
      // Fallback to a different geocoding service or manual Kerala region detection
      const locationName = await getKeralaRegionName(lat, lng) || {
        display_name: `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
        city: 'Unknown Location',
        state: 'Kerala',
        country: 'India',
        coordinates: `${lat.toFixed(6)}, ${lng.toFixed(6)}`,
        accuracy: `GPS ¬±${userLocation?.accuracy ? Math.round(userLocation.accuracy) + 'm' : 'unknown'}`,
        full_address: `Coordinates: ${lat.toFixed(6)}, ${lng.toFixed(6)}`
      };
      
      console.log('üìç Using fallback location:', locationName);
      setUserLocationName(locationName);
      
    } catch (error) {
      console.log('‚ùå All geocoding failed:', error);
      
      // Set fallback with coordinates
      setUserLocationName({
        display_name: `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
        city: 'Unknown Location',
        state: 'Kerala',
        country: 'India',
        coordinates: `${lat.toFixed(6)}, ${lng.toFixed(6)}`,
        accuracy: `GPS ¬±${userLocation?.accuracy ? Math.round(userLocation.accuracy) + 'm' : 'unknown'}`,
        full_address: `GPS Coordinates: ${lat.toFixed(6)}, ${lng.toFixed(6)}`
      });
    }
  }, [userLocation]);

  // ===== KERALA REGION DETECTION HELPER =====
  const getKeralaRegionName = useCallback(async (lat, lng) => {
    try {
      // Known Kerala regions with approximate boundaries
      const keralaRegions = [
        { name: 'Ernakulam', bounds: { minLat: 9.8, maxLat: 10.3, minLng: 76.2, maxLng: 76.8 } },
        { name: 'Kottayam', bounds: { minLat: 9.3, maxLat: 9.8, minLng: 76.4, maxLng: 76.9 } },
        { name: 'Thrissur', bounds: { minLat: 10.3, maxLat: 10.8, minLng: 76.0, maxLng: 76.5 } },
        { name: 'Palakkad', bounds: { minLat: 10.6, maxLat: 11.0, minLng: 76.4, maxLng: 77.0 } },
        { name: 'Kozhikode', bounds: { minLat: 11.0, maxLat: 11.5, minLng: 75.5, maxLng: 76.2 } },
        { name: 'Kannur', bounds: { minLat: 11.8, maxLat: 12.4, minLng: 75.0, maxLng: 75.8 } },
        { name: 'Kasaragod', bounds: { minLat: 12.2, maxLat: 12.8, minLng: 74.8, maxLng: 75.4 } }
      ];
      
      for (const region of keralaRegions) {
        const bounds = region.bounds;
        if (lat >= bounds.minLat && lat <= bounds.maxLat && 
            lng >= bounds.minLng && lng <= bounds.maxLng) {
          return {
            display_name: `${region.name}, Kerala`,
            city: region.name,
            state: 'Kerala',
            country: 'India',
            coordinates: `${lat.toFixed(6)}, ${lng.toFixed(6)}`,
            accuracy: 'GPS (Regional)',
            full_address: `${region.name} District, Kerala, India`
          };
        }
      }
      
      return null;
    } catch (error) {
      return null;
    }
  }, []);

  // ===== ENHANCED SMART LOCATION DETECTION =====
  const startSmartLocationDetection = useCallback(async () => {
    console.log('üìç Starting enhanced smart location detection...');
    setLocationStatus('detecting');
    setIsLocationUpdating(true);

    try {
      // Step 1: Try GPS first for most accurate location
      if (navigator.geolocation) {
        try {
          console.log('üéØ Attempting GPS location...');
          const gpsLocation = await getGPSLocation();
          
          if (gpsLocation) {
            console.log('‚úÖ GPS location successful:', gpsLocation);
            setUserLocation(gpsLocation);
            setLocationStatus('gps_detected');
            
            // Get precise location name
            await getLocationName(gpsLocation.lat, gpsLocation.lng);
            
            // Update dashboard with GPS data immediately
            await updateDashboardWithLocation(gpsLocation);
            return; // GPS successful, no need for IP fallback
          }
        } catch (gpsError) {
          console.log('‚ùå GPS detection failed:', gpsError.message);
          // Continue to IP fallback
        }
      }

      // Step 2: Fallback to IP-based location if GPS fails
      console.log('üåê Falling back to IP-based location...');
      const ipLocation = await getIPLocation();
      
      if (ipLocation) {
        console.log('‚úÖ IP location successful:', ipLocation);
        setUserLocation(ipLocation);
        setUserLocationName({ 
          display_name: `${ipLocation.city}, ${ipLocation.region} (approximate)`,
          city: ipLocation.city,
          state: ipLocation.region,
          country: ipLocation.country,
          accuracy: 'IP-based (~10km)',
          coordinates: `${ipLocation.lat.toFixed(4)}, ${ipLocation.lng.toFixed(4)}`
        });
        setLocationStatus('ip_detected');
        
        // Update with IP-based data
        await updateDashboardWithLocation(ipLocation);
      } else {
        throw new Error('Both GPS and IP location failed');
      }

    } catch (error) {
      console.log('‚ùå All location detection failed:', error);
      setLocationStatus('failed');
      setError('Unable to detect your location. Using default station data.');
    } finally {
      setIsLocationUpdating(false);
    }
  }, [getGPSLocation, getLocationName]);

  // ===== UPDATE DASHBOARD WITH LOCATION =====
  const updateDashboardWithLocation = useCallback(async (locationData) => {
    try {
      console.log('üîÑ Updating dashboard with location data...');
      setDataSource('loading');

      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }

      const controller = new AbortController();
      abortControllerRef.current = controller;
      
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      // Save user location to backend first
      try {
        await fetch(`${API_BASE_URL}/api/save-user-location/`, {
          method: 'POST',
          headers: { 
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            username: username,
            latitude: locationData.lat,
            longitude: locationData.lng,
            accuracy: locationData.accuracy,
            source: locationData.source
          })
        });
        console.log('‚úÖ User location saved to backend');
      } catch (locationSaveError) {
        console.warn('‚ö†Ô∏è Failed to save user location:', locationSaveError);
      }

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      url.searchParams.append('lat', locationData.lat.toString());
      url.searchParams.append('lng', locationData.lng.toString());

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('‚úÖ Location-specific data received');
      
      setDataSource('live');
      processDashboardData(data);
      setLastUpdateTime(new Date());
      setError(null);
      
    } catch (error) {
      console.error('‚ùå Error updating with location:', error);
      
      if (error.name !== 'AbortError') {
        setError(`Failed to load live data: ${error.message}`);
      }
      
      setDataSource('cached');
    }
  }, [username, API_BASE_URL]);

  // ===== PROCESS DASHBOARD DATA WITH ENHANCED IDW SUPPORT =====
  const processDashboardData = useCallback((data) => {
    if (!data) {
      console.warn('No data to process');
      return;
    }

    setDashboardData(data);
    
    // Extract health data
    if (data.health_data) {
      setHealthData(data.health_data);
    }

    // Handle user location data
    if (data.user_location) {
      const locationData = data.user_location;
      setUserLocation({
        lat: locationData.latitude,
        lng: locationData.longitude,
        accuracy: locationData.accuracy || null,
        source: 'api',
        timestamp: Date.now()
      });
      
      if (locationData.location_name) {
        setUserLocationName(locationData.location_name);
      }
    }

    // Handle nearest sensor information
    if (data.nearest_sensor_info) {
      const sensorInfo = data.nearest_sensor_info;
      setNearestStation({
        id: sensorInfo.current_device_id,
        distance: sensorInfo.distance_to_nearest,
        name: sensorInfo.current_station_name,
        aqi: sensorInfo.current_aqi,
        isWithin1km: sensorInfo.is_within_1km
      });
    }

    // Handle IDW interpolated data
    if (data.idw_status === 'active' && data.idw_interpolated_values && data.interpolated_aqi) {
      setInterpolatedData({
        values: data.idw_interpolated_values,
        aqi: data.interpolated_aqi
      });
      setDataType('interpolated');
      console.log('‚úÖ Using IDW interpolated data for user location');
    } else if (data.nearest_sensor_info) {
      // Use nearest station data
      setDataType('station');
      console.log('‚úÖ Using nearest station data');
    } else {
      // Fallback to first available station
      if (data.stations) {
        const firstStationId = Object.keys(data.stations)[0];
        if (firstStationId && data.stations[firstStationId]) {
          setNearestStation({
            id: firstStationId,
            distance: null,
            name: data.stations[firstStationId].station_info?.name || 'Station 1',
            aqi: data.stations[firstStationId].highest_sub_index || 50,
            isWithin1km: false
          });
        }
      }
      setDataType('station');
      console.log('‚úÖ Using fallback station data');
    }

    console.log('üìä Dashboard data processed:', {
      dataType: dataType,
      hasUserLocation: !!data.user_location,
      hasIDWData: data.idw_status === 'active',
      nearestStation: data.nearest_sensor_info?.current_station_name
    });
  }, [dataType]);

  // ===== INITIALIZATION =====
  useEffect(() => {
