  }, [loadDashboardInstantly, startSmartLocationDetection]);

  // ===== MEMOIZED CALCULATIONS =====
  const currentValues = useMemo(() => {
    if (!dashboardData) return {};
    
    if (dataType === 'interpolated' && interpolatedData?.values) {
      return interpolatedData.values;
    }
    
    if (nearestStation && dashboardData.stations && dashboardData.stations[nearestStation.id]) {
