    const firstStation = Object.values(dashboardData.stations || {})[0];
    return firstStation?.averages || {};
  }, [dashboardData, dataType, interpolatedData, nearestStation]);

  const currentAQI = useMemo(() => {
    if (dataType === 'interpolated' && interpolatedData?.aqi) {
      return interpolatedData.aqi;
    }
    
    if (nearestStation) {
      return nearestStation.aqi;
    }
    
    return 50; // Default AQI
  }, [dataType, interpolatedData, nearestStation]);

  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
