          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">üè† Home</a></li>
            <li><a href="/forecast" className="nav-link">üìà Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">üìã Health Update</a></li>
            <li><a href="/health-report" className="nav-link">üìÑ Health Report</a></li>
            <li><a href="/add-family" className="nav-link">üë• Add Family</a></li>
            <li className="user-info">üë§ <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">üö™ Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        ‚ÑπÔ∏è <span>
          {userLocationName?.display_name || nearestStation?.name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}
          {nearestStation && nearestStation.distance !== null && !isNaN(nearestStation.distance) && (
            ` ‚Ä¢ Distance to station: ${nearestStation.distance.toFixed(1)}km`
          )}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header with Integrated Health Status */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">üë§</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                {LocationStatus}
                {/* Integrated Health Status */}
                {healthData && (
                  <div className="health-status-inline">
                    <span className="health-label">Your Health Risk Level: </span>
                    <span className={`health-level ${healthData.risk_level.toLowerCase()}`}>
                      {healthData.risk_level} (Score: {healthData.score})
                    </span>
                  </div>
                )}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
              </div>
              <div className="aqi-status">
                {dataType === 'interpolated' ? 'Your Location AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                {userLocationName?.display_name || nearestStation?.name || 'Your Location'}
                {dataType === 'interpolated' && (
                  <span className="smart-indicator"> üéØ Smart</span>
                )}
              </div>
              <div className="last-updated">
                Last updated: {lastUpdateTime.toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Sensor Notification */}
        <div className="sensor-notification">
          <div className="notification-icon">üì°</div>
          <div className="notification-content">
            <strong>
              {dataType === 'interpolated' ? 
                `üìç Showing personalized air quality for your location` :
                `Nearest Sensor: ${nearestStation?.name || 'Station 1 (ASIET Campus)'}`
              }
            </strong>
            {nearestStation?.distance && (
              <span> - {nearestStation.distance.toFixed(1)}km away</span>
            )}
            <div className="notification-description">
              {dataType === 'interpolated' ? 
                'Smart analysis active - calculated specifically for your coordinates using data from multiple monitoring stations' :
                'You are viewing data from our closest monitoring station. Enable precise location for personalized readings.'
              }
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="action-buttons">
          {(locationStatus === 'failed' || locationStatus === 'ip_detected') && (
            <button onClick={handleEnableLocation} className="action-btn primary">
              üéØ Enable Precise GPS Location
            </button>
          )}
          {dataSource === 'sample' && (
            <button onClick={handleEnableLocation} className="action-btn primary">
              üìç Get Live Data for Your Location
            </button>
          )}
          <button onClick={handleRefreshData} className="action-btn secondary">
            üîÑ Refresh Data
          </button>
          {locationStatus === 'gps_detected' && (
            <button 
              onClick={() => {
                console.log('Current location:', userLocation);
                console.log('Location name:', userLocationName);
                alert(`Your Location:\n${userLocationName?.coordinates}\n${userLocationName?.display_name}\nAccuracy: ${userLocation?.accuracy ? Math.round(userLocation.accuracy) + 'm' : 'Unknown'}`);
              }} 
              className="action-btn info"
            >
              üìç View Location Details
            </button>
          )}
        </div>

