import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/Dashboard.css';

// It is best practice to put these in their own files: LazyMap.js and LazyChart.js
const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

// --- Helper Functions ---
const getIPLocation = async () => {
    try {
        const response = await fetch('https://ipapi.co/json/');
        if (!response.ok) return null;
        const data = await response.json();
        return { lat: data.latitude, lng: data.longitude, city: data.city, region: data.region };
    } catch (error) {
        console.warn('IP location fetch failed:', error);
        return null;
    }
};
const calculateDistance = (lat1, lon1, lat2, lon2) => { /* ... distance calculation logic ... */ };
const getAQIColor = (aqi) => { /* ... color logic ... */ };
const getAQIStatus = (aqi) => { /* ... status logic ... */ };


function Dashboard() {
    const { user, logout } = useAuth();
    const [username] = useState(user?.name || 'User');
    const [selectedParameter, setSelectedParameter] = useState('pm25');
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationName, setUserLocationName] = useState('Detecting location...');
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [dataSource, setDataSource] = useState('loading');

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const processDashboardData = useCallback((data) => {
        setDashboardData(data);
    }, []);
    
    // --- Smart Location Detection and Data Fetching ---
    const startSmartLocationDetection = useCallback(async () => {
        setLoading(true);
        let location = null;
        let locationName = "Default Location";

        // Step 1: Try IP Geolocation first
        const ipLocation = await getIPLocation();
        if (ipLocation) {
            location = ipLocation;
            locationName = `${ipLocation.city}, ${ipLocation.region}`;
            setUserLocation(location);
            setUserLocationName(locationName);
        }

        // Step 2: Try high-accuracy GPS
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (pos) => {
                    const gpsLocation = { lat: pos.coords.latitude, lng: pos.coords.longitude };
                    setUserLocation(gpsLocation);
                    setUserLocationName("Your Precise Location"); // Update name for GPS
                    location = gpsLocation;
                },
                () => {
                    console.warn("GPS permission denied. Using IP-based location.");
                },
                { enableHighAccuracy: true, timeout: 5000, maximumAge: 60000 }
            );
        }
        
        // Step 3: Fetch dashboard data
        try {
            const url = `${API_BASE_URL}/api/dashboard/?username=${username}`;
            const response = await fetch(url);
            if (!response.ok) {
                const errData = await response.json();
                if (errData.redirect_to) navigate(errData.redirect_to);
                throw new Error(errData.error);
            }
            const data = await response.json();
            processDashboardData(data);
            setDataSource('live');
        } catch (err) {
            setError(err.message);
            setDataSource('error');
        } finally {
            setLoading(false);
        }
    }, [username, navigate, API_BASE_URL, processDashboardData]);

    useEffect(() => {
        startSmartLocationDetection();
    }, [startSmartLocationDetection]);

    useEffect(() => {
        if (userLocation && dashboardData?.stations) {
            const stations = Object.entries(dashboardData.stations);
            let nearestDist = Infinity, nearestId = null;
            stations.forEach(([id, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
            });
            setNearestStation({ id: nearestId, distance: nearestDist });
            
            let weightedSum = 0, weightSum = 0;
            stations.forEach(([, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
                weightedSum += (station.highest_sub_index || 0) * weight;
                weightSum += weight;
            });
            if (weightSum > 0) {
                setInterpolatedData({ aqi: Math.round(weightedSum / weightSum) });
            }
        }
    }, [userLocation, dashboardData]);

    const displayAqi = interpolatedData?.aqi || Object.values(dashboardData?.stations || {})[0]?.highest_sub_index || 0;
    const alertInfo = useMemo(() => {
        const aqi = displayAqi;
        if (aqi <= 50) return { className: 'good', message: 'Air quality is good. Enjoy your day!' };
        if (aqi <= 100) return { className: 'moderate', message: 'Air quality is moderate. Sensitive individuals should limit outdoor exertion.' };
        return { className: 'unhealthy', message: 'Air quality is unhealthy. It is advisable to reduce outdoor activities.' };
    }, [displayAqi]);
    
    if (loading) return <div className="panel-loader"><div className="loading-spinner"></div><h2>Loading Dashboard...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;
    if (!dashboardData) return <div>No data available.</div>;

    const { health_data, stations, forecasts } = dashboardData;
    const forecastForNearest = nearestStation ? forecasts[nearestStation.id] : null;
    const currentValues = nearestStation ? stations[nearestStation.id]?.averages : Object.values(stations)[0]?.averages;

    return (
        <div className="dashboard-page">
            <nav className="navbar">{/* --- Your Navbar from HomePage.js --- */}</nav>
            <div className="main-container">
                <div className={`alert-banner ${alertInfo.className}`}>
                    <i className="fas fa-info-circle"></i>
                    <span>{alertInfo.message}</span>
                </div>
                <div className="welcome-header">
                    <div className="welcome-text">
                        <h1>Welcome, {username}!</h1>
                        <p>{userLocationName}</p>
                    </div>
                    <div className="aqi-overview">
                        <div className="aqi-value" style={{ color: getAQIColor(displayAqi) }}>{Math.round(displayAqi)}</div>
                        <div className="aqi-status">{getAQIStatus(displayAqi)}</div>
                    </div>
                </div>

                {nearestStation && (
                    <div className="proximity-indicator">
                        <div className="proximity-icon">{nearestStation.distance <= 1 ? 'üéØ' : 'üì°'}</div>
                        <div className="proximity-content">
                            <h4>{nearestStation.distance <= 1 ? 'Smart Analysis Active' : 'Nearest Station Data'}</h4>
                            <p>{`You are ${nearestStation.distance.toFixed(1)}km from ${stations[nearestStation.id]?.station_info.name}. Showing ${nearestStation.distance <= 1 ? 'personalized' : 'nearest station'} AQI.`}</p>
                        </div>
                    </div>
                )}
                
                <div className="dashboard-grid">
                    <div className="dashboard-card">
                        <h3>Sensor Map</h3>
                        <Suspense fallback={<div className="panel-loader">Loading Map...</div>}>
                            <LazyMap userLocation={userLocation} stations={stations} />
                        </Suspense>
                    </div>
                    <div className="dashboard-card">
                        <h3>4-Day Forecast</h3>
                        <Suspense fallback={<div className="panel-loader">Loading Chart...</div>}>
                            <LazyChart forecastData={forecastForNearest} selectedParameter={selectedParameter} />
                        </Suspense>
                    </div>
                </div>
                
                <div className="weather-section">
                  <h2 className="section-title">üå§Ô∏è Weather Conditions (Nearest Station)</h2>
                  <div className="weather-grid">
                    <div className="weather-card">
                        <div>üå°Ô∏è Temperature</div>
                        <div>{currentValues?.temp?.toFixed(1) || 'N/A'}¬∞C</div>
                    </div>
                    <div className="weather-card">
                        <div>üíß Humidity</div>
                        <div>{currentValues?.hum?.toFixed(1) || 'N/A'}%</div>
                    </div>
                    <div className="weather-card">
                        <div>üìè Pressure</div>
                        <div>{currentValues?.pre || 'N/A'} hPa</div>
                    </div>
                  </div>
                </div>

                 <div className="health-section">
                    <div className="health-content">
                        <h3>Your Health Status</h3>
                        <p>Risk Level: <span style={{color: health_data.risk_level === 'Low' ? '#10b981' : '#ef4444', fontWeight: 'bold'}}>{health_data.risk_level}</span></p>
                        <h4>Recommendations:</h4>
                        <ul>
                            {health_data.recommendations.slice(0, 3).map((rec, index) => <li key={index}>{rec}</li>)}
                        </ul>
                    </div>
                    <div className="health-advisory">
                        <h3>General Health Advisory</h3>
                        <p>People with asthma, heart disease, or other respiratory conditions should follow health advisories closely and limit exposure during high AQI periods.</p>
                    </div>
                </div>

            </div>
            <footer className="footer">{/* --- Your Footer from HomePage.js --- */}</footer>
        </div>
    );
}

export default Dashboard;
