import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { useAuth } from '../App';
import './css/MapPage.css';

// Helper function to get AQI color
const getAQIColor = (aqi) => {
  if (aqi === null || aqi === undefined) return '#6b7280';
  if (aqi <= 50) return '#10b981';
  if (aqi <= 100) return '#f59e0b';
  if (aqi <= 150) return '#ef4444';
  if (aqi <= 200) return '#dc2626';
  if (aqi <= 300) return '#7c2d12';
  return '#4c1d95';
};

// Helper function to get AQI status
const getAQIStatus = (aqi) => {
  if (aqi === null || aqi === undefined) return 'Unknown';
  if (aqi <= 50) return 'Good';
  if (aqi <= 100) return 'Moderate';
  if (aqi <= 150) return 'Unhealthy for Sensitive Groups';
  if (aqi <= 200) return 'Unhealthy';
  if (aqi <= 300) return 'Very Unhealthy';
  return 'Hazardous';
};

// Main Component
const MapPage = () => {
  const [map, setMap] = useState(null);
  const [stations, setStations] = useState({});
  const [selectedStationId, setSelectedStationId] = useState('lora-v1');
  const [forecastData, setForecastData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isForecastLoading, setIsForecastLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isPanelOpen, setIsPanelOpen] = useState(true);

  const mapRef = useRef(null);
  const markersRef = useRef({});
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Memoized chart options for better performance
  const chartOptions = useMemo(() => ({
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          color: '#94a3b8',
          font: {
            size: 12
          }
        },
        grid: {
          color: '#334155'
        }
      },
      x: {
        ticks: {
          color: '#94a3b8',
          font: {
            size: 12
          }
        },
        grid: {
          color: '#334155'
        }
      }
    },
    elements: {
      point: {
        radius: 4,
        hoverRadius: 6
      }
    }
  }), []);

  // Memoized chart data
  const chartData = useMemo(() => ({
    labels: forecastData?.map(d => d.day) || [],
    datasets: [{
      label: `${selectedParameter.toUpperCase()} Forecast`,
      data: forecastData?.map(d => d[`${selectedParameter}_max`]) || [],
      borderColor: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      tension: 0.4,
      fill: true
    }]
  }), [forecastData, selectedParameter]);

  // Navigation handlers
  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  const handleLogout = useCallback(() => {
    logout();
    navigate('/');
  }, [logout, navigate]);

  // Effect 1: Load Leaflet and initialize map (runs once)
  useEffect(() => {
    let isMounted = true;
    const initialize = async () => {
      try {
        if (!window.L) {
          // Load Leaflet CSS
          const link = document.createElement('link');
          link.rel = "stylesheet";
          link.href = "https://unpkg.com/leaflet@1.7.1/dist/leaflet.css";
          document.head.appendChild(link);

          // Load Leaflet JS
          const script = document.createElement('script');
          script.src = "https://unpkg.com/leaflet@1.7.1/dist/leaflet.js";
          script.async = true;
          document.head.appendChild(script);
          
          await new Promise((resolve, reject) => {
            script.onload = resolve;
            script.onerror = reject;
          });
        }

        if (isMounted && mapRef.current && !mapRef.current._leaflet_id) {
          const mapInstance = window.L.map(mapRef.current, {
            zoomControl: false,
            attributionControl: false
          }).setView([10.176, 76.430], 15);

          // Add zoom control to top-right
          window.L.control.zoom({
            position: 'topright'
          }).addTo(mapInstance);

          // Use original OpenStreetMap tiles (no dark filter)
          window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '© OpenStreetMap contributors',
            maxZoom: 19
          }).addTo(mapInstance);

          setMap(mapInstance);
        }
      } catch (err) {
        if (isMounted) setError("Failed to load map library.");
      }
    };
    
    initialize();
    return () => { isMounted = false; };
  }, []);

  // Effect 2: Fetch real-time data
  useEffect(() => {
    if (!map) return;
    
    let isMounted = true;
    const fetchRealtimeData = async () => {
      setIsLoading(true);
      setError(null);
      try {
        const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
        if (!response.ok) throw new Error(`Server error: ${response.status}`);
        const data = await response.json();
        if (isMounted) {
          setStations(data.stations);
          // Set first station as default if none selected
          if (!selectedStationId && Object.keys(data.stations).length > 0) {
            setSelectedStationId(Object.keys(data.stations)[0]);
          }
        }
      } catch (err) {
        if (isMounted) setError(err.message);
      } finally {
        if (isMounted) setIsLoading(false);
      }
    };

    fetchRealtimeData();
    
    // Refresh data every 30 seconds
    const interval = setInterval(fetchRealtimeData, 30000);
    return () => {
      clearInterval(interval);
      isMounted = false;
    };
  }, [map, API_BASE_URL, selectedStationId]);

  // Effect 3: Fetch forecast data
  useEffect(() => {
    if (!selectedStationId) return;
    
    let isMounted = true;
    const fetchForecastData = async () => {
      setIsForecastLoading(true);
      try {
        const response = await fetch(`${API_BASE_URL}/api/station/${selectedStationId}/forecast/`);
        if (!response.ok) throw new Error(`Forecast server error: ${response.status}`);
        const data = await response.json();
        if (isMounted) setForecastData(data.forecast_data);
      } catch (err) {
        console.error("Forecast fetch error:", err);
        if (isMounted) setForecastData([]);
      } finally {
        if (isMounted) setIsForecastLoading(false);
      }
    };

    fetchForecastData();
    return () => { isMounted = false; };
  }, [selectedStationId, API_BASE_URL]);

  // Effect 4: Update map markers
  useEffect(() => {
    if (!map || Object.keys(stations).length === 0) return;

    Object.entries(stations).forEach(([id, station]) => {
      const { lat, lng, name } = station.station_info;
      const aqi = station.highest_sub_index;
      const status = getAQIStatus(aqi);
      
      const popupContent = `
        <div style="font-family: Inter, sans-serif; min-width: 200px;">
          <h3 style="margin: 0 0 8px 0; color: #1e293b; font-size: 16px;">${name}</h3>
          <div style="display: flex; align-items: center; gap: 8px; margin-bottom: 8px;">
            <span style="font-size: 24px; font-weight: bold; color: ${getAQIColor(aqi)};">
              ${aqi ? Math.round(aqi) : 'N/A'}
            </span>
            <span style="color: #64748b; font-size: 14px;">AQI</span>
          </div>
          <div style="color: #475569; font-size: 12px; margin-bottom: 8px;">${status}</div>
          <button onclick="window.selectStation('${id}')" 
                  style="background: #3b82f6; color: white; border: none; padding: 6px 12px; 
                         border-radius: 6px; font-size: 12px; cursor: pointer;">
            View Details
          </button>
        </div>
      `;

      if (markersRef.current[id]) {
        markersRef.current[id]
          .setLatLng([lat, lng])
          .setPopupContent(popupContent)
          .setStyle({ 
            fillColor: getAQIColor(aqi),
            color: selectedStationId === id ? '#ffffff' : '#334155',
            weight: selectedStationId === id ? 4 : 2
          });
      } else {
        const marker = window.L.circleMarker([lat, lng], {
          radius: selectedStationId === id ? 18 : 15,
          fillColor: getAQIColor(aqi),
          color: selectedStationId === id ? '#ffffff' : '#334155',
          weight: selectedStationId === id ? 4 : 2,
          opacity: 1,
          fillOpacity: 0.8
        }).addTo(map);

        marker.bindPopup(popupContent).on('click', () => {
          setSelectedStationId(id);
          setIsPanelOpen(true);
        });

        markersRef.current[id] = marker;
      }
    });

    // Global function for popup button
    window.selectStation = (stationId) => {
      setSelectedStationId(stationId);
      setIsPanelOpen(true);
    };

  }, [map, stations, selectedStationId]);

  const selectedStationData = stations[selectedStationId];
  const pollutants = ['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'];

  return (
    <div className="map-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" loading="lazy" width="40" height="40" />
            AirAware
          </Link>
          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>
          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><Link to="/" className="nav-link">Home</Link></li>
            <li><Link to="/map" className="nav-link active">Live Map</Link></li>
            {user ? (
              <>
                <li><Link to="/dashboard" className="nav-link">Dashboard</Link></li>
                <li className="nav-link user-greeting">Hello, {user.name}</li>
                <li>
                  <button onClick={handleLogout} className="nav-link login-btn nav-button">
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </button>
                </li>
              </>
            ) : (
              <li>
                <Link to="/login" className="nav-link login-btn">
                  <i className="fas fa-sign-in-alt"></i> Login
                </Link>
              </li>
            )}
          </ul>
        </div>
      </nav>

      {/* Map Container */}
      <div className="map-container">
        <div id="map" ref={mapRef} className="map-element"></div>
        
        {/* Map Controls */}
        <div className="map-controls">
          <button 
            className={`panel-toggle ${isPanelOpen ? 'active' : ''}`}
            onClick={() => setIsPanelOpen(!isPanelOpen)}
            title={isPanelOpen ? 'Hide Panel' : 'Show Panel'}
          >
            <i className={`fas ${isPanelOpen ? 'fa-chevron-right' : 'fa-chevron-left'}`}></i>
          </button>
        </div>

        {/* Quick AQI Summary */}
        {selectedStationData && (
          <div className="quick-aqi-summary">
            <div className="quick-aqi-value" style={{ color: getAQIColor(selectedStationData.highest_sub_index) }}>
              {Math.round(selectedStationData.highest_sub_index) || 'N/A'}
            </div>
            <div className="quick-aqi-label">
              {selectedStationData.station_info.name}
            </div>
          </div>
        )}
      </div>

      {/* Details Panel */}
      <div className={`details-panel ${isPanelOpen ? 'open' : ''}`}>
        {error && (
          <div className="error-message">
            <i className="fas fa-exclamation-triangle"></i>
            {error}
          </div>
        )}
        
        {isLoading ? (
          <div className="panel-loader">
            <div className="loading-spinner"></div>
            <p>Loading stations...</p>
          </div>
        ) : (
          <>
            <div className="panel-header">
              <h2 className="panel-title">
                <i className="fas fa-map-marker-alt"></i>
                Monitoring Stations
              </h2>
              <div className="station-selector">
                {Object.entries(stations).map(([stationId, station]) => (
                  <button 
                    key={stationId} 
                    className={`station-btn ${selectedStationId === stationId ? 'active' : ''}`} 
                    onClick={() => setSelectedStationId(stationId)}
                  >
                    <div className="station-btn-content">
                      <span className="station-name">{station.station_info.name}</span>
                      <span 
                        className="station-aqi"
                        style={{ color: getAQIColor(station.highest_sub_index) }}
                      >
                        {Math.round(station.highest_sub_index) || 'N/A'}
                      </span>
                    </div>
                  </button>
                ))}
              </div>
            </div>

            <div className="panel-content">
              {selectedStationData ? (
                <>
                  {/* AQI Overview */}
                  <div className="aqi-overview-card" style={{ borderTopColor: getAQIColor(selectedStationData.highest_sub_index) }}>
                    <div className="aqi-overview-header">
                      <h3>Current Air Quality</h3>
                      <span 
                        className="aqi-status-badge"
                        style={{ backgroundColor: getAQIColor(selectedStationData.highest_sub_index) }}
                      >
                        {getAQIStatus(selectedStationData.highest_sub_index)}
                      </span>
                    </div>
                    <div className="aqi-value" style={{ color: getAQIColor(selectedStationData.highest_sub_index) }}>
                      {Math.round(selectedStationData.highest_sub_index) || 'N/A'}
                    </div>
                    <div className="aqi-status-text">Air Quality Index</div>
                    <div className="last-updated">
                      Last updated: {selectedStationData.latest_item?.time || 'N/A'}
                    </div>
                  </div>

                  {/* Pollutant Metrics */}
                  <div className="metrics-section">
                    <h3 className="section-title">
                      <i className="fas fa-chart-bar"></i>
                      Pollutant Levels
                    </h3>
                    <div className="metrics-grid">
                      {pollutants.map(pollutant => (
                        <div key={pollutant} className="metric-card">
                          <div className="metric-label">{pollutant.toUpperCase()}</div>
                          <div className="metric-value">
                            {selectedStationData.averages?.[pollutant] 
                              ? Math.round(selectedStationData.averages[pollutant]) 
                              : '--'
                            }
                          </div>
                          <div className="metric-unit">μg/m³</div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Forecast Section */}
                  <div className="forecast-section">
                    <h3 className="section-title">
                      <i className="fas fa-chart-line"></i>
                      4-Day Forecast
                    </h3>
                    
                    <div className="parameter-controls">
                      {pollutants.map(param => (
                        <button 
                          key={param} 
                          className={`parameter-btn ${selectedParameter === param ? 'active' : ''}`} 
                          onClick={() => setSelectedParameter(param)}
                        >
                          {param.toUpperCase()}
                        </button>
                      ))}
                    </div>

                    <div className="chart-container">
                      {isForecastLoading ? (
                        <div className="chart-loader">
                          <div className="loading-spinner"></div>
                          <p>Loading forecast...</p>
                        </div>
                      ) : forecastData && forecastData.length > 0 ? (
                        <Line data={chartData} options={chartOptions} />
                      ) : (
                        <div className="no-data-message">
                          <i className="fas fa-chart-line"></i>
                          <p>Forecast data not available</p>
                        </div>
                      )}
                    </div>
                  </div>
                </>
              ) : (
                <div className="no-selection">
                  <i className="fas fa-map-marker-alt"></i>
                  <p>Select a monitoring station to view details</p>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default MapPage;
