import React, { useState, useEffect, useRef } from 'react';
import './css/Dashboard.css';

function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [username, setUsername] = useState('');
  const [userLocation, setUserLocation] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [nearestStation, setNearestStation] = useState(null);
  const [forecastData, setForecastData] = useState([]);
  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [allStationsData, setAllStationsData] = useState({});
  const [interpolatedValues, setInterpolatedValues] = useState({});
  const mapRef = useRef(null);
  const chartRef = useRef(null);

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Station locations for IDW calculation
  const STATION_LOCATIONS = {
    "lora-v1": { lat: 10.178385739668958, lon: 76.43052237497399, name: "ASIET Campus Station" },
    "loradev2": { lat: 10.17095090340159, lon: 76.42962876824544, name: "Mattoor Junction Station" }
  };

  useEffect(() => {
    initializeDashboard();
    
    // Set up real-time updates
    const interval = setInterval(() => {
      if (userLocation) {
        fetchAllData();
      }
    }, 30000); // Every 30 seconds

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (userLocation) {
      fetchAllData();
    }
  }, [userLocation]);

  const initializeDashboard = () => {
    // Get username from localStorage
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const userName = user.name || 'User';
    setUsername(userName);

    if (!userName || userName === 'User') {
      setError('User not found. Please login again.');
      setLoading(false);
      return;
    }

    // Get user location with high accuracy
    getUserLocation();
  };

  const getUserLocation = () => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by this browser.');
      setLoading(false);
      return;
    }

    const options = {
      enableHighAccuracy: true,
      timeout: 10000,
      maximumAge: 300000 // 5 minutes
    };

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const location = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          accuracy: position.coords.accuracy
        };
        setUserLocation(location);
        console.log('User location obtained:', location);
      },
      (error) => {
        console.error('Geolocation error:', error);
        // Use default location (ASIET Campus) if geolocation fails
        setUserLocation({
          lat: 10.178385739668958,
          lng: 76.43052237497399,
          accuracy: null
        });
      },
      options
    );
  };

  // IDW (Inverse Distance Weighting) interpolation
  const calculateIDW = (userLat, userLng, stationsData, parameter, power = 2) => {
    let weightedSum = 0;
    let weightSum = 0;
    
    for (const [stationId, stationData] of Object.entries(stationsData)) {
      if (!STATION_LOCATIONS[stationId] || !stationData.latest_item) continue;
      
      const stationLat = STATION_LOCATIONS[stationId].lat;
      const stationLng = STATION_LOCATIONS[stationId].lon;
      
      // Calculate distance in kilometers
      const distance = calculateDistance(userLat, userLng, stationLat, stationLng);
      
      if (distance === 0) {
        // If user is exactly at station location, return station value
        return parseFloat(stationData.latest_item[parameter] || 0);
      }
      
      const weight = 1 / Math.pow(distance, power);
      const value = parseFloat(stationData.latest_item[parameter] || 0);
      
      weightedSum += weight * value;
      weightSum += weight;
    }
    
    return weightSum > 0 ? weightedSum / weightSum : 0;
  };

  const calculateDistance = (lat1, lng1, lat2, lng2) => {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const findNearestStation = (userLat, userLng) => {
    let nearestStation = null;
    let minDistance = Infinity;

    for (const [stationId, location] of Object.entries(STATION_LOCATIONS)) {
      const distance = calculateDistance(userLat, userLng, location.lat, location.lon);
      if (distance < minDistance) {
        minDistance = distance;
        nearestStation = {
          id: stationId,
          name: location.name,
          distance: distance,
          ...location
        };
      }
    }

    return nearestStation;
  };

  const fetchAllData = async () => {
    try {
      setLoading(true);

      // Fetch all stations data
      const allStationsResponse = await fetch(`${API_BASE_URL}/api/all-devices/`);
      const allStationsData = await allStationsResponse.json();
      setAllStationsData(allStationsData);

      // Find nearest station
      const nearest = findNearestStation(userLocation.lat, userLocation.lng);
      setNearestStation(nearest);

      // Calculate IDW interpolated values for user location
      const parameters = ['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'];
      const interpolated = {};
      
      parameters.forEach(param => {
        interpolated[param] = calculateIDW(
          userLocation.lat, 
          userLocation.lng, 
          allStationsData, 
          param
        );
      });
      setInterpolatedValues(interpolated);

      // Fetch dashboard data with username
      await fetchDashboardData();

      // Fetch forecast data for nearest station
      await fetchForecastData(nearest?.id || 'lora-v1');

    } catch (err) {
      console.error('Error fetching all data:', err);
      setError('Failed to fetch data. Please check your connection.');
    } finally {
      setLoading(false);
    }
  };

  const fetchDashboardData = async () => {
    try {
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      
      if (userLocation) {
        url.searchParams.append('lat', userLocation.lat);
        url.searchParams.append('lng', userLocation.lng);
      }

      const response = await fetch(url);
      const data = await response.json();

      if (response.ok) {
        if (data.redirect_to === '/health-assessment') {
          window.location.href = '/health-assessment';
          return;
        }
        setDashboardData(data);
        setError(null);
      } else {
        if (response.status === 404 && data.redirect_to) {
          window.location.href = data.redirect_to;
          return;
        }
        setError(data.message || data.error || 'Failed to fetch dashboard data');
      }
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setError('Network error - please check your connection');
    }
  };

  const fetchForecastData = async (stationId) => {
    try {
      // This would typically be a separate API endpoint for forecast data
      // For now, we'll generate sample forecast data
      const mockForecast = generateMockForecast();
      setForecastData(mockForecast);
    } catch (err) {
      console.error('Error fetching forecast data:', err);
    }
  };

  const generateMockForecast = () => {
    const days = ['Today', 'Tomorrow', 'Day 3', 'Day 4', 'Day 5'];
    return days.map((day, index) => ({
      day,
      pm25_max: Math.round(25 + Math.random() * 30),
      pm10_max: Math.round(40 + Math.random() * 40),
      so2_max: Math.round(20 + Math.random() * 30),
      no2_max: Math.round(30 + Math.random() * 25),
      co_max: (1.0 + Math.random() * 2.0).toFixed(1),
      o3_max: Math.round(40 + Math.random() * 35),
      nh3_max: Math.round(10 + Math.random() * 20)
    }));
  };

  // Initialize Leaflet map
  useEffect(() => {
    if (userLocation && !mapRef.current) {
      initializeMap();
    }
  }, [userLocation]);

  const initializeMap = () => {
    if (typeof window !== 'undefined' && window.L && userLocation) {
      const map = window.L.map('dashboard-map').setView([userLocation.lat, userLocation.lng], 12);
      
      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Â© OpenStreetMap contributors'
      }).addTo(map);

      // Add user location marker
      const userIcon = window.L.divIcon({
        html: '<i class="fas fa-user-circle" style="color: #3b82f6; font-size: 20px;"></i>',
        iconSize: [20, 20],
        className: 'custom-div-icon'
      });
      
      window.L.marker([userLocation.lat, userLocation.lng], { icon: userIcon })
        .addTo(map)
        .bindPopup('Your Location')
        .openPopup();

      // Add station markers
      Object.entries(STATION_LOCATIONS).forEach(([stationId, location]) => {
        const stationData = allStationsData[stationId];
        const aqi = stationData?.highest_sub_index || 'N/A';
        
        const stationIcon = window.L.divIcon({
          html: `<div class="station-marker">
                  <i class="fas fa-broadcast-tower"></i>
                  <span>${aqi}</span>
                 </div>`,
          iconSize: [40, 40],
          className: 'custom-station-icon'
        });

        window.L.marker([location.lat, location.lon], { icon: stationIcon })
          .addTo(map)
          .bindPopup(`
            <strong>${location.name}</strong><br/>
            AQI: ${aqi}<br/>
            Status: ${getAQIStatus(aqi).status}
          `);
      });

      mapRef.current = map;
    }
  };

  // Initialize forecast chart
  useEffect(() => {
    if (forecastData.length > 0 && !chartRef.current) {
      initializeForecastChart();
    }
  }, [forecastData, selectedParameter]);

  const initializeForecastChart = () => {
    if (typeof window !== 'undefined' && window.Chart && forecastData.length > 0) {
      const ctx = document.getElementById('forecast-chart');
      if (!ctx) return;

      if (chartRef.current) {
        chartRef.current.destroy();
      }

      const data = forecastData.map(item => item[`${selectedParameter}_max`]);
      const labels = forecastData.map(item => item.day);

      chartRef.current = new window.Chart(ctx, {
        type: 'line',
        data: {
          labels: labels,
          datasets: [{
            label: selectedParameter.toUpperCase(),
            data: data,
            borderColor: '#3b82f6',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            tension: 0.4,
            fill: true
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      });
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const getAQIStatus = (aqiValue) => {
    if (!aqiValue || aqiValue === 'N/A') return { status: 'Unknown', color: '#6b7280', alertClass: 'good' };
    
    const value = parseInt(aqiValue, 10);
    if (value <= 50) {
      return { status: 'GOOD', color: '#10b981', alertClass: 'good' };
    } else if (value <= 100) {
      return { status: 'MODERATE', color: '#f59e0b', alertClass: 'moderate' };
    } else if (value <= 150) {
      return { status: 'UNHEALTHY', color: '#ef4444', alertClass: 'unhealthy' };
    } else {
      return { status: 'HAZARDOUS', color: '#7c2d12', alertClass: 'hazardous' };
    }
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return dateString;
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleRetry = () => {
    initializeDashboard();
  };

  const handleParameterChange = (parameter) => {
    setSelectedParameter(parameter);
    
    // Update active button
    document.querySelectorAll('.param-btn').forEach(btn => {
      btn.classList.remove('active');
    });
    document.querySelector(`[data-param="${parameter}"]`).classList.add('active');
    
    // Reinitialize chart with new parameter
    if (chartRef.current) {
      chartRef.current.destroy();
      chartRef.current = null;
    }
    setTimeout(initializeForecastChart, 100);
  };

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading dashboard data...</p>
          <small>Detecting location and fetching sensor data...</small>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-page">
        <div className="error-container">
          <div className="error-message">
            <h2>â ï¸ Dashboard Unavailable</h2>
            <p>{error}</p>
            <button onClick={handleRetry} className="retry-button">
              Retry
            </button>
            {error.includes('login') && (
              <button onClick={handleLogout} className="retry-button" style={{ marginLeft: '10px' }}>
                Go to Login
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  // Use interpolated values for display, fallback to nearest station data
  const currentData = dashboardData?.nearest_sensor_info || {};
  const healthData = dashboardData?.health_data || {};
  const displayValues = {
    pm25: interpolatedValues.pm25?.toFixed(1) || currentData.current_sensor_data?.pm25 || '25',
    pm10: interpolatedValues.pm10?.toFixed(1) || currentData.current_sensor_data?.pm10 || '40',
    so2: interpolatedValues.so2?.toFixed(1) || currentData.current_sensor_data?.so2 || '35',
    no2: interpolatedValues.no2?.toFixed(1) || currentData.current_sensor_data?.no2 || '37',
    co: interpolatedValues.co?.toFixed(1) || currentData.current_sensor_data?.co || '1.2',
    o3: interpolatedValues.o3?.toFixed(1) || currentData.current_sensor_data?.o3 || '45',
    nh3: interpolatedValues.nh3?.toFixed(1) || currentData.current_sensor_data?.nh3 || '15'
  };

  // Calculate interpolated AQI
  const interpolatedAQI = Math.max(...Object.values(interpolatedValues).map(v => v || 0));
  const aqiStatus = getAQIStatus(interpolatedAQI || currentData.current_aqi);

