import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/HealthReport.css';

const LazyChart = React.lazy(() => import('./LazyChart'));

// --- Helper Functions (same as Dashboard.js) ---
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const getAQIColor = (aqi) => {
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 150) return '#ef4444';
    return '#7c2d12';
};

const getAQIStatus = (aqi) => {
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    return 'Hazardous';
};

// --- IDW Interpolation Function (same as Dashboard.js) ---
const calculateIDWInterpolation = (userLocation, stations) => {
    const stationIds = Object.keys(stations);
    let totalWeight = 0;
    const weightedValues = {
        pm25: 0, pm10: 0, so2: 0, no2: 0, 
        co: 0, o3: 0, nh3: 0, temp: 0, hum: 0, pre: 0
    };
    let weightedAqi = 0;

    stationIds.forEach(stationId => {
        const station = stations[stationId];
        const distance = calculateDistance(
            userLocation.lat,
            userLocation.lng,
            station.station_info.lat,
            station.station_info.lng
        );

        const safeDistance = Math.max(distance, 0.001);
        const weight = 1.0 / (safeDistance ** 2);
        totalWeight += weight;

        const averages = station.averages || {};
        Object.keys(weightedValues).forEach(param => {
            if (averages[param] !== undefined) {
                weightedValues[param] += averages[param] * weight;
            }
        });

        weightedAqi += (station.highest_sub_index || 0) * weight;
    });

    const interpolated_values = {};
    Object.keys(weightedValues).forEach(param => {
        interpolated_values[param] = totalWeight > 0 ? 
            Math.round((weightedValues[param] / totalWeight) * 100) / 100 : 0;
    });

    const interpolated_aqi = totalWeight > 0 ? 
        Math.round(weightedAqi / totalWeight) : 50;

    return {
        interpolated_values,
        interpolated_aqi,
        stations_used: stationIds.length,
        method: 'idw'
    };
};

// --- Get User Location Function ---
const getUserLocation = () => {
    return new Promise((resolve, reject) => {
        if (!navigator.geolocation) {
            reject(new Error('Geolocation not supported'));
            return;
        }

        navigator.geolocation.getCurrentPosition(
            (position) => {
                const location = {
                    lat: position.coords.latitude,
                    lng: position.coords.longitude,
                    accuracy: position.coords.accuracy,
                    source: 'gps',
                    timestamp: Date.now()
                };
                
                if (isNaN(location.lat) || isNaN(location.lng)) {
                    reject(new Error('Invalid GPS coordinates'));
                    return;
                }
                
                resolve(location);
            },
            (error) => {
                reject(error);
            },
            { 
                enableHighAccuracy: true, 
                timeout: 10000, 
                maximumAge: 300000
            }
        );
    });
};

// Health Recommendations based on AQI and Health Risk Level
const getHealthRecommendations = (aqi, healthRiskLevel) => {
    const baseRecommendations = {
        good: {
            general: [
                'ðŸŒŸ Perfect air quality - enjoy all outdoor activities',
                'ðŸš¶â€â™‚ï¸ Great time for outdoor exercise and sports',
                'ðŸªŸ Open windows to let fresh air in',
                'ðŸ‘¶ Safe conditions for children to play outside'
            ],
            sensitive: [
                'âœ… Excellent conditions for people with respiratory issues',
                'ðŸƒâ€â™€ï¸ No restrictions on outdoor activities',
                'ðŸ’¨ Breathe easy - air quality is optimal'
            ]
        },
        moderate: {
            general: [
                'âš ï¸ Air quality is acceptable for most people',
                'ðŸƒâ€â™‚ï¸ Reduce prolonged outdoor exertion if sensitive',
                'ðŸ˜· Consider wearing a mask during heavy traffic hours',
                'ðŸŒ… Best outdoor times: early morning and late evening'
            ],
            sensitive: [
                'âš ï¸ Limit prolonged outdoor activities',
                'ðŸ˜· Wear N95 mask when going outside',
                'ðŸ  Stay indoors during peak pollution hours',
                'ðŸ’Š Keep rescue medications readily available'
            ]
        },
        unhealthy: {
            general: [
                'ðŸš¨ Avoid prolonged outdoor activities',
                'ðŸ  Stay indoors as much as possible',
                'ðŸ˜· Wear N95 mask if you must go outside',
                'ðŸªŸ Keep windows and doors closed'
            ],
            sensitive: [
                'ðŸš¨ URGENT: Stay indoors immediately',
                'ðŸ¥ Seek medical attention if experiencing symptoms',
                'ðŸ˜· Mandatory N95 mask for any outdoor exposure',
                'ðŸ“ž Contact doctor if breathing difficulties occur'
            ]
        },
        hazardous: {
            general: [
                'ðŸš¨ EMERGENCY: Avoid all outdoor activities',
                'ðŸ  Remain indoors with air purification',
                'ðŸ˜· N95 mask mandatory for any exposure',
                'ðŸ¥ Seek immediate medical help for symptoms'
            ],
            sensitive: [
                'ðŸš¨ HEALTH EMERGENCY: Immediate indoor shelter',
                'ðŸ¥ Seek emergency medical attention',
                'ðŸ“ž Call emergency services if severe symptoms',
                'ðŸš— Consider relocation to cleaner air area'
            ]
        }
    };

    // Determine category
    let category = 'good';
    if (aqi > 150) category = 'hazardous';
    else if (aqi > 100) category = 'unhealthy'; 
    else if (aqi > 50) category = 'moderate';

    // Determine if user is sensitive based on health risk level
    const isSensitive = healthRiskLevel === 'High' || healthRiskLevel === 'Critical';
    
    return {
        recommendations: isSensitive ? baseRecommendations[category].sensitive : baseRecommendations[category].general,
        isSensitive,
        category,
        isEmergency: category === 'hazardous' || (category === 'unhealthy' && isSensitive)
    };
};

// Emergency contacts based on AQI level
const getEmergencyContacts = (aqi) => {
    if (aqi <= 100) return null;
    
    return {
        primary: {
            name: 'Kerala Pollution Control Board',
            number: '0471-2418566',
            description: '24/7 Air Quality Emergency Hotline'
        },
        medical: {
            name: 'Medical Emergency',
            number: '108',
            description: 'Immediate medical assistance'
        },
        poison: aqi > 150 ? {
            name: 'Poison Control',
            number: '1066',
            description: 'Pollution-related health emergencies'
        } : null
    };
};

function HealthReport() {
    const { user } = useAuth();
    const [username] = useState(user?.name || null);
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [currentTime, setCurrentTime] = useState(new Date());
    
    // --- ADDED: State for location and interpolation (same as Dashboard) ---
    const [userLocation, setUserLocation] = useState(null);
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [locationStatus, setLocationStatus] = useState('initializing');
    const [currentDataInfo, setCurrentDataInfo] = useState(null);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
    ? 'https://airaware-app-gcw7.onrender.com' 
    : 'http://localhost:8000';

    // Update time every minute
    useEffect(() => {
        const timer = setInterval(() => setCurrentTime(new Date()), 60000);
        return () => clearInterval(timer);
    }, []);

    const fetchReportData = useCallback(async () => {
        if (!username) { navigate('/login'); return; }
        setLoading(true);
        try {
            const url = `${API_BASE_URL}/api/health-report/?username=${username}`;
            const response = await fetch(url);
            if (!response.ok) {
                const errData = await response.json();
                if (errData.redirect_to) navigate(errData.redirect_to);
                throw new Error(errData.error);
            }
            const data = await response.json();
            setReportData(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    // --- ADDED: Get user location ---
    const getUserLocationForReport = useCallback(async () => {
        try {
            setLocationStatus('detecting');
            const location = await getUserLocation();
            setUserLocation(location);
            setLocationStatus('gps_detected');
            console.log('ðŸ“ Health Report: Location obtained:', location);
        } catch (error) {
            console.log('ðŸ“ Health Report: Location detection failed:', error);
            setLocationStatus('failed');
        }
    }, []);

    useEffect(() => {
        fetchReportData();
        getUserLocationForReport();
    }, [fetchReportData, getUserLocationForReport]);

    // --- ADDED: Process location and interpolation (same logic as Dashboard.js) ---
    useEffect(() => {
        if (userLocation && reportData?.stations) {
            console.log('ðŸ”„ Health Report: Processing location and stations data...');
            
            const stations = reportData.stations;
            const stationIds = Object.keys(stations);
            
            // Find nearest station
            let nearestDist = Infinity, nearestId = null;
            stationIds.forEach((id) => {
                const station = stations[id];
                const dist = calculateDistance(
                    userLocation.lat, 
                    userLocation.lng, 
                    station.station_info.lat, 
                    station.station_info.lng
                );
                if (dist < nearestDist) { 
                    nearestDist = dist; 
                    nearestId = id; 
                }
            });
            
            setNearestStation({ 
                id: nearestId, 
                distance: nearestDist,
                station: stations[nearestId]
            });

            // Determine if user is within interpolation range (1km from any station)
            const stationDistances = {};
            stationIds.forEach(stationId => {
                const station = stations[stationId];
                const distance = calculateDistance(
                    userLocation.lat,
                    userLocation.lng,
                    station.station_info.lat,
                    station.station_info.lng
                );
                stationDistances[stationId] = { distance, station };
            });

            const isWithinSensorRange = Object.values(stationDistances).some(s => s.distance <= 1.0);
            
            if (isWithinSensorRange || nearestDist <= 1.0) {
                // User is within 1km - use interpolation
                const idwResult = calculateIDWInterpolation(userLocation, stations);
                
                setInterpolatedData({ 
                    aqi: idwResult.interpolated_aqi,
                    values: idwResult.interpolated_values
                });
                
                setCurrentDataInfo({
                    method: 'location_interpolation',
                    source: 'interpolated',
                    explanation: `You are ${nearestDist.toFixed(1)}km from the nearest sensor. Showing calculated air quality for your exact location using data from nearby monitoring stations.`,
                    values: idwResult.interpolated_values,
                    aqi: idwResult.interpolated_aqi,
                    station_name: `Your Location (Calculated)`,
                    is_interpolated: true,
                    distance: nearestDist,
                    data_type: 'Your Location Data (Calculated)'
                });
                
                console.log('âœ… Health Report: Using interpolated data - AQI:', idwResult.interpolated_aqi);
            } else {
                // User is >1km away - use nearest station
                const nearestStationData = stations[nearestId];
                
                setCurrentDataInfo({
                    method: 'nearest_station',
                    source: 'nearest_station',
                    explanation: `You are ${nearestDist.toFixed(1)}km from the nearest sensor. Showing data from ${nearestStationData.station_info.name} (nearest monitoring station).`,
                    values: nearestStationData.averages || {},
                    aqi: nearestStationData.highest_sub_index || 50,
                    station_name: nearestStationData.station_info.name,
                    is_interpolated: false,
                    distance: nearestDist,
                    data_type: 'Nearest Station Data'
                });
                
                console.log('âœ… Health Report: Using nearest station data - AQI:', nearestStationData.highest_sub_index);
            }
        } else if (reportData?.stations) {
            // No location - use default station
            const stations = reportData.stations;
            const defaultStation = stations['lora-v1'] || stations[Object.keys(stations)[0]] || {};
            
            setCurrentDataInfo({
                method: 'default_station',
                source: 'default',
                explanation: 'Location not available. Showing data from default monitoring station.',
                values: defaultStation.averages || {},
                aqi: defaultStation.highest_sub_index || 50,
                station_name: defaultStation.station_info?.name || 'ASIET Campus Station',
                is_interpolated: false,
                distance: null,
                data_type: 'Default Station Data'
            });
            
            console.log('âœ… Health Report: Using default station data');
        }
    }, [userLocation, reportData]);

    // --- UPDATED: Memoized calculations using correct AQI logic ---
    const displayAqi = useMemo(() => {
        if (currentDataInfo?.is_interpolated && interpolatedData) {
            return interpolatedData.aqi;
        } else if (currentDataInfo?.aqi) {
            return currentDataInfo.aqi;
        } else {
            return Object.values(reportData?.stations || {})[0]?.highest_sub_index || 0;
        }
    }, [currentDataInfo, interpolatedData, reportData]);

    const healthRecommendations = useMemo(() => {
        if (!reportData?.health_assessment) return null;
        return getHealthRecommendations(displayAqi, reportData.health_assessment.risk_level);
    }, [displayAqi, reportData]);

    const emergencyContacts = useMemo(() => {
        return getEmergencyContacts(displayAqi);
    }, [displayAqi]);

    const aqiStatus = useMemo(() => {
        const status = getAQIStatus(displayAqi);
        const color = getAQIColor(displayAqi);
        return { status, color };
    }, [displayAqi]);

    // Event handlers
    const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
    const handleLogout = useCallback(() => {
        localStorage.clear();
        navigate('/login');
    }, [navigate]);

    const handleRefresh = useCallback(() => {
        fetchReportData();
        getUserLocationForReport();
    }, [fetchReportData, getUserLocationForReport]);

    const handlePrint = useCallback(() => {
        window.print();
    }, []);

    if (loading) return <div className="panel-loader"><h2>ðŸ¥ Generating Your Health Report...</h2><div className="loading-spinner"></div></div>;
    
    if (error) return (
      <div className="error-message">
        <h2>âš ï¸ Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate('/dashboard')} className="retry-btn">
          ðŸ“Š Go to Dashboard
        </button>
      </div>
    );
    
    if (!reportData) return <div className="error-message"><h2>ðŸ“Š No Report Data</h2><p>Unable to generate your health report at this time.</p></div>;

    const { health_assessment, stations, forecasts } = reportData;
    const forecastForNearest = nearestStation ? forecasts[nearestStation.id] : null;

    return (
        <div className="report-page">
            {/* Real-time Status Bar */}
            <div className="realtime-status">
                ðŸ”´ LIVE HEALTH REPORT â€¢ Updated: {currentTime.toLocaleTimeString('en-IN')} â€¢ 
                {currentDataInfo?.is_interpolated ? ' Smart Location Analysis' : ' Government Standards Applied'}
            </div>

            {/* Navigation (same as Dashboard) */}
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" style={{ marginRight: '12px' }} />
                        AirAware Kerala
                    </Link>

                    <div className="menu-toggle" onClick={toggleMenu}>â˜°</div>

                    <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                        <li><Link to="/" className="nav-link">ðŸ  Home</Link></li>
                        <li><Link to="/dashboard" className="nav-link">Profile</Link></li>
                        <li><Link to="/health-assessment" className="nav-link">ðŸ“‹ Health Update</Link></li>
                        <li><Link to="/add-family" className="nav-link">ðŸ‘¥ Add Family</Link></li>
                        <li><Link to="/map" className="nav-link">ðŸ—ºï¸ Live Map</Link></li>
                        <li className="user-info">ðŸ‘¤ <span>{username}</span></li>
                        <li>
                            <button onClick={handleLogout} className="nav-link login-btn">ðŸšª Logout</button>
                        </li>
                    </ul>
                </div>
            </nav>

            {/* Health Alert Banner */}
            {healthRecommendations?.isEmergency && (
                <div className="emergency-alert-banner">
                    ðŸš¨ <strong>HEALTH EMERGENCY ALERT:</strong> 
                    AQI {Math.round(displayAqi)} - {aqiStatus.status} conditions detected.
                    {healthRecommendations.isSensitive && ' High-risk individual - immediate action required!'}
                </div>
            )}

            {/* UPDATED: AQI Alert Banner with location awareness */}
            <div className={`alert-banner ${aqiStatus.status.toLowerCase()}`} style={{ backgroundColor: getAQIColor(displayAqi) + '20', borderBottom: `3px solid ${getAQIColor(displayAqi)}` }}>
                â„¹ï¸ <span>
                    <strong>CURRENT AIR QUALITY:</strong> 
                    {currentDataInfo?.is_interpolated ? ' Your Location' : ' Nearest Station'} AQI is {Math.round(displayAqi)} - {aqiStatus.status}
                    {currentDataInfo?.distance && ` â€¢ Distance: ${currentDataInfo.distance.toFixed(1)}km from nearest sensor`}
                </span>
            </div>

            <div className="report-container">
                {/* Enhanced Header Section */}
                <div className="header-section">
                    <div className="official-seal">ðŸ›ï¸</div>
                    <div className="government-badge">
                        Air Quality Monitoring
                    </div>
                    <div className="document-id">
                        DOC-ID: HR-{username}-{new Date().getFullYear()}{String(new Date().getMonth() + 1).padStart(2, '0')}{String(new Date().getDate()).padStart(2, '0')}
                    </div>
                    <h1>ðŸ¥ Official Air Quality Health Report</h1>
                    <p>
                        Personalized health assessment based on your location's air quality and personal health profile.
                        {currentDataInfo?.is_interpolated && ' Using advanced spatial interpolation for precise location analysis.'}
                    </p>
                    <div className="report-metadata">
                        <div className="metadata-item">
                            <strong>Generated:</strong> {new Date().toLocaleString('en-IN')}
                        </div>
                        <div className="metadata-item">
                            <strong>Patient:</strong> {username}
                        </div>
                        <div className="metadata-item">
                            <strong>Location:</strong> {currentDataInfo?.is_interpolated ? 'Your Current Location' : 'Nearest Station Data'}
                        </div>
                        <div className="metadata-item">
                            <strong>Data Source:</strong> {currentDataInfo?.station_name || 'ASIET Campus Station'}
                        </div>
                    </div>
                </div>

                {/* UPDATED: Executive Summary with correct AQI */}
                <div className="executive-summary">
