import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/AdminDashboard.css';

// --- Edit User Modal Component ---
const EditUserModal = ({ user, onClose, onSave }) => {
    const [name, setName] = useState(user.name);
    const [phone, setPhone] = useState(user.phone_number);

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave({ ...user, name, phone_number: phone });
    };

    return (
        <div className="modal-backdrop">
            <div className="modal-content">
                <h2>Edit User: {user.name}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Full Name</label>
                        <input
                            id="name"
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="phone">Phone Number</label>
                        <input
                            id="phone"
                            type="text"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            required
                        />
                    </div>
                    <div className="modal-actions">
                        <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
                        <button type="submit" className="btn-primary">Save Changes</button>
                    </div>
                </form>
            </div>
        </div>
    );
};


const AdminDashboard = () => {
    const [dashboardData, setDashboardData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [lastRefresh, setLastRefresh] = useState(new Date());
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingUser, setEditingUser] = useState(null);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
        ? 'https://airaware-app-gcw7.onrender.com' 
        : 'http://localhost:8000';

    const formatTimestamp = (timestamp) => {
        if (!timestamp) return 'No data';
        try {
            const date = new Date(timestamp);
            return date.toLocaleString('en-IN', { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' });
        } catch { return timestamp; }
    };
    
    const getAQIStatus = (aqi) => {
        if (aqi === null || aqi === undefined) return { status: 'No Data', class: 'aqi-moderate' };
        const value = parseInt(aqi);
        if (value <= 50) return { status: 'Good', class: 'aqi-good' };
        if (value <= 100) return { status: 'Moderate', class: 'aqi-moderate' };
        if (value <= 200) return { status: 'Unhealthy', class: 'aqi-unhealthy' };
        return { status: 'Hazardous', class: 'aqi-hazardous' };
    };

    const fetchData = useCallback(async () => {
        setError(null);
        try {
            const response = await fetch(`${API_BASE_URL}/api/admin/dashboard/`);
            if (!response.ok) throw new Error(`HTTP ${response.status}: Could not fetch data`);
            const data = await response.json();
            setDashboardData(data);
            setLastRefresh(new Date());
        } catch (err) {
            setError(err.message.includes('fetch') ? `Cannot connect to server.` : err.message);
        } finally {
            setLoading(false);
        }
    }, [API_BASE_URL]);


    useEffect(() => {
        if (!localStorage.getItem('admin_user')) {
            navigate('/admin/login');
            return;
        }
        fetchData();
        const interval = setInterval(fetchData, 30000);
        return () => clearInterval(interval);
    }, [fetchData, navigate]);
    
    const handleDeleteUser = async (userId, userName) => {
        if (window.confirm(`Are you sure you want to delete user: ${userName}? This action cannot be undone.`)) {
            try {
                const response = await fetch(`${API_BASE_URL}/api/admin/users/delete/${userId}/`, {
                    method: 'DELETE',
                });

                if (response.status === 204) {
                    alert('User deleted successfully.');
                    fetchData();
                    return;
                }

                const contentType = response.headers.get("content-type");
                let errorText;

                if (contentType && contentType.indexOf("application/json") !== -1) {
                    const errData = await response.json();
                    errorText = errData.error || JSON.stringify(errData);
                } else {
                    errorText = await response.text();
                }
                
                throw new Error(errorText);

            } catch (err) {
                console.error("Delete error:", err);
                alert(`Error: Could not delete the user.\n\nServer response:\n${err.message}`);
            }
        }
    };

    const handleUpdateUser = async (updatedUser) => {
        try {
            const response = await fetch(`${API_BASE_URL}/api/admin/users/update/${updatedUser.id}/`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    name: updatedUser.name,
                    phone_number: updatedUser.phone_number
                })
            });
            if (!response.ok) {
                 const errData = await response.json();
                 throw new Error(errData.error || 'Update failed');
            }
            const savedUser = await response.json();
            
            setDashboardData(prevData => ({
                ...prevData,
                users: prevData.users.map(u => u.id === savedUser.id ? savedUser : u)
            }));
            alert('User updated successfully.');
            setIsModalOpen(false);
            setEditingUser(null);
        } catch (err) {
            console.error("Update error:", err);
            alert(`Error: Could not update user details. ${err.message}`);
        }
    };

    const openEditModal = (user) => {
        setEditingUser(user);
        setIsModalOpen(true);
    };

    const handleLogout = () => {
        localStorage.removeItem('admin_user');
        navigate('/admin/login');
    };

    const handleRefresh = () => {
        setLoading(true);
        fetchData();
    };

    if (loading) return <div className="panel-loader"><h2>Loading Command Center...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p><button onClick={handleRefresh}>Retry</button></div>;
    if (!dashboardData) return <div className="panel-loader"><h2>No Data Available</h2></div>;

    const { station_data = {}, users = [], health_assessments = [] } = dashboardData;
    const allParameters = ['pm25', 'pm10', 'co', 'nh3', 'no2', 'so2', 'o3', 'temperature', 'humidity'];

    return (
        <div className="main-wrapper">
            {isModalOpen && <EditUserModal user={editingUser} onClose={() => setIsModalOpen(false)} onSave={handleUpdateUser} />}
            
            <aside className="sidebar">
                <div className="sidebar-header"><h1 className="sidebar-title"><i className="fas fa-wind"></i> AQI Monitor</h1></div>
                <ul className="nav flex-column">
                    <li className="nav-item"><a className="nav-link active" href="#"><i className="fas fa-tachometer-alt nav-icon"></i> Dashboard</a></li>
                    <li className="nav-item"><a className="nav-link" href="#stations"><i className="fas fa-satellite-dish nav-icon"></i> Stations</a></li>
                    <li className="nav-item"><a className="nav-link" href="#users"><i className="fas fa-users nav-icon"></i> Users</a></li>
                    <li className="nav-item"><a className="nav-link" href="#assessments"><i className="fas fa-heartbeat nav-icon"></i> Health Reports</a></li>
                </ul>
            </aside>

            <main className="content-area">
                <div className="topbar">
                    <div>
                        <h1 className="topbar-title">Admin Command Center</h1>
                        <p>Last Updated: {formatTimestamp(lastRefresh)}</p>
                    </div>
                    <div className="user-dropdown">
                        <button onClick={handleRefresh} className="btn-refresh"><i className="fas fa-sync-alt"></i> Refresh</button>
                        <button onClick={handleLogout} className="btn-logout"><i className="fas fa-sign-out-alt"></i> Logout</button>
                    </div>
                </div>
                
                <div className="station-grid" id="stations">
                    {Object.entries(station_data).map(([stationId, stationInfo]) => {
                        const latestItem = stationInfo.latest_item || {};
                        const aqiStatus = getAQIStatus(stationInfo.aqi);
                        return (
                            <div className="dashboard-card" key={stationId}>
                                <div className="card-header">
                                    <h5 className="card-title"><i className="fas fa-broadcast-tower"></i> {stationId.toUpperCase().replace('-', ' ')}</h5>
                                    <span className={`status-badge ${latestItem.received_at ? 'status-ONLINE' : 'status-OFFLINE'}`}>
                                        {latestItem.received_at ? 'ONLINE' : 'OFFLINE'}
                                    </span>
                                </div>
                                <div className="card-body">
                                    <div style={{ textAlign: 'center', marginBottom: '16px' }}>
                                        <div className={`aqi-value-large ${aqiStatus.class}`}>{stationInfo.aqi ? Math.round(stationInfo.aqi) : 'N/A'}</div>
                                        <div className="aqi-status-text">{aqiStatus.status}</div>
                                    </div>
                                    <div className="station-details">
                                        {allParameters.map(param => (
                                            <div className="detail-item" key={param}>
                                                <div className="detail-label">{param.toUpperCase()}</div>
                                                <div className="detail-value">{latestItem[param] !== undefined && latestItem[param] !== null ? String(latestItem[param]) : 'N/A'}</div>
                                            </div>
                                        ))}
                                    </div>
                                    <div className="last-updated">
                                        <i className="fas fa-clock"></i> Last Received: {formatTimestamp(latestItem.received_at)}
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                </div>

                <div className="data-card" id="users">
                    <div className="card-header"><h4 className="card-title"><i className="fas fa-users"></i> Registered Users ({users.length})</h4></div>
                    <div className="card-body p-0">
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>User ID</th>
                                    <th>Full Name</th>
                                    <th>Phone Number</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(user => (
                                    <tr key={user.id}>
                                        <td><strong>#{user.id}</strong></td>
                                        <td>{user.name}</td>
                                        <td>{user.phone_number}</td>
                                        <td>
                                            <button className="btn-action btn-edit" onClick={() => openEditModal(user)}><i className="fas fa-pencil-alt"></i> Edit</button>
                                            <button className="btn-action btn-delete" onClick={() => handleDeleteUser(user.id, user.name)}><i className="fas fa-trash-alt"></i> Delete</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
                
                <div className="data-card" id="assessments">
                    <div className="card-header"><h4 className="card-title"><i className="fas fa-heartbeat"></i> Health Assessments ({health_assessments.length})</h4></div>
                    <div className="card-body p-0">
                        {health_assessments.length > 0 ? (
                            <table className="data-table">
                                <thead>
                                    <tr>
                                        <th>Assessment ID</th>
                                        <th>User Name</th>
                                        <th>Age Group</th>
                                        <th>Gender</th>
                                        <th>Health Score</th>
                                        <th>Risk Level</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {health_assessments.map(assessment => {
                                        const score = assessment.health_score;
                                        let riskLevel = 'Low';
                                        let riskClass = 'risk-low';
                                        if (score >= 100) { riskLevel = 'High'; riskClass = 'risk-high'; }
                                        else if (score >= 50) { riskLevel = 'Moderate'; riskClass = 'risk-moderate'; }
                                        return (
                                            <tr key={assessment.id}>
                                                <td><strong>#{assessment.id}</strong></td>
                                                <td>{assessment.user__name}</td>
                                                <td>{assessment.age_group}</td>
                                                <td>{assessment.gender}</td>
                                                <td><span className={`health-score ${riskClass}`}>{score}</span></td>
                                                <td><span className={`status-badge ${riskClass}`}>{riskLevel}</span></td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        ) : (
                            <div style={{ padding: '40px', textAlign: 'center', color: '#6b7280' }}><p>No health assessments found</p></div>
                        )}
                    </div>
                </div>

                <div className="data-card">
                    <div className="card-header"><h4 className="card-title"><i className="fas fa-chart-bar"></i> System Overview</h4></div>
                    <div className="card-body">
                        <div className="station-details">
                            <div className="detail-item">
                                <div className="detail-label">Active Stations</div>
                                <div className="detail-value">{Object.values(station_data).filter(station => station.latest_item?.received_at).length}/{Object.keys(station_data).length}</div>
                            </div>
                            <div className="detail-item">
                                <div className="detail-label">Total Users</div>
                                <div className="detail-value">{users.length}</div>
                            </div>
                            <div className="detail-item">
                                <div className="detail-label">Health Assessments</div>
                                <div className="detail-value">{health_assessments.length}</div>
                            </div>
                            <div className="detail-item">
                                <div className="detail-label">High Risk Users</div>
                                <div className="detail-value" style={{ color: '#dc2626' }}>{health_assessments.filter(h => h.health_score >= 100).length}</div>
                            </div>
                            <div className="detail-item">
                                <div className="detail-label">Average AQI</div>
                                <div className="detail-value">
                                    {Object.values(station_data).length > 0 ? 
                                        Math.round(Object.values(station_data).filter(s => s.aqi).reduce((sum, s) => sum + s.aqi, 0) / Object.values(station_data).filter(s => s.aqi).length) || 'N/A'
                                        : 'N/A'
                                    }
                                </div>
                            </div>
                            <div className="detail-item">
                                <div className="detail-label">System Status</div>
                                <div className="detail-value" style={{ color: '#16a34a' }}><i className="fas fa-check-circle"></i> Operational</div>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default AdminDashboard;
