// Fixed Dashboard.js - Add username parameter to API call

import React, { useState, useEffect } from 'react';
import './css/Dashboard.css';

function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [username, setUsername] = useState('');
  const [userLocation, setUserLocation] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  useEffect(() => {
    // Get username from localStorage
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const userName = user.name || 'User';
    setUsername(userName);

    // Get user location
    getUserLocation();

    // Fetch dashboard data ONLY after we have username
    if (userName && userName !== 'User') {
      fetchDashboardData(userName);
    } else {
      setError('User not found in localStorage. Please login again.');
      setLoading(false);
    }

    // Set up auto-refresh ONLY if we have username
    let interval;
    if (userName && userName !== 'User') {
      interval = setInterval(() => fetchDashboardData(userName), 30000); // Every 30 seconds
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, []);

  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }
  };

  const fetchDashboardData = async (userName) => {
    if (!userName || userName === 'User') {
      setError('Username not available. Please login again.');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      
      // Build URL with username parameter
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', userName);
      
      // Add location parameters if available
      if (userLocation) {
        url.searchParams.append('lat', userLocation.lat);
        url.searchParams.append('lng', userLocation.lng);
      }

      console.log('Fetching dashboard data from:', url.toString());

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();
      console.log('Dashboard API response:', data);

      if (response.ok) {
        // Check if we need to redirect to health assessment
        if (data.redirect_to === '/health-assessment') {
          window.location.href = '/health-assessment';
          return;
        }
        
        setDashboardData(data);
        setError(null);
      } else {
        // Handle specific error cases
        if (response.status === 404 && data.redirect_to) {
          // User needs to complete health assessment
          window.location.href = data.redirect_to;
          return;
        }
        
        setError(data.message || data.error || 'Failed to fetch dashboard data');
      }
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setError('Network error - please check your connection');
    } finally {
      setLoading(false);
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const getAQIStatus = (aqiValue) => {
    if (!aqiValue) return { status: 'Unknown', color: '#6b7280', alertClass: 'good' };
    
    const value = parseInt(aqiValue, 10);
    if (value <= 50) {
      return { status: 'GOOD', color: '#10b981', alertClass: 'good' };
    } else if (value <= 100) {
      return { status: 'MODERATE', color: '#f59e0b', alertClass: 'moderate' };
    } else if (value <= 150) {
      return { status: 'UNHEALTHY', color: '#ef4444', alertClass: 'unhealthy' };
    } else {
      return { status: 'HAZARDOUS', color: '#7c2d12', alertClass: 'hazardous' };
    }
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return dateString;
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleRetry = () => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const userName = user.name;
    if (userName) {
      fetchDashboardData(userName);
    } else {
      setError('Please login again');
    }
  };

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-page">
        <div className="error-container">
          <div className="error-message">
            <h2>âš ï¸ Dashboard Unavailable</h2>
            <p>{error}</p>
            <button onClick={handleRetry} className="retry-button">
              Retry
            </button>
            {error.includes('login') && (
              <button onClick={handleLogout} className="retry-button" style={{ marginLeft: '10px' }}>
                Go to Login
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  // Extract data safely with fallbacks
  const currentData = dashboardData?.nearest_sensor_info || {};
  const healthData = dashboardData?.health_data || {};
  const aqiStatus = getAQIStatus(currentData.current_aqi);

  return (
    <div className="dashboard-page">
      {/* Real-time Status Indicator */}
      <div className="realtime-status">
        ðŸ”´ LIVE â€¢ Updates every 30s
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">
              <i className="fas fa-home"></i> Home
            </a></li>
            <li><a href="/forecast" className="nav-link">
              <i className="fas fa-chart-line"></i> Forecasting
            </a></li>
            <li><a href="/health-assessment" className="nav-link">
              <i className="fas fa-notes-medical"></i> Health Update
            </a></li>
            <li><a href="/health-report" className="nav-link">
              <i className="fas fa-file-medical-alt"></i> Health Report
            </a></li>
            <li><a href="/add-family" className="nav-link">
              <i className="fas fa-user-plus"></i> Add Family
            </a></li>

            <li className="user-info">
              <i className="fas fa-user-circle"></i>
              <span>{username}</span>
            </li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">
                <i className="fas fa-sign-out-alt"></i> Logout
              </button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.alertClass}`}>
        <i className="fas fa-info-circle alert-icon"></i>
        <span>
          Current AQI: {currentData.current_aqi || 'N/A'} - {aqiStatus.status}
          {currentData.is_within_1km && ' (Within 1km of sensor)'}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">
                <i className="fas fa-user"></i>
              </div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                <div className="location-info">
                  <i className="fas fa-map-marker-alt"></i>
                  <span>
                    {userLocation 
                      ? `ðŸ“ ${currentData.current_station_name || 'Detecting location...'}`
                      : 'ðŸ“ Enable location for better accuracy'
                    }
                  </span>
                </div>
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {currentData.current_aqi || 'N/A'}
              </div>
              <div className="aqi-status">Air Quality Index</div>
              <div className="aqi-badge">
                {currentData.current_station_name || 'Station 1'}
              </div>
              <div className="last-updated">
                Last updated: {formatDateTime(currentData.current_sensor_data?.received_at)}
              </div>
            </div>
          </div>
        </div>

        {/* Health Status Section - Display health data if available */}
        {healthData && healthData.score && (
          <div className="health-section">
            <div className="health-content">
              <div className="health-chart-container">
                <div className="card-header">
                  <h3 className="card-title">â¤ï¸ Your Health Status</h3>
                </div>
                <div className="health-score-display">
                  <div className="health-score-circle" style={{ borderColor: healthData.risk_color }}>
                    <div className="health-score-value" style={{ color: healthData.risk_color }}>
                      {healthData.score}
                    </div>
                    <div className="health-risk-level" style={{ color: healthData.risk_color }}>
                      {healthData.risk_level}
                    </div>
                  </div>
                  <div className="health-score-label">Health Score</div>
                </div>
              </div>
              <div className="health-alert">
                <h3>
                  <i className="fas fa-shield-alt"></i>
                  Personalized Health Advisory
                </h3>
                <p>
                  Based on your health assessment (Risk Level: <strong style={{ color: healthData.risk_color }}>
                  {healthData.risk_level}</strong>), 
                  {healthData.is_high_risk 
                    ? ' we strongly recommend following our personalized guidelines and consulting healthcare professionals.' 
                    : ' you can enjoy normal outdoor activities with standard precautions.'
                  }
                </p>
                
                {/* Display recommendations if available */}
                {healthData.recommendations && healthData.recommendations.length > 0 && (
                  <div className="health-recommendations">
                    <h4>ðŸŽ¯ Personalized Recommendations:</h4>
                    <ul>
                      {healthData.recommendations.slice(0, 3).map((rec, index) => (
                        <li key={index}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
                
                {/* Priority actions for high-risk users */}
                {healthData.priority_actions && healthData.priority_actions.length > 0 && (
                  <div className="priority-actions">
                    <h4>âš ï¸ Priority Actions:</h4>
                    <ul>
                      {healthData.priority_actions.slice(0, 2).map((action, index) => (
                        <li key={index} style={{ color: '#ef4444' }}>{action}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Rest of your existing dashboard content... */}
        {/* Sensor Notification */}
        <div className="sensor-notification">
          <i className="fas fa-broadcast-tower notification-icon"></i>
          <strong>ðŸ“¡ Nearest Sensor: {currentData.current_station_name || 'Station 1'}</strong>
          {currentData.distance_to_nearest && (
            <span> - {currentData.distance_to_nearest.toFixed(1)}km away</span>
          )}
          <br />
          You are viewing data from our closest monitoring station.
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">ðŸŒ¬ï¸ Current Air Quality Conditions</h2>
          <div className="metrics-grid">
            <MetricCard
              icon="fas fa-flask"
              value={currentData.current_sensor_data?.nh3 || '15'}
              label="NHâ‚ƒ (Ammonia)"
              unit="Âµg/mÂ³"
            />
            <MetricCard
              icon="fas fa-sun"
              value={currentData.current_sensor_data?.o3 || '45'}
              label="Oâ‚ƒ (Ozone)"
              unit="Âµg/mÂ³"
            />
            <MetricCard
              icon="fas fa-industry"
              value={currentData.current_sensor_data?.so2 || '35'}
              label="SOâ‚‚ (Sulfur Dioxide)"
              unit="Âµg/mÂ³"
            />
            <MetricCard
              icon="fas fa-cloud"
              value={currentData.current_sensor_data?.co || '1.2'}
              label="CO (Carbon Monoxide)"
              unit="mg/mÂ³"
            />
            <MetricCard
              icon="fas fa-smog"
              value={currentData.current_sensor_data?.no2 || '37'}
              label="NOâ‚‚ (Nitrogen Dioxide)"
              unit="Âµg/mÂ³"
            />
            <MetricCard
              icon="fas fa-seedling"
              value={currentData.current_sensor_data?.pm25 || '25'}
              label="PM2.5"
              unit="Âµg/mÂ³"
            />
            <MetricCard
              icon="fas fa-leaf"
              value={currentData.current_sensor_data?.pm10 || '40'}
              label="PM10"
              unit="Âµg/mÂ³"
            />
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ—ºï¸ Interactive Sensor Map</h3>
            </div>
            <div className="map-container">
              <div className="map-placeholder">
                <i className="fas fa-map-marked-alt"></i>
                <p>Interactive map will be loaded here</p>
                <button className="map-button">View Full Map</button>
              </div>
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ“Š AQI Forecast</h3>
            </div>
            <div className="forecast-content">
              <div className="forecast-controls">
                <button className="param-btn active">PM2.5</button>
                <button className="param-btn">PM10</button>
                <button className="param-btn">NOâ‚‚</button>
                <button className="param-btn">Oâ‚ƒ</button>
              </div>
              <div className="forecast-chart">
                <p>Forecast data will be displayed here</p>
              </div>
            </div>
          </div>
        </div>

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">ðŸŒ¤ï¸ Weather Conditions</h2>
          <div className="weather-grid">
            <WeatherCard
              icon="fas fa-temperature-high"
              value={`${currentData.current_sensor_data?.temp || '28'}Â°C`}
              label="Temperature"
            />
            <WeatherCard
              icon="fas fa-tint"
              value={`${currentData.current_sensor_data?.hum || '65'}%`}
              label="Humidity"
            />
            <WeatherCard
              icon="fas fa-compress-alt"
              value={`${currentData.current_sensor_data?.pre || '1013'} hPa`}
              label="Atmospheric Pressure"
            />
          </div>
        </div>

        {/* Data Disclaimer */}
        <div className="disclaimer">
          <h3>
            <i className="fas fa-info-circle"></i>
            ðŸ“‹ Data Methodology & Government Compliance
          </h3>
          <p>
            <strong>Advanced Air Quality Monitoring System:</strong> This government-approved 
            platform monitors air quality across Kerala and provides accurate updates for your 
            location using data from nearby monitoring stations.
          </p>

          <div className="disclaimer-highlight">
            <p><strong>ðŸ›ï¸ Kerala Government Air Quality Network:</strong></p>
            <ul>
              <li><strong>5 monitoring stations:</strong> Government-approved, CPCB-certified equipment</li>
              <li><strong>Real-time quality assurance:</strong> Automated data validation and accuracy verification</li>
              <li><strong>Government compliance:</strong> Full adherence to CPCB and Kerala State Pollution Control Board standards</li>
              <li><strong>AQI Persistence:</strong> Values remain constant for 12-hour periods for health planning stability</li>
            </ul>
          </div>

          <p>
            <strong>âš ï¸ Information & Usage:</strong> Air quality readings are updated every few seconds. 
            For serious health concerns, always rely on official sources and medical advice.
          </p>

          <p>
            <strong>ðŸ”„ System Status:</strong> 
            <span style={{ color: '#10b981', fontWeight: 600 }}> OPERATIONAL</span> | 
            Last update: {formatDateTime(currentData.current_sensor_data?.received_at)}
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirQuality Kerala</h3>
              <p className="footer-text">
                Leading provider of real-time air quality monitoring solutions in Kerala, 
                committed to environmental sustainability and public health awareness.
              </p>
              <div className="social-links">
                <a href="#" className="social-link"><i className="fab fa-facebook-f"></i></a>
                <a href="#" className="social-link"><i className="fab fa-twitter"></i></a>
                <a href="#" className="social-link"><i className="fab fa-linkedin-in"></i></a>
                <a href="#" className="social-link"><i className="fab fa-instagram"></i></a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <a href="/" className="footer-link">Home</a>
              <a href="/map" className="footer-link">Live Map</a>
              <a href="/forecast" className="footer-link">Forecasting</a>
              <a href="/health-report" className="footer-link">Health Report</a>
            </div>

            <div className="footer-section">
              <h3>Services</h3>
              <a href="/forecast" className="footer-link">AQI Forecasting</a>
              <a href="/health-assessment" className="footer-link">Health Assessment</a>
              <a href="/map" className="footer-link">Live AQI Map</a>
              <a href="#" className="footer-link">Data Analytics</a>
            </div>

            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Vidya Bharathi Nagar, Mattoor<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AirQuality Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

// Helper Components
function MetricCard({ icon, value, label, unit }) {
  return (
    <div className="metric-card">
      <div className="metric-icon">
        <i className={icon}></i>
      </div>
      <div className="metric-value">{value}</div>
      <div className="metric-label">{label}</div>
      {unit && <div className="metric-unit">{unit}</div>}
    </div>
  );
}

function WeatherCard({ icon, value, label }) {
  return (
    <div className="weather-card">
      <div className="weather-icon">
        <i className={icon}></i>
      </div>
      <div className="weather-value">{value}</div>
      <div className="weather-label">{label}</div>
    </div>
  );
}

export default Dashboard;
