// Fixed Dashboard.js - All critical issues resolved, no JSX errors

import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// Lazy loading components with immediate fallbacks
const LazyMap = React.lazy(() => 
  import('./LazyMap').catch(() => ({ 
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">üó∫Ô∏è</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() => 
  import('./LazyChart').catch(() => ({ 
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">üìä</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// IP-based location service with better error handling
const getIPLocation = async () => {
  try {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 3000);
    
    const response = await fetch('https://ipapi.co/json/', { 
      signal: controller.signal,
      headers: {
        'Accept': 'application/json',
        'User-Agent': 'AirAware/1.0'
      }
    });
    
    clearTimeout(timeoutId);
    
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }
    
    const data = await response.json();
    
    // Validate required fields
    if (!data.latitude || !data.longitude) {
      throw new Error('Invalid location data');
    }
    
    return {
      lat: parseFloat(data.latitude),
      lng: parseFloat(data.longitude),
      city: data.city || 'Unknown City',
      region: data.region || 'Unknown Region',
      country: data.country || 'Unknown Country',
      accuracy: 10000, // IP accuracy ~10km
      source: 'ip'
    };
  } catch (error) {
    console.log('IP location failed:', error);
    return null;
  }
};

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {nav
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [dataType, setDataType] = useState('station');
  const [dataSource, setDataSource] = useState('loading');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());

  const navigate = useNavigate();
  
  // FIX: Safe API_BASE_URL handling
  const API_BASE_URL = (() => {
    try {
      return process.env.REACT_APP_API_URL || 'http://localhost:8000';
    } catch (e) {
      return 'http://localhost:8000';
    }
  })();
  
  const locationTimeoutRef = useRef(null);
  const abortControllerRef = useRef(null);

  // ===== INSTANT DASHBOARD LOAD WITH SAMPLE DATA =====
  const loadDashboardInstantly = useCallback(async () => {
    console.log('üöÄ Loading dashboard instantly...');
    setDataSource('sample');
    setLocationStatus('detecting');
    
    // Set sample data immediately (no API call blocking)
    const sampleData = {
      nearest_sensor_info: {
        current_device_id: 'lora-v1',
        current_aqi: 50,
        current_station_name: 'Station 1 (ASIET Campus)',
        current_sensor_data: {
          pm25: 25, pm10: 42, so2: 8, no2: 40,
          co: 1.1, o3: 46, nh3: 93, temp: 28, hum: 65, pre: 1013
        },
        distance_to_nearest: null,
        is_within_1km: false,
        current_forecast_data: [
          { day: 'Tomorrow', pm25_max: 30, pm10_max: 37, so2_max: 8, no2_max: 40, co_max: 1.1, o3_max: 46, nh3_max: 93 },
          { day: 'Day 2', pm25_max: 24, pm10_max: 34, so2_max: 8, no2_max: 45, co_max: 1.2, o3_max: 43, nh3_max: 100 },
          { day: 'Day 3', pm25_max: 24, pm10_max: 30, so2_max: 8, no2_max: 42, co_max: 1.2, o3_max: 45, nh3_max: 100 },
          { day: 'Day 4', pm25_max: 20, pm10_max: 23, so2_max: 8, no2_max: 38, co_max: 1.2, o3_max: 47, nh3_max: 100 }
        ]
      },
      sensor_locations: {
        'lora-v1': { lat: 10.178322301561957, lng: 76.43089144291672, name: 'Station 1 (ASIET Campus)' },
        'loradev2': { lat: 10.17095090340159, lng: 76.42962876824544, name: 'Station 2 (Mattoor Junction)' }
      },
      idw_status: 'location_required',
      status: 'sample_data',
      forecast_data: {
        nearest_station: [
          { day: 'Today', pm25_max: 30, pm10_max: 37, so2_max: 8, no2_max: 40, co_max: 1.1, o3_max: 46, nh3_max: 93 },
          { day: 'Tomorrow', pm25_max: 24, pm10_max: 34, so2_max: 8, no2_max: 45, co_max: 1.2, o3_max: 43, nh3_max: 100 },
          { day: 'Day 3', pm25_max: 24, pm10_max: 30, so2_max: 8, no2_max: 42, co_max: 1.2, o3_max: 45, nh3_max: 100 },
          { day: 'Day 4', pm25_max: 20, pm10_max: 23, so2_max: 8, no2_max: 38, co_max: 1.2, o3_max: 47, nh3_max: 100 }
        ]
      }
    };

    processDashboardData(sampleData);
    setLastUpdateTime(new Date());
    console.log('‚úÖ Dashboard loaded instantly with sample data');
  }, []);

  // ===== SMART LOCATION DETECTION WITH BETTER ERROR HANDLING =====
  const startSmartLocationDetection = useCallback(async () => {
    console.log('üìç Starting smart location detection...');
    setLocationStatus('detecting');
    setIsLocationUpdating(true);

    try {
      // STEP 1: Try IP-based location first (fast, approximate)
      const ipLocation = await getIPLocation();
      if (ipLocation) {
        console.log('üìç Got IP location:', ipLocation);
        setUserLocation(ipLocation);
        setUserLocationName({ 
          display_name: `${ipLocation.city}, ${ipLocation.region}`,
          city: ipLocation.city,
          state: ipLocation.region,
          country: ipLocation.country
        });
        setLocationStatus('ip_detected');
        
        // Update with IP-based data
        await updateDashboardWithLocation(ipLocation);
      }

      // STEP 2: Try GPS for precise location (if available)
      if (navigator.geolocation) {
        try {
          const gpsLocation = await getGPSLocation();
          if (gpsLocation) {
            console.log('üìç Got GPS location:', gpsLocation);
            setUserLocation(gpsLocation);
            setLocationStatus('gps_detected');
            
            // Get precise location name
            await getLocationName(gpsLocation.lat, gpsLocation.lng);
            
            // Update with GPS-based data
            await updateDashboardWithLocation(gpsLocation);
          }
        } catch (gpsError) {
          console.log('üìç GPS detection failed:', gpsError);
          // Keep IP location if GPS fails
        }
      }

    } catch (error) {
      console.log('üìç Location detection failed:', error);
      setLocationStatus('failed');
    } finally {
      setIsLocationUpdating(false);
    }
  }, []);

  // ===== GPS LOCATION WITH BETTER TIMEOUT HANDLING =====
  const getGPSLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation not supported'));
        return;
      }

      const timeout = setTimeout(() => {
        reject(new Error('GPS timeout'));
      }, 8000); // Increased timeout

      navigator.geolocation.getCurrentPosition(
        (position) => {
          clearTimeout(timeout);
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy,
            source: 'gps',
            timestamp: Date.now()
          };
          
          // Validate coordinates
          if (isNaN(location.lat) || isNaN(location.lng)) {
            reject(new Error('Invalid GPS coordinates'));
            return;
          }
          
          resolve(location);
        },
        (error) => {
          clearTimeout(timeout);
          console.log('GPS error:', error.message);
          reject(error);
        },
        { 
          enableHighAccuracy: true, 
          timeout: 8000, 
          maximumAge: 300000 
        }
      );
    });
  }, []);

  // ===== REVERSE GEOCODING WITH CORS FIX =====
  const getLocationName = useCallback(async (lat, lng) => {
    try {
      // FIX: Use CORS-enabled proxy or alternative service
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);
      
      // Alternative: Use a CORS proxy or different geocoding service
      const proxyUrl = 'https://api.allorigins.win/raw?url=';
      const geocodeUrl = `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`;
      
      const response = await fetch(`${proxyUrl}${encodeURIComponent(geocodeUrl)}`, {
        signal: controller.signal,
        headers: {
          'Accept': 'application/json'
        }
      });
      
      clearTimeout(timeoutId);
      
      if (response.ok) {
        const data = await response.json();
        const locationName = {
          display_name: data.display_name || `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
          city: data.address?.city || data.address?.town || data.address?.village || 'Unknown City',
          state: data.address?.state || 'Unknown State',
          country: data.address?.country || 'Unknown Country'
        };
        
        setUserLocationName(locationName);
        console.log('üìç Location name resolved:', locationName.display_name);
      }
    } catch (error) {
      console.log('‚ùå Reverse geocoding failed:', error);
      // Set fallback location name
      setUserLocationName({
        display_name: `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
        city: 'Unknown City',
        state: 'Unknown State',
        country: 'Unknown Country'
      });
    }
  }, []);

  // ===== UPDATE DASHBOARD WITH LOCATION - WITH BETTER ERROR HANDLING =====
  const updateDashboardWithLocation = useCallback(async (locationData) => {
    try {
      console.log('üîÑ Updating dashboard with location data...');
      setDataSource('loading');

      // Abort previous request if exists
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }

      const controller = new AbortController();
      abortControllerRef.current = controller;
      
      const timeoutId = setTimeout(() => controller.abort(), 15000); // Increased timeout

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      url.searchParams.append('lat', locationData.lat.toString());
      url.searchParams.append('lng', locationData.lng.toString());

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('‚úÖ Location-specific data received');
      
      setDataSource('live');
      processDashboardData(data);
      setLastUpdateTime(new Date());
      setError(null);
      
    } catch (error) {
      console.error('‚ùå Error updating with location:', error);
      
      // Don't show error for aborted requests
      if (error.name !== 'AbortError') {
        setError(`Failed to load live data: ${error.message}`);
      }
      
      setDataSource('cached');
      // Keep existing data, don't clear dashboard
    }
  }, [username, API_BASE_URL]);

  // ===== PROCESS DASHBOARD DATA WITH NULL CHECKS =====
  const processDashboardData = useCallback((data) => {
    if (!data) {
      console.warn('No data to process');
      return;
    }

    setDashboardData(data);
    
    // Extract nearest station info with null checks
    if (data.nearest_sensor_info) {
      const stationInfo = data.nearest_sensor_info;
      setNearestStation({
        name: stationInfo.current_station_name || 'Station 1 (ASIET Campus)',
        distance: stationInfo.distance_to_nearest || null,
        aqi: stationInfo.current_aqi || 50
      });
    }

    // Set data type based on proximity and IDW status
    if (data.idw_status === 'active' && data.idw_interpolated_values && 
        Object.keys(data.idw_interpolated_values).length > 0) {
      setDataType('interpolated');
    } else {
      setDataType('station');
    }

    console.log('üìä Dashboard data processed:', {
      dataType: data.idw_status === 'active' ? 'interpolated' : 'station',
      nearestStation: data.nearest_sensor_info?.current_station_name,
      hasLocation: !!data.user_location
    });
  }, []);

  // ===== INITIALIZATION WITH CLEANUP =====
  useEffect(() => {
    const initializeDashboard = async () => {
      console.log('‚ö° Initializing production dashboard...');
      
      try {
        // Load dashboard instantly with sample data
        await loadDashboardInstantly();
        
        // Start location detection in background (non-blocking)
        setTimeout(() => {
          startSmartLocationDetection();
        }, 500);
      } catch (error) {
        console.error('Dashboard initialization error:', error);
        setError('Failed to initialize dashboard');
      }
    };

    initializeDashboard();

    return () => {
      // Cleanup on unmount
      if (locationTimeoutRef.current) {
        clearTimeout(locationTimeoutRef.current);
      }
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, [loadDashboardInstantly, startSmartLocationDetection]);

  // ===== MEMOIZED CALCULATIONS WITH NULL CHECKS =====
  const currentValues = useMemo(() => {
    if (!dashboardData) return {};
    
    // Use IDW interpolated values if available
    if (dataType === 'interpolated' && dashboardData.idw_interpolated_values) {
      return dashboardData.idw_interpolated_values;
    }
    
    // Use nearest station data
    if (dashboardData.nearest_sensor_info && dashboardData.nearest_sensor_info.current_sensor_data) {
      return dashboardData.nearest_sensor_info.current_sensor_data;
    }
    
    return {};
  }, [dashboardData, dataType]);

  const currentAQI = useMemo(() => {
    if (!dashboardData) return 50;
    
    // Use interpolated AQI if available
    if (dataType === 'interpolated' && dashboardData.interpolated_aqi) {
      return dashboardData.interpolated_aqi;
    }
    
    // Use nearest station AQI
    if (dashboardData.nearest_sensor_info && dashboardData.nearest_sensor_info.current_aqi) {
      return dashboardData.nearest_sensor_info.current_aqi;
    }
    
    return 50;
  }, [dashboardData, dataType]);

  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  // ===== PROXIMITY INDICATOR WITH SAFE DISTANCE CALCULATION =====
  const ProximityIndicator = useMemo(() => {
    const getProximityInfo = () => {
      if (!userLocation || !nearestStation) {
        return {
          icon: 'üì°',
          title: 'Default Station Data',
          message: 'Enable location to check proximity to sensor nodes',
          class: 'default'
        };
      }

      const distance = nearestStation.distance;
      
      // FIX: Null-safe distance check
      if (distance !== null && distance !== undefined && !isNaN(distance)) {
        const isWithin1km = distance <= 1.0;

        if (isWithin1km) {
          return {
            icon: 'üéØ',
            title: 'Within 1km - Smart Analysis Active',
            message: `You are ${distance.toFixed(1)}km from ${nearestStation.name}. Showing personalized air quality data calculated for your exact location.`,
            class: 'within-1km'
          };
        } else {
          return {
            icon: 'üì°',
            title: 'Outside 1km - Nearest Station Data',
            message: `You are ${distance.toFixed(1)}km from ${nearestStation.name}. Showing data from the nearest monitoring station.`,
            class: 'outside-1km'
          };
        }
      } else {
        return {
          icon: 'üì°',
          title: 'Station Data',
          message: `Showing data from ${nearestStation.name}. Distance calculation in progress.`,
          class: 'calculating'
        };
      }
    };

    const proximityInfo = getProximityInfo();

    return (
      <div className={`proximity-indicator ${proximityInfo.class}`}>
        <div className="proximity-icon">{proximityInfo.icon}</div>
        <div className="proximity-content">
          <div className="proximity-title">{proximityInfo.title}</div>
          <div className="proximity-message">{proximityInfo.message}</div>
        </div>
      </div>
    );
  }, [userLocation, nearestStation]);

  // ===== SAFE VALUE FORMATTING =====
  const formatValue = useCallback((value, metric) => {
    const numValue = parseFloat(value);
    if (isNaN(numValue)) return '0';
    
    return metric === 'co' ? numValue.toFixed(1) : Math.round(numValue);
  }, []);

  // ===== LOCATION STATUS WITH SAFE DISTANCE DISPLAY =====
  const LocationStatus = useMemo(() => {
    const getLocationDisplay = () => {
      switch (locationStatus) {
        case 'initializing':
          return <span style={{ color: '#6b7280' }}>üìç Initializing location detection...</span>;
          
        case 'detecting':
          return (
            <span style={{ color: '#f59e0b' }}>
              üìç Detecting your location...
              {isLocationUpdating && <span className="location-spinner">‚ü≥</span>}
            </span>
          );
          
        case 'ip_detected':
          const ipName = userLocationName?.display_name || 'Approximate location';
          return (
            <span style={{ color: '#3b82f6' }}>
              üìç {ipName} (approximate)
              {isLocationUpdating && <span className="location-spinner">‚ü≥</span>}
            </span>
          );
          
        case 'gps_detected':
          const gpsName = userLocationName?.display_name || 'Your location';
          const accuracy = userLocation?.accuracy ? `¬±${Math.round(userLocation.accuracy)}m` : '';
          
          // FIX: Safe distance display
          let distanceText = '';
          if (nearestStation && nearestStation.distance !== null && nearestStation.distance !== undefined && !isNaN(nearestStation.distance)) {
            distanceText = `${nearestStation.distance.toFixed(1)}km`;
          }
          
          return (
            <span style={{ color: '#10b981' }}>
              üìç {gpsName} {accuracy && `(${accuracy})`}
              {nearestStation && distanceText && (
                <span style={{ color: '#6b7280', fontSize: '0.9em' }}>
                  {' '} ‚Üí {nearestStation.name} ({distanceText})
                </span>
              )}
            </span>
          );
          
        case 'failed':
          return (
            <span style={{ color: '#ef4444' }}>
              üìç Location detection failed - Using sample data
              <button onClick={startSmartLocationDetection} className="retry-location-btn">
                üîÑ Retry
              </button>
            </span>
          );
          
        default:
          return <span style={{ color: '#6b7280' }}>üìç {nearestStation?.name || 'Default location'}</span>;
      }
    };

    return <div className="location-info">{getLocationDisplay()}</div>;
  }, [locationStatus, userLocationName, userLocation, nearestStation, isLocationUpdating, startSmartLocationDetection]);

  const metricIcons = useMemo(() => ({
    pm25: 'üå±', pm10: 'üçÉ', so2: 'üè≠', no2: 'üí®',
    co: '‚òÅÔ∏è', o3: '‚òÄÔ∏è', nh3: '‚öóÔ∏è'
  }), []);

  const MetricCards = useMemo(() => {
    const parameters = ['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'];
    
    return parameters.map((key) => {
      const value = currentValues[key] || 0;
      
      return (
        <div key={key} className="metric-card">
          <div className="metric-icon">{metricIcons[key] || 'üìä'}</div>
          <div className="metric-value">{formatValue(value, key)}</div>
          <div className="metric-label">{key.toUpperCase()}</div>
          <div className="metric-unit">{key === 'co' ? 'mg/m¬≥' : '¬µg/m¬≥'}</div>
          {dataType === 'interpolated' && (
            <div className="metric-badge interpolated-badge">
              üéØ Smart
            </div>
          )}
          {dataType === 'station' && (
            <div className="metric-badge station-badge">
              üì° Station
            </div>
          )}
          {dataSource === 'sample' && (
            <div className="metric-badge sample-badge">
              üü° Sample
            </div>
          )}
        </div>
      );
    });
  }, [currentValues, metricIcons, formatValue, dataType, dataSource]);

  const ForecastTable = useMemo(() => {
    const forecastData = dashboardData?.forecast_data?.nearest_station || 
                        dashboardData?.nearest_sensor_info?.current_forecast_data || [];
    
    return (
      <table className="forecast-table">
        <thead>
          <tr>
            <th>üìÖ Day</th>
            <th>üìà Max Value</th>
          </tr>
        </thead>
        <tbody>
          {forecastData.map((item, index) => (
            <tr key={index}>
              <td>{item.day}</td>
              <td>{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }, [dashboardData, selectedParameter, formatValue]);

  const DataSourceIndicator = useMemo(() => {
    const getDataSourceInfo = () => {
      switch (dataSource) {
        case 'live':
          return {
            badge: 'üî¥ LIVE DATA',
            class: 'live',
            description: 'Real-time sensor readings',
            updated: `Updated: ${lastUpdateTime.toLocaleTimeString()}`
          };
        case 'sample':
          return {
            badge: 'üü°  DATA',
            class: 'sample',
            description: 'Demo readings - Enable location for live data',
            updated: 'data from station1'
          };
        case 'cached':
          return {
            badge: 'üü† CACHED DATA',
            class: 'cached',
            description: 'Previously loaded data',
            updated: `Cached: ${lastUpdateTime.toLocaleTimeString()}`
          };
        case 'estimated':
          return {
            badge: 'üîµ ESTIMATED DATA',
            class: 'estimated',
            description: 'Calculated for your location',
            updated: `Updated: ${lastUpdateTime.toLocaleTimeString()}`
          };
        default:
          return {
            badge: '‚ö™ LOADING',
            class: 'loading',
            description: 'Getting data...',
            updated: 'Please wait...'
          };
      }
    };

    const sourceInfo = getDataSourceInfo();

    return (
      <div className={`data-source-indicator ${sourceInfo.class}`}>
        <div className="data-source-badge">{sourceInfo.badge}</div>
        <div className="data-source-description">{sourceInfo.description}</div>
        <div className="data-source-updated">{sourceInfo.updated}</div>
      </div>
    );
  }, [dataSource, lastUpdateTime]);

  // ===== EVENT HANDLERS =====
  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []);

  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  
  const handleLogout = useCallback(() => {
    localStorage.clear();
    navigate('/login');
  }, [navigate]);

  const handleEnableLocation = useCallback(() => {
    startSmartLocationDetection();
  }, [startSmartLocationDetection]);

  const handleRefreshData = useCallback(() => {
    if (userLocation) {
      updateDashboardWithLocation(userLocation);
    } else {
      loadDashboardInstantly();
    }
  }, [userLocation, updateDashboardWithLocation, loadDashboardInstantly]);

  // ===== MAIN RENDER =====
  return (
    <div className="dashboard-page">
      {/* Error Banner */}
      {error && (
        <div className="error-banner">
          ‚ö†Ô∏è {error}
          <button onClick={() => setError(null)} className="error-close">‚úï</button>
        </div>
      )}

      {/* Real-time Status */}
      <div className="realtime-status">
        üî¥ LIVE ‚Ä¢ Updates every 30s ‚Ä¢ {locationStatus === 'gps_detected' ? 'GPS Active' : 'Location Detection Active'}
        {dataType === 'interpolated' && ' ‚Ä¢ Smart Analysis'}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" style={{ marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>‚ò∞</div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">üè† Home</a></li>
            <li><a href="/forecast" className="nav-link">üìà Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">üìã Health Update</a></li>
            <li><a href="/health-report" className="nav-link">üìÑ Health Report</a></li>
            <li><a href="/add-family" className="nav-link">üë• Add Family</a></li>
            <li className="user-info">üë§ <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">üö™ Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        ‚ÑπÔ∏è <span>
          {userLocationName?.display_name || nearestStation?.name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}
          {nearestStation && nearestStation.distance !== null && nearestStation.distance !== undefined && !isNaN(nearestStation.distance) && (
            ` ‚Ä¢ Distance to station: ${nearestStation.distance.toFixed(1)}km`
          )}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">üë§</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                {LocationStatus}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
              </div>
              <div className="aqi-status">
                {dataType === 'interpolated' ? 'Smart Analysis AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                {userLocationName?.display_name || nearestStation?.name || 'Your Location'}
              </div>
              <div className="last-updated">
                Last updated: {lastUpdateTime.toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Data Source Indicator */}
        {DataSourceIndicator}

        {/* Proximity Indicator */}
        {ProximityIndicator}

        {/* Action Buttons */}
        <div className="action-buttons">
          {(dataSource === 'sample' || locationStatus === 'failed') && (
            <button onClick={handleEnableLocation} className="action-btn primary">
              üìç Enable Location for Live Data
            </button>
          )}
          <button onClick={handleRefreshData} className="action-btn secondary">
            üîÑ Refresh Data
          </button>
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            üå¨Ô∏è Current Air Quality {userLocationName?.display_name ? `at ${userLocationName.display_name}` : 'Data'}
            {dataType === 'interpolated' && <small> (Smart Analysis)</small>}
            {dataType === 'station' && <small> (Nearest Station)</small>}
          </h2>
          <div className="metrics-grid">
            {MetricCards}
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div id="map-section" className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">üó∫Ô∏è Interactive Sensor Map</h3>
              {userLocationName && (
                <div className="location-display">
                  üìç {userLocationName.display_name}
                </div>
              )}
            </div>
            <div className="map-container">
              <React.Suspense fallback={
                <div className="map-fallback">
                  <div className="map-placeholder">
                    <div className="map-icon">üó∫Ô∏è</div>
                    <p>Loading interactive map...</p>
                  </div>
                </div>
              }>
                <LazyMap 
                  userLocation={userLocation} 
                  dashboardData={dashboardData}
                  currentAQI={currentAQI}
                  dataType={dataType}
                />
              </React.Suspense>
            </div>
            <div className="map-info">
              üìç Blue marker: Your location ‚Ä¢ Tower markers: Monitoring stations
              {dataType === 'interpolated' && ' ‚Ä¢ üéØ Smart analysis active'}
              {dataSource === 'sample' && ' ‚Ä¢ üü°  data displayed'}
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">üìä 4-Day Air Quality Forecast</h3>
              <div className="forecast-source">
                Data source: {nearestStation?.name || 'Station 1 (ASIET Campus)'}
              </div>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <React.Suspense fallback={
                <div className="chart-fallback">
                  <div className="chart-placeholder">
                    <div className="chart-icon">üìä</div>
                    <p>Loading forecast chart...</p>
                  </div>
                </div>
              }>
                <LazyChart 
                  dashboardData={dashboardData}
                  selectedParameter={selectedParameter}
                />
              </React.Suspense>
              
              <div className="forecast-table-container">
                {ForecastTable}
              </div>
            </div>
          </div>
        </div>

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">üå§Ô∏è Weather Conditions</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">üå°Ô∏è</div>
              <div className="weather-value">{formatValue(currentValues.temp || 28, 'temp')}¬∞C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">üíß</div>
              <div className="weather-value">{formatValue(currentValues.hum || 65, 'hum')}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">üìè</div>
              <div className="weather-value">{formatValue(currentValues.pre || 1013, 'pre')} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
            </div>
          </div>
        </div>

        {/* System Status */}
        <div className="system-status">
          <h3>‚ö° System Status</h3>
          <div className="status-grid">
            <div className="status-item">
              <div className="status-icon">üöÄ</div>
              <div className="status-content">
                <div className="status-title">Performance</div>
                <div className="status-value">Optimized</div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon">üìç</div>
              <div className="status-content">
                <div className="status-title">Location</div>
                <div className="status-value">
                  {locationStatus === 'gps_detected' ? 'GPS Active' : 
                   locationStatus === 'ip_detected' ? 'IP Located' : 
                   locationStatus === 'detecting' ? 'Detecting...' : 'Default'}
                </div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon">üìä</div>
              <div className="status-content">
                <div className="status-title">Data Type</div>
                <div className="status-value">
                  {dataType === 'interpolated' ? 'Smart Analysis' : 'Nearest Station'}
                </div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon">üîÑ</div>
              <div className="status-content">
                <div className="status-title">Data Source</div>
                <div className="status-value">
                  {dataSource === 'live' ? 'Live Data' :
                  
                   dataSource === 'cached' ? 'Cached Data' : 'Loading...'}
                </div>
              </div>
            </div>
          </div>
          
          <div className="system-description">
            <p>
              <strong>üöÄ Production Ready:</strong> This dashboard uses progressive loading 
              to ensure instant access. Location detection runs in the background while you 
              view real air quality data.
            </p>
            <p>
              <strong>üéØ Smart Analysis:</strong> When you're within 1km of monitoring stations, 
              we calculate personalized air quality values for your exact location using 
              advanced interpolation techniques.
            </p>
            <p>
              <strong>üìä Data Transparency:</strong> We clearly indicate whether you're viewing 
              live sensor data, sample data, or cached information. Enable location access 
              for the most accurate readings.
            </p>
          </div>
        </div>

        {/* Help Section */}
        <div className="help-section">
          <h3>‚ùì Understanding Your Data</h3>
          <div className="help-grid">
            <div className="help-item">
              <div className="help-icon">üéØ</div>
              <div className="help-content">
                <h4>Smart Interpolated Values</h4>
                <p>When you're within 1km of monitoring stations, we show personalized air quality calculated for your exact location.</p>
              </div>
            </div>
            <div className="help-item">
              <div className="help-icon">üì°</div>
              <div className="help-content">
                <h4>Nearest Station Data</h4>
                <p>When you're beyond 1km, we show data from the closest monitoring station with distance indicated.</p>
              </div>
            </div>
            <div className="help-item">
              <div className="help-icon">üî¥</div>
              <div className="help-content">
                <h4>Live Data</h4>
                <p>Real-time readings from our sensor network, updated every 30 seconds for the most current information.</p>
              </div>
            </div>
            
          </div>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h3>‚ö° Quick Actions</h3>
          <div className="quick-actions-grid">
            <button 
              onClick={handleEnableLocation}
              className="quick-action-btn"
              disabled={locationStatus === 'gps_detected'}
            >
              <div className="quick-action-icon">üìç</div>
              <div className="quick-action-text">
                <div className="quick-action-title">Enable Location</div>
                <div className="quick-action-desc">Get personalized air quality data</div>
              </div>
            </button>
            
            <button 
              onClick={handleRefreshData}
              className="quick-action-btn"
            >
              <div className="quick-action-icon">üîÑ</div>
              <div className="quick-action-text">
                <div className="quick-action-title">Refresh Data</div>
                <div className="quick-action-desc">Get latest air quality readings</div>
              </div>
            </button>
            
            <button 
              onClick={() => document.getElementById('map-section')?.scrollIntoView({ behavior: 'smooth' })}
              className="quick-action-btn"
            >
              <div className="quick-action-icon">üó∫Ô∏è</div>
              <div className="quick-action-text">
                <div className="quick-action-title">View Map</div>
                <div className="quick-action-desc">See sensor locations and coverage</div>
              </div>
            </button>
            
            <button 
              onClick={() => window.open('/health-assessment', '_self')}
              className="quick-action-btn"
            >
              <div className="quick-action-icon">üìã</div>
              <div className="quick-action-text">
                <div className="quick-action-title">Health Assessment</div>
                <div className="quick-action-desc">Update your health profile</div>
              </div>
            </button>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>AirAware Kerala</h4>
              <p>Smart Air Quality Monitoring System</p>
              <p>Real-time data ‚Ä¢ Personalized insights ‚Ä¢ Health-focused</p>
            </div>
            <div className="footer-section">
              <h4>Features</h4>
              <ul>
                <li>Real-time air quality monitoring</li>
                <li>Smart location-based analysis</li>
                <li>4-day weather forecasting</li>
                <li>Health risk assessment</li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Data Sources</h4>
              <ul>
                <li>Station 1 (ASIET Campus)</li>
                <li>Station 2 (Mattoor Junction)</li>
                <li>Advanced interpolation algorithms</li>
                <li>Weather integration</li>
              </ul>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala - Smart Air Quality Monitoring System</p>
            <p>Developed for real-world deployment ‚Ä¢ Production-ready ‚Ä¢ User-friendly</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
