import React, { useState, useEffect, useRef, useCallback, useMemo, Suspense } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import logoImage from '../assets/aqi.webp'; 
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';
import { useAuth } from '../App';
import './css/MapPage.css';

ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler
);

// --- Helper Functions ---
const getAQIColor = (aqi) => {
    if (aqi === null || aqi === undefined) return '#6b7280';
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 200) return '#ef4444';
    return '#7c2d12';
};

const getAQIStatus = (aqi) => {
    if (aqi === null || aqi === undefined) return 'Unknown';
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    if (aqi <= 200) return 'Severe';
    if (aqi <= 300) return 'Very Severe';
    return 'Hazardous';
};

const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const createAqiIcon = (aqi) => {
    const color = getAQIColor(aqi);
    const iconHtml = `<div style="background-color: ${color};" class="aqi-marker-icon"><div class="aqi-marker-icon-inner">${Math.round(aqi) || 'N/A'}</div></div>`;
    return window.L.divIcon({ html: iconHtml, className: 'custom-div-icon', iconSize: [40, 40], iconAnchor: [20, 40] });
};

const createStationPopupContent = (station, stationId) => {
    const { station_info, averages, highest_sub_index } = station;
    const pollutants = [
        { key: 'pm25', name: 'PM2.5', unit: 'Âµg/mÂ³' }, { key: 'pm10', name: 'PM10', unit: 'Âµg/mÂ³' },
        { key: 'so2', name: 'SOâ‚‚', unit: 'Âµg/mÂ³' }, { key: 'no2', name: 'NOâ‚‚', unit: 'Âµg/mÂ³' },
        { key: 'co', name: 'CO', unit: 'Âµg/mÂ³' }, { key: 'o3', name: 'Oâ‚ƒ', unit: 'Âµg/mÂ³' },
        { key: 'nh3', name: 'NHâ‚ƒ', unit: 'Âµg/mÂ³' }
    ];
    const readingsHtml = pollutants.map(p => `
        <div class="popup-reading-item">
            <div class="popup-reading-label">${p.name}</div>
            <div class="popup-reading-value">${(averages?.[p.key]?.toFixed(2)) ?? 'N/A'}<span class="popup-reading-unit">${p.unit}</span></div>
        </div>`).join('');
    return `<div class="map-station-popup">...</div>`; // Simplified for brevity, original content is fine
};

const MapPage = () => {
    const [mapInstance, setMapInstance] = useState(null);
    const [stations, setStations] = useState({});
    const [selectedStationId, setSelectedStationId] = useState('lora-v1');
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [forecastData, setForecastData] = useState([]);
    const [isForecastLoading, setIsForecastLoading] = useState(false);
    const [selectedParameter, setSelectedParameter] = useState('pm25_max');
    const [forecastUpdatedAt, setForecastUpdatedAt] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationData, setUserLocationData] = useState(null);
    const [nearestStation, setNearestStation] = useState(null);
    const [isLocationLoading, setIsLocationLoading] = useState(false);
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
    const [showMobileMenu, setShowMobileMenu] = useState(false);
    const [activeTab, setActiveTab] = useState('stations');
    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);
    
    const mapRef = useRef(null);
    const markersRef = useRef({});
    const userLocationMarkerRef = useRef(null);
    
    const { user, logout } = useAuth();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
        ? 'https://airaware-app-gcw7.onrender.com' 
        : 'http://localhost:8000';

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
            setIsMobileView(window.innerWidth <= 768);
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {
        let map;
        if (mapRef.current && !mapRef.current._leaflet_id) {
            map = window.L.map(mapRef.current, { zoomControl: false }).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', { attribution: 'Â© OpenStreetMap' }).addTo(map);
            window.L.control.zoom({ position: 'topleft' }).addTo(map);
            setMapInstance(map);
        }
    }, []);

    const calculateIDWInterpolation = useCallback((locationData, stations) => {
        const realStationIds = ['lora-v1', 'loradev2'];
        const realStations = {};
        realStationIds.forEach(id => {
            if (stations[id]) realStations[id] = stations[id];
        });
        if (Object.keys(realStations).length === 0) return { interpolated_values: {}, interpolated_aqi: 50 };
        
        let totalWeight = 0;
        const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0 };
        let weightedAqi = 0;

        Object.values(realStations).forEach(station => {
            const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
            const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
            totalWeight += weight;
            Object.keys(weightedValues).forEach(param => {
                if (station.averages?.[p.key] !== undefined) weightedValues[param] += station.averages[p.key] * weight;
            });
            weightedAqi += (station.highest_sub_index || 0) * weight;
        });

        const interpolated_values = {};
        Object.keys(weightedValues).forEach(param => {
            interpolated_values[param] = totalWeight > 0 ? parseFloat((weightedValues[param] / totalWeight).toFixed(2)) : 0;
        });
        const interpolated_aqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
        return { interpolated_values, interpolated_aqi };
    }, []);

    const fetchRealtimeData = useCallback(async () => {
        setIsLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
            if (!response.ok) throw new Error('Failed to fetch data');
            const data = await response.json();
            setStations(data.stations);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    }, [API_BASE_URL]);

    useEffect(() => {
        fetchRealtimeData();
        const interval = setInterval(fetchRealtimeData, 60000);
        return () => clearInterval(interval);
    }, [fetchRealtimeData]);
            
    const trackUserLocation = useCallback(() => {
        if (!user) {
             alert("Please login to use this feature.");
             return;
        }
        if (navigator.geolocation) {
            setIsLocationLoading(true);
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const loc = { lat: position.coords.latitude, lng: position.coords.longitude };
                    setUserLocation(loc);
                    setIsLocationLoading(false);
                    if (mapInstance) mapInstance.setView(loc, 15);
                },
                (err) => {
                    setIsLocationLoading(false);
                    alert("Could not get your location. Please enable location services.");
                }
            );
        }
    }, [mapInstance, user]);

    // === CORRECTED USER LOCATION DATA CALCULATION LOGIC ===
    useEffect(() => {
        if (userLocation && Object.keys(stations).length > 0 && user) {
            const realStationIds = ['lora-v1', 'loradev2'];
            const realStations = {};
            realStationIds.forEach(id => {
                if (stations[id]) realStations[id] = stations[id];
            });

            if (Object.keys(realStations).length === 0) return;

            const estimation = calculateIDWInterpolation(userLocation, realStations);

            const { lat, lng } = realStations['lora-v1'].station_info;
            const distanceToNearest = calculateDistance(userLocation.lat, userLocation.lng, lat, lng);
            
            setNearestStation({ 
                id: 'lora-v1', 
                distance: distanceToNearest,
                station: realStations['lora-v1']
            });
            
            const isHighConfidence = distanceToNearest <= 5.0;

            setUserLocationData({
                method: 'smart_estimation',
                source: 'calculated',
                explanation: isHighConfidence
                  ? `Showing high-accuracy air quality calculated for your exact location.`
                  : `Showing a regional estimation for your area, as you are ${distanceToNearest.toFixed(1)}km from our nearest physical sensor.`,
                values: estimation.interpolated_values,
                aqi: estimation.interpolated_aqi,
                station_name: `Your Location`,
                is_estimated: true,
                distance_message: isHighConfidence ? `ðŸŽ¯ High-Accuracy Data` : `ðŸŒ Regional Estimation`
            });
        }
    }, [userLocation, stations, user, calculateIDWInterpolation]);

    useEffect(() => {
        if (!selectedStationId) return;
        const fetchForecast = async () => {
            setIsForecastLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/api/station/${selectedStationId}/forecast/`);
                if (!response.ok) throw new Error('Failed to fetch forecast');
                const data = await response.json();
                setForecastData(data.forecast_data || []);
                setForecastUpdatedAt(data.forecast_updated_at || null);
            } catch (err) {
                setForecastData([]);
            } finally {
                setIsForecastLoading(false);
            }
        };
        fetchForecast();
    }, [selectedStationId, API_BASE_URL]);

    useEffect(() => {
        if (mapInstance && Object.keys(stations).length > 0) {
            Object.values(markersRef.current).forEach(marker => marker.remove());
            markersRef.current = {};
            Object.entries(stations).forEach(([id, station]) => {
                const { lat, lng } = station.station_info;
                const marker = window.L.marker([lat, lng], { icon: createAqiIcon(station.highest_sub_index) }).addTo(mapInstance);
                marker.on('click', () => setSelectedStationId(id));
                markersRef.current[id] = marker;
            });
        }
        if (mapInstance && userLocation) {
             const userIcon = window.L.divIcon({
                html: '<div class="user-location-marker"><i class="fas fa-user"></i></div>',
                className: 'custom-user-icon', iconSize: [30, 30], iconAnchor: [15, 15]
            });
            if (userLocationMarkerRef.current) {
                userLocationMarkerRef.current.setLatLng(userLocation);
            } else {
                userLocationMarkerRef.current = window.L.marker(userLocation, { icon: userIcon }).addTo(mapInstance);
            }
        }
    }, [mapInstance, stations, userLocation]);

    // Other functions like forecast chart data/options and mobile renderers remain the same...

    return (
        <div className="map-page">
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
                        AirAware
                    </Link>
                    {/* Simplified nav for brevity */}
                    <div className="nav-right">
                        {user ? (
                            <>
                                <span className="user-name">{user.name}</span>
                                <button onClick={logout} className="logout-btn">Logout</button>
                            </>
                        ) : (
                            <Link to="/login" className="login-btn">Login</Link>
                        )}
                    </div>
                </div>
            </nav>

            <div className="main-content">
                <div className="map-container">
                    <div id="map" ref={mapRef} className="map-element"></div>
                    <button onClick={trackUserLocation} className="my-location-btn" disabled={isLocationLoading}>
                        {isLocationLoading ? <i className="fas fa-spinner fa-spin"></i> : <i className="fas fa-crosshairs"></i>}
                    </button>
                </div>
                
                {/* Simplified Desktop Panel for brevity */}
                {!isMobile && (
                    <div className="data-panel">
                        {userLocationData ? (
                             <div className="user-location-panel">
                                <div className="user-location-header">
                                    <span>{userLocationData.distance_message}</span>
                                </div>
                                <div className="user-aqi-value" style={{color: getAQIColor(userLocationData.aqi)}}>
                                    {Math.round(userLocationData.aqi)}
                                </div>
                                <p>{userLocationData.explanation}</p>
                             </div>
                        ) : (
                             <div className="login-prompt-panel">
                                <p>Login and enable location to see personalized data for your area.</p>
                             </div>
                        )}
                        <div className="station-list-section">
                            <h2 className="section-title">Monitoring Stations</h2>
                             {Object.entries(stations).map(([id, station]) => (
                                <button key={id} className={`station-btn ${selectedStationId === id ? 'active' : ''}`} onClick={() => setSelectedStationId(id)}>
                                    <span>{station.station_info.name}</span>
                                    <span className="station-aqi" style={{backgroundColor: getAQIColor(station.highest_sub_index)}}>
                                        {Math.round(station.highest_sub_index)}
                                    </span>
                                </button>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default MapPage;
