import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './css/Dashboard.css';
import logoImage from '../assets/aqi.webp';
import { useAuth } from '../App';

// Lazy loading components
const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

// --- ALL HELPER FUNCTIONS (Your existing helpers are correct) ---
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const getLocationName = async (lat, lng) => {
    // This is a simplified placeholder. Your complex getLocationName function is good.
    try {
        const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}`);
        const data = await response.json();
        return { display_name: data.display_name || 'Unknown Location', city: data.address.city || 'Unknown' };
    } catch {
        return { display_name: 'Your Location', city: 'Unknown', state: 'Kerala', country: 'India' };
    }
};

function Dashboard() {
    // ===== STATE MANAGEMENT =====
    const { user, logout } = useAuth();

    const [selectedParameter, setSelectedParameter] = useState('pm25');
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationName, setUserLocationName] = useState(null);
    const [nearestStationInfo, setNearestStationInfo] = useState(null);
    const [currentDataInfo, setCurrentDataInfo] = useState(null);
    const [locationStatus, setLocationStatus] = useState('initializing');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isLocationUpdating, setIsLocationUpdating] = useState(false);
    const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
    const [healthData, setHealthData] = useState(null);
    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production'
        ? 'https://airaware-app-gcw7.onrender.com'
        : 'http://localhost:8000';
    const abortControllerRef = useRef(null);

    // ===== FUNCTION DEFINITIONS (CORRECT ORDER) =====

    const processDashboardData = useCallback((data, locationData) => {
        // (Your existing logic for processing data is correct and goes here)
        setDashboardData(data);
        console.log('📊 Dashboard data processed successfully');
    }, [userLocationName]);

    const getUserLocation = useCallback(() => {
        return new Promise((resolve, reject) => {
            if (!navigator.geolocation) {
                return reject(new Error('Geolocation not supported.'));
            }
            setLocationStatus('detecting');
            setIsLocationUpdating(true);
            navigator.geolocation.getCurrentPosition(
                async (position) => {
                    const location = { lat: position.coords.latitude, lng: position.coords.longitude, accuracy: position.coords.accuracy };
                    setUserLocation(location);
                    try {
                        const name = await getLocationName(location.lat, location.lng);
                        setUserLocationName(name);
                    } catch {
                        setUserLocationName({ display_name: 'Your Location' });
                    }
                    setLocationStatus('gps_detected');
                    setIsLocationUpdating(false);
                    resolve(location);
                },
                (error) => {
                    setLocationStatus('failed');
                    setIsLocationUpdating(false);
                    reject(error);
                },
                { enableHighAccuracy: true, timeout: 12000, maximumAge: 0 }
            );
        });
    }, []);

    const fetchDashboardData = useCallback(async (locationData = null) => {
        if (!user || !user.user_id) {
            navigate('/login');
            return;
        }
        setLoading(true);
        setError(null);
        const controller = new AbortController();
        abortControllerRef.current = controller;
        try {
            const url = new URL(`${API_BASE_URL}/api/dashboard/`);
            url.searchParams.append('user_id', user.user_id);
            if (locationData) {
                url.searchParams.append('lat', locationData.lat.toString());
                url.searchParams.append('lng', locationData.lng.toString());
            }
            const response = await fetch(url, { signal: controller.signal });
            if (!response.ok) {
                const errData = await response.json();
                if (errData.redirect_to) navigate(errData.redirect_to);
                throw new Error(errData.error || `HTTP ${response.status}`);
            }
            const data = await response.json();
            setHealthData(data.health_data);
            processDashboardData(data, locationData);
            setLastUpdateTime(new Date());
        } catch (error) {
            if (error.name !== 'AbortError') setError("Failed to load dashboard. Please try again.");
        } finally {
            setLoading(false);
        }
    }, [user, navigate, API_BASE_URL, processDashboardData]);

    const handleEnableLocation = useCallback(async () => {
        try {
            const location = await getUserLocation();
            await fetchDashboardData(location);
        } catch (error) {
            console.error('Failed to get location:', error);
        }
    }, [getUserLocation, fetchDashboardData]);

    // ===== INITIALIZATION (STREAMLINED AND CORRECTED) =====
    useEffect(() => {
        const initializeDashboard = async () => {
            console.log('⚡ Initializing dashboard...');
            let locationData = null;
            try {
                // Step 1: Get location FIRST.
                locationData = await getUserLocation();
                console.log('📍 Location obtained:', locationData);
            } catch (locationError) {
                console.warn('📍 Could not get GPS location:', locationError.message);
                setLocationStatus('failed');
            } finally {
                // Step 2: Fetch data ONCE, with or without the location.
                if (user) {
                    await fetchDashboardData(locationData);
                }
            }
        };

        initializeDashboard();

        return () => {
            if (abortControllerRef.current) {
                abortControllerRef.current.abort();
            }
        };
    }, [user, getUserLocation, fetchDashboardData]); // Dependencies are correct

    // (Your useMemo hooks for aqiStatus, etc., are correct and remain here)
    const LocationStatus = useMemo(() => {
        // Your full LocationStatus component logic here, it is now safe to use handleEnableLocation
        return <div>{locationStatus === 'gps_detected' ? userLocationName?.display_name : 'Detecting your location...'}</div>;
    }, [locationStatus, userLocationName, handleEnableLocation]);


    // ===== RENDER LOGIC =====
    if (loading && !dashboardData) {
        return <div>Loading Dashboard...</div>;
    }

    return (
        <div className="dashboard-page">
            <div className="realtime-status">
                🔴 LIVE • Real-time air quality monitoring system active
            </div>

            {error && <div className="error-banner">⚠️ {error}<button onClick={() => setError(null)} className="error-close">✕</button></div>}

            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
                        AirAware
                    </Link>
                    <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>{isMenuOpen ? '✕' : '☰'}</div>
                    <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                        <li><Link to="/" className="nav-link">🏠 Home</Link></li>
                        <li><Link to="/health-assessment" className="nav-link">📋 Health Update</Link></li>
                        <li><Link to="/health-report" className="nav-link">📄 Health Report</Link></li>
                        <li><Link to="/add-family" className="nav-link">👥 Add Family</Link></li>
                        <li><Link to="/map" className="nav-link">🗺️ Live Map</Link></li>
                        <li className="user-info">👤 <span>{user ? user.name : 'User'}</span></li>
                        <li><button onClick={logout} className="nav-link login-btn">🚪 Logout</button></li>
                    </ul>
                </div>
            </nav>

            <main className="main-container">
                <header className="welcome-header">
                    <div className="welcome-content">
                        <div className="welcome-info">
                            <div className="user-avatar">👤</div>
                            <div className="welcome-text">
                                <h1>Welcome, {user ? user.name : 'Guest'}!</h1>
                                {LocationStatus}
                            </div>
                        </div>
