// 1. Updated App.js - Main routing with session management
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import Login from './components/Login';
import Signup from './components/Signup';
import HealthAssessment from './components/HealthAssessment';
import Dashboard from './components/Dashboard';

// Session management utility
const SessionManager = {
  // Session duration: 1 hour (3600 seconds)
  SESSION_DURATION: 60 * 60 * 1000, // 1 hour in milliseconds

  // Check if user is logged in
  isLoggedIn: () => {
    const user = localStorage.getItem('user');
    const loginTime = localStorage.getItem('loginTime');

    if (!user || !loginTime) return false;

    const currentTime = new Date().getTime();
    const sessionAge = currentTime - parseInt(loginTime);

    // Check if session has expired (1 hour)
    if (sessionAge > SessionManager.SESSION_DURATION) {
      SessionManager.logout();
      return false;
    }

    return true;
  },

  // Get current user
  getCurrentUser: () => {
    if (!SessionManager.isLoggedIn()) return null;

    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch (error) {
      console.error('Error parsing user data:', error);
      SessionManager.logout();
      return null;
    }
  },

  // Login user
  login: (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('loginTime', new Date().getTime().toString());

    // Set auto-logout timer
    SessionManager.setAutoLogout();
  },

  // Logout user
  logout: () => {
    localStorage.removeItem('user');
    localStorage.removeItem('loginTime');
    localStorage.removeItem('userLocation');

    // Clear any existing timeout
    if (window.sessionTimeout) {
      clearTimeout(window.sessionTimeout);
    }

    // Redirect to home page
    window.location.href = '/';
  },

  // Set auto-logout timer
  setAutoLogout: () => {
    if (window.sessionTimeout) {
      clearTimeout(window.sessionTimeout);
    }

    window.sessionTimeout = setTimeout(() => {
      alert('Your session has expired. Please login again.');
      SessionManager.logout();
    }, SessionManager.SESSION_DURATION);
  },

  // Extend session (call this on user activity)
  extendSession: () => {
    if (SessionManager.isLoggedIn()) {
      const user = SessionManager.getCurrentUser();
      if (user) {
        SessionManager.login(user); // Reset timer
      }
    }
  }
};

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  if (!SessionManager.isLoggedIn()) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

// Auth Context for global state
const AuthContext = React.createContext();

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check authentication status on app load
  useEffect(() => {
    const checkAuth = () => {
      const currentUser = SessionManager.getCurrentUser();
      setUser(currentUser);
      setLoading(false);

      // Set up auto-logout if user is logged in
      if (currentUser) {
        SessionManager.setAutoLogout();
      }
    };

    checkAuth();

    // Check session every 5 minutes
    const sessionCheck = setInterval(() => {
      const currentUser = SessionManager.getCurrentUser();
      if (currentUser !== user) {
        setUser(currentUser);
      }
    }, 5 * 60 * 1000); // 5 minutes

    return () => clearInterval(sessionCheck);
  }, [user]);

  // Extend session on user activity
  useEffect(() => {
    const handleUserActivity = () => {
      SessionManager.extendSession();
    };

    // Add event listeners for user activity
    const events = ['mousedown', 'mousemove', 'keypress', 'scroll', 'touchstart'];
    events.forEach(event => {
      document.addEventListener(event, handleUserActivity, true);
    });

    return () => {
      events.forEach(event => {
        document.removeEventListener(event, handleUserActivity, true);
      });
    };
  }, []);

  const authValue = {
    user,
    login: (userData) => {
      SessionManager.login(userData);
      setUser(userData);
    },
    logout: () => {
      SessionManager.logout();
      setUser(null);
    },
    isLoggedIn: () => SessionManager.isLoggedIn(),
    extendSession: () => SessionManager.extendSession()
  };

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        flexDirection: 'column',
        gap: '20px'
      }}>
        <div style={{
          width: '50px',
          height: '50px',
          border: '4px solid #e5e7eb',
          borderTop: '4px solid #3b82f6',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite'
        }}></div>
        <p>Loading AirAware...</p>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={authValue}>
      <Router>
        <div className="App">
          <Routes>
            {/* Home page - accessible to everyone */}
            <Route path="/" element={<HomePage />} />

            {/* Auth routes - redirect to dashboard if already logged in */}
            <Route 
              path="/login" 
              element={user ? <Navigate to="/dashboard" replace /> : <Login />} 
            />
            <Route 
              path="/signup" 
              element={user ? <Navigate to="/dashboard" replace /> : <Signup />} 
            />

            {/* Protected routes - require authentication */}
            <Route 
              path="/health-assessment" 
              element={
                <ProtectedRoute>
                  <HealthAssessment />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/dashboard" 
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } 
            />

            {/* Catch all route - redirect to home */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
