import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import './css/MapPage.css';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';


const MapPage = () => {
  // State management
  const [mapInstance, setMapInstance] = useState(null);
  const [stations, setStations] = useState({});
  const [selectedStation, setSelectedStation] = useState('lora-v1');
  const [currentStationData, setCurrentStationData] = useState({});
  const [forecastData, setForecastData] = useState([]);
  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isPanelOpen, setIsPanelOpen] = useState(true);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [username, setUsername] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [mapReady, setMapReady] = useState(false);

  // Refs
  const mapRef = useRef(null);
  const markersRef = useRef({});
  const coverageCirclesRef = useRef([]);

  // API Base URL - Fixed environment variable handling
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Station locations and info
  const STATION_LOCATIONS = useMemo(() => ({
    'lora-v1': {
      lat: 10.178322301561957,
      lng: 76.43089144291672,
      name: 'Station 1 (ASIET Campus)',
      description: 'Main campus monitoring station'
    },
    'loradev2': {
      lat: 10.17095090340159,
      lng: 76.42962876824544,
      name: 'Station 2 (Mattoor Junction)',
      description: 'Urban junction monitoring station'
    }
  }), []);

  // Initialize map - FIXED: Added proper container checks
  const initializeMap = useCallback(() => {
    // Check if Leaflet is loaded
    if (!window.L) {
      console.log('Leaflet not loaded yet');
      return null;
    }

    // Check if map container exists and is accessible
    if (!mapRef.current) {
      console.log('Map container ref not ready');
      return null;
    }

    // Check if the DOM element exists
    const mapContainer = document.getElementById('map');
    if (!mapContainer) {
      console.log('Map DOM element not found');
      return null;
    }

    // Check if map is already initialized
    if (mapInstance) {
      console.log('Map already initialized');
      return mapInstance;
    }

    // Check if the container already has a map
    if (mapContainer._leaflet_id) {
      console.log('Map container already has a Leaflet instance');
      return null;
    }

    try {
      console.log('Initializing map...');
      const map = window.L.map('map', {
        zoomControl: false,
        preferCanvas: true
      }).setView([10.176, 76.430], 15);

      // Add tile layer
      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap contributors',
        maxZoom: 19
      }).addTo(map);

      // Add zoom control to top right
      window.L.control.zoom({
        position: 'topright'
      }).addTo(map);

      console.log('Map initialized successfully');
      setMapInstance(map);
      setMapReady(true);
      return map;
    } catch (error) {
      console.error('Error initializing map:', error);
      setError('Failed to initialize map');
      return null;
    }
  }, [mapInstance]);

  // Get AQI color
  const getAQIColor = useCallback((aqi) => {
    if (!aqi) return '#6b7280';
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 150) return '#ef4444';
    if (aqi <= 200) return '#dc2626';
    if (aqi <= 300) return '#7c2d12';
    return '#4c1d95';
  }, []);

  // Get AQI status text
  const getAQIStatus = useCallback((aqi) => {
    if (!aqi) return 'Unknown';
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    if (aqi <= 200) return 'Severe';
    if (aqi <= 300) return 'Very Severe';
    return 'Hazardous';
  }, []);

  // Create marker for station
  const createStationMarker = useCallback((stationId, stationInfo, stationData, map) => {
    if (!map || !window.L) return null;

    const aqi = stationData?.highest_sub_index || 50;
    const color = getAQIColor(aqi);

    const marker = window.L.circleMarker([stationInfo.lat, stationInfo.lng], {
      radius: 15,
      fillColor: color,
      color: '#ffffff',
      weight: 3,
      opacity: 1,
      fillOpacity: 0.8
    }).addTo(map);

    // Create popup content
    const popupContent = `
      <div class="marker-popup">
        <h3>${stationInfo.name}</h3>
        <p>${stationInfo.description}</p>
        <div class="aqi-info">
          <div class="aqi-value" style="color: ${color}">AQI: ${aqi}</div>
          <div class="aqi-status">${getAQIStatus(aqi)}</div>
        </div>
        <button onclick="window.selectStation('${stationId}')" class="select-station-btn">
          View Details
        </button>
      </div>
    `;

    marker.bindPopup(popupContent);

    // Click handler
    marker.on('click', () => {
      setSelectedStation(stationId);
      setIsPanelOpen(true);
    });

    return marker;
  }, [getAQIColor, getAQIStatus]);

  // Update map markers
  const updateMapMarkers = useCallback((stationsData, map) => {
    if (!map || !window.L) return;

    // Clear existing markers
    Object.values(markersRef.current).forEach(marker => {
      if (marker) map.removeLayer(marker);
    });
    markersRef.current = {};

    // Add new markers
    Object.entries(STATION_LOCATIONS).forEach(([stationId, stationInfo]) => {
      const stationData = stationsData[stationId];
      const marker = createStationMarker(stationId, stationInfo, stationData, map);
      if (marker) {
        markersRef.current[stationId] = marker;
      }
    });
  }, [STATION_LOCATIONS, createStationMarker]);

  // Fetch stations data
  const fetchStationsData = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/api/all-devices/`);
      const data = await response.json();

      if (response.ok) {
        setStations(data);
        setCurrentStationData(data[selectedStation] || {});
        setLastUpdate(new Date().toLocaleTimeString());
        setError(null);

        // Update map if it exists
        if (mapInstance) {
          updateMapMarkers(data, mapInstance);
        }
      } else {
        setError(data.message || 'Failed to fetch station data');
      }
    } catch (err) {
      console.error('Error fetching stations data:', err);
      setError('Network error - please check your connection');
    } finally {
      setLoading(false);
    }
  }, [API_BASE_URL, selectedStation, mapInstance, updateMapMarkers]);

  // Fetch forecast data
  const fetchForecastData = useCallback(async () => {
    try {
      // Mock forecast data since Chart.js is removed
      const mockForecastData = [
        { day: 'Today', pm25_max: 45, pm10_max: 65, no2_max: 35, o3_max: 55, so2_max: 25, co_max: 1.2, nh3_max: 150 },
        { day: 'Tomorrow', pm25_max: 52, pm10_max: 72, no2_max: 42, o3_max: 62, so2_max: 30, co_max: 1.5, nh3_max: 165 },
        { day: 'Day 3', pm25_max: 38, pm10_max: 58, no2_max: 28, o3_max: 48, so2_max: 22, co_max: 1.1, nh3_max: 140 },
        { day: 'Day 4', pm25_max: 41, pm10_max: 61, no2_max: 31, o3_max: 51, so2_max: 26, co_max: 1.3, nh3_max: 155 },
        { day: 'Day 5', pm25_max: 47, pm10_max: 68, no2_max: 38, o3_max: 58, so2_max: 28, co_max: 1.4, nh3_max: 160 }
      ];
      setForecastData(mockForecastData);
    } catch (err) {
      console.error('Error fetching forecast data:', err);
    }
  }, []);

  // Get user location
  const getUserLocation = useCallback(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const userPos = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(userPos);

          if (mapInstance) {
            // Add user location marker
            const userMarker = window.L.marker([userPos.lat, userPos.lng], {
              icon: window.L.divIcon({
                className: 'user-location-marker',
                html: '<i class="fas fa-user-circle"></i>',
                iconSize: [20, 20]
              })
            }).addTo(mapInstance);

            userMarker.bindPopup('Your Location');
          }
        },
        (error) => {
          console.error('Error getting user location:', error);
        }
      );
    }
  }, [mapInstance]);

  // Simple chart data without Chart.js
  const getForecastDisplay = () => {
    if (!forecastData.length) return null;

    const parameterKey = `${selectedParameter}_max`;
    
    return forecastData.map((data, index) => (
      <div key={index} className="forecast-day-item">
        <div className="forecast-day">{data.day}</div>
        <div className="forecast-value" style={{ color: getAQIColor(data[parameterKey]) }}>
          {data[parameterKey] || 0}
        </div>
      </div>
    ));
  };

  // FIXED: Load Leaflet and initialize map properly
  useEffect(() => {
    let mounted = true;

    const loadLeafletAndInitialize = async () => {
      try {
        // Check if Leaflet is already loaded
        if (window.L) {
          if (mounted) {
            // Wait a bit for DOM to be ready
            setTimeout(() => {
              if (mounted) {
                const map = initializeMap();
                if (map) {
                  fetchStationsData();
                  fetchForecastData();
                }
              }
            }, 100);
          }
          return;
        }

        // Load Leaflet CSS
        const link = document.createElement('link');
        link.rel = 'stylesheet';
        link.href = 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/leaflet.css';
        document.head.appendChild(link);

        // Load Leaflet JS
        const script = document.createElement('script');
        script.src = 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/leaflet.min.js';
        
        script.onload = () => {
          if (mounted) {
            // Wait for script to be fully processed
            setTimeout(() => {
              if (mounted && window.L) {
                const map = initializeMap();
                if (map) {
                  fetchStationsData();
                  fetchForecastData();
                }
              }
            }, 200);
          }
        };

        script.onerror = () => {
          if (mounted) {
            setError('Failed to load map library');
          }
        };

        document.head.appendChild(script);

      } catch (error) {
        console.error('Error loading Leaflet:', error);
        if (mounted) {
          setError('Failed to load map');
        }
      }
    };

    loadLeafletAndInitialize();

    // Get username from localStorage with error handling
    try {
      const storedUsername = localStorage.getItem('username');
      if (storedUsername) {
        setUsername(storedUsername);
      }
    } catch (error) {
      console.error('Error reading username from localStorage:', error);
    }

    // Expose function to global scope for popup buttons
    window.selectStation = (stationId) => {
      setSelectedStation(stationId);
      setIsPanelOpen(true);
    };

    return () => {
      mounted = false;
      if (window.selectStation) {
        delete window.selectStation;
      }
      // Clean up map instance
      if (mapInstance) {
        try {
          mapInstance.remove();
        } catch (e) {
          console.log('Map cleanup error:', e);
        }
      }
    };
  }, []); // Remove dependencies to prevent re-initialization

  // Update current station data when selection changes
  useEffect(() => {
    if (stations[selectedStation]) {
      setCurrentStationData(stations[selectedStation]);
    }
  }, [selectedStation, stations]);

  // Auto-refresh data - only when map is ready
  useEffect(() => {
    if (!mapReady) return;

    const interval = setInterval(fetchStationsData, 30000); // 30 seconds
    return () => clearInterval(interval);
  }, [fetchStationsData, mapReady]);

  // Health recommendations based on AQI
  const getHealthRecommendations = useCallback((aqi) => {
    if (!aqi) return ['Monitor air quality regularly'];
    
    if (aqi <= 50) {
      return [
        'Outdoor activities are safe for most individuals',
        'Perfect time for exercise and outdoor sports',
        'Windows can be kept open for ventilation'
      ];
    } else if (aqi <= 100) {
      return [
        'Sensitive individuals should monitor symptoms',
        'Reduce prolonged outdoor activities',
        'Consider indoor exercise alternatives'
      ];
    } else {
      return [
        'Limit outdoor activities, especially for sensitive groups',
        'Wear masks when going outside',
        'Keep windows closed and use air purifiers',
        'Avoid strenuous outdoor exercise'
      ];
    }
  }, []);

  const currentAQI = currentStationData?.highest_sub_index || 50;
  const currentStatus = getAQIStatus(currentAQI);
  const currentColor = getAQIColor(currentAQI);
  const healthTips = getHealthRecommendations(currentAQI);

  if (loading && !mapReady) {
    return (
      <div className="map-loading">
        <div className="loading-spinner"></div>
        <p>Loading air quality map...</p>
      </div>
    );
  }

  return (
    <div className="map-page">
      {/* Navigation */}
      <nav className="map-navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <i className="fas fa-wind"></i>
            AirAware
          </a>
          <ul className="nav-links">
            <li><a href="/" className="nav-link">
              <i className="fas fa-home"></i> Home
            </a></li>
            <li><a href="/dashboard" className="nav-link">
              <i className="fas fa-user"></i> Dashboard
            </a></li>
            <li><a href="/forecast" className="nav-link">
              <i className="fas fa-chart-line"></i> Forecasting
            </a></li>
            {username && (
              <li className="user-info">
                <i className="fas fa-user-circle"></i>
                <span>{username}</span>
              </li>
            )}
          </ul>
        </div>
      </nav>

      {/* Status Bar */}
      <div className="status-bar">
        <div className="status-left">
          <div className="app-title">
            <i className="fas fa-map-marked-alt"></i>
            Interactive Air Quality Map
          </div>
          <div className="current-station-info">
            <div className="station-icon">
              <i className="fas fa-broadcast-tower"></i>
            </div>
            <div className="station-details">
              <h4>{STATION_LOCATIONS[selectedStation]?.name || 'Loading...'}</h4>
              <p>AQI: {currentAQI} - {currentStatus}</p>
            </div>
          </div>
        </div>
        
        <div className="aqi-classes">
          <div className="aqi-class good">Good (0-50)</div>
          <div className="aqi-class moderate">Moderate (51-100)</div>
          <div className="aqi-class unhealthy">Unhealthy (101-150)</div>
          <div className="aqi-class severe">Severe (151-200)</div>
          <div className="aqi-class very-severe">Very Severe (201-300)</div>
          <div className="aqi-class hazardous">Hazardous (301+)</div>
        </div>
      </div>

      {/* Main Container */}
      <div className="map-container">
        {/* Map Section */}
        <div className="map-section">
          {/* FIXED: Added proper styling and ensured the container is available */}
          <div 
            id="map" 
            ref={mapRef}
            style={{
              width: '100%',
              height: '100%',
              minHeight: '500px',
              background: '#f8f9fa'
            }}
          ></div>

          {/* Map Controls */}
          <div className="map-controls">
            <button 
              className="map-control-btn" 
              title="My location"
              onClick={getUserLocation}
              disabled={!mapReady}
            >
              <i className="fas fa-location-arrow"></i>
            </button>
            <button 
              className="map-control-btn" 
              title="Refresh data"
              onClick={fetchStationsData}
              disabled={!mapReady}
            >
              <i className="fas fa-sync-alt"></i>
            </button>
            <button 
              className="map-control-btn" 
              title="Toggle panel"
              onClick={() => setIsPanelOpen(!isPanelOpen)}
            >
              <i className={`fas fa-chevron-${isPanelOpen ? 'right' : 'left'}`}></i>
            </button>
          </div>

          {/* Error Notification */}
          {error && (
            <div className="error-notification">
              <span>{error}</span>
              <button onClick={() => setError(null)}>×</button>
            </div>
          )}

          {/* Map Loading Indicator */}
          {!mapReady && (
            <div className="map-loading-overlay">
              <div className="loading-spinner"></div>
              <p>Initializing map...</p>
            </div>
          )}
        </div>

        {/* Details Panel */}
        <div className={`details-panel ${isPanelOpen ? 'open' : 'closed'}`}>
          {/* Panel Header */}
          <div className="panel-header">
            <h2 className="panel-title">
              <i className="fas fa-chart-area"></i>
              {STATION_LOCATIONS[selectedStation]?.name || 'Station Details'}
            </h2>
            <p className="panel-subtitle">Real-time air quality monitoring</p>
            {lastUpdate && (
              <div className="last-updated">
                Last updated: {lastUpdate}
              </div>
            )}
          </div>

          <div className="panel-content">
            {/* Station Selector */}
            <div className="station-selector">
              {Object.entries(STATION_LOCATIONS).map(([stationId, stationInfo]) => (
                <button
                  key={stationId}
                  className={`station-btn ${selectedStation === stationId ? 'active' : ''}`}
                  onClick={() => setSelectedStation(stationId)}
                >
                  {stationInfo.name}
                </button>
              ))}
            </div>

            {/* AQI Overview */}
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: currentColor }}>
                {currentAQI}
              </div>
              <div className="aqi-status-text">Air Quality Index</div>
              <div className="station-badge" style={{ backgroundColor: currentColor }}>
                {currentStatus}
              </div>
            </div>

            {/* Metrics Grid */}
            <div className="metrics-grid">
              {[
                { key: 'nh3', label: 'NH₃', icon: 'fas fa-flask', unit: 'µg/m³' },
                { key: 'o3', label: 'O₃', icon: 'fas fa-sun', unit: 'µg/m³' },
                { key: 'so2', label: 'SO₂', icon: 'fas fa-industry', unit: 'µg/m³' },
                { key: 'co', label: 'CO', icon: 'fas fa-cloud', unit: 'mg/m³' },
                { key: 'no2', label: 'NO₂', icon: 'fas fa-smog', unit: 'µg/m³' },
                { key: 'pm25', label: 'PM2.5', icon: 'fas fa-seedling', unit: 'µg/m³' },
                { key: 'pm10', label: 'PM10', icon: 'fas fa-leaf', unit: 'µg/m³' }
              ].map((metric) => {
                const value = currentStationData?.averages?.[metric.key];
                const subIndex = currentStationData?.sub_indices?.[metric.key];
                return (
                  <div key={metric.key} className="metric-card">
                    <div className="metric-icon">
                      <i className={metric.icon}></i>
                    </div>
                    <div className="metric-value">
                      {value ? `${Math.round(value)}` : '--'}
                    </div>
                    <div className="metric-label">{metric.label}</div>
                    <div className="metric-unit">{metric.unit}</div>
                    {subIndex && (
                      <div className="metric-subindex">
                        Index: {Math.round(subIndex)}
                      </div>
                    )}
                  </div>
                );
              })}
            </div>

            {/* Forecast Section - Simple display without Chart.js */}
            <div className="forecast-section">
              <div className="forecast-title">
                <div>
                  <i className="fas fa-chart-line"></i>
                  5-Day Forecast
                </div>
                <small>{STATION_LOCATIONS[selectedStation]?.name}</small>
              </div>

              {/* Parameter Controls */}
              <div className="parameter-controls">
                {[
                  { key: 'pm25', label: 'PM2.5', icon: 'fas fa-lungs' },
                  { key: 'pm10', label: 'PM10', icon: 'fas fa-dust' },
                  { key: 'no2', label: 'NO₂', icon: 'fas fa-smog' },
                  { key: 'o3', label: 'O₃', icon: 'fas fa-cloud' },
                  { key: 'so2', label: 'SO₂', icon: 'fas fa-industry' },
                  { key: 'co', label: 'CO', icon: 'fas fa-car' },
                  { key: 'nh3', label: 'NH₃', icon: 'fas fa-flask' }
                ].map((param) => (
                  <button
                    key={param.key}
                    className={`parameter-btn ${selectedParameter === param.key ? 'active' : ''}`}
                    onClick={() => setSelectedParameter(param.key)}
                  >
                    <i className={param.icon}></i> {param.label}
                  </button>
                ))}
              </div>

              {/* Forecast Display */}
              <div className="forecast-display">
                {getForecastDisplay()}
              </div>
            </div>

            {/* Health Recommendations */}
            <div className="health-tips">
              <div className="tips-title">
                <i className="fas fa-heartbeat"></i>
                Health Recommendations
              </div>

              {healthTips.map((tip, index) => (
                <div key={index} className="tip-item">
                  <div className="tip-icon">
                    <i className={index === 0 ? 'fas fa-running' : index === 1 ? 'fas fa-lungs' : 'fas fa-home'}></i>
                  </div>
                  <div className="tip-content">{tip}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MapPage;
