import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { useAuth } from '../App';
import './css/MapPage.css';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

// --- Helper Functions ---
const getAQIColor = (aqi) => {
    if (aqi === null || aqi === undefined) return '#6b7280';
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 200) return '#ef4444';
    return '#7c2d12';
};
const getAQIStatus = (aqi) => {
    if (aqi === null || aqi === undefined) return 'Unknown';
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy';
    if (aqi <= 200) return 'Severe';
    if (aqi <= 300) return 'Very Severe';
    return 'Hazardous';
};
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};
const createAqiIcon = (aqi) => {
    const color = getAQIColor(aqi);
    const iconHtml = `<div style="background-color: ${color};" class="aqi-marker-icon"><div class="aqi-marker-icon-inner">${Math.round(aqi) || 'N/A'}</div></div>`;
    return window.L.divIcon({ html: iconHtml, className: 'custom-div-icon', iconSize: [32, 32], iconAnchor: [16, 32] });
};

const MapPage = () => {
    const [mapInstance, setMapInstance] = useState(null);
    const [stations, setStations] = useState({});
    const [selectedStationId, setSelectedStationId] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isPanelOpen, setIsPanelOpen] = useState(true);
    const [forecastData, setForecastData] = useState([]);
    const [isForecastLoading, setIsForecastLoading] = useState(false);
    const [selectedParameter, setSelectedParameter] = useState('pm25_max');
    const [forecastUpdatedAt, setForecastUpdatedAt] = useState(null);
    
    const [userLocation, setUserLocation] = useState(null);
    const [nearestStation, setNearestStation] = useState(null);
    const [isLocating, setIsLocating] = useState(false);

    const mapRef = useRef(null);
    const markersRef = useRef({});
    const userLocationMarkerRef = useRef(null);
    const { user } = useAuth();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    useEffect(() => {
        let map;
        if (mapRef.current && !mapRef.current._leaflet_id) {
            map = window.L.map(mapRef.current, { zoomControl: false }).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
            window.L.control.zoom({ position: 'topleft' }).addTo(map);
            setMapInstance(map);
        }
        return () => { if (map) map.remove(); };
    }, []);
    
    const trackUserLocation = useCallback(() => {
        setIsLocating(true);
        setNearestStation(null);
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const loc = { lat: position.coords.latitude, lng: position.coords.longitude };
                    setUserLocation(loc);
                    if (mapInstance) {
                        mapInstance.setView(loc, 14);
                        if (userLocationMarkerRef.current) userLocationMarkerRef.current.setLatLng(loc);
                        else userLocationMarkerRef.current = window.L.marker(loc).addTo(mapInstance);
                    }
                    setIsLocating(false);
                },
                (err) => {
                    alert("Could not get your location. Please enable location services.");
                    setIsLocating(false);
                }
            );
        }
    }, [mapInstance]);

    const fetchRealtimeData = useCallback(async () => {
        setIsLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
            const data = await response.json();
            setStations(data.stations);
            if (!selectedStationId && Object.keys(data.stations).length > 0) {
                setSelectedStationId(Object.keys(data.stations)[0]);
            }
        } catch (err) { setError(err.message); } finally { setIsLoading(false); }
    }, [API_BASE_URL, selectedStationId]);

    useEffect(() => {
        fetchRealtimeData();
        const interval = setInterval(fetchRealtimeData, 60000); // Refreshes every 60 seconds
        return () => clearInterval(interval);
    }, [fetchRealtimeData]);

    useEffect(() => {
        if (userLocation && Object.keys(stations).length > 0) {
            let nearestDist = Infinity, nearestId = null;
            Object.entries(stations).forEach(([id, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
            });
            setNearestStation({ id: nearestId, distance: nearestDist });
        }
    }, [userLocation, stations]);
    
    useEffect(() => {
        if (!selectedStationId) return;
        const fetchForecast = async () => {
            setIsForecastLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/api/station/${selectedStationId}/forecast/`);
                const data = await response.json();
                setForecastData(data.forecast_data || []);
                setForecastUpdatedAt(data.forecast_updated_at || null);
            } catch (err) { console.error("Forecast fetch error:", err); } 
            finally { setIsForecastLoading(false); }
        };
        fetchForecast();
    }, [selectedStationId, API_BASE_URL]);
    
    useEffect(() => {
        if (!mapInstance || Object.keys(stations).length === 0) return;
        Object.values(markersRef.current).forEach(marker => marker.remove());
        markersRef.current = {};
        Object.entries(stations).forEach(([id, station]) => {
            const marker = window.L.marker([station.station_info.lat, station.station_info.lng], { icon: createAqiIcon(station.highest_sub_index) }).addTo(mapInstance);
            marker.on('click', () => {
                setSelectedStationId(id);
                setIsPanelOpen(true);
                mapInstance.setView([station.station_info.lat, station.station_info.lng], 15);
            });
            markersRef.current[id] = marker;
        });
    }, [mapInstance, stations, selectedStationId]);

    const selectedStationData = stations[selectedStationId];
    const pollutants = [
        { key: 'pm25', name: 'PM2.5' }, { key: 'pm10', name: 'PM10' }, { key: 'so2', name: 'SO₂' }, 
        { key: 'no2', name: 'NO₂' }, { key: 'co', name: 'CO' }, { key: 'o3', name: 'O₃' }, { key: 'nh3', name: 'NH₃' }
    ];

    const forecastChartData = useMemo(() => {
        if (!forecastData || forecastData.length === 0) return { labels: [], datasets: [] };
        const labels = forecastData.map(d => new Date(d.day).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
        const dataPoints = forecastData.map(d => d[selectedParameter]);
        return {
            labels,
            datasets: [{
                label: selectedParameter.replace('_max', '').toUpperCase(),
                data: dataPoints,
                borderColor: '#3b82f6',
                backgroundColor: 'rgba(59, 130, 246, 0.2)',
                fill: true,
                tension: 0.4,
            }]
        };
    }, [forecastData, selectedParameter]);
    const forecastChartOptions = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: { legend: { display: false } },
        scales: { x: { ticks: { color: '#94a3b8' } }, y: { ticks: { color: '#94a3b8' } } }
    };

    return (
        <div className="map-page">
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src="/aqi.webp" alt="Logo" width="32" height="32" />
                        AirAware
                    </Link>
                    <ul className="nav-links">
                        <li><Link to="/" className="nav-link">Home</Link></li>
                        <li><Link to="/map" className="nav-link active">Live Map</Link></li>
                        {user ? (
                            <li><Link to="/dashboard" className="nav-link">Dashboard</Link></li>
                        ) : (
                            <li><Link to="/login" className="nav-link login-btn">Login</Link></li>
                        )}
                    </ul>
                </div>
            </nav>
            <div className="main-content">
                <div className="map-container">
                    <div id="map" ref={mapRef} className="map-element"></div>
                    <button onClick={trackUserLocation} className="my-location-btn" title="Find My Location">
                        <i className="fas fa-crosshairs"></i>
                    </button>
                </div>
                <div className={`details-panel ${isPanelOpen ? 'open' : ''}`}>
                    <button className="panel-toggle" onClick={() => setIsPanelOpen(!isPanelOpen)} title={isPanelOpen ? "Hide Panel" : "Show Panel"}>
                        <i className={`fas ${isPanelOpen ? 'fa-chevron-right' : 'fa-chevron-left'}`}></i>
                    </button>
                    <div className="panel-header">
                         <h2 className="panel-title"><i className="fas fa-satellite-dish"></i> Monitoring Stations</h2>
                         <div className="station-selector">
                            {Object.entries(stations).map(([id, station]) => (
                                <button key={id} className={`station-btn ${selectedStationId === id ? 'active' : ''}`} onClick={() => setSelectedStationId(id)}>
                                    <div className="station-btn-content">
                                        <span>{station.station_info.name}</span>
                                        <span className="station-aqi" style={{ color: getAQIColor(station.highest_sub_index) }}>
                                            {Math.round(station.highest_sub_index) || 'N/A'}
                                        </span>
                                    </div>
                                </button>
                            ))}
                        </div>
                    </div>
                    <div className="panel-content">
                        {isLocating && <div className="user-location-panel"><div className="user-location-header"><i className="fas fa-spinner fa-spin"></i><span>Locating...</span></div></div>}
                        {nearestStation && (
                            <div className="user-location-panel">
                                {nearestStation.distance < 1 ? (
                                    <>
                                        <div className="user-location-header"><i className="fas fa-street-view"></i><span>AQI at Your Location</span></div>
                                        <div className="user-location-body">
                                            <div className="user-aqi-value">{stations[nearestStation.id]?.highest_sub_index}</div>
                                            <div className="user-location-details">
                                                <span>Using data from nearest station</span><br />
                                                <span>{stations[nearestStation.id]?.station_info.name} ({nearestStation.distance.toFixed(1)} km)</span>
                                            </div>
                                        </div>
                                    </>
                                ) : (
                                    <>
                                        <div className="user-location-header"><i className="fas fa-exclamation-triangle"></i><span>Location Status</span></div>
                                        <p>You are not within 1km of a sensor, so a precise AQI for your location cannot be provided.</p>
                                        <div className="user-location-details">
                                            <span>Nearest: {stations[nearestStation.id]?.station_info.name} ({nearestStation.distance.toFixed(1)} km away)</span>
                                        </div>
                                    </>
                                )}
                            </div>
                        )}
                        {isLoading ? <div className="panel-loader"><div className="loading-spinner"></div></div> :
                        selectedStationData ? (
                            <div>
                                <h3>{selectedStationData.station_info.name}</h3>
                                <p style={{fontSize: '0.8rem', color: '#94a3b8', marginTop: '-0.5rem', marginBottom: '1.5rem'}}>
                                    Current AQI: {Math.round(selectedStationData.highest_sub_index)} ({getAQIStatus(selectedStationData.highest_sub_index)})
                                    <br />
                                    Last Updated: {selectedStationData.last_updated_on || 'N/A'}
                                </p>
                                <div className="pollutant-metrics">
                                    <div className="pollutant-grid">
                                        {pollutants.map(p => (
                                            <div className="metric-card" key={p.key}>
                                                <div className="metric-label">{p.name}</div>
                                                <div className="metric-value">
                                                    {(selectedStationData.averages?.[p.key]?.toFixed(2)) ?? 'N/A'}
                                                    <span className="metric-unit"> µg/m³</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                                <div className="forecast-section">
                                    <div className="forecast-header">
                                        <h4 className="forecast-title">4-Day Forecast</h4>
                                        <select className="parameter-selector" value={selectedParameter} onChange={(e) => setSelectedParameter(e.target.value)}>
                                            <option value="pm25_max">PM2.5</option>
                                            <option value="pm10_max">PM10</option>
                                            <option value="so2_max">SO₂</option>
                                            <option value="no2_max">NO₂</option>
                                            <option value="co_max">CO</option>
                                            <option value="o3_max">O₃</option>
                                            <option value="nh3_max">NH₃</option>
                                        </select>
                                    </div>
                                    {forecastUpdatedAt && <div style={{ fontSize: '0.75rem', color: '#94a3b8', marginBottom: '0.5rem' }}>Updated: {new Date(forecastUpdatedAt).toLocaleString()}</div>}
                                    {isForecastLoading ? <div className="panel-loader"><p>Loading forecast...</p></div> :
                                    <div className="chart-container">
                                        {forecastChartData?.labels?.length > 0 ? (
                                            <Line options={forecastChartOptions} data={forecastChartData} />
                                        ) : (
                                            <p style={{color: '#94a3b8', fontSize: '0.8rem', textAlign: 'center', marginTop: '2rem'}}>No forecast data available.</p>
                                        )}
                                    </div>}
                                </div>
                            </div>
                        ) : null}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MapPage;
