import React, { useState, useEffect, useRef, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './css/Dashboard.css';
import logoImage from '../assets/aqi.webp'; 

// Lazy loading components
const LazyMap = React.lazy(() =>
  import('./LazyMap').catch(() => ({
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">🗺️</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() =>
  import('./LazyChart').catch(() => ({
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">📊</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// Utility Functions
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
           Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
           Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// Known accurate locations for the area
const KNOWN_LOCATIONS = {
  '10.1783_76.4309': { display_name: 'ASIET Campus, Kalady', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1710_76.4296': { display_name: 'Mattoor Junction, Kalady', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1750_76.4300': { display_name: 'Kalady Town, Kerala', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1765_76.4285': { display_name: 'Kalady Railway Station Area', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1800_76.4700': { display_name: 'Perumbavoor, Kerala', city: 'Perumbavoor', state: 'Kerala', country: 'India' },
  '10.1900_76.3900': { display_name: 'Angamaly, Kerala', city: 'Angamaly', state: 'Kerala', country: 'India' }
};

// Enhanced reverse geocoding with multiple fallback strategies
const getLocationName = async (lat, lng) => {
  for (const [key, location] of Object.entries(KNOWN_LOCATIONS)) {
    const [knownLat, knownLng] = key.split('_').map(Number);
    if (calculateDistance(lat, lng, knownLat, knownLng) < 2.0) {
      return location;
    }
  }

  const geocodingResults = await Promise.allSettled([
    fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=16&addressdetails=1&accept-language=en`).then(res => res.json()).then(data => parseOpenStreetMapResult(data)),
    fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lng}&localityLanguage=en`).then(res => res.json()).then(data => parseBigDataCloudResult(data))
  ]);
  const validResults = geocodingResults.filter(r => r.status === 'fulfilled' && r.value).map(r => r.value);
  if (validResults.length > 0) return validResults.find(r => r.city) || validResults[0];

  return getRegionalFallback(lat, lng);
};

const parseOpenStreetMapResult = (data) => {
  if (!data?.address) return null;
  const { address: addr } = data;
  const primaryLocation = addr.village || addr.town || addr.city || addr.county || 'Unknown';
  return { display_name: `${primaryLocation}, ${addr.state || 'Kerala'}`, city: primaryLocation, state: addr.state || 'Kerala', country: addr.country || 'India' };
};

const parseBigDataCloudResult = (data) => {
  if (!data?.locality) return null;
  return { display_name: `${data.locality}, ${data.principalSubdivision || 'Kerala'}`, city: data.locality, state: data.principalSubdivision || 'Kerala', country: data.countryName || 'India' };
};

const getRegionalFallback = (lat, lng) => {
  if (lat >= 9.8 && lat <= 10.4 && lng >= 76.1 && lng <= 76.6) {
    return { display_name: 'Near Ernakulam, Kerala', city: 'Ernakulam District', state: 'Kerala', country: 'India' };
  }
  return { display_name: `Location in Kerala (${lat.toFixed(3)}, ${lng.toFixed(3)})`, city: 'Kerala', state: 'Kerala', country: 'India' };
};

function Dashboard() {
  const [username] = useState(() => (JSON.parse(localStorage.getItem('user') || '{}').name || 'User'));
  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStationInfo, setNearestStationInfo] = useState(null);
  const [currentDataInfo, setCurrentDataInfo] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
  const [healthData, setHealthData] = useState(null);
  const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.NODE_ENV === 'production' ? 'https://airaware-app-gcw7.onrender.com' : 'http://localhost:8000';
  const abortControllerRef = useRef(null);

  useEffect(() => {
    const handleResize = () => setIsMobileView(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const calculateIDWInterpolation = useCallback((locationData, stations) => {
    let totalWeight = 0;
    const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0, temp: 0, hum: 0, pre: 0 };
    let weightedAqi = 0;
    Object.values(stations).forEach(station => {
        const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
        const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
        totalWeight += weight;
        Object.keys(weightedValues).forEach(param => {
            if (station.averages?.[param] !== undefined) weightedValues[param] += station.averages[param] * weight;
        });
        weightedAqi += (station.highest_sub_index || 0) * weight;
    });
    const interpolated_values = {};
    Object.keys(weightedValues).forEach(param => {
        interpolated_values[param] = totalWeight > 0 ? parseFloat((weightedValues[param] / totalWeight).toFixed(2)) : 0;
    });
    const interpolated_aqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
    return { interpolated_values, interpolated_aqi };
  }, []);

  const processDashboardData = useCallback((data, locationData) => {
    if (!data?.stations) return;
    setDashboardData(data);
    const stations = data.stations;
    const stationIds = Object.keys(stations);

    if (locationData && stationIds.length > 0) {
        const realStationIds = ['lora-v1', 'loradev2'];
        let nearestRealDistance = Infinity;
        let nearestRealStationId = 'lora-v1';

        realStationIds.forEach(id => {
            if (stations[id]) {
                const distance = calculateDistance(locationData.lat, locationData.lng, stations[id].station_info.lat, stations[id].station_info.lng);
                if (distance < nearestRealDistance) {
                    nearestRealDistance = distance;
                    nearestRealStationId = id;
                }
            }
        });
        
        const nearestStation = stations[nearestRealStationId];
        setNearestStationInfo({ id: nearestRealStationId, name: nearestStation.station_info.name, distance: nearestRealDistance });

        const idwResult = calculateIDWInterpolation(locationData, stations);
        const isHighConfidence = nearestRealDistance <= 2.0;

        setCurrentDataInfo({
            method: 'location_interpolation',
            source: 'interpolated',
            explanation: isHighConfidence
              ? `You are ${nearestRealDistance.toFixed(1)}km from our nearest physical sensor. Showing high-accuracy air quality calculated for your exact location.`
              : `You are outside our primary sensor network (${nearestRealDistance.toFixed(1)}km away). Showing an estimated air quality for your area based on regional data models.`,
            values: idwResult.interpolated_values,
            aqi: idwResult.interpolated_aqi,
            station_name: `Your Location (${userLocationName?.city || 'Current Position'})`,
            is_interpolated: true,
            show_distance_message: true,
            distance_message: isHighConfidence ? `🎯 High-Confidence data for your location` : `🔮 Estimated data for your location`,
            data_type: 'Your Location Data (Calculated)'
        });
    } else {
        const defaultStation = stations['lora-v1'] || stations[stationIds[0]] || {};
        setCurrentDataInfo({
            method: 'default_station',
            source: 'default',
            explanation: 'Location not available. Showing data from our primary monitoring station.',
            values: defaultStation.averages || {},
            aqi: defaultStation.highest_sub_index || 50,
            station_name: defaultStation.station_info?.name || 'ASIET Campus Station',
            is_interpolated: false,
            show_distance_message: false,
            distance_message: null,
            data_type: 'Default Station Data'
        });
    }
  }, [userLocationName, calculateIDWInterpolation]);

  const loadSampleData = useCallback(() => {
    console.warn('Loading sample data as a fallback.');
    const sampleData = {
        stations: { 'lora-v1': { station_info: { name: 'ASIET Campus Station' }, averages: { pm25: 25, pm10: 42 }, highest_sub_index: 50 } },
        health_data: { risk_level: 'Low', score: 75, recommendations: ['Enjoy outdoor activities'] }
    };
    setHealthData(sampleData.health_data);
    processDashboardData(sampleData, null);
  }, [processDashboardData]);

  const fetchDashboardData = useCallback(async (locationData = null) => {
    setLoading(true);
    setError(null);
    if (abortControllerRef.current) abortControllerRef.current.abort();
    abortControllerRef.current = new AbortController();
    try {
        const url = new URL(`${API_BASE_URL}/api/dashboard/`);
        url.searchParams.append('username', username);
        if (locationData) {
            url.searchParams.append('lat', locationData.lat);
            url.searchParams.append('lng', locationData.lng);
        }
        const response = await fetch(url, { signal: abortControllerRef.current.signal });
        if (!response.ok) throw new Error('Server error');
        const data = await response.json();
        if (data.health_data) setHealthData(data.health_data);
        processDashboardData(data, locationData);
        setLastUpdateTime(new Date());
    } catch (error) {
        console.error('❌ Error fetching dashboard data:', error);
        if (error.name !== 'AbortError') {
            loadSampleData(); // Graceful fallback
        }
    } finally {
        setLoading(false);
    }
  }, [username, API_BASE_URL, processDashboardData, loadSampleData]);

  const getUserLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) return reject(new Error('Geolocation not supported'));
      setLocationStatus('detecting');
      setIsLocationUpdating(true);
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const location = { lat: position.coords.latitude, lng: position.coords.longitude };
          try {
            const name = await getLocationName(location.lat, location.lng);
            setUserLocationName(name);
          } catch (e) {
            setUserLocationName(getRegionalFallback(location.lat, location.lng));
          }
          setUserLocation(location);
          setLocationStatus('gps_detected');
          setIsLocationUpdating(false);
          resolve(location);
        },
        (error) => {
          setLocationStatus('failed');
          setIsLocationUpdating(false);
          reject(error);
        }, { enableHighAccuracy: true, timeout: 10000, maximumAge: 300000 }
      );
    });
  }, []);

  useEffect(() => {
    const initializeDashboard = async () => {
      await fetchDashboardData(); // Fetch default data first
      try {
        const location = await getUserLocation();
        await fetchDashboardData(location); // Then fetch location-specific data
      } catch (locationError) {
        console.warn('Location could not be obtained. Showing default data.');
      }
    };
    initializeDashboard();
    return () => { if (abortControllerRef.current) abortControllerRef.current.abort(); };
  }, [fetchDashboardData, getUserLocation]);

  const currentAQI = useMemo(() => currentDataInfo?.aqi || 50, [currentDataInfo]);
  const aqiStatus = useMemo(() => {
    if (currentAQI <= 50) return { status: 'GOOD', class: 'good' };
    if (currentAQI <= 100) return { status: 'MODERATE', class: 'moderate' };
    if (currentAQI <= 150) return { status: 'UNHEALTHY', class: 'unhealthy' };
    return { status: 'HAZARDOUS', class: 'hazardous' };
  }, [currentAQI]);

  const LocationStatus = useMemo(() => {
    switch (locationStatus) {
      case 'detecting':
        return <span style={{ color: '#f59e0b' }}>📍 Detecting your location... <span className="location-spinner">⟳</span></span>;
      case 'gps_detected':
        return <span style={{ color: '#10b981' }}>📍 {userLocationName?.display_name || 'Your location'}</span>;
      case 'failed':
        return null; // Show nothing on failure
      default:
        return <span style={{ color: '#6b7280' }}>📍 Initializing...</span>;
    }
  }, [locationStatus, userLocationName]);
  
  const handleLogout = useCallback(() => {
      localStorage.clear();
      navigate('/login');
  }, [navigate]);

  return (
    <div className="dashboard-page">
      <div className="realtime-status">🔴 LIVE • Real-time air quality monitoring system active</div>

      <nav className="navbar">
        <div className="navbar-content">
            <Link to="/" className="navbar-brand">
                <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
                AirAware
            </Link>
            <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>{isMenuOpen ? '✕' : '☰'}</div>
            <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                <li><Link to="/" className="nav-link" onClick={() => setIsMenuOpen(false)}>🏠 Home</Link></li>
                <li><Link to="/health-assessment" className="nav-link" onClick={() => setIsMenuOpen(false)}>📋 Health Update</Link></li>
                <li><Link to="/health-report" className="nav-link" onClick={() => setIsMenuOpen(false)}>📄 Health Report</Link></li>
                <li><Link to="/add-family" className="nav-link" onClick={() => setIsMenuOpen(false)}>👥 Add Family</Link></li>
                <li><Link to="/map" className="nav-link" onClick={() => setIsMenuOpen(false)}>🗺️ Live Map</Link></li>
                <li className="user-info">👤 <span>{username}</span></li>
                <li><button onClick={handleLogout} className="nav-link login-btn">🚪 Logout</button></li>
            </ul>
        </div>
      </nav>

      <div className={`alert-banner ${aqiStatus.class}`}>
        ℹ️ <span>{currentDataInfo?.station_name || '...'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}</span>
      </div>

      <div className="main-container">
        <div className="welcome-header">
            <div className="welcome-content">
                <div className="welcome-info">
                    <div className="user-avatar">👤</div>
                    <div className="welcome-text">
                        <h1>Welcome, {username}!</h1>
                        <div className="location-info">{LocationStatus}</div>
                    </div>
                </div>
                <div className="aqi-overview">
                    <div className="aqi-value" style={{ color: aqiStatus.color }}>{Math.round(currentAQI)}</div>
                    <div className="aqi-status">{currentDataInfo?.is_interpolated ? 'Your Location AQI' : 'Nearest Station AQI'}</div>
                </div>
            </div>
        </div>
        
        {currentDataInfo?.show_distance_message && (
            <div className="location-context-banner-horizontal">
                <div className="location-main-content">
                    <div className="location-icon-section">
                        <div className="location-badge-large">{currentDataInfo.distance_message}</div>
                    </div>
                    <div className="location-details-section">
                        <h3>{currentDataInfo.explanation}</h3>
                    </div>
                </div>
            </div>
        )}
        
        <div className="action-buttons">
          <button onClick={() => fetchDashboardData(userLocation)} className="action-btn secondary" disabled={loading}>
            {loading ? '🔄 Refreshing...' : '🔄 Refresh Data'}
          </button>
        </div>

        <div id="map-section" className="dashboard-card map-card">
          <div className="card-header">
            <h3 className="card-title">🗺️ Live Sensor Network</h3>
            <div className="map-legend">
              <span><span className="legend-dot user-location"></span> Your Location</span>
              <span><span className="legend-dot sensor-station"></span> Sensor Stations</span>
            </div>
          </div>
          <div className="map-container">
            <Suspense fallback={<div className="map-fallback"><p>Loading Map...</p></div>}>
              <LazyMap
                userLocation={userLocation}
                stations={dashboardData?.stations}
                currentAQI={currentAQI}
              />
            </Suspense>
          </div>
          <div className="map-info">
            <span className="status-indicator">🔴 LIVE • Sensor Network Active</span>
          </div>
        </div>

        <div className="dashboard-card forecast-card">
            <div className="card-header">
              <h3 className="card-title">📊 4-Day Air Quality Forecast</h3>
            </div>
            <div className="forecast-content">
              <div className="chart-section">
                <Suspense fallback={<div className="chart-fallback"><p>Loading Chart...</p></div>}>
                  <LazyChart
                    forecastData={nearestStationInfo ? dashboardData?.forecasts?.[nearestStationInfo.id] : dashboardData?.forecasts?.['lora-v1']}
                    selectedParameter={selectedParameter}
                  />
                </Suspense>
              </div>
              <div className="forecast-table-section">
                <table className="forecast-table">
                  <thead>
                    <tr>
                        <th>Day</th>
                        <th>Max {selectedParameter.toUpperCase()}</th>
                        <th>Unit</th>
                    </tr>
                  </thead>
                  <tbody>
                    {(nearestStationInfo && dashboardData?.forecasts?.[nearestStationInfo.id] || dashboardData?.forecasts?.['lora-v1'] || []).map((item, index) => (
                      <tr key={index}>
                        <td>{item.day}</td>
                        <td>{item[`${selectedParameter}_max`] || 'N/A'}</td>
                        <td>{selectedParameter === 'co' ? 'mg/m³' : 'µg/m³'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
