import React, { useEffect, useState, useCallback, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../App'; // Import your auth context
import './css/HomePage.css';

function HomePage() {
  const [homeData, setHomeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  
  const navigate = useNavigate();
  const { user, logout } = useAuth(); // Use your existing auth context

  // API Base URL - change this to match your Django server
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Memoize particles to prevent recreation on every render
  const particles = useMemo(() => {
    const particleElements = [];
    for (let i = 0; i < 10; i++) { // Reduced from 15 to 10
      particleElements.push(
        <div
          key={i}
          className="particle"
          style={{
            left: Math.random() * 100 + '%',
            top: Math.random() * 100 + '%',
            width: Math.random() * 6 + 4 + 'px',
            height: Math.random() * 6 + 4 + 'px',
            animationDelay: Math.random() * 8 + 's',
          }}
        />
      );
    }
    return particleElements;
  }, []);

  // Optimized fetch function with timeout and better error handling
  const fetchHomeData = useCallback(async () => {
    try {
      setLoading(true);
      
      // Add timeout to prevent hanging
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 second timeout
      
      const response = await fetch(`${API_BASE_URL}/api/home/`, {
        signal: controller.signal,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });
      
      clearTimeout(timeoutId);
      
      const data = await response.json();
      
      console.log('Home API Response:', data); // Debug log
      
      if (response.ok) {
        setHomeData(data);
        setError(null);
      } else {
        setError(data.message || data.error || 'Failed to fetch data');
      }
    } catch (err) {
      console.error('Error fetching home data:', err);
      if (err.name === 'AbortError') {
        setError('Request timeout - please check your connection');
      } else {
        setError('Network error - please check your connection');
      }
    } finally {
      setLoading(false);
    }
  }, [API_BASE_URL]);

  useEffect(() => {
    fetchHomeData();
    // Reduced refresh interval from 30 seconds to 60 seconds to reduce requests
    const interval = setInterval(fetchHomeData, 60000);
    return () => clearInterval(interval);
  }, [fetchHomeData]);

  // Memoize AQI status calculation
  const aqiStatus = useMemo(() => {
    const getAQIStatus = (aqiValue) => {
      if (!aqiValue && aqiValue !== 0) return { status: 'Unknown', icon: 'fas fa-question', color: '#6b7280' };
      
      const value = parseInt(aqiValue, 10);
      if (value <= 50) {
        return { status: 'GOOD', icon: 'fas fa-smile', color: '#10b981' };
      } else if (value <= 100) {
        return { status: 'MODERATE', icon: 'fas fa-meh', color: '#f59e0b' };
      } else if (value <= 150) {
        return { status: 'UNHEALTHY', icon: 'fas fa-frown', color: '#ef4444' };
      } else if (value <= 200) {
        return { status: 'SEVERE', icon: 'fas fa-exclamation-triangle', color: '#dc2626' };
      } else if (value <= 300) {
        return { status: 'VERY SEVERE', icon: 'fas fa-skull', color: '#7c2d12' };
      } else {
        return { status: 'HAZARDOUS', icon: 'fas fa-biohazard', color: '#4c1d95' };
      }
    };
    return getAQIStatus(homeData?.highest_sub_index);
  }, [homeData?.highest_sub_index]);

  const toggleMenu = useCallback(() => {
    setIsMenuOpen(prev => !prev);
  }, []);

  // Navigation handlers
  const handleMapNavigation = useCallback(() => {
    navigate('/map');
  }, [navigate]);

  const handleDashboardNavigation = useCallback(() => {
    if (user) {
      navigate('/dashboard');
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleLogout = useCallback(() => {
    logout();
    navigate('/');
  }, [logout, navigate]);

  // Show a faster loading screen with skeleton
  if (loading && !homeData) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading air quality data...</p>
        <div className="loading-skeleton">
          <div className="skeleton-text"></div>
          <div className="skeleton-aqi"></div>
          <div className="skeleton-text small"></div>
        </div>
      </div>
    );
  }

  // Error component
  if (error && !homeData) {
    return (
      <div className="error-container">
        <div className="error-message">
          <h2> Data Unavailable</h2>
          <p>{error}</p>
          <button onClick={fetchHomeData} className="retry-button">
            Retry
          </button>
        </div>
      </div>
    );
  }

  // Extract data with fallbacks - CORRECTED for your API structure
  const currentAQI = homeData?.highest_sub_index || "N/A";
  const lastUpdateDate = homeData?.latest_item?.date || 'N/A';
  const lastUpdateTime = homeData?.latest_item?.time || 'N/A';
  const stationName = homeData?.station_name || 'ASIET Campus Station';

  return (
    <div className="homepage">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <img 
              src="/aqi.webp" 
              alt="AQM Logo" 
              loading="lazy" 
              width="40" 
              height="40"
            />
            AirAware
          </Link>

          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><Link to="/" className="nav-link">Home</Link></li>
            <li><a href="#cities" className="nav-link">Cities</a></li>
            <li><a href="#services" className="nav-link">Services</a></li>
            <li><a href="#contact" className="nav-link">Contact</a></li>
            <li><Link to="/map" className="nav-link">Live Map</Link></li>

            {user ? (
              <>
                <li>
                  <button onClick={handleDashboardNavigation} className="nav-link nav-button">
                    Dashboard
                  </button>
                </li>
                <li className="nav-link user-greeting">Hello, {user.name}</li>
                <li>
                  <button onClick={handleLogout} className="nav-link login-btn nav-button">
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </button>
                </li>
              </>
            ) : (
              <li><Link to="/login" className="nav-link login-btn">
                <i className="fas fa-sign-in-alt"></i> Login
              </Link></li>
            )}
          </ul>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero-section" id="home">
        <div className="hero-overlay"></div>
        <div className="particles">
          {particles}
        </div>
        <div className="hero-content">
          <h1 className="hero-title">Air Quality Monitoring</h1>
          <p className="hero-subtitle">Live air quality updates and insights from Kalady, Ernakulam</p>
          <p className="hero-note">
            Real-time data from {stationName}
          </p>
          
          <div className="aqi-display">
            <div className="aqi-label">
              Current AQI  Last Updated: <span>
                {lastUpdateDate} at {lastUpdateTime}
              </span>
            </div>
            <div className="aqi-value" style={{ color: aqiStatus.color }}>
              {currentAQI}
            </div>
            <div className="aqi-status">AQI - {aqiStatus.status}</div>
            <div className="aqi-icon">
              <i className={aqiStatus.icon} style={{ color: aqiStatus.color }}></i>
            </div>
            
            {/* Show error if there's an issue but we have cached data */}
            {error && homeData && (
              <div className="aqi-error">
                <small> Using cached data - {error}</small>
              </div>
            )}
          </div>

          {/* Updated CTA button with proper navigation */}
          <button onClick={handleMapNavigation} className="cta-button">
            <i className="fas fa-map-marked-alt"></i>
            View Live AQI Map
          </button>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="stats-container">
          <div className="section-header">
            <h2 className="section-title">Kerala Air Quality Network</h2>
            <p className="section-subtitle">
              Comprehensive monitoring across multiple locations for accurate air quality assessment
            </p>
          </div>

          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">2</div>
              <div className="stat-label">Active Monitoring Stations</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">24/7</div>
              <div className="stat-label">Real-time Monitoring</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">7</div>
              <div className="stat-label">Key Pollutants Tracked</div>
            </div>
          </div>
        </div>
      </section>

      {/* AQI Scale Section */}
      <section className="section aqi-scale-section">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Understanding Air Quality Index</h2>
            <p className="section-subtitle">
              Know what the AQI numbers mean for your health and daily activities
            </p>
          </div>

          <div className="aqi-scale-grid">
            {[
              { range: 'Good (0-50)', class: 'aqi-good', description: 'Air quality is satisfactory. Air pollution poses little or no risk for everyone.' },
              { range: 'Moderate (51-100)', class: 'aqi-moderate', description: 'Air quality is acceptable. However, sensitive individuals may experience minor issues.' },
              { range: 'Unhealthy (101-200)', class: 'aqi-unhealthy', description: 'Members of sensitive groups may experience health effects. Limit outdoor activities.' },
              { range: 'Hazardous (201+)', class: 'aqi-hazardous', description: 'Health alert: The risk of health effects is increased for everyone. Avoid outdoor activities.' }
            ].map((item, index) => (
              <div key={index} className="aqi-scale-item">
                <div className="aqi-range">
                  <div className={`aqi-color-indicator ${item.class}`}></div>
                  <div className="aqi-range-text">{item.range}</div>
                </div>
                <div className="aqi-description">{item.description}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Cities Section */}
      <section className="section cities-section" id="cities">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Monitoring Locations</h2>
            <p className="section-subtitle">
              Air quality data from strategic locations across Ernakulam district
            </p>
          </div>

          <div className="cities-grid">
            {[
              { 
                icon: 'fas fa-university', 
                name: 'ASIET Campus Station', 
                type: 'Educational Institution',
                description: 'Main campus monitoring station'
              },
              { 
                icon: 'fas fa-city', 
                name: 'Mattoor Junction Station', 
                type: 'Urban Area',
                description: 'Urban junction monitoring station'
              }
            ].map((location, index) => (
              <div key={index} className="city-card" onClick={handleMapNavigation}>
                <div className="city-icon">
                  <i className={location.icon}></i>
                </div>
                <div className="city-content">
                  <div className="city-name">{location.name}</div>
                  <div className="city-type">{location.type}</div>
                  <div className="city-description">{location.description}</div>
                </div>
                <div className="city-action">
                  <span>View on Map</span>
                  <i className="fas fa-arrow-right"></i>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section className="section services-section" id="services">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Our Services</h2>
            <p className="section-subtitle">
              Advanced air quality solutions powered by cutting-edge technology
            </p>
          </div>

          <div className="services-grid">
            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-map-marked-alt"></i>
              </div>
              <h3 className="service-title">Interactive AQI Map</h3>
              <p className="service-description">
                Explore real-time air quality data across multiple monitoring stations with our interactive map featuring live updates and forecasting.
              </p>
              <button onClick={handleMapNavigation} className="service-button">
                <i className="fas fa-arrow-right"></i>
                View Map
              </button>
            </div>

            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-heartbeat"></i>
              </div>
              <h3 className="service-title">Health Risk Assessment</h3>
              <p className="service-description">
                Receive personalized health recommendations based on current air quality conditions and your individual health profile.
              </p>
              <button onClick={handleDashboardNavigation} className="service-button">
                <i className="fas fa-arrow-right"></i>
                Get Assessment
              </button>
            </div>

            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <h3 className="service-title">AQI Forecasting</h3>
              <p className="service-description">
                Get accurate air quality predictions with advanced meteorological models and machine learning algorithms for better planning.
              </p>
              <button onClick={handleMapNavigation} className="service-button">
                <i className="fas fa-arrow-right"></i>
                View Forecast
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer" id="contact">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirQuality Kerala</h3>
              <p className="footer-text">
                Leading provider of real-time air quality monitoring solutions in Kerala, committed to environmental sustainability and public health awareness.
              </p>
              <div className="social-links">
                <a href="#" className="social-link"><i className="fab fa-facebook-f"></i></a>
                <a href="#" className="social-link"><i className="fab fa-twitter"></i></a>
                <a href="#" className="social-link"><i className="fab fa-linkedin-in"></i></a>
                <a href="#" className="social-link"><i className="fab fa-instagram"></i></a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <Link to="/" className="footer-link">Home</Link>
              <a href="#cities" className="footer-link">Monitoring Stations</a>
              <a href="#services" className="footer-link">Services</a>
              <Link to="/map" className="footer-link">Live Map</Link>
              {user && (
                <button onClick={handleLogout} className="footer-link footer-button">
                  Logout
                </button>
              )}
            </div>

            <div className="footer-section">
              <h3>Services</h3>
              <button onClick={handleMapNavigation} className="footer-link footer-button">
                Interactive Map
              </button>
              <button onClick={handleDashboardNavigation} className="footer-link footer-button">
                Health Assessment
              </button>
              <button onClick={handleMapNavigation} className="footer-link footer-button">
                AQI Forecasting
              </button>
              <a href="#" className="footer-link">Data Analytics</a>
            </div>

            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Adi Shankara Institute of Engineering and Technology<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AirQuality Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default HomePage;
