import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './css/Dashboard.css';
import logoImage from '../assets/aqi.webp';

// Lazy loading components
const LazyMap = React.lazy(() =>
  import('./LazyMap').catch(() => ({
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">üó∫Ô∏è</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() =>
  import('./LazyChart').catch(() => ({
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">üìä</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// Utility Functions
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c; 
};

const KNOWN_LOCATIONS = {
  '10.1783_76.4309': { display_name: 'ASIET Campus, Kalady', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1710_76.4296': { display_name: 'Mattoor Junction, Kalady', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1750_76.4300': { display_name: 'Kalady Town, Kerala', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1765_76.4285': { display_name: 'Kalady Railway Station Area', city: 'Kalady', state: 'Kerala', country: 'India' },
  '10.1800_76.4700': { display_name: 'Perumbavoor, Kerala', city: 'Perumbavoor', state: 'Kerala', country: 'India' },
  '10.1900_76.3900': { display_name: 'Angamaly, Kerala', city: 'Angamaly', state: 'Kerala', country: 'India' }
};

const parseOpenStreetMapResult = (data) => {
  if (!data || !data.address) return null;
  const addr = data.address;
  const primaryLocation = addr.village || addr.town || addr.city || addr.county || 'Unknown Location';
  return {
    display_name: `${primaryLocation}, ${addr.state || 'Kerala'}`,
    city: primaryLocation,
    state: addr.state || 'Kerala',
    country: addr.country || 'India'
  };
};

const parseBigDataCloudResult = (data) => {
  if (!data || !data.locality) return null;
  return {
    display_name: `${data.locality}, ${data.principalSubdivision || 'Kerala'}`,
    city: data.locality,
    state: data.principalSubdivision || 'Kerala',
    country: data.countryName || 'India'
  };
};

const parseLocationIQResult = (data) => {
    if (!data || !data.address) return null;
    const addr = data.address;
    const primaryLocation = addr.village || addr.town || addr.city || addr.county;
    return {
        display_name: `${primaryLocation}, ${addr.state || 'Kerala'}`,
        city: primaryLocation,
        state: addr.state || 'Kerala',
        country: addr.country || 'India'
    };
};

const getRegionalFallback = (lat, lng) => {
    if (lat >= 10.15 && lat <= 10.20 && lng >= 76.40 && lng <= 76.45) {
        return { display_name: 'Kalady Area, Kerala', city: 'Kalady', state: 'Kerala', country: 'India' };
    }
    return { display_name: `Location in Kerala (${lat.toFixed(3)}, ${lng.toFixed(3)})`, city: 'Kerala', state: 'Kerala', country: 'India' };
};

const getLocationName = async (lat, lng) => {
  for (const [key, location] of Object.entries(KNOWN_LOCATIONS)) {
    const [knownLat, knownLng] = key.split('_').map(Number);
    if (calculateDistance(lat, lng, knownLat, knownLng) < 2.0) {
      return location;
    }
  }

  const geocodingResults = await Promise.allSettled([
    fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=16&addressdetails=1&accept-language=en`).then(res => res.json()).then(parseOpenStreetMapResult),
    fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lng}&localityLanguage=en`).then(res => res.json()).then(parseBigDataCloudResult),
    fetch(`https://us1.locationiq.com/v1/reverse.php?key=demo&lat=${lat}&lon=${lng}&format=json`).then(res => res.json()).then(parseLocationIQResult).catch(() => null)
  ]);

  const validResults = geocodingResults.filter(r => r.status === 'fulfilled' && r.value).map(r => r.value);
  if (validResults.length > 0) return validResults.find(r => r.city) || validResults[0];

  return getRegionalFallback(lat, lng);
};


function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [user, setUser] = useState(() => {
    try {
      const storedUser = localStorage.getItem('user');
      return storedUser ? JSON.parse(storedUser) : null;
    } catch {
      return null;
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStationInfo, setNearestStationInfo] = useState(null);
  const [currentDataInfo, setCurrentDataInfo] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
  const [healthData, setHealthData] = useState(null);
  const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.NODE_ENV === 'production'
    ? 'https://airaware-app-gcw7.onrender.com'
    : 'http://localhost:8000';
  const abortControllerRef = useRef(null);

  // ===== LIFECYCLE & EVENT HANDLERS =====
  useEffect(() => {
    const handleResize = () => setIsMobileView(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const getUserLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        return reject(new Error('Geolocation not supported.'));
      }
      setLocationStatus('detecting');
      setIsLocationUpdating(true);
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const location = { lat: position.coords.latitude, lng: position.coords.longitude, accuracy: position.coords.accuracy, source: 'gps', timestamp: Date.now() };
          if (isNaN(location.lat) || isNaN(location.lng)) return reject(new Error('Invalid GPS coordinates'));
          try {
            setUserLocationName(await getLocationName(location.lat, location.lng));
          } catch (nameError) {
            setUserLocationName(getRegionalFallback(location.lat, location.lng));
          }
          setUserLocation(location);
          setLocationStatus('gps_detected');
          setIsLocationUpdating(false);
          resolve(location);
        },
        (error) => {
          setLocationStatus('failed');
          setIsLocationUpdating(false);
          reject(error);
        },
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
      );
    });
  }, []);

  const fetchDashboardData = useCallback(async (locationData = null) => {
    if (!user || !user.user_id) {
      navigate('/login');
      return;
    }
    setLoading(true);
    setError(null);
    if (abortControllerRef.current) abortControllerRef.current.abort();
    const controller = new AbortController();
    abortControllerRef.current = controller;

    try {
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('user_id', user.user_id);
      if (locationData) {
        url.searchParams.append('lat', locationData.lat.toString());
        url.searchParams.append('lng', locationData.lng.toString());
      }
      const response = await fetch(url, { signal: controller.signal });
      if (!response.ok) {
        const errData = await response.json();
        if (errData.redirect_to) navigate(errData.redirect_to);
        throw new Error(errData.error || `HTTP ${response.status}`);
      }
      const data = await response.json();
      setHealthData(data.health_data);
      setDashboardData(data);
      setLastUpdateTime(new Date());
    } catch (error) {
      if (error.name !== 'AbortError') setError("Failed to load dashboard. Please try again.");
    } finally {
      setLoading(false);
    }
  }, [user, navigate, API_BASE_URL]);

  useEffect(() => {
    const initialize = async () => {
      let location = null;
      try {
        location = await getUserLocation();
      } catch (e) {
        console.warn("Could not get user location on init.");
      }
      await fetchDashboardData(location);
    };
    initialize();
    return () => {
      if (abortControllerRef.current) abortControllerRef.current.abort();
    };
  }, [getUserLocation, fetchDashboardData]);

  // ===== DATA PROCESSING =====
  const calculateIDWInterpolation = useCallback((locationData, stations) => {
    let totalWeight = 0, weightedAqi = 0;
    const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0, temp: 0, hum: 0, pre: 0 };
    Object.values(stations).forEach(station => {
      const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
      const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
      totalWeight += weight;
      Object.keys(weightedValues).forEach(param => {
        if (station.averages?.[param] !== undefined) weightedValues[param] += station.averages[param] * weight;
      });
      weightedAqi += (station.highest_sub_index || 0) * weight;
    });
    const interpolated_values = {};
    Object.keys(weightedValues).forEach(param => interpolated_values[param] = totalWeight > 0 ? (weightedValues[param] / totalWeight) : 0);
    return { interpolated_values, interpolated_aqi: totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50 };
  }, []);

  useEffect(() => {
    if (!dashboardData || !dashboardData.stations) return;
    const stations = dashboardData.stations;
    if (userLocation) {
      let nearestDist = Infinity, nearestId = null;
      Object.entries(stations).forEach(([id, station]) => {
        const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
        if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
      });
      setNearestStationInfo({ id: nearestId, name: stations[nearestId].station_info.name, distance: nearestDist, aqi: stations[nearestId].highest_sub_index || 50 });
      if (nearestDist <= 2.0) {
        const idw = calculateIDWInterpolation(userLocation, stations);
        setCurrentDataInfo({ values: idw.interpolated_values, aqi: idw.interpolated_aqi, is_interpolated: true, station_name: `Your Location` });
      } else {
        setCurrentDataInfo({ values: stations[nearestId].averages, aqi: stations[nearestId].highest_sub_index, is_interpolated: false, station_name: stations[nearestId].station_info.name });
      }
    } else {
      const defaultStation = stations['lora-v1'] || Object.values(stations)[0];
      setCurrentDataInfo({ values: defaultStation.averages, aqi: defaultStation.highest_sub_index, is_interpolated: false, station_name: defaultStation.station_info.name });
    }
  }, [dashboardData, userLocation, calculateIDWInterpolation, userLocationName]);

  // ===== MEMOIZED VALUES =====
  const currentValues = useMemo(() => currentDataInfo?.values || {}, [currentDataInfo]);
  const currentAQI = useMemo(() => currentDataInfo?.aqi || 50, [currentDataInfo]);
  const aqiStatus = useMemo(() => {
    if (currentAQI <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (currentAQI <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (currentAQI <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  // ===== EVENT HANDLERS =====
  const handleLogout = useCallback(() => {
    localStorage.clear();
    window.location.href = '/login';
  }, []);

  const handleNavLinkClick = useCallback(() => { 
    if (isMobileView) setIsMenuOpen(false); 
  }, [isMobileView]);

  return (
    <div className="dashboard-page">
      <div className="realtime-status">
        üî¥ LIVE ‚Ä¢ Real-time air quality monitoring system active
      </div>

      {error && <div className="error-banner">‚ö†Ô∏è {error}<button onClick={() => setError(null)} className="error-close">‚úï</button></div>}

      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
            AirAware
          </Link>
          <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>{isMenuOpen ? '‚úï' : '‚ò∞'}</div>
          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><Link to="/" className="nav-link" onClick={handleNavLinkClick}>üè† Home</Link></li>
            <li><Link to="/health-assessment" className="nav-link" onClick={handleNavLinkClick}>üìã Health Update</Link></li>
            <li><Link to="/health-report" className="nav-link" onClick={handleNavLinkClick}>üìÑ Health Report</Link></li>
            <li><Link to="/add-family" className="nav-link" onClick={handleNavLinkClick}>üë• Add Family</Link></li>
            <li><Link to="/map" className="nav-link" onClick={handleNavLinkClick}>üó∫Ô∏è Live Map</Link></li>
            <li className="user-info">üë§ <span>{user ? user.name : 'User'}</span></li>
            <li><button onClick={handleLogout} className="nav-link login-btn">üö™ Logout</button></li>
          </ul>
        </div>
      </nav>

      <div className={`alert-banner ${aqiStatus.class}`}>
        ‚ÑπÔ∏è <span>{currentDataInfo?.station_name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}</span>
      </div>

      <main className="main-container">
        <header className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">üë§</div>
              <div className="welcome-text">
                <h1>Welcome, {user ? user.name : 'Guest'}!</h1>
                <div className="location-info">
                  {locationStatus === 'gps_detected' ? `üìç ${userLocationName?.display_name}` : 'üìç Detecting location...'}
                </div>
                {healthData && (
                  <div className="health-status-inline">
                    <span className="health-label">Your Health Risk: </span>
                    <span className={`health-level ${healthData.risk_level.toLowerCase()}`}>{healthData.risk_level}</span>
                  </div>
                )}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>{Math.round(currentAQI)}</div>
              <div className="aqi-status">{currentDataInfo?.is_interpolated ? 'Your Location AQI' : 'Nearest Station AQI'}</div>
              <div className="last-updated">Updated: {lastUpdateTime.toLocaleTimeString()}</div>
            </div>
          </div>
        </header>

        <section className="metrics-section">
          <h2 className="section-title">üå¨Ô∏è Air Quality Readings</h2>
          <div className="metrics-grid">
            {['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'].map(key => (
              <div key={key} className="metric-card">
                <div className="metric-value">{currentValues[key]?.toFixed(1) || 'N/A'}</div>
                <div className="metric-label">{key.toUpperCase()}</div>
              </div>
            ))}
          </div>
        </section>

        <section className="dashboard-grid">
          <div className="dashboard-card map-card">
            <h3 className="card-title">üó∫Ô∏è Live Sensor Network</h3>
            <div className="map-container">
              <React.Suspense fallback={<div className="map-fallback">Loading Map...</div>}>
                <LazyMap userLocation={userLocation} stations={dashboardData?.stations} />
              </React.Suspense>
            </div>
          </div>

          <div className="dashboard-card forecast-card">
            <h3 className="card-title">üìä 4-Day Forecast</h3>
            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3'].map(p => <button key={p} className={`param-btn ${selectedParameter === p ? 'active' : ''}`} onClick={() => setSelectedParameter(p)}>{p.toUpperCase()}</button>)}
            </div>
            <div className="chart-section">
              <React.Suspense fallback={<div className="chart-fallback">Loading Chart...</div>}>
                <LazyChart forecastData={dashboardData?.forecasts?.[nearestStationInfo?.id || 'lora-v1']} selectedParameter={selectedParameter} />
              </React.Suspense>
            </div>
          </div>
        </section>

        {healthData && (
            <section className="health-section">
                <h2 className="section-title">‚ù§Ô∏è Health Status Monitor</h2>
                <div className="health-content">
                    <div className="health-display">
                        <div className="health-score-circle">
                            <div className="health-score-value">{healthData.score}</div>
                            <div className="health-score-label">Score</div>
                        </div>
                        <div className="health-details">
                            <div className={`risk-level ${healthData.risk_level.toLowerCase()}`}>{healthData.risk_level} Risk</div>
                            <div className="health-recommendations">
                                {healthData.recommendations?.map((rec, i) => <div key={i} className="recommendation">‚Ä¢ {rec}</div>)}
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        )}

      </main>

      <footer className="footer">
        <div className="footer-container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>AirAware Kerala</h4>
              <p>Smart Air Quality Monitoring</p>
            </div>
            <div className="footer-section">
              <h4>Quick Links</h4>
              <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/health-report">Health Report</Link></li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Contact</h4>
              <p>Adi Shankara Institute, Kalady</p>
              <p>Email: aiiot@adishankara.ac.in</p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
