import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/Login.css';
import logoImage from '../assets/aqi.webp'; 

function Login() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [showOTP, setShowOTP] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);
  
  const navigate = useNavigate();

  // Safe API URL
  const API_BASE_URL = process.env.NODE_ENV === 'production' 
    ? 'https://airaware-app-gcw7.onrender.com' 
    : 'http://localhost:8000';
  
  // Mobile view detection effect
  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 768);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Simple particles without complex animations
  const createParticles = () => {
    const particles = [];
    for (let i = 0; i < 20; i++) {
      particles.push(
        <div
          key={i}
          className="particle"
          style={{
            left: Math.random() * 100 + '%',
            top: Math.random() * 100 + '%',
            width: '4px',
            height: '4px',
            backgroundColor: 'rgba(255,255,255,0.3)',
            borderRadius: '50%',
            position: 'absolute',
            animationDelay: Math.random() * 5 + 's'
          }}
        />
      );
    }
    return particles;
  };

  // Clear messages
  const clearMessages = () => {
    setError('');
    setSuccess('');
  };

  // Validate phone number - handles +91 prefix (13 characters total)
  const validatePhoneNumber = (phone) => {
    const cleanPhone = phone.replace(/[^\d+]/g, '');
    if (cleanPhone.length === 13 && cleanPhone.startsWith('+91')) {
      const numberPart = cleanPhone.substring(3);
      if (numberPart.length === 10 && numberPart.match(/^[6-9]/)) {
        return cleanPhone;
      }
    }
    return null;
  };

  // Handle phone number input - enforce +91 prefix
  const handlePhoneNumberChange = (e) => {
    let value = e.target.value.replace(/[^\d+]/g, '');
    if (value.length > 0 && !value.startsWith('+91')) {
      if (value.match(/^\d/)) {
        value = '+91' + value;
      }
    }
    if (!value.startsWith('+91') && value.length > 0) {
      value = '+91';
    }
    if (value.length > 13) {
      value = value.substring(0, 13);
    }
    setPhoneNumber(value);
    clearMessages();
  };

  // Handle OTP input
  const handleOTPChange = (e) => {
    const value = e.target.value.replace(/\D/g, '').slice(0, 6);
    setOtp(value);
    clearMessages();
  };

  // Handle sending OTP
  const handleSendOTP = async (e) => {
    e.preventDefault();
    if (loading) return;
    setLoading(true);
    clearMessages();

    try {
      const validatedPhone = validatePhoneNumber(phoneNumber);
      if (!validatedPhone) {
        setError('Please enter a valid phone number with +91 prefix (e.g., +919876543210)');
        setLoading(false);
        return;
      }

      const response = await fetch(`${API_BASE_URL}/api/send-otp/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone_number: validatedPhone }),
      });

      const data = await response.json();
      if (response.ok && data.success) {
        setShowOTP(true);
        setSuccess('OTP sent successfully! Check your phone.');
        setPhoneNumber(validatedPhone);
      } else {
        setError(data.error || data.message || 'Failed to send OTP');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle OTP verification
  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    if (loading) return;
    setLoading(true);
    clearMessages();

    try {
      if (!otp || otp.length !== 6) {
        setError('Please enter a valid 6-digit OTP');
        setLoading(false);
        return;
      }

      const response = await fetch(`${API_BASE_URL}/api/verify-otp/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ otp_code: otp, phone_number: phoneNumber }),
      });

      const data = await response.json();
      if (response.ok && data.success) {
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('loginTime', Date.now().toString());
        
        setSuccess('Login successful! Redirecting...');
        
        setTimeout(() => {
          navigate(data.redirect_to || '/dashboard');
        }, 1500);
        
      } else {
        setError(data.error || data.message || 'Invalid OTP');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle back to phone
  const handleBackToPhone = () => {
    setShowOTP(false);
    setOtp('');
    clearMessages();
  };

  return (
    <div className="login-page">
      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
            AirAware
          </Link>
        </div>
      </nav>

      <section className="login-section">
        <div className="particles">
          {createParticles()}
        </div>
        
        <div className="login-container">
          <div className="login-header">
            <h2>üë§ {showOTP ? 'Verify OTP' : 'Login'}</h2>
            <p>{showOTP ? `Enter the 6-digit OTP sent to ${phoneNumber}` : 'Welcome back! Please log in to your account.'}</p>
            <li><Link to="/">üè† Home</Link></li>
          </div>

          {success && <div className="success-message">‚úÖ {success}</div>}
          {error && <div className="error-message">‚ö†Ô∏è {error}</div>}

          {!showOTP ? (
            <form onSubmit={handleSendOTP}>
              <div className="form-group">
                <label htmlFor="phone_number">Enter your phone number</label>
                <input
                  type="tel"
                  id="phone_number"
                  value={phoneNumber}
                  onChange={handlePhoneNumberChange}
                  placeholder="+919876543210"
                  required
                  disabled={loading}
                  maxLength="13"
                />
                <small>Enter mobile number with +91 prefix (13 characters total)</small>
              </div>
              <button type="submit" disabled={loading || !phoneNumber}>
                {loading ? 'üì§ Sending OTP...' : 'üì§ Send OTP'}
              </button>
            </form>
          ) : (
            <form onSubmit={handleVerifyOTP}>
              <div className="form-group">
                <label htmlFor="otp">Enter OTP</label>
                <input
                  type="text"
                  id="otp"
                  value={otp}
                  onChange={handleOTPChange}
                  placeholder="Enter 6-digit OTP"
                  maxLength="6"
                  required
                  disabled={loading}
                  className="otp-input"
                />
                <small>Enter the 6-digit OTP sent to your phone</small>
              </div>
              
              <button type="submit" disabled={loading || otp.length !== 6} className="verify-btn">
                {loading ? 'üîÑ Verifying...' : '‚úÖ Verify OTP'}
              </button>
              
              <div className="back-button-container">
                <button type="button" onClick={handleBackToPhone} disabled={loading} className="back-btn">
                  ‚Üê Back to Phone Number
                </button>
              </div>
            </form>
          )}

          <div className="login-footer">
            <p><Link to="/">üîë Forgot Password?</Link></p>
            <p>Don't have an account? <Link to="/signup">üë§ Sign Up</Link></p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Login;
