import React, { useEffect, useState, useCallback, useMemo } from 'react';
import './css/HomePage.css';

function HomePage() {
  const [homeData, setHomeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [username, setUsername] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [retryCount, setRetryCount] = useState(0);

  // API Base URL with fallback
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Optimized fetch with faster timeout and better error handling
  const fetchHomeData = useCallback(async () => {
    const startTime = Date.now();
    console.log('ðŸš€ Starting API call to:', `${API_BASE_URL}/api/home/`);
    
    try {
      // Create abort controller with faster timeout (5 seconds instead of 10)
      const controller = new AbortController();
      const timeoutId = setTimeout(() => {
        controller.abort();
        console.log('â° Request timeout after 5 seconds');
      }, 5000); // Reduced timeout for faster failure detection

      const response = await fetch(`${API_BASE_URL}/api/home/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Cache-Control': 'no-cache', // Prevent caching issues
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      const responseTime = Date.now() - startTime;
      console.log(`âœ… Response received in ${responseTime}ms`);
      
      if (!response.ok) {
        throw new Error(`Server returned ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      console.log('ðŸ“Š Data received:', data);
      
      setHomeData(data);
      setError(null);
      setRetryCount(0); // Reset retry count on success
      
    } catch (err) {
      const responseTime = Date.now() - startTime;
      console.error(`âŒ Error after ${responseTime}ms:`, err.message);
      
      let errorMessage;
      if (err.name === 'AbortError') {
        errorMessage = `Connection timeout (${responseTime}ms) - Django server may be slow or not responding`;
      } else if (err.message.includes('Failed to fetch')) {
        errorMessage = `Cannot connect to Django server at ${API_BASE_URL}`;
      } else {
        errorMessage = `API Error: ${err.message}`;
      }
      
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [API_BASE_URL]);

  // Optimized particles creation with reduced count
  const createParticles = useCallback(() => {
    const particlesContainer = document.getElementById('particles');
    if (!particlesContainer || particlesContainer.children.length > 0) {
      return; // Skip if container doesn't exist or particles already created
    }

    console.log('âœ¨ Creating optimized particles');
    
    // Create only 15 particles for better performance
    for (let i = 0; i < 15; i++) {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.top = Math.random() * 100 + '%';
      particle.style.width = Math.random() * 3 + 2 + 'px'; // Smaller particles
      particle.style.height = particle.style.width;
      particle.style.animationDelay = Math.random() * 6 + 's'; // Shorter animation
      particlesContainer.appendChild(particle);
    }
  }, []);

  const toggleMenu = useCallback(() => {
    setIsMenuOpen(prev => !prev);
  }, []);

  // Memoized AQI status calculation
  const getAQIStatus = useMemo(() => {
    return (aqiValue) => {
      if (!aqiValue) return { status: 'Unknown', icon: 'fas fa-question', color: '#6b7280' };
      
      const value = parseInt(aqiValue, 10);
      if (value <= 50) {
        return { status: 'GOOD', icon: 'fas fa-smile', color: '#10b981' };
      } else if (value <= 100) {
        return { status: 'MODERATE', icon: 'fas fa-meh', color: '#f59e0b' };
      } else if (value <= 150) {
        return { status: 'UNHEALTHY', icon: 'fas fa-frown', color: '#ef4444' };
      } else {
        return { status: 'HAZARDOUS', icon: 'fas fa-biohazard', color: '#7c2d12' };
      }
    };
  }, []);

  const formatDateTime = useCallback((dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      console.error('Date formatting error:', error);
      return dateString;
    }
  }, []);

  // Retry function with exponential backoff
  const handleRetry = useCallback(() => {
    const newRetryCount = retryCount + 1;
    setRetryCount(newRetryCount);
    setLoading(true);
    setError(null);
    
    console.log(`ðŸ”„ Retry attempt ${newRetryCount}`);
    
    // Add small delay for retries to prevent spam
    setTimeout(() => {
      fetchHomeData();
    }, Math.min(1000 * newRetryCount, 3000)); // Max 3 second delay
  }, [fetchHomeData, retryCount]);

  // Skip API call for now - load page immediately
  const skipAndLoadPage = useCallback(() => {
    console.log('â­ï¸ Skipping API call, loading page with default data');
    setHomeData({
      highest_sub_index: 45,
      latest_item: {
        received_at: new Date().toISOString(),
      },
      status: 'demo'
    });
    setError(null);
    setLoading(false);
  }, []);

  useEffect(() => {
    console.log('ðŸ  HomePage component mounted');
    
    // Create particles immediately for visual feedback
    createParticles();
    
    // Start API call
    fetchHomeData();
    
    // Set up auto-refresh with longer interval
    const interval = setInterval(() => {
      if (!document.hidden && homeData && !error) {
        console.log('ðŸ”„ Auto-refresh triggered');
        fetchHomeData();
      }
    }, 30000); // 30 seconds instead of 5

    return () => {
      console.log('ðŸ§¹ Cleaning up');
      clearInterval(interval);
    };
  }, [fetchHomeData, createParticles, homeData, error]);

  const aqiStatus = getAQIStatus(homeData?.highest_sub_index);

  // Fast loading screen with skip option
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading air quality data...</p>
        <small style={{ marginTop: '10px', opacity: 0.7 }}>
          Connecting to: {API_BASE_URL}
        </small>
        <div style={{ marginTop: '20px' }}>
          <button 
            onClick={skipAndLoadPage}
            style={{
              background: '#3b82f6',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            Skip and Load Page â†’
          </button>
        </div>
      </div>
    );
  }

  // Enhanced error screen with multiple options
  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">
          <h2>âš ï¸ Connection Issue</h2>
          <p style={{ color: '#ef4444', marginBottom: '20px' }}>{error}</p>
          
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap', justifyContent: 'center' }}>
            <button onClick={handleRetry} className="retry-button">
              ðŸ”„ Retry ({retryCount}/3)
            </button>
            <button onClick={skipAndLoadPage} className="retry-button">
              â­ï¸ Load Demo Page
            </button>
          </div>

          <details style={{ 
            marginTop: '20px', 
            textAlign: 'left', 
            background: '#f5f5f5', 
            padding: '15px', 
            borderRadius: '8px',
            fontSize: '14px'
          }}>
            <summary style={{ cursor: 'pointer', fontWeight: 'bold' }}>
              ðŸ”§ Troubleshooting Tips
            </summary>
            <div style={{ marginTop: '10px' }}>
              <p><strong>1. Check Django Server:</strong></p>
              <code style={{ background: '#eee', padding: '2px 4px' }}>
                python manage.py runserver 8000
              </code>
              
              <p style={{ marginTop: '10px' }}><strong>2. Test API Manually:</strong></p>
              <a 
                href={`${API_BASE_URL}/api/home/`} 
                target="_blank" 
                rel="noopener noreferrer"
                style={{ color: '#3b82f6' }}
              >
                {API_BASE_URL}/api/home/
              </a>
              
              <p style={{ marginTop: '10px' }}><strong>3. Install Dependencies:</strong></p>
              <code style={{ background: '#eee', padding: '2px 4px' }}>
                pip install djangorestframework django-cors-headers
              </code>
            </div>
          </details>
        </div>
      </div>
    );
  }

  return (
    <div className="homepage">
      {/* Demo indicator if using demo data */}
      {homeData?.status === 'demo' && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          background: '#f59e0b',
          color: 'white',
          padding: '8px 12px',
          borderRadius: '6px',
          fontSize: '12px',
          zIndex: 9999,
          fontWeight: 'bold'
        }}>
          ðŸ“Š Demo Mode - API Unavailable
        </div>
      )}

      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="#" className="navbar-brand">
            <img 
              src="/aqi.webp" 
              alt="AQM Logo"
              onError={(e) => {
                e.target.style.display = 'none';
                console.warn('Logo image not found');
              }}
              loading="lazy" // Lazy load images
            />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="#home" className="nav-link">Home</a></li>
            <li><a href="#cities" className="nav-link">Cities</a></li>
            <li><a href="#services" className="nav-link">Services</a></li>
            <li><a href="#contact" className="nav-link">Contact</a></li>
            <li><a href="/forecast" className="nav-link">Forecasting</a></li>

            {username ? (
              <>
                <li><a href="/profile" className="nav-link">Profile</a></li>
                <li className="nav-link">Hello, {username}</li>
                <li><a href="/logout" className="nav-link login-btn">
                  <i className="fas fa-sign-out-alt"></i> Logout
                </a></li>
              </>
            ) : (
              <li><a href="/login" className="nav-link login-btn">
                <i className="fas fa-sign-in-alt"></i> Login
              </a></li>
            )}
          </ul>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero-section" id="home">
        <div className="hero-overlay"></div>
        <div id="particles" className="particles"></div>
        <div className="hero-content">
          <h1 className="hero-title">Air Quality Monitoring</h1>
          <p className="hero-subtitle">Live air quality updates and insights from Kalady, Ernakulam</p>
          <p style={{ fontSize: '14px', color: '#ccc' }}>
            This value reflects combined readings from 5 monitoring stations in the area.
          </p>
          
          <div className="aqi-display">
            <div className="aqi-label">
              Current AQI â€¢ Last Updated: <span>
                {formatDateTime(homeData?.latest_item?.received_at)}
              </span>
            </div>
            <div className="aqi-value">
              {homeData?.highest_sub_index || "N/A"}
            </div>
            <div className="aqi-status">AQI - {aqiStatus.status}</div>
            <div className="aqi-icon">
              <i className={aqiStatus.icon} style={{ color: aqiStatus.color }}></i>
            </div>
          </div>

          <a href="/map" className="cta-button">
            <i className="fas fa-map-marked-alt"></i>
            View Live AQI Map
          </a>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="stats-container">
          <div className="section-header">
            <h2 className="section-title">Kerala Air Quality Network</h2>
            <p className="section-subtitle">
              Comprehensive monitoring across multiple locations for accurate air quality assessment
            </p>
          </div>

          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">5</div>
              <div className="stat-label">Monitoring Stations</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">24/7</div>
              <div className="stat-label">Real-time Monitoring</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">6</div>
              <div className="stat-label">Key Pollutants Tracked</div>
            </div>
          </div>
        </div>
      </section>

      {/* AQI Scale Section */}
      <section className="section aqi-scale-section">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Understanding Air Quality Index</h2>
            <p className="section-subtitle">
              Know what the AQI numbers mean for your health and daily activities
            </p>
          </div>

          <div className="aqi-scale-grid">
            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-good"></div>
                <div className="aqi-range-text">Good (0-50)</div>
              </div>
              <div className="aqi-description">
                Air quality is satisfactory. Air pollution poses little or no risk for everyone.
              </div>
            </div>

            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-moderate"></div>
                <div className="aqi-range-text">Moderate (51-100)</div>
              </div>
              <div className="aqi-description">
                Air quality is acceptable. However, sensitive individuals may experience minor issues.
              </div>
            </div>

            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-unhealthy"></div>
                <div className="aqi-range-text">Unhealthy (101-200)</div>
              </div>
              <div className="aqi-description">
                Members of sensitive groups may experience health effects. Limit outdoor activities.
              </div>
            </div>

            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-hazardous"></div>
                <div className="aqi-range-text">Hazardous (201+)</div>
              </div>
              <div className="aqi-description">
                Health alert: The risk of health effects is increased for everyone. Avoid outdoor activities.
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Cities Section */}
      <section className="section cities-section" id="cities">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Monitoring Locations</h2>
            <p className="section-subtitle">
              Air quality data from strategic locations across Ernakulam district
            </p>
          </div>

          <div className="cities-grid">
            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-university"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Adi Shankara Institute</div>
                <div className="city-type">Educational Institution</div>
              </a>
            </div>

            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-city"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Matoor, Ernakulam</div>
                <div className="city-type">Urban Area</div>
              </a>
            </div>

            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-building"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Marotichodu Community Hall</div>
                <div className="city-type">Community Center</div>
              </a>
            </div>

            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-home"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Kaipatoor, Ernakulam</div>
                <div className="city-type">Residential Area</div>
              </a>
            </div>

            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-landmark"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Kalady Panchayat Office</div>
                <div className="city-type">Government Office</div>
              </a>
            </div>
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section className="section services-section" id="services">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Our Services</h2>
            <p className="section-subtitle">
              Advanced air quality solutions powered by cutting-edge technology
            </p>
          </div>

          <div className="services-grid">
            <div className="service-card">
              <div className="service-icon">
