import React, { useEffect, useState, useCallback } from 'react';
import './css/HomePage.css';

function HomePage() {
  const [homeData, setHomeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [username, setUsername] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [debugInfo, setDebugInfo] = useState({});

  // API Base URL - change this to match your Django server
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  const fetchHomeData = useCallback(async () => {
    console.log('🔄 Starting API call...');
    console.log('📡 API URL:', `${API_BASE_URL}/api/home/`);
    
    const startTime = Date.now();
    
    try {
      // Create timeout controller
      const controller = new AbortController();
      const timeoutId = setTimeout(() => {
        controller.abort();
        console.log('⏰ Request timed out after 10 seconds');
      }, 10000);

      console.log('📤 Sending request...');
      
      const response = await fetch(`${API_BASE_URL}/api/home/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      const responseTime = Date.now() - startTime;
      
      console.log('📡 Response received in', responseTime, 'ms');
      console.log('📊 Response status:', response.status);
      console.log('📋 Response headers:', response.headers);
      
      setDebugInfo({
        url: `${API_BASE_URL}/api/home/`,
        status: response.status,
        responseTime: responseTime,
        timestamp: new Date().toLocaleString()
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('❌ HTTP Error:', response.status, errorText);
        throw new Error(`HTTP ${response.status}: ${response.statusText || 'Unknown error'}`);
      }
      
      const data = await response.json();
      console.log('✅ Data received:', data);
      
      // Validate data structure
      if (data && typeof data === 'object') {
        setHomeData(data);
        setError(null);
        console.log('✅ Data set successfully');
      } else {
        console.error('❌ Invalid data format:', data);
        throw new Error('Invalid data format received from server');
      }
      
    } catch (err) {
      const responseTime = Date.now() - startTime;
      console.error('❌ Fetch Error Details:', {
        name: err.name,
        message: err.message,
        stack: err.stack,
        responseTime: responseTime
      });
      
      let errorMessage = 'Unknown error occurred';
      
      if (err.name === 'AbortError') {
        errorMessage = `Request timeout after ${responseTime}ms - Django server may be slow or not running`;
      } else if (err.message.includes('Failed to fetch')) {
        errorMessage = `Cannot connect to Django server at ${API_BASE_URL} - Check if server is running on port 8000`;
      } else if (err.message.includes('NetworkError')) {
        errorMessage = 'Network error - Check your internet connection and firewall settings';
      } else if (err.message.includes('CORS')) {
        errorMessage = 'CORS error - Django CORS settings need to be configured';
      } else {
        errorMessage = `API Error: ${err.message}`;
      }
      
      setError(errorMessage);
      setDebugInfo({
        url: `${API_BASE_URL}/api/home/`,
        error: err.message,
        errorType: err.name,
        responseTime: responseTime,
        timestamp: new Date().toLocaleString()
      });
    } finally {
      setLoading(false);
      console.log('🏁 Fetch operation completed');
    }
  }, [API_BASE_URL]);

  const createParticles = useCallback(() => {
    console.log('✨ Creating particles...');
    const particlesContainer = document.getElementById('particles');
    if (!particlesContainer) {
      console.warn('⚠️ Particles container not found');
      return;
    }

    // Only create if not already created
    if (particlesContainer.children.length > 0) {
      console.log('✨ Particles already exist, skipping');
      return;
    }

    // Create fewer particles for better performance
    for (let i = 0; i < 20; i++) {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.top = Math.random() * 100 + '%';
      particle.style.width = Math.random() * 4 + 2 + 'px';
      particle.style.height = particle.style.width;
      particle.style.animationDelay = Math.random() * 8 + 's';
      particlesContainer.appendChild(particle);
    }
    console.log('✨ Particles created successfully');
  }, []);

  const toggleMenu = useCallback(() => {
    setIsMenuOpen(prev => !prev);
  }, []);

  const getAQIStatus = useCallback((aqiValue) => {
    if (!aqiValue) return { status: 'Unknown', icon: 'fas fa-question', color: '#6b7280' };
    
    const value = parseInt(aqiValue, 10);
    if (value <= 50) {
      return { status: 'GOOD', icon: 'fas fa-smile', color: '#10b981' };
    } else if (value <= 100) {
      return { status: 'MODERATE', icon: 'fas fa-meh', color: '#f59e0b' };
    } else if (value <= 150) {
      return { status: 'UNHEALTHY', icon: 'fas fa-frown', color: '#ef4444' };
    } else {
      return { status: 'HAZARDOUS', icon: 'fas fa-biohazard', color: '#7c2d12' };
    }
  }, []);

  const formatDateTime = useCallback((dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return dateString;
    }
  }, []);

  const handleRetry = useCallback(() => {
    console.log('🔄 Manual retry triggered');
    setLoading(true);
    setError(null);
    setDebugInfo({});
    fetchHomeData();
  }, [fetchHomeData]);

  const handleTestConnection = useCallback(async () => {
    console.log('🧪 Testing connection...');
    try {
      const response = await fetch(`${API_BASE_URL}/api/health/`);
      const data = await response.json();
      alert(`Connection test: ${response.ok ? 'SUCCESS' : 'FAILED'}\nResponse: ${JSON.stringify(data, null, 2)}`);
    } catch (err) {
      alert(`Connection test FAILED: ${err.message}`);
    }
  }, [API_BASE_URL]);

  useEffect(() => {
    console.log('🚀 HomePage component mounted');
    console.log('🔧 Environment:', {
      NODE_ENV: process.env.NODE_ENV,
      API_URL: API_BASE_URL,
      userAgent: navigator.userAgent,
      timestamp: new Date().toISOString()
    });
    
    // Initial data fetch
    fetchHomeData();
    
    // Create particles
    createParticles();

    // Auto-refresh with visibility check
    const interval = setInterval(() => {
      if (!document.hidden && !error) {
        console.log('🔄 Auto-refresh triggered');
        fetchHomeData();
      }
    }, 30000); // Reduced to 30 seconds

    return () => {
      console.log('🧹 Cleaning up HomePage');
      clearInterval(interval);
    };
  }, [fetchHomeData, createParticles, error]);

  // Loading state with debug info
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading air quality data...</p>
        <small style={{ marginTop: '10px', opacity: 0.7 }}>
          Connecting to: {API_BASE_URL}
        </small>
        {debugInfo.responseTime && (
          <small style={{ marginTop: '5px', opacity: 0.5 }}>
            Response time: {debugInfo.responseTime}ms
          </small>
        )}
      </div>
    );
  }

  // Error state with detailed debugging
  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">
          <h2>⚠️ Connection Problem</h2>
          <p style={{ color: '#ef4444', marginBottom: '20px' }}>{error}</p>
          
          <div style={{ marginBottom: '20px' }}>
            <button onClick={handleRetry} className="retry-button" style={{ marginRight: '10px' }}>
              🔄 Retry Connection
            </button>
            <button onClick={handleTestConnection} className="retry-button">
              🧪 Test Connection
            </button>
          </div>

          <details style={{ textAlign: 'left', backgroundColor: '#f5f5f5', padding: '15px', borderRadius: '8px' }}>
            <summary style={{ cursor: 'pointer', fontWeight: 'bold' }}>🔍 Debug Information</summary>
            <pre style={{ fontSize: '12px', marginTop: '10px', whiteSpace: 'pre-wrap' }}>
{`API URL: ${debugInfo.url || API_BASE_URL + '/api/home/'}
Status: ${debugInfo.status || 'No response'}
Error: ${debugInfo.error || 'Unknown'}
Error Type: ${debugInfo.errorType || 'Unknown'}
Response Time: ${debugInfo.responseTime || 'N/A'}ms
Timestamp: ${debugInfo.timestamp || new Date().toLocaleString()}

Troubleshooting Steps:
1. Check if Django server is running: python manage.py runserver 8000
2. Test API directly: visit ${API_BASE_URL}/api/home/ in browser
3. Check Django logs for errors
4. Verify CORS settings in Django settings.py
5. Make sure 'rest_framework' and 'corsheaders' are installed

Common Fixes:
- pip install djangorestframework django-cors-headers
- Add CORS_ALLOW_ALL_ORIGINS = True to Django settings
- Restart Django server after settings changes`}
            </pre>
          </details>
        </div>
      </div>
    );
  }

  const aqiStatus = getAQIStatus(homeData?.highest_sub_index);

  return (
    <div className="homepage">
      {/* Debug info in development */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{ 
          position: 'fixed', 
          top: '10px', 
          right: '10px', 
          background: 'rgba(0,0,0,0.8)', 
          color: 'white', 
          padding: '10px', 
          borderRadius: '5px', 
          fontSize: '12px',
          zIndex: 9999,
          maxWidth: '300px'
        }}>
          <strong>Debug Info:</strong><br/>
          API: {API_BASE_URL}<br/>
          Data: {homeData ? '✅ Loaded' : '❌ No data'}<br/>
          AQI: {homeData?.highest_sub_index || 'N/A'}<br/>
          Last Update: {debugInfo.timestamp || 'N/A'}
        </div>
      )}

      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="#" className="navbar-brand">
            <img 
              src="/aqi.webp" 
              alt="AQM Logo"
              onError={(e) => {
                e.target.style.display = 'none';
                console.warn('⚠️ Logo image not found');
              }}
              onLoad={() => console.log('✅ Logo loaded')}
            />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="#home" className="nav-link">Home</a></li>
            <li><a href="#cities" className="nav-link">Cities</a></li>
            <li><a href="#services" className="nav-link">Services</a></li>
            <li><a href="#contact" className="nav-link">Contact</a></li>
            <li><a href="/forecast" className="nav-link">Forecasting</a></li>

            {username ? (
              <>
                <li><a href="/profile" className="nav-link">Profile</a></li>
                <li className="nav-link">Hello, {username}</li>
                <li><a href="/logout" className="nav-link login-btn">
                  <i className="fas fa-sign-out-alt"></i> Logout
                </a></li>
              </>
            ) : (
              <li><a href="/login" className="nav-link login-btn">
                <i className="fas fa-sign-in-alt"></i> Login
              </a></li>
            )}
          </ul>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero-section" id="home">
        <div className="hero-overlay"></div>
        <div id="particles" className="particles"></div>
        <div className="hero-content">
          <h1 className="hero-title">Air Quality Monitoring</h1>
          <p className="hero-subtitle">Live air quality updates and insights from Kalady, Ernakulam</p>
          <p style={{ fontSize: '14px', color: '#ccc' }}>
            This value reflects combined readings from 5 monitoring stations in the area.
          </p>
          
          <div className="aqi-display">
            <div className="aqi-label">
              Current AQI • Last Updated: <span>
                {formatDateTime(homeData?.latest_item?.received_at)}
              </span>
            </div>
            <div className="aqi-value">
              {homeData?.highest_sub_index || "N/A"}
            </div>
            <div className="aqi-status">AQI - {aqiStatus.status}</div>
            <div className="aqi-icon">
              <i className={aqiStatus.icon} style={{ color: aqiStatus.color }}></i>
            </div>
          </div>

          <a href="/map" className="cta-button">
            <i className="fas fa-map-marked-alt"></i>
            View Live AQI Map
          </a>
        </div>
      </section>

      {/* Rest of your existing sections remain the same */}
      {/* Stats Section */}
      <section className="stats-section">
        <div className="stats-container">
          <div className="section-header">
            <h2 className="section-title">Kerala Air Quality Network</h2>
            <p className="section-subtitle">
              Comprehensive monitoring across multiple locations for accurate air quality assessment
            </p>
          </div>

          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">5</div>
              <div className="stat-label">Monitoring Stations</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">24/7</div>
              <div className="stat-label">Real-time Monitoring</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">6</div>
              <div className="stat-label">Key Pollutants Tracked</div>
            </div>
          </div>
        </div>
      </section>

      {/* AQI Scale Section */}
      <section className="section aqi-scale-section">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Understanding Air Quality Index</h2>
            <p className="section-subtitle">
              Know what the AQI numbers mean for your health and daily activities
            </p>
          </div>

          <div className="aqi-scale-grid">
            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-good"></div>
                <div className="aqi-range-text">Good (0-50)</div>
              </div>
              <div className="aqi-description">
                Air quality is satisfactory. Air pollution poses little or no risk for everyone.
              </div>
            </div>

            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-moderate"></div>
                <div className="aqi-range-text">Moderate (51-100)</div>
              </div>
              <div className="aqi-description">
                Air quality is acceptable. However, sensitive individuals may experience minor issues.
              </div>
            </div>

            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-unhealthy"></div>
                <div className="aqi-range-text">Unhealthy (101-200)</div>
              </div>
              <div className="aqi-description">
                Members of sensitive groups may experience health effects. Limit outdoor activities.
              </div>
            </div>

            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-hazardous"></div>
                <div className="aqi-range-text">Hazardous (201+)</div>
              </div>
              <div className="aqi-description">
                Health alert: The risk of health effects is increased for everyone. Avoid outdoor activities.
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Cities Section */}
      <section className="section cities-section" id="cities">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Monitoring Locations</h2>
            <p className="section-subtitle">
              Air quality data from strategic locations across Ernakulam district
            </p>
          </div>

          <div className="cities-grid">
            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-university"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Adi Shankara Institute</div>
                <div className="city-type">Educational Institution</div>
              </a>
            </div>

            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-city"></i>
              </div>
              <a href="/map" className="city-link">
                <div className="city-name">Matoor, Ernakulam</div>
                <div className="city-type">Urban Area</div>
              </a>
            </div>

            <div className="city-card">
              <div className="city-icon">
                <i className="fas fa-building"></i>
              </div>
