import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// Lazy loading components with fallbacks
const LazyMap = React.lazy(() => 
  import('./LazyMap').catch(() => ({ 
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">🗺️</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() => 
  import('./LazyChart').catch(() => ({ 
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">📊</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// Utility Functions
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + 
           Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
           Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// IP-based location service
const getIPLocation = async () => {
  try {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 5000);
    
    const response = await fetch('https://ipapi.co/json/', { 
      signal: controller.signal,
      headers: {
        'Accept': 'application/json',
        'User-Agent': 'AirAware/1.0'
      }
    });
    
    clearTimeout(timeoutId);
    
    if (!response.ok) throw new Error(`HTTP ${response.status}`);
    
    const data = await response.json();
    
    if (!data.latitude || !data.longitude) {
      throw new Error('Invalid location data');
    }
    
    return {
      lat: parseFloat(data.latitude),
      lng: parseFloat(data.longitude),
      city: data.city || 'Unknown City',
      region: data.region || 'Unknown Region',
      country: data.country || 'Unknown Country',
      accuracy: 10000,
      source: 'ip'
    };
  } catch (error) {
    console.log('IP location failed:', error);
    return null;
  }
};

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStation, setNearestStation] = useState(null);
  const [interpolatedData, setInterpolatedData] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [dataType, setDataType] = useState('station');
  const [dataSource, setDataSource] = useState('loading');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
  const [healthData, setHealthData] = useState(null);

  const navigate = useNavigate();
  
  const API_BASE_URL = (() => {
    try {
      return process.env.REACT_APP_API_URL || 'http://localhost:8000';
    } catch (e) {
      return 'http://localhost:8000';
    }
  })();
  
  const locationTimeoutRef = useRef(null);
  const abortControllerRef = useRef(null);

  // ===== INSTANT DASHBOARD LOAD WITH ENHANCED SAMPLE DATA =====
  const loadDashboardInstantly = useCallback(async () => {
    console.log('🚀 Loading dashboard instantly...');
    setDataSource('sample');
    setLocationStatus('detecting');
    
    const sampleData = {
      stations: {
        'lora-v1': {
          station_info: {
            name: 'Station 1 (ASIET Campus)',
            lat: 10.178322301561957,
            lng: 76.43089144291672
          },
          averages: {
            pm25: 25, pm10: 42, so2: 8, no2: 40,
            co: 1.1, o3: 46, nh3: 93, temp: 28, hum: 65, pre: 1013
          },
          highest_sub_index: 50
        },
        'loradev2': {
          station_info: {
            name: 'Station 2 (Mattoor Junction)',
            lat: 10.17095090340159,
            lng: 76.42962876824544
          },
          averages: {
            pm25: 22, pm10: 38, so2: 7, no2: 35,
            co: 1.0, o3: 43, nh3: 88, temp: 27, hum: 68, pre: 1012
          },
          highest_sub_index: 45
        }
      },
      forecasts: {
        'lora-v1': [
          { day: 'Today', pm25_max: 30, pm10_max: 37, so2_max: 8, no2_max: 40, co_max: 1.1, o3_max: 46, nh3_max: 93 },
          { day: 'Tomorrow', pm25_max: 24, pm10_max: 34, so2_max: 8, no2_max: 45, co_max: 1.2, o3_max: 43, nh3_max: 100 },
          { day: 'Day 3', pm25_max: 24, pm10_max: 30, so2_max: 8, no2_max: 42, co_max: 1.2, o3_max: 45, nh3_max: 100 },
          { day: 'Day 4', pm25_max: 20, pm10_max: 23, so2_max: 8, no2_max: 38, co_max: 1.2, o3_max: 47, nh3_max: 100 }
        ]
      },
      health_data: {
        risk_level: 'Low',
        score: 75,
        recommendations: ['Enjoy outdoor activities', 'Open windows for fresh air']
      },
      status: 'sample_data'
    };

    processDashboardData(sampleData);
    setLastUpdateTime(new Date());
    console.log('✅ Dashboard loaded instantly with sample data');
  }, []);

  // ===== SMART LOCATION DETECTION =====
  const startSmartLocationDetection = useCallback(async () => {
    console.log('📍 Starting smart location detection...');
    setLocationStatus('detecting');
    setIsLocationUpdating(true);

    try {
      // Step 1: Try IP-based location first
      const ipLocation = await getIPLocation();
      if (ipLocation) {
        console.log('📍 Got IP location:', ipLocation);
        setUserLocation(ipLocation);
        setUserLocationName({ 
          display_name: `${ipLocation.city}, ${ipLocation.region}`,
          city: ipLocation.city,
          state: ipLocation.region,
          country: ipLocation.country
        });
        setLocationStatus('ip_detected');
        
        // Update with IP-based data
        await updateDashboardWithLocation(ipLocation);
      }

      // Step 2: Try GPS for precise location
      if (navigator.geolocation) {
        try {
          const gpsLocation = await getGPSLocation();
          if (gpsLocation) {
            console.log('📍 Got GPS location:', gpsLocation);
            setUserLocation(gpsLocation);
            setLocationStatus('gps_detected');
            
            // Get precise location name
            await getLocationName(gpsLocation.lat, gpsLocation.lng);
            
            // Update with GPS-based data
            await updateDashboardWithLocation(gpsLocation);
          }
        } catch (gpsError) {
          console.log('📍 GPS detection failed:', gpsError);
        }
      }

    } catch (error) {
      console.log('📍 Location detection failed:', error);
      setLocationStatus('failed');
    } finally {
      setIsLocationUpdating(false);
    }
  }, []);

  // ===== GPS LOCATION =====
  const getGPSLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation not supported'));
        return;
      }

      const timeout = setTimeout(() => {
        reject(new Error('GPS timeout'));
      }, 10000);

      navigator.geolocation.getCurrentPosition(
        (position) => {
          clearTimeout(timeout);
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy,
            source: 'gps',
            timestamp: Date.now()
          };
          
          if (isNaN(location.lat) || isNaN(location.lng)) {
            reject(new Error('Invalid GPS coordinates'));
            return;
          }
          
          resolve(location);
        },
        (error) => {
          clearTimeout(timeout);
          console.log('GPS error:', error.message);
          reject(error);
        },
        { 
          enableHighAccuracy: true, 
          timeout: 10000, 
          maximumAge: 300000 
        }
      );
    });
  }, []);

  // ===== REVERSE GEOCODING =====
  const getLocationName = useCallback(async (lat, lng) => {
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);
      
      // Use a CORS proxy for geocoding
      const proxyUrl = 'https://api.allorigins.win/raw?url=';
      const geocodeUrl = `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`;
      
      const response = await fetch(`${proxyUrl}${encodeURIComponent(geocodeUrl)}`, {
        signal: controller.signal,
        headers: { 'Accept': 'application/json' }
      });
      
      clearTimeout(timeoutId);
      
      if (response.ok) {
        const data = await response.json();
        const locationName = {
          display_name: data.display_name || `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
          city: data.address?.city || data.address?.town || data.address?.village || 'Unknown City',
          state: data.address?.state || 'Unknown State',
          country: data.address?.country || 'Unknown Country'
        };
        
        setUserLocationName(locationName);
        console.log('📍 Location name resolved:', locationName.display_name);
      }
    } catch (error) {
      console.log('❌ Reverse geocoding failed:', error);
      setUserLocationName({
        display_name: `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
        city: 'Unknown City',
        state: 'Unknown State',
        country: 'Unknown Country'
      });
    }
  }, []);

  // ===== UPDATE DASHBOARD WITH LOCATION =====
  const updateDashboardWithLocation = useCallback(async (locationData) => {
    try {
      console.log('🔄 Updating dashboard with location data...');
      setDataSource('loading');

      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }

      const controller = new AbortController();
      abortControllerRef.current = controller;
      
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      // Save user location to backend first
      try {
        await fetch(`${API_BASE_URL}/api/save-user-location/`, {
          method: 'POST',
          headers: { 
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            username: username,
            latitude: locationData.lat,
            longitude: locationData.lng,
            accuracy: locationData.accuracy,
            source: locationData.source
          })
        });
        console.log('✅ User location saved to backend');
      } catch (locationSaveError) {
        console.warn('⚠️ Failed to save user location:', locationSaveError);
      }

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      url.searchParams.append('lat', locationData.lat.toString());
      url.searchParams.append('lng', locationData.lng.toString());

      const response = await fetch(url, {
        signal: controller.signal,
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('✅ Location-specific data received');
      
      setDataSource('live');
      processDashboardData(data);
      setLastUpdateTime(new Date());
      setError(null);
      
    } catch (error) {
      console.error('❌ Error updating with location:', error);
      
      if (error.name !== 'AbortError') {
        setError(`Failed to load live data: ${error.message}`);
      }
      
      setDataSource('cached');
    }
  }, [username, API_BASE_URL]);

  // ===== PROCESS DASHBOARD DATA =====
  const processDashboardData = useCallback((data) => {
    if (!data) {
      console.warn('No data to process');
      return;
    }

    setDashboardData(data);
    
    // Extract health data
    if (data.health_data) {
      setHealthData(data.health_data);
    }

    // Calculate nearest station and interpolated data if location available
    if (userLocation && data.stations) {
      const stations = Object.entries(data.stations);
      let nearestDist = Infinity, nearestId = null;

      stations.forEach(([id, station]) => {
        const dist = calculateDistance(
          userLocation.lat, 
          userLocation.lng, 
          station.station_info.lat, 
          station.station_info.lng
        );
        if (dist < nearestDist) { 
          nearestDist = dist; 
          nearestId = id; 
        }
      });

      setNearestStation({ 
        id: nearestId, 
        distance: nearestDist,
        name: data.stations[nearestId]?.station_info.name || 'Station 1',
        aqi: data.stations[nearestId]?.highest_sub_index || 50
      });
      
      // Calculate interpolated data using IDW
      let weightedSum = 0, weightSum = 0;
      const interpolatedValues = {};
      
      stations.forEach(([, station]) => {
        const dist = calculateDistance(
          userLocation.lat, 
          userLocation.lng, 
          station.station_info.lat, 
          station.station_info.lng
        );
        const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
        
        // Calculate weighted AQI
        weightedSum += (station.highest_sub_index || 0) * weight;
        weightSum += weight;
        
        // Calculate weighted values for each parameter
        Object.keys(station.averages || {}).forEach(param => {
          if (!interpolatedValues[param]) interpolatedValues[param] = { sum: 0, weights: 0 };
          interpolatedValues[param].sum += (station.averages[param] || 0) * weight;
          interpolatedValues[param].weights += weight;
        });
      });
      
      if (weightSum > 0) {
        const finalInterpolated = {};
        Object.keys(interpolatedValues).forEach(param => {
          finalInterpolated[param] = interpolatedValues[param].sum / interpolatedValues[param].weights;
        });
        
        setInterpolatedData({ 
          aqi: Math.round(weightedSum / weightSum),
          values: finalInterpolated
        });
        
        // Set data type based on distance
        setDataType(nearestDist <= 1.0 ? 'interpolated' : 'station');
      }
    } else {
      // Default to first station if no location
      const firstStation = Object.keys(data.stations || {})[0];
      if (firstStation) {
        setNearestStation({
          id: firstStation,
          distance: null,
          name: data.stations[firstStation].station_info.name,
          aqi: data.stations[firstStation].highest_sub_index
        });
      }
      setDataType('station');
    }

    console.log('📊 Dashboard data processed');
  }, [userLocation]);

  // ===== INITIALIZATION =====
  useEffect(() => {
    const initializeDashboard = async () => {
      console.log('⚡ Initializing enhanced dashboard...');
      
      try {
        await loadDashboardInstantly();
        
        setTimeout(() => {
          startSmartLocationDetection();
        }, 500);
      } catch (error) {
        console.error('Dashboard initialization error:', error);
        setError('Failed to initialize dashboard');
      }
    };

    initializeDashboard();

    return () => {
      if (locationTimeoutRef.current) {
        clearTimeout(locationTimeoutRef.current);
      }
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, [loadDashboardInstantly, startSmartLocationDetection]);

  // ===== MEMOIZED CALCULATIONS =====
  const currentValues = useMemo(() => {
    if (!dashboardData) return {};
    
    if (dataType === 'interpolated' && interpolatedData?.values) {
      return interpolatedData.values;
    }
    
    if (nearestStation && dashboardData.stations && dashboardData.stations[nearestStation.id]) {
      return dashboardData.stations[nearestStation.id].averages || {};
    }
    
    // Fallback to first station
    const firstStation = Object.values(dashboardData.stations || {})[0];
    return firstStation?.averages || {};
  }, [dashboardData, dataType, interpolatedData, nearestStation]);

  const currentAQI = useMemo(() => {
    if (dataType === 'interpolated' && interpolatedData?.aqi) {
      return interpolatedData.aqi;
    }
    
    if (nearestStation) {
      return nearestStation.aqi;
    }
    
    return 50; // Default AQI
  }, [dataType, interpolatedData, nearestStation]);

  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  // ===== UI COMPONENTS =====
  const LocationStatus = useMemo(() => {
    const getLocationDisplay = () => {
      switch (locationStatus) {
        case 'initializing':
          return <span style={{ color: '#6b7280' }}>📍 Initializing location detection...</span>;
          
        case 'detecting':
          return (
            <span style={{ color: '#f59e0b' }}>
              📍 Detecting your location...
              {isLocationUpdating && <span className="location-spinner">⟳</span>}
            </span>
          );
          
        case 'ip_detected':
          const ipName = userLocationName?.display_name || 'Approximate location';
          return (
            <span style={{ color: '#3b82f6' }}>
              📍 {ipName} (approximate)
              {isLocationUpdating && <span className="location-spinner">⟳</span>}
            </span>
          );
          
        case 'gps_detected':
          const gpsName = userLocationName?.display_name || 'Your location';
          const accuracy = userLocation?.accuracy ? `±${Math.round(userLocation.accuracy)}m` : '';
          
          let distanceText = '';
          if (nearestStation && nearestStation.distance !== null && !isNaN(nearestStation.distance)) {
            distanceText = `${nearestStation.distance.toFixed(1)}km`;
          }
          
          return (
            <span style={{ color: '#10b981' }}>
              📍 {gpsName} {accuracy && `(${accuracy})`}
              {nearestStation && distanceText && (
                <span style={{ color: '#6b7280', fontSize: '0.9em' }}>
                  {' '} → {nearestStation.name} ({distanceText})
                </span>
              )}
            </span>
          );
          
        case 'failed':
          return (
            <span style={{ color: '#ef4444' }}>
              📍 Location detection failed - Using sample data
              <button onClick={startSmartLocationDetection} className="retry-location-btn">
                🔄 Retry
              </button>
            </span>
          );
          
        default:
          return <span style={{ color: '#6b7280' }}>📍 {nearestStation?.name || 'Default location'}</span>;
      }
    };

    return <div className="location-info">{getLocationDisplay()}</div>;
  }, [locationStatus, userLocationName, userLocation, nearestStation, isLocationUpdating, startSmartLocationDetection]);

  const formatValue = useCallback((value, metric) => {
    const numValue = parseFloat(value);
    if (isNaN(numValue)) return '0';
    
    return metric === 'co' ? numValue.toFixed(1) : Math.round(numValue);
  }, []);

  const metricIcons = useMemo(() => ({
    pm25: '🌱', pm10: '🍃', so2: '🏭', no2: '💨',
    co: '☁️', o3: '☀️', nh3: '⚗️'
  }), []);

  const MetricCards = useMemo(() => {
    const parameters = ['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'];
    
    return parameters.map((key) => {
      const value = currentValues[key] || 0;
      
      return (
        <div key={key} className="metric-card">
          <div className="metric-icon">{metricIcons[key] || '📊'}</div>
          <div className="metric-value">{formatValue(value, key)}</div>
          <div className="metric-label">{key.toUpperCase()}</div>
          <div className="metric-unit">{key === 'co' ? 'mg/m³' : 'µg/m³'}</div>
          {dataType === 'interpolated' && (
            <div className="metric-badge interpolated-badge">🎯 Smart</div>
          )}
          {dataType === 'station' && (
            <div className="metric-badge station-badge">📡 Station</div>
          )}
          {dataSource === 'sample' && (
            <div className="metric-badge sample-badge">🟡 Sample</div>
          )}
        </div>
      );
    });
  }, [currentValues, metricIcons, formatValue, dataType, dataSource]);

  // ===== EVENT HANDLERS =====
  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []);

  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  
  const handleLogout = useCallback(() => {
    localStorage.clear();
    navigate('/login');
  }, [navigate]);

  const handleEnableLocation = useCallback(() => {
    startSmartLocationDetection();
  }, [startSmartLocationDetection]);

  const handleRefreshData = useCallback(() => {
    if (userLocation) {
      updateDashboardWithLocation(userLocation);
    } else {
      loadDashboardInstantly();
    }
  }, [userLocation, updateDashboardWithLocation, loadDashboardInstantly]);

  // ===== MAIN RENDER =====
  return (
    <div className="dashboard-page">
      {/* Real-time Status */}
      <div className="realtime-status">
        🔴 LIVE • Updates every 30s • {locationStatus === 'gps_detected' ? 'GPS Active' : 'Location Detection Active'}
        {dataType === 'interpolated' && ' • Smart Analysis'}
      </div>

      {/* Error Banner */}
      {error && (
        <div className="error-banner">
          ⚠️ {error}
          <button onClick={() => setError(null)} className="error-close">✕</button>
        </div>
      )}

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" style={{ marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>☰</div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">🏠 Home</a></li>
            <li><a href="/forecast" className="nav-link">📈 Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">📋 Health Update</a></li>
            <li><a href="/health-report" className="nav-link">📄 Health Report</a></li>
            <li><a href="/add-family" className="nav-link">👥 Add Family</a></li>
            <li className="user-info">👤 <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">🚪 Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        ℹ️ <span>
          {userLocationName?.display_name || nearestStation?.name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}
          {nearestStation && nearestStation.distance !== null && !isNaN(nearestStation.distance) && (
            ` • Distance to station: ${nearestStation.distance.toFixed(1)}km`
          )}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header with Integrated Health Status */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">👤</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                {LocationStatus}
                {/* Integrated Health Status */}
                {healthData && (
                  <div className="health-status-inline">
                    <span className="health-label">Your Health Risk Level: </span>
                    <span className={`health-level ${healthData.risk_level.toLowerCase()}`}>
                      {healthData.risk_level} (Score: {healthData.score})
                    </span>
                  </div>
                )}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
              </div>
              <div className="aqi-status">
                {dataType === 'interpolated' ? 'Your Location AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                {userLocationName?.display_name || nearestStation?.name || 'Your Location'}
                {dataType === 'interpolated' && (
                  <span className="smart-indicator"> 🎯 Smart</span>
                )}
              </div>
              <div className="last-updated">
                Last updated: {lastUpdateTime.toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Remove separate health status banner since it's now integrated */}

        {/* Enhanced Sensor Notification */}
        <div className="sensor-notification">
          <div className="notification-icon">📡</div>
          <div className="notification-content">
            <strong>
              {dataType === 'interpolated' ? 
                `📍 Showing personalized air quality for your location` :
                `Nearest Sensor: ${nearestStation?.name || 'Station 1 (ASIET Campus)'}`
              }
            </strong>
            {nearestStation?.distance && (
              <span> - {nearestStation.distance.toFixed(1)}km away</span>
            )}
            <div className="notification-description">
              {dataType === 'interpolated' ? 
                'Smart analysis active - calculated specifically for your coordinates using data from multiple monitoring stations' :
                'You are viewing data from our closest monitoring station. Enable precise location for personalized readings.'
              }
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="action-buttons">
          {(dataSource === 'sample' || locationStatus === 'failed') && (
            <button onClick={handleEnableLocation} className="action-btn primary">
              📍 Enable Location for Live Data
            </button>
          )}
          <button onClick={handleRefreshData} className="action-btn secondary">
            🔄 Refresh Data
          </button>
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            🌬️ {dataType === 'interpolated' ? 'Your Location Air Quality' : 'Current Air Quality Conditions'}
            {dataType === 'interpolated' && <small> (Calculated for your coordinates)</small>}
            {dataType === 'station' && <small> (From nearest station)</small>}
          </h2>
          <div className="metrics-grid">
            {MetricCards}
          </div>
        </div>

        {/* Enhanced Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map with better styling */}
          <div id="map-section" className="dashboard-card map-card">
            <div className="card-header">
              <h3 className="card-title">🗺️ Live Sensor Network</h3>
              <div className="map-controls">
                {userLocationName && (
                  <div className="location-display">
                    📍 {userLocationName.display_name}
                  </div>
                )}
                <div className="map-legend">
                  <span className="legend-item">
                    <span className="legend-dot user-location"></span>
                    Your Location
                  </span>
                  <span className="legend-item">
                    <span className="legend-dot sensor-station"></span>
                    Sensor Stations
                  </span>
                </div>
              </div>
            </div>
            <div className="map-container">
              <React.Suspense fallback={
                <div className="map-fallback">
                  <div className="map-placeholder">
                    <div className="map-icon">🗺️</div>
                    <p>Loading interactive map...</p>
                    <div className="loading-spinner"></div>
                  </div>
                </div>
              }>
                <LazyMap 
                  userLocation={userLocation} 
                  stations={dashboardData?.stations}
                  currentAQI={currentAQI}
                  dataType={dataType}
                  nearestStation={nearestStation}
                />
              </React.Suspense>
            </div>
            <div className="map-info">
              <div className="map-status">
                <span className="status-indicator">
                  🔴 LIVE • {Object.keys(dashboardData?.stations || {}).length} stations active
                </span>
                {dataType === 'interpolated' && (
                  <span className="smart-badge">🎯 Smart Analysis Active</span>
                )}
              </div>
              <div className="map-description">
                Blue marker shows your location • Tower markers show monitoring stations • 
                Click any marker for detailed readings
              </div>
            </div>
          </div>

          {/* Enhanced AQI Forecast */}
          <div className="dashboard-card forecast-card">
            <div className="card-header">
              <h3 className="card-title">📊 4-Day Air Quality Forecast</h3>
              <div className="forecast-info">
                <div className="forecast-source">
                  Data source: {nearestStation?.name || 'Station 1 (ASIET Campus)'}
                </div>
                <div className="forecast-update">
                  Updated: {lastUpdateTime.toLocaleTimeString()}
                </div>
              </div>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                  title={`View ${param.toUpperCase()} forecast`}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <div className="chart-section">
                <React.Suspense fallback={
                  <div className="chart-fallback">
                    <div className="chart-placeholder">
                      <div className="chart-icon">📊</div>
                      <p>Loading forecast chart...</p>
                      <div className="loading-spinner"></div>
                    </div>
                  </div>
                }>
                  <LazyChart 
                    forecastData={nearestStation ? dashboardData?.forecasts?.[nearestStation.id] : null}
                    selectedParameter={selectedParameter}
                  />
                </React.Suspense>
              </div>
              
              <div className="forecast-table-section">
                <table className="forecast-table">
                  <thead>
                    <tr>
                      <th>📅 Day</th>
                      <th>📈 Max {selectedParameter.toUpperCase()}</th>
                      <th>📊 Unit</th>
                    </tr>
                  </thead>
                  <tbody>
                    {(nearestStation && dashboardData?.forecasts?.[nearestStation.id] || []).map((item, index) => (
                      <tr key={index}>
                        <td className="day-cell">{item.day}</td>
                        <td className="value-cell">{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
                        <td className="unit-cell">{selectedParameter === 'co' ? 'mg/m³' : 'µg/m³'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Health Status Section */}
        {healthData && (
          <div className="health-section">
            <div className="health-content">
              <div className="health-chart-section">
                <div className="card-header">
                  <h3 className="card-title">❤️ Health Status Monitor</h3>
                </div>
                <div className="health-display">
                  <div className="health-score-circle">
                    <div className="health-score-value">{healthData.score}</div>
                    <div className="health-score-label">Health Score</div>
                  </div>
                  <div className="health-details">
                    <div className={`risk-level ${healthData.risk_level.toLowerCase()}`}>
                      {healthData.risk_level} Risk
                    </div>
                    <div className="health-recommendations">
                      {healthData.recommendations?.map((rec, index) => (
                        <div key={index} className="recommendation">• {rec}</div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
              <div className="health-advisory">
                <h3>
                  <i className="fas fa-shield-alt"></i>
                  Health Advisory
                </h3>
                <p>
                  People with asthma, heart disease, older adults, and young children are more 
                  susceptible to air pollution. Please follow health advisories closely and seek 
                  medical attention if you experience any adverse symptoms.
                </p>
                {healthData.risk_level === 'High' && (
                  <div className="emergency-contact">
                    <strong>Emergency Contact:</strong> Kerala Pollution Control Board – 0471-2418566
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">🌤️ Weather Conditions</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">🌡️</div>
              <div className="weather-value">{formatValue(currentValues.temp || 28, 'temp')}°C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">💧</div>
              <div className="weather-value">{formatValue(currentValues.hum || 65, 'hum')}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">📏</div>
              <div className="weather-value">{formatValue(currentValues.pre || 1013, 'pre')} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
            </div>
          </div>
        </div>

        {/* Data Source Indicator */}
        <div className="data-source-section">
          <h3>⚡ System Status</h3>
          <div className="status-grid">
            <div className="status-item">
              <div className="status-icon">🚀</div>
              <div className="status-content">
                <div className="status-title">Performance</div>
                <div className="status-value">Optimized</div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon">📍</div>
              <div className="status-content">
                <div className="status-title">Location</div>
                <div className="status-value">
                  {locationStatus === 'gps_detected' ? 'GPS Active' : 
                   locationStatus === 'ip_detected' ? 'IP Located' : 
                   locationStatus === 'detecting' ? 'Detecting...' : 'Default'}
                </div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon">📊</div>
              <div className="status-content">
                <div className="status-title">Data Type</div>
                <div className="status-value">
                  {dataType === 'interpolated' ? 'Smart Analysis' : 'Nearest Station'}
                </div>
              </div>
            </div>
            <div className="status-item">
              <div className="status-icon">🔄</div>
              <div className="status-content">
                <div className="status-title">Data Source</div>
                <div className="status-value">
                  {dataSource === 'live' ? 'Live Data' :
                   dataSource === 'sample' ? 'Sample Data' : 
                   dataSource === 'cached' ? 'Cached Data' : 'Loading...'}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Data Disclaimer */}
        <div className="disclaimer">
          <h3>
            <i className="fas fa-info-circle"></i>
            📋 Data Methodology & Government Compliance
          </h3>
          <p>
            <strong>Advanced Air Quality Monitoring System:</strong> This government-approved platform 
            monitors air quality across Kerala and provides accurate updates for your location using 
            data from nearby monitoring stations.
          </p>

          <div className="disclaimer-highlight">
            <p><strong>🏛️ Kerala Government Air Quality Network:</strong></p>
            <ul>
              <li><strong>Real-time monitoring:</strong> Government-approved, CPCB-certified equipment providing calibrated readings</li>
              <li><strong>Quality assurance:</strong> Automated data validation, outlier detection, and accuracy verification</li>
              <li><strong>Government compliance:</strong> Full adherence to CPCB and Kerala State Pollution Control Board standards</li>
              <li><strong>Smart interpolation:</strong> Advanced algorithms provide personalized air quality estimates for your location</li>
            </ul>
          </div>

          <p>
            <strong>⚠️ Information & Usage:</strong> Air quality readings are updated regularly to help you 
            stay informed. Please note that air conditions can change depending on traffic, weather, or 
            nearby activities. For any serious health concerns, always rely on official sources and medical advice.
          </p>

          <p>
            <strong>🔄 System Status:</strong> The system is working smoothly and updates regularly to show 
            the latest air quality near you. <strong>Status:</strong> 
            <span style={{ color: 'var(--success-color)', fontWeight: '600' }}> OPERATIONAL</span> | 
            Last update: <span>{lastUpdateTime.toLocaleTimeString()}</span>
          </p>

          <p>
            <strong>🏥 Health & Safety Tips:</strong> Use this information to plan your day better. If you 
            have breathing problems or heart conditions, follow health guidelines and check with your doctor 
            when needed. Emergency contact: Kerala Pollution Control Board – 0471-2418566
          </p>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h3>⚡ Quick Actions</h3>
          <div className="quick-actions-grid">
            <button 
              onClick={handleEnableLocation}
              className="quick-action-btn"
              disabled={locationStatus === 'gps_detected'}
            >
              <div className="quick-action-icon">📍</div>
              <div className="quick-action-text">
                <div className="quick-action-title">Enable Location</div>
                <div className="quick-action-desc">Get personalized air quality data</div>
              </div>
            </button>
            
            <button 
              onClick={handleRefreshData}
              className="quick-action-btn"
            >
              <div className="quick-action-icon">🔄</div>
              <div className="quick-action-text">
                <div className="quick-action-title">Refresh Data</div>
                <div className="quick-action-desc">Get latest air quality readings</div>
              </div>
            </button>
            
            <button 
              onClick={() => document.getElementById('map-section')?.scrollIntoView({ behavior: 'smooth' })}
              className="quick-action-btn"
            >
              <div className="quick-action-icon">🗺️</div>
              <div className="quick-action-text">
                <div className="quick-action-title">View Map</div>
                <div className="quick-action-desc">See sensor locations and coverage</div>
              </div>
            </button>
            
            <button 
              onClick={() => window.open('/health-assessment', '_self')}
              className="quick-action-btn"
            >
              <div className="quick-action-icon">📋</div>
              <div className="quick-action-text">
                <div className="quick-action-title">Health Assessment</div>
                <div className="quick-action-desc">Update your health profile</div>
              </div>
            </button>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>AirAware Kerala</h4>
              <p>Smart Air Quality Monitoring System</p>
              <p>Real-time data • Personalized insights • Health-focused</p>
              <div className="social-links">
                <a href="#" className="social-link">📘</a>
                <a href="#" className="social-link">🐦</a>
                <a href="#" className="social-link">💼</a>
                <a href="#" className="social-link">📷</a>
              </div>
            </div>
            <div className="footer-section">
              <h4>Quick Links</h4>
              <ul>
                <li><a href="/">🏠 Home</a></li>
                <li><a href="/forecast">📈 Forecasting</a></li>
                <li><a href="/health-report">📄 Health Report</a></li>
                <li><a href="/add-family">👥 Add Family</a></li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Data Sources</h4>
              <ul>
                <li>Station 1 (ASIET Campus)</li>
                <li>Station 2 (Mattoor Junction)</li>
                <li>Advanced interpolation algorithms</li>
                <li>Weather integration</li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Contact Information</h4>
              <p>
                Vidya Bharathi Nagar, Mattoor<br/>
                Kalady 683574, Ernakulam<br/>
                Kerala, India
              </p>
              <p>
                <strong>Email:</strong> aiiot@adishankara.ac.in<br/>
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala - Smart Air Quality Monitoring System</p>
            <p>Developed for real-world deployment • Production-ready • User-friendly</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
