import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

function Dashboard() {
  // ===== LOCATION TRACKING STATE =====
  const [username, setUsername] = useState(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    return user.name || 'User';
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleString());
  const [systemStatus, setSystemStatus] = useState('OPERATIONAL');

  // ===== REAL LOCATION TRACKING =====
  const [userLocation, setUserLocation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('detecting'); // detecting, found, error, denied
  const [nearestStation, setNearestStation] = useState(null);
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const mapRef = useRef(null);
  const chartRef = useRef(null);
  const navigate = useNavigate();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // ===== LOCATION DETECTION FUNCTION =====
  const getUserLocation = useCallback(async () => {
    console.log('📍 Starting location detection...');
    setLocationStatus('detecting');

    if (!navigator.geolocation) {
      console.warn('⚠️ Geolocation not supported');
      setLocationStatus('error');
      useDefaultLocation();
      return;
    }

    const options = {
      enableHighAccuracy: true,
      timeout: 15000,
      maximumAge: 300000 // 5 minutes
    };

    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, options);
      });

      const locationData = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: Date.now(),
        source: 'gps'
      };

      console.log('✅ GPS location obtained:', locationData);
      setUserLocation(locationData);
      setLocationStatus('found');

      // Save location to backend
      await saveLocationToBackend(locationData);

      // Fetch dashboard data with real location
      await fetchDashboardWithLocation(locationData);

    } catch (error) {
      console.error('❌ Geolocation error:', error);
      
      if (error.code === 1) { // PERMISSION_DENIED
        setLocationStatus('denied');
      } else {
        setLocationStatus('error');
      }
      
      useDefaultLocation();
    }
  }, [username]);

  // ===== SAVE LOCATION TO BACKEND =====
  const saveLocationToBackend = useCallback(async (locationData) => {
    try {
      console.log('💾 Saving location to backend...');
      
      const response = await fetch(`${API_BASE_URL}/api/save-user-location/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: username,
          latitude: locationData.lat,
          longitude: locationData.lng,
          accuracy: locationData.accuracy,
          source: locationData.source
        })
      });

      const result = await response.json();
      
      if (response.ok) {
        console.log('✅ Location saved to backend');
      } else {
        console.warn('⚠️ Failed to save location:', result.message);
      }
    } catch (error) {
      console.error('❌ Error saving location:', error);
    }
  }, [username, API_BASE_URL]);

  // ===== FETCH DASHBOARD DATA WITH LOCATION =====
  const fetchDashboardWithLocation = useCallback(async (locationData) => {
    try {
      console.log('🔄 Fetching dashboard data with location...');
      setLoading(true);
      
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      
      if (locationData) {
        url.searchParams.append('lat', locationData.lat);
        url.searchParams.append('lng', locationData.lng);
      }

      console.log('📡 Calling backend API:', url.toString());

      const response = await fetch(url, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('✅ Dashboard data received from backend');
      
      setDashboardData(data);
      setNearestStation({
        name: data.nearest_sensor_info?.current_station_name,
        distance: data.nearest_sensor_info?.distance_to_nearest,
        aqi: data.nearest_sensor_info?.current_aqi || data.interpolated_aqi
      });
      
      setError(null);
      setLoading(false);

    } catch (error) {
      console.error('❌ Error fetching dashboard data:', error);
      setError(error.message);
      setLoading(false);
      
      // Use fallback data
      useFallbackData();
    }
  }, [username, API_BASE_URL]);

  // ===== USE DEFAULT LOCATION (FALLBACK) =====
  const useDefaultLocation = useCallback(async () => {
    console.log('📍 Using default location (ASIET Campus)');
    
    const defaultLocation = {
      lat: 10.178385739668958,
      lng: 76.43052237497399,
      accuracy: null,
      timestamp: Date.now(),
      source: 'default'
    };

    setUserLocation(defaultLocation);
    await fetchDashboardWithLocation(defaultLocation);
  }, [fetchDashboardWithLocation]);

  // ===== FALLBACK DATA =====
  const useFallbackData = useCallback(() => {
    console.log('📊 Using fallback dashboard data');
    
    const fallbackData = {
      interpolated_aqi: 85,
      nearest_sensor_info: {
        current_aqi: 85,
        current_station_name: 'ASIET Campus Station',
        distance_to_nearest: 0.5,
        current_sensor_data: {
          pm25: 25.5, pm10: 42.3, so2: 15.8, no2: 37.2,
          co: 1.2, o3: 45.6, nh3: 12.4, temp: 28, hum: 65, pre: 1013
        }
      },
      forecast_data: {
        nearest_station: [
          { day: 'Today', pm25_max: 30.2, pm10_max: 37.0, so2_max: 2.4, no2_max: 40.1, co_max: 1.1, o3_max: 3.7, nh3_max: 92.7 },
          { day: 'Tomorrow', pm25_max: 24.1, pm10_max: 34.2, so2_max: 2.4, no2_max: 45.2, co_max: 1.2, o3_max: 3.2, nh3_max: 100.0 },
          { day: 'Day 3', pm25_max: 24.3, pm10_max: 30.3, so2_max: 2.2, no2_max: 42.1, co_max: 1.2, o3_max: 3.5, nh3_max: 100.0 },
          { day: 'Day 4', pm25_max: 19.7, pm10_max: 23.4, so2_max: 2.3, no2_max: 38.5, co_max: 1.2, o3_max: 3.7, nh3_max: 100.0 }
        ]
      },
      sensor_locations: {
        'lora-v1': { lat: 10.178322301561957, lng: 76.43089144291672, name: 'Station 1 (ASIET Campus)' },
        'loradev2': { lat: 10.17095090340159, lng: 76.42962876824544, name: 'Station 2 (Mattoor Junction)' }
      },
      idw_status: 'active',
      timestamp: new Date().toISOString()
    };

    setDashboardData(fallbackData);
    setNearestStation({
      name: 'ASIET Campus Station',
      distance: 0.5,
      aqi: 85
    });
    setLoading(false);
  }, []);

  // ===== INITIALIZATION =====
  useEffect(() => {
    console.log('🎯 Dashboard mounted - starting location tracking');
    
    // Start location detection immediately
    getUserLocation();

    // Update current time
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date().toLocaleString());
    }, 30000);

    // Initialize libraries after DOM is ready
    setTimeout(() => {
      initializeMap();
      initializeChart();
    }, 1000);

    return () => {
      clearInterval(timeInterval);
      if (mapRef.current) {
        try { mapRef.current.remove(); } catch (e) {}
      }
      if (chartRef.current) {
        try { chartRef.current.destroy(); } catch (e) {}
      }
    };
  }, [getUserLocation]);

  // ===== LOCATION STATUS COMPONENT =====
  const LocationStatus = () => {
    const getLocationDisplay = () => {
      switch (locationStatus) {
        case 'detecting':
          return (
            <span style={{ color: '#f59e0b' }}>
              📍 Detecting your location...
            </span>
          );
        case 'found':
          return (
            <span style={{ color: '#10b981' }}>
              📍 GPS Location (±{Math.round(userLocation?.accuracy || 0)}m)
              {nearestStation && ` → ${nearestStation.name} (${nearestStation.distance?.toFixed(1)}km)`}
            </span>
          );
        case 'denied':
          return (
            <span style={{ color: '#ef4444' }}>
              📍 Location access denied - Using default area
              <button 
                onClick={getUserLocation}
                style={{ marginLeft: '8px', background: 'none', border: '1px solid #ef4444', color: '#ef4444', padding: '2px 8px', borderRadius: '4px', cursor: 'pointer' }}
              >
                Retry
              </button>
            </span>
          );
        case 'error':
          return (
            <span style={{ color: '#ef4444' }}>
              📍 Location error - Using default area
              <button 
                onClick={getUserLocation}
                style={{ marginLeft: '8px', background: 'none', border: '1px solid #ef4444', color: '#ef4444', padding: '2px 8px', borderRadius: '4px', cursor: 'pointer' }}
              >
                Retry
              </button>
            </span>
          );
        default:
          return <span>📍 Location status unknown</span>;
      }
    };

    return (
      <div className="location-info">
        {getLocationDisplay()}
      </div>
    );
  };

  // ===== MAP INITIALIZATION =====
  const initializeMap = useCallback(() => {
    if (!window.L || !dashboardData || !userLocation) {
      return;
    }

    const mapContainer = document.getElementById('dashboard-map');
    if (!mapContainer) return;

    try {
      console.log('🗺️ Initializing map with real location...');

      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }

      mapContainer.innerHTML = '';
      mapContainer.style.height = '300px';

      const map = window.L.map('dashboard-map', {
        center: [userLocation.lat, userLocation.lng],
        zoom: 13
      });

      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap'
      }).addTo(map);

      // Add user location marker
      const userIcon = window.L.divIcon({
        html: `<div style="background: #3b82f6; color: white; border-radius: 50%; width: 24px; height: 24px; display: flex; align-items: center; justify-content: center; border: 3px solid white; box-shadow: 0 2px 8px rgba(0,0,0,0.3); font-size: 12px;">📍</div>`,
        iconSize: [30, 30],
        iconAnchor: [15, 15]
      });

      const userMarker = window.L.marker([userLocation.lat, userLocation.lng], { icon: userIcon })
        .addTo(map)
        .bindPopup(`
          <div style="text-align: center; font-size: 12px;">
            <strong>Your Location</strong><br/>
            ${locationStatus === 'found' ? 
              `GPS: ±${Math.round(userLocation.accuracy || 0)}m` : 
              'Default Location'
            }<br/>
            AQI: <strong>${dashboardData.interpolated_aqi || dashboardData.nearest_sensor_info?.current_aqi || 'N/A'}</strong>
          </div>
        `)
        .openPopup();

      // Add station markers
      Object.entries(dashboardData.sensor_locations || {}).forEach(([id, location]) => {
        const stationData = dashboardData.all_stations_data?.[id];
        const aqi = stationData?.highest_sub_index || dashboardData.nearest_sensor_info?.current_aqi || 80;
        
        const stationIcon = window.L.divIcon({
          html: `<div style="background: #10b981; color: white; border-radius: 8px; padding: 4px 8px; font-size: 11px; font-weight: bold; box-shadow: 0 2px 6px rgba(0,0,0,0.2);">📡 ${Math.round(aqi)}</div>`,
          iconSize: [60, 35],
          iconAnchor: [30, 17]
        });

        window.L.marker([location.lat, location.lng], { icon: stationIcon })
          .addTo(map)
          .bindPopup(`
            <div style="text-align: center; font-size: 12px;">
              <strong>${location.name}</strong><br/>
              AQI: <strong>${Math.round(aqi)}</strong><br/>
              Distance: ${calculateDistance(userLocation.lat, userLocation.lng, location.lat, location.lng).toFixed(1)}km
            </div>
          `);
      });

      mapRef.current = map;
      console.log('✅ Map initialized with user location');

    } catch (error) {
      console.error('❌ Map initialization failed:', error);
    }
  }, [dashboardData, userLocation, locationStatus]);

  // ===== CHART INITIALIZATION =====
  const initializeChart = useCallback(() => {
    if (!window.Chart || !dashboardData) {
      return;
    }

    const canvas = document.getElementById('forecast-chart');
    if (!canvas) return;

    try {
      if (chartRef.current) {
        chartRef.current.destroy();
      }

      const forecastData = dashboardData.forecast_data?.nearest_station || [];
      const data = forecastData.map(item => item[`${selectedParameter}_max`] || 0);
      const labels = forecastData.map(item => item.day);

      const ctx = canvas.getContext('2d');
      chartRef.current = new window.Chart(ctx, {
        type: 'line',
        data: {
          labels: labels,
          datasets: [{
            label: selectedParameter.toUpperCase(),
            data: data,
            borderColor: '#3b82f6',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            tension: 0.4,
            fill: true
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: { legend: { display: true } },
          scales: { y: { beginAtZero: true } }
        }
      });

      console.log('✅ Chart initialized');
    } catch (error) {
      console.error('❌ Chart initialization failed:', error);
    }
  }, [selectedParameter, dashboardData]);

  // Re-initialize map when location or data changes
  useEffect(() => {
    if (dashboardData && userLocation) {
      setTimeout(initializeMap, 500);
    }
  }, [dashboardData, userLocation, initializeMap]);

  // Re-initialize chart when parameter changes
  useEffect(() => {
    if (dashboardData) {
      setTimeout(initializeChart, 100);
    }
  }, [dashboardData, selectedParameter, initializeChart]);

  // ===== UTILITY FUNCTIONS =====
  const calculateDistance = (lat1, lng1, lat2, lng2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getAQIStatus = (aqi) => {
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  };

  const getMetricIcon = (metric) => {
    const icons = {
      pm25: '🌱', pm10: '🍃', so2: '🏭', no2: '💨',
      co: '☁️', o3: '☀️', nh3: '⚗️'
    };
    return icons[metric] || '📊';
  };

  const formatValue = (value, metric) => {
    if (metric === 'co') return parseFloat(value).toFixed(1);
    return Math.round(parseFloat(value));
  };

  const handleParameterChange = (param) => {
    setSelectedParameter(param);
  };

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const scrollToMap = () => {
    document.getElementById('map-section')?.scrollIntoView({ behavior: 'smooth' });
  };

  // ===== LOADING STATE =====
  if (loading) {
    return (
      <div className="dashboard-page">
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white',
          padding: '2rem'
        }}>
          <div style={{
            width: '60px',
            height: '60px',
            border: '4px solid rgba(255, 255, 255, 0.3)',
            borderTop: '4px solid white',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            marginBottom: '2rem'
          }}></div>
          <h2>Loading Dashboard...</h2>
          <p>
            {locationStatus === 'detecting' ? '📍 Getting your location...' :
             locationStatus === 'found' ? '📡 Fetching air quality data...' :
             '📊 Loading dashboard...'}
          </p>
          <LocationStatus />
        </div>
      </div>
    );
  }

  // ===== ERROR STATE =====
  if (error && !dashboardData) {
    return (
      <div className="dashboard-page">
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          padding: '2rem'
        }}>
          <div style={{
            background: '#fef2f2',
            border: '2px solid #ef4444',
            borderRadius: '16px',
            padding: '2rem',
            textAlign: 'center',
            maxWidth: '500px'
          }}>
            <h2 style={{ color: '#dc2626', marginBottom: '1rem' }}>⚠️ Dashboard Error</h2>
            <p style={{ marginBottom: '1.5rem' }}>{error}</p>
            <LocationStatus />
            <div style={{ marginTop: '1rem' }}>
              <button 
                onClick={getUserLocation}
                style={{
                  background: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  marginRight: '10px'
                }}
              >
                Retry
              </button>
              <button 
                onClick={() => window.location.reload()}
                style={{
                  background: '#6b7280',
                  color: 'white',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '8px',
                  cursor: 'pointer'
                }}
              >
                Refresh Page
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // ===== MAIN DASHBOARD RENDER =====
  if (!dashboardData) {
    return <div>Loading...</div>;
  }

  const currentValues = dashboardData.idw_interpolated_values || 
                       dashboardData.nearest_sensor_info?.current_sensor_data || 
                       dashboardData.nearest_sensor_info || {};
  
  const currentAQI = dashboardData.interpolated_aqi || 
                    dashboardData.nearest_sensor_info?.current_aqi || 85;
  
  const aqiStatus = getAQIStatus(currentAQI);

  return (
    <div className="dashboard-page">
      {/* Real-time Status */}
      <div className="realtime-status">
        🔴 LIVE • Updates every 30s • {locationStatus === 'found' ? 'GPS Tracking' : 'Default Location'} • Last updated: {currentTime}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" style={{ width: '40px', height: '40px', marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>☰</div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">🏠 Home</a></li>
            <li><a href="/forecast" className="nav-link">📈 Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">📋 Health Update</a></li>
            <li><a href="/health-report" className="nav-link">📄 Health Report</a></li>
            <li><a href="/add-family" className="nav-link">👥 Add Family</a></li>
            <li className="user-info">👤 <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">🚪 Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        ℹ️ <span>Your Location AQI: {Math.round(currentAQI)} - {aqiStatus.status}
        {nearestStation && ` • Nearest: ${nearestStation.name} (${nearestStation.distance?.toFixed(1)}km)`}</span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">👤</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                <LocationStatus />
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(currentAQI)}
              </div>
              <div className="aqi-status">
                {dashboardData.idw_status === 'active' ? 'Smart Analysis AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                {nearestStation?.name || 'Your Location'}
              </div>
              <div className="last-updated">
                Last updated: {new Date(dashboardData.timestamp).toLocaleTimeString()}
              </div>
            </div>
          </div>
        </div>

        {/* Sensor Notification */}
        <div className="sensor-notification">
          📡 <strong>Nearest Sensor: {nearestStation?.name || 'ASIET Campus Station'}</strong> - 
          {locationStatus === 'found' ? 
            `You are ${nearestStation?.distance?.toFixed(1) || '0.5'}km from the monitoring station.` :
            'You are viewing data from our closest monitoring station.'
          }
          <a href="#map-section" onClick={scrollToMap}> Click on the map section below</a>
          to see your exact location and all sensor stations.
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            🌬️ Current Air Quality at Your Location
            {dashboardData.idw_status === 'active' && <small>(Smart Analysis)</small>}
          </h2>
          <div className="metrics-grid">
            {Object.entries(currentValues).slice(0, 7).map(([key, value]) => (
              <div key={key} className="metric-card">
                <div className="metric-icon">{getMetricIcon(key)}</div>
                <div className="metric-value">{formatValue(value, key)}</div>
                <div className="metric-label">{key.toUpperCase()}</div>
                <div className="metric-unit">{key === 'co' ? 'mg/m³' : 'µg/m³'}</div>
                {dashboardData.idw_status === 'active' && (
                  <div className="metric-badge" style={{ background: '#10b981', color: 'white', fontSize: '0.7rem', padding: '2px 6px', borderRadius: '8px', position: 'absolute', top: '8px', right: '8px' }}>
                    🎯 Smart
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div id="map-section" className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">🗺️ Interactive Sensor Map</h3>
            </div>
            <div className="map-container">
              <div id="dashboard-map" style={{ height: '300px', width: '100%' }}></div>
            </div>
            <div style={{ marginTop: '12px', textAlign: 'center', fontSize: '0.875rem', color: '#6b7280' }}>
              📍 Blue marker: Your location • Tower markers: Monitoring stations
              {locationStatus !== 'found' && (
                <span style={{ color: '#f59e0b' }}> • Enable location for GPS accuracy</span>
              )}
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">📊 4-Day Air Quality Forecast</h3>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <div className="chart-container" style={{ height: '200px', marginTop: '15px' }}>
                <canvas id="forecast-chart"></canvas>
              </div>
              
              <div className="forecast-table-container">
                <table className="forecast-table">
                  <thead>
                    <tr>
                      <th>📅 Day</th>
                      <th>📈 Max Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    {(dashboardData.forecast_data?.nearest_station || []).map((item, index) => (
                      <tr key={index}>
                        <td>{item.day}</td>
                        <td>{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Health Status Section */}
        {dashboardData.health_data && (
          <div className="health-section">
            <div className="health-content">
              <div>
                <div className="card-header">
                  <h3 className="card-title">❤️ Personalized Health Status</h3>
                </div>
                <div className="health-score-display">
                  <div className="health-score-circle" style={{ borderColor: dashboardData.health_data.risk_color }}>
                    <div className="health-score-value" style={{ color: dashboardData.health_data.risk_color }}>
                      {dashboardData.health_data.score}
                    </div>
                    <div className="health-risk-level" style={{ color: dashboardData.health_data.risk_color }}>
                      {dashboardData.health_data.risk_level}
                    </div>
                  </div>
                </div>
              </div>
              <div className="health-alert">
                <h3>🛡️ Smart Health Advisory</h3>
                <p>
                  Based on your health profile and current air quality at your location (AQI: {Math.round(currentAQI)}), 
                  your risk level is <strong style={{ color: dashboardData.health_data.risk_color }}>
                  {dashboardData.health_data.risk_level}</strong>.
                  {dashboardData.health_data.is_high_risk 
                    ? ' We recommend staying indoors and following safety guidelines.' 
                    : ' You can enjoy normal outdoor activities with basic precautions.'
                  }
                </p>
                
                {dashboardData.health_data.recommendations && dashboardData.health_data.recommendations.length > 0 && (
                  <div className="health-recommendations">
                    <h4>💡 Smart Recommendations:</h4>
                    <ul>
                      {dashboardData.health_data.recommendations.slice(0, 3).map((rec, index) => (
                        <li key={index}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">🌤️ Weather Conditions (Live Data)</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">🌡️</div>
              <div className="weather-value">{currentValues.temp || '28'}°C</div>
              <div className="weather-label">Temperature</div>
              <div className="weather-source">
                <small>Source: {nearestStation?.name || 'Default Station'}</small>
              </div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">💧</div>
              <div className="weather-value">{currentValues.hum || '65'}%</div>
              <div className="weather-label">Humidity</div>
              <div className="weather-source">
                <small>Source: {nearestStation?.name || 'Default Station'}</small>
              </div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">📏</div>
              <div className="weather-value">{currentValues.pre || '1013'} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
              <div className="weather-source">
                <small>Source: {nearestStation?.name || 'Default Station'}</small>
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Data Disclaimer */}
        <div className="disclaimer">
          <h3>ℹ️ 📋 Smart Data Processing & Location Tracking</h3>
          <p>
            <strong>🎯 Location-Specific Air Quality:</strong> {dashboardData.idw_status === 'active' ? 
              'This system uses advanced algorithms to calculate air quality values specific to your exact GPS location using data from multiple monitoring stations.' :
              'This system shows data from your nearest monitoring station for the most relevant air quality information.'
            }
          </p>

          <div className="disclaimer-highlight">
            <p><strong>{dashboardData.idw_status === 'active' ? '🧠 Smart Analysis Method:' : '📡 Nearest Station Method:'}</strong></p>
            <ul>
              {dashboardData.idw_status === 'active' ? (
                <>
                  <li><strong>GPS Integration:</strong> Uses your precise location coordinates for calculations</li>
                  <li><strong>Multi-station intelligence:</strong> Combines readings from multiple monitoring stations</li>
                  <li><strong>Distance-based weighting:</strong> Stations closer to you have more influence</li>
                  <li><strong>Real-time updates:</strong> Fresh data every 30 seconds with location tracking</li>
                </>
              ) : (
                <>
                  <li><strong>Closest station finder:</strong> Automatically selects nearest monitoring station</li>
                  <li><strong>Distance calculation:</strong> Shows exact distance to selected station</li>
                  <li><strong>Live updates:</strong> Fresh data every 30 seconds</li>
                  <li><strong>Location fallback:</strong> Uses default location if GPS unavailable</li>
                </>
              )}
              <li><strong>Government compliance:</strong> All stations follow official CPCB protocols</li>
            </ul>
          </div>

          <p>
            <strong>📍 About Your Location:</strong> 
            {locationStatus === 'found' ? 
              `Your GPS location is being tracked (±${Math.round(userLocation?.accuracy || 0)}m accuracy). Air quality readings are calculated specifically for where you are!` :
              'Location services are not enabled. Using default area readings. Enable location for personalized air quality data.'
            }
          </p>

          <p>
            <strong>🟢 System Status:</strong> 
            <span style={{ color: '#10b981', fontWeight: 600 }}> LIVE & OPERATIONAL</span> | 
            {dashboardData.idw_status === 'active' ? 'Smart Analysis On' : 'Station Mode'} | 
            Location: {locationStatus === 'found' ? 
              `GPS Active (±${Math.round(userLocation?.accuracy || 0)}m)` : 
              'Default Area'
            } | 
            Nearest Station: {nearestStation?.name || 'ASIET Campus'} ({nearestStation?.distance?.toFixed(1) || '0.5'}km)
          </p>

          <p>
            <strong>🏥 Health & Safety:</strong> Air quality values are personalized to your location. 
            For health concerns, consult medical professionals. 
            Emergency: Kerala Pollution Control Board – 0471-2418566
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirAware Kerala</h3>
              <p className="footer-text">
                Advanced air quality monitoring with real-time GPS location tracking, 
                providing accurate readings for better health decisions across Kerala.
              </p>
              <div className="social-links">
                <a href="#" className="social-link">📘</a>
                <a href="#" className="social-link">🐦</a>
                <a href="#" className="social-link">💼</a>
                <a href="#" className="social-link">📸</a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <a href="/" className="footer-link">Home</a>
              <a href="/map" className="footer-link">Live Map</a>
              <a href="/forecast" className="footer-link">Forecasting</a>
              <a href="/health-report" className="footer-link">Health Report</a>
            </div>

            <div className="footer-section">
              <h3>Services</h3>
              <a href="/forecast" className="footer-link">AQI Forecasting</a>
              <a href="/health-assessment" className="footer-link">Health Assessment</a>
              <a href="/map" className="footer-link">Live AQI Map</a>
              <a href="#" className="footer-link">Smart Analytics</a>
            </div>

            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Vidya Bharathi Nagar, Mattoor<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
