import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { useAuth } from '../App';
import './css/MapPage.css';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const getAQIColor = (aqi) => { /* ... color function ... */ };
const getAQIStatus = (aqi) => { /* ... status function ... */ };
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const MapPage = () => {
    const [mapInstance, setMapInstance] = useState(null);
    const [stations, setStations] = useState({});
    const [selectedStationId, setSelectedStationId] = useState('lora-v1');
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isPanelOpen, setIsPanelOpen] = useState(true);
    const [forecastData, setForecastData] = useState([]);
    const [isForecastLoading, setIsForecastLoading] = useState(false);
    const [selectedParameter, setSelectedParameter] = useState('pm25_max');
    const [forecastUpdatedAt, setForecastUpdatedAt] = useState(null);
    
    // State for User Location & Interpolation
    const [userLocation, setUserLocation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [nearestStation, setNearestStation] = useState(null);

    const mapRef = useRef(null);
    const markersRef = useRef({});
    const navigate = useNavigate();
    const { user } = useAuth();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    useEffect(() => {
        let map;
        if (mapRef.current) {
            map = window.L.map(mapRef.current).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
            setMapInstance(map);
        }
        return () => { if (map) map.remove(); };
    }, []);

    useEffect(() => {
        if (user && navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => setUserLocation({ lat: position.coords.latitude, lng: position.coords.longitude }),
                (err) => console.warn(`Geolocation error: ${err.message}`)
            );
        }
    }, [user]);

    const fetchRealtimeData = useCallback(async () => {
        setIsLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
            const data = await response.json();
            setStations(data.stations);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    }, [API_BASE_URL]);

    useEffect(() => {
        fetchRealtimeData();
        const interval = setInterval(fetchRealtimeData, 30000);
        return () => clearInterval(interval);
    }, [fetchRealtimeData]);

    useEffect(() => {
        if (userLocation && Object.keys(stations).length > 0) {
            let weightedSum = 0, weightSum = 0, nearestDist = Infinity, nearestId = null;
            Object.entries(stations).forEach(([id, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                if (dist < nearestDist) {
                    nearestDist = dist;
                    nearestId = id;
                }
                const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
                weightedSum += station.highest_sub_index * weight;
                weightSum += weight;
            });
            setNearestStation({ id: nearestId, distance: nearestDist });
            if (weightSum > 0) setInterpolatedData({ aqi: Math.round(weightedSum / weightSum) });
        }
    }, [userLocation, stations]);

    useEffect(() => {
        if (!selectedStationId) return;
        const fetchForecast = async () => { /* ... fetch forecast logic ... */ };
        fetchForecast();
    }, [selectedStationId, API_BASE_URL]);
    
    useEffect(() => {
        if (!mapInstance || Object.keys(stations).length === 0) return;
        Object.values(markersRef.current).forEach(marker => marker.remove());
        markersRef.current = {};
        Object.entries(stations).forEach(([id, station]) => {
            const { lat, lng } = station.station_info;
            const aqi = station.highest_sub_index;
            const markerOptions = { radius: selectedStationId === id ? 12 : 8, fillColor: getAQIColor(aqi), color: selectedStationId === id ? '#ffffff' : '#1e293b', weight: selectedStationId === id ? 3 : 1.5, opacity: 1, fillOpacity: 0.85 };
            const marker = window.L.circleMarker([lat, lng], markerOptions).addTo(mapInstance);
            marker.on('click', () => {
                setSelectedStationId(id);
                setIsPanelOpen(true);
                mapInstance.setView([lat, lng], 15);
            });
            markersRef.current[id] = marker;
        });
    }, [mapInstance, stations, selectedStationId]);

    const selectedStationData = stations[selectedStationId];
    // ... (Memo for chart data and options) ...
    const pollutants = [ /* ... pollutants array ... */ ];

    return (
        <div className="map-page">
            <nav className="navbar">{/* ... your navbar JSX ... */}</nav>
            <div className="main-content">
                <div className="map-container"><div id="map" ref={mapRef} className="map-element"></div></div>
                <div className={`details-panel ${isPanelOpen ? 'open' : ''}`}>
                    <button className="panel-toggle" onClick={() => setIsPanelOpen(!isPanelOpen)}>{/* ... */}</button>
                    <div className="panel-header">
                         <h2 className="panel-title"><i className="fas fa-satellite-dish"></i> Monitoring Stations</h2>
                         <div className="station-selector">
                            {Object.values(stations).map(station => (
                                <button key={station.station_info.name} className={`station-btn ${selectedStationId === Object.keys(stations).find(key => stations[key] === station) ? 'active' : ''}`} onClick={() => setSelectedStationId(Object.keys(stations).find(key => stations[key] === station))}>
                                    <div className="station-btn-content">
                                        <span>{station.station_info.name}</span>
                                        <span className="station-aqi" style={{ color: getAQIColor(station.highest_sub_index) }}>
                                            {Math.round(station.highest_sub_index) || 'N/A'}
                                        </span>
                                    </div>
                                </button>
                            ))}
                        </div>
                    </div>
                    <div className="panel-content">
                        {user && interpolatedData && nearestStation && (
                            <div className="user-location-panel">
                                <div className="user-location-header"><i className="fas fa-street-view"></i><span>AQI at Your Location</span></div>
                                <div className="user-location-body">
                                    <div className="user-aqi-value">{nearestStation.distance < 1 ? stations[nearestStation.id]?.highest_sub_index : interpolatedData.aqi}</div>
                                    <div className="user-location-details">
                                        <span>{nearestStation.distance < 1 ? 'Using nearest station' : 'Interpolated value'}</span><br />
                                        <span>Nearest: {stations[nearestStation.id]?.station_info.name} ({nearestStation.distance.toFixed(1)} km)</span>
                                    </div>
                                </div>
                            </div>
                        )}
                        {isLoading ? <div className="panel-loader"><div className="loading-spinner"></div></div> :
                        error ? <div>Error: {error}</div> :
                        selectedStationData ? (
                            <div>
                                <h3>{selectedStationData.station_info.name}</h3>
                                <p>Current AQI: {Math.round(selectedStationData.highest_sub_index)} ({getAQIStatus(selectedStationData.highest_sub_index)})</p>
                                <div className="pollutant-metrics">{/* ... pollutant grid ... */}</div>
                                <div className="forecast-section">{/* ... forecast chart ... */}</div>
                            </div>
                        ) : null}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MapPage;
