import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

function Dashboard() {
    const [username] = useState(() => (JSON.parse(localStorage.getItem('user') || '{}')).name || 'User');
    const [selectedParameter, setSelectedParameter] = useState('pm25');
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const fetchDashboardData = useCallback(async () => {
        setLoading(true);
        try {
            const url = `${API_BASE_URL}/api/dashboard/?username=${username}`;
            const response = await fetch(url);
            if (!response.ok) {
                const errData = await response.json();
                if (errData.redirect_to) navigate(errData.redirect_to);
                throw new Error(errData.error || 'Failed to fetch dashboard data');
            }
            const data = await response.json();
            setDashboardData(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        fetchDashboardData();
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
                (err) => console.warn("Geolocation is disabled or failed.")
            );
        }
    }, [fetchDashboardData]);

    useEffect(() => {
        if (userLocation && dashboardData?.stations) {
            const stations = Object.entries(dashboardData.stations);
            let nearestDist = Infinity, nearestId = null;

            stations.forEach(([id, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                if (dist < nearestDist) { nearestDist = dist; nearestId = id; }
            });
            setNearestStation({ id: nearestId, distance: nearestDist });
            
            let weightedSum = 0, weightSum = 0;
            stations.forEach(([, station]) => {
                const dist = calculateDistance(userLocation.lat, userLocation.lng, station.station_info.lat, station.station_info.lng);
                const weight = 1 / Math.pow(dist === 0 ? 0.001 : dist, 2);
                weightedSum += (station.highest_sub_index || 0) * weight;
                weightSum += weight;
            });
            if (weightSum > 0) {
                setInterpolatedData({ aqi: Math.round(weightedSum / weightSum) });
            }
        }
    }, [userLocation, dashboardData]);

    const displayData = useMemo(() => {
        if (!dashboardData) return null;
        return {
            aqi: interpolatedData?.aqi,
            nearest: nearestStation ? {
                name: dashboardData.stations[nearestStation.id]?.station_info.name,
                distance: nearestStation.distance.toFixed(1)
            } : null
        };
    }, [dashboardData, nearestStation, interpolatedData]);

    if (loading) return <div className="panel-loader"><div className="loading-spinner"></div><h2>Loading Dashboard...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;
    if (!dashboardData) return <div>No data available.</div>;

    const { health_data, stations, forecasts } = dashboardData;
    const forecastForNearest = nearestStation ? forecasts[nearestStation.id] : null;
    const currentValues = nearestStation ? stations[nearestStation.id]?.averages : Object.values(stations)[0]?.averages;

    return (
        <div className="dashboard-page">
            <nav className="navbar">{/* ... Your Navbar JSX ... */}</nav>
            <div className="main-container">
                <div className="welcome-header">
                    <h1>Welcome, {username}!</h1>
                    <p>Your health risk is currently <span style={{color: health_data.risk_level === 'Low' ? '#10b981' : '#ef4444', fontWeight: 'bold'}}>{health_data.risk_level}</span> (Score: {health_data.score})</p>
                </div>

                {displayData?.aqi !== undefined && displayData.nearest && (
                    <div className="user-location-panel">
                        <div className="user-location-header"><i className="fas fa-street-view"></i><span>AQI at Your Location</span></div>
                        <div className="user-location-body">
                            <div className="user-aqi-value">{displayData.aqi}</div>
                            <div className="user-location-details">
                                <span>Interpolated Value</span><br />
                                <span>Nearest: {displayData.nearest.name} ({displayData.nearest.distance} km)</span>
                            </div>
                        </div>
                    </div>
                )}
                
                <div className="dashboard-grid">
                    <div className="dashboard-card">
                        <h3>Sensor Map</h3>
                        <Suspense fallback={<div className="panel-loader">Loading Map...</div>}>
                            <LazyMap userLocation={userLocation} stations={stations} />
                        </Suspense>
                    </div>
                    <div className="dashboard-card">
                        <h3>4-Day Forecast</h3>
                        <select className="parameter-selector" value={selectedParameter} onChange={(e) => setSelectedParameter(e.target.value)}>
                            <option value="pm25">PM2.5</option>
                            <option value="pm10">PM10</option>
                            <option value="so2">SO‚ÇÇ</option>
                        </select>
                        <Suspense fallback={<div className="panel-loader">Loading Chart...</div>}>
                            <LazyChart forecastData={forecastForNearest} selectedParameter={selectedParameter} />
                        </Suspense>
                    </div>
                </div>
                
                <div className="weather-section">
                  <h2 className="section-title">üå§Ô∏è Weather Conditions</h2>
                  <div className="weather-grid">
                    <div className="weather-card">
                        <div>üå°Ô∏è Temperature</div>
                        <div>{currentValues?.temp?.toFixed(1) || 'N/A'}¬∞C</div>
                    </div>
                    <div className="weather-card">
                        <div>üíß Humidity</div>
                        <div>{currentValues?.hum?.toFixed(1) || 'N/A'}%</div>
                    </div>
                    <div className="weather-card">
                        <div>üìè Pressure</div>
                        <div>{currentValues?.pre || 'N/A'} hPa</div>
                    </div>
                  </div>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
