import React, { useEffect, useState, useCallback, useMemo } from 'react';
import './css/HomePage.css';

function HomePage() {
  const [homeData, setHomeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Safe API_BASE_URL handling
  const API_BASE_URL = (() => {
    try {
      return process.env.REACT_APP_API_URL || 'http://localhost:8000';
    } catch (e) {
      return 'http://localhost:8000';
    }
  })();

  // Check session validity (1 hour timeout)
  const checkSessionValidity = useCallback(() => {
    try {
      const userData = localStorage.getItem('user');
      const loginTime = localStorage.getItem('loginTime');
      
      if (userData && loginTime) {
        const currentTime = Date.now();
        const sessionDuration = currentTime - parseInt(loginTime);
        const oneHour = 60 * 60 * 1000; // 1 hour in milliseconds
        
        if (sessionDuration > oneHour) {
          // Session expired - clear localStorage
          localStorage.removeItem('user');
          localStorage.removeItem('loginTime');
          setUser(null);
          console.log('Session expired after 1 hour');
          return null;
        }
        
        const parsedUser = JSON.parse(userData);
        setUser(parsedUser);
        return parsedUser;
      }
      return null;
    } catch (error) {
      console.error('Error checking session:', error);
      localStorage.removeItem('user');
      localStorage.removeItem('loginTime');
      setUser(null);
      return null;
    }
  }, []);

  // Check authentication on mount
  useEffect(() => {
    checkSessionValidity();
    
    // Check session every 5 minutes
    const sessionCheckInterval = setInterval(checkSessionValidity, 5 * 60 * 1000);
    
    return () => clearInterval(sessionCheckInterval);
  }, [checkSessionValidity]);

  // Optimized particles (reduced count)
  const particles = useMemo(() => {
    const particleElements = [];
    for (let i = 0; i < 8; i++) {
      particleElements.push(
        <div
          key={i}
          className="particle"
          style={{
            left: Math.random() * 100 + '%',
            top: Math.random() * 100 + '%',
            width: Math.random() * 4 + 3 + 'px',
            height: Math.random() * 4 + 3 + 'px',
            animationDelay: Math.random() * 6 + 's',
            willChange: 'transform',
          }}
        />
      );
    }
    return particleElements;
  }, []);

  // Optimized fetch function
  const fetchHomeData = useCallback(async () => {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 10000);

    try {
      setError(null);
      
      // Build URL with username if logged in
      const url = new URL(`${API_BASE_URL}/api/home/`);
      if (user?.name) {
        url.searchParams.append('username', user.name);
      }

      const response = await fetch(url, {
        signal: controller.signal,
        headers: {
          'Accept': 'application/json',
          'Cache-Control': 'no-cache',
        }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      
      if (data.error) {
        throw new Error(data.message || data.error);
      }
      
      setHomeData(data);
      setError(null);
      
    } catch (err) {
      clearTimeout(timeoutId);
      
      if (err.name === 'AbortError') {
        setError('Request timeout - please check your connection');
      } else {
        console.error('Error fetching home data:', err);
        setError(err.message || 'Failed to load data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [API_BASE_URL, user?.name]);

  useEffect(() => {
    fetchHomeData();

    // Refresh every 60 seconds
    const interval = setInterval(fetchHomeData, 60000);

    return () => clearInterval(interval);
  }, [fetchHomeData]);

  // AQI status calculation
  const aqiStatus = useMemo(() => {
    const getAQIStatus = (aqiValue) => {
      if (!aqiValue || isNaN(aqiValue)) {
        return { status: 'Unknown', icon: 'fas fa-question', color: '#6b7280' };
      }
      
      const value = parseInt(aqiValue, 10);
      if (value <= 50) {
        return { status: 'GOOD', icon: 'fas fa-smile', color: '#10b981' };
      } else if (value <= 100) {
        return { status: 'MODERATE', icon: 'fas fa-meh', color: '#f59e0b' };
      } else if (value <= 150) {
        return { status: 'UNHEALTHY', icon: 'fas fa-frown', color: '#ef4444' };
      } else {
        return { status: 'HAZARDOUS', icon: 'fas fa-biohazard', color: '#7c2d12' };
      }
    };
    return getAQIStatus(homeData?.highest_sub_index);
  }, [homeData?.highest_sub_index]);

  const toggleMenu = useCallback(() => {
    setIsMenuOpen(prev => !prev);
  }, []);

  // Logout handler
  const handleLogout = useCallback(() => {
    localStorage.removeItem('user');
    localStorage.removeItem('loginTime');
    setUser(null);
    window.location.href = '/login';
  }, []);

  // Loading component
  if (loading) {
    return (
      <div className="loading-container" style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#f8fafc'
      }}>
        <div className="loading-content" style={{ textAlign: 'center' }}>
          <div className="loading-spinner" style={{ 
            width: '40px', 
            height: '40px', 
            border: '4px solid #e5e7eb', 
            borderTop: '4px solid #3b82f6',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 1rem'
          }}></div>
          <p style={{ color: '#6b7280', fontSize: '1rem' }}>Loading air quality data...</p>
        </div>
      </div>
    );
  }

  // Error component
  if (error) {
    return (
      <div className="error-container" style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#f8fafc',
        padding: '2rem'
      }}>
        <div className="error-message" style={{ 
          textAlign: 'center', 
          padding: '2rem',
          backgroundColor: 'white',
          borderRadius: '0.75rem',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          maxWidth: '400px'
        }}>
          <h2 style={{ color: '#ef4444', marginBottom: '1rem', fontSize: '1.5rem' }}> Data Unavailable</h2>
          <p style={{ color: '#6b7280', marginBottom: '1.5rem', lineHeight: '1.5' }}>{error}</p>
          <button 
            onClick={fetchHomeData} 
            className="retry-button"
            style={{
              padding: '0.75rem 1.5rem',
              backgroundColor: '#3b82f6',
              color: 'white',
              border: 'none',
              borderRadius: '0.5rem',
              cursor: 'pointer',
              fontSize: '1rem',
              fontWeight: '500',
              transition: 'background-color 0.3s ease'
            }}
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="homepage">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img 
              src="/aqi.webp" 
              alt="AQM Logo" 
              loading="lazy"
              width="40" 
              height="40"
              style={{ marginRight: '0.75rem' }}
            />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="#home" className="nav-link">Home</a></li>
            <li><a href="#cities" className="nav-link">Cities</a></li>
            <li><a href="#services" className="nav-link">Services</a></li>
            <li><a href="#contact" className="nav-link">Contact</a></li>
            <li><a href="/forecast" className="nav-link">Forecasting</a></li>

            {user ? (
              <>
                <li><a href="/dashboard" className="nav-link">Dashboard</a></li>
                <li><a href="/profile" className="nav-link">Profile</a></li>
                <li className="nav-link user-greeting" style={{ color: '#3b82f6', fontWeight: '500' }}>
                   Hello, {user.name}
                </li>
                <li>
                  <button 
                    onClick={handleLogout} 
                    className="nav-link login-btn" 
                    style={{ 
                      background: 'none', 
                      border: 'none', 
                      cursor: 'pointer',
                      color: 'inherit',
                      fontSize: 'inherit'
                    }}
                  >
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </button>
                </li>
              </>
            ) : (
              <li><a href="/login" className="nav-link login-btn">
                <i className="fas fa-sign-in-alt"></i> Login
              </a></li>
            )}
          </ul>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero-section" id="home">
        <div className="hero-overlay"></div>
        <div className="particles">
          {particles}
        </div>
        <div className="hero-content">
          <h1 className="hero-title">Air Quality Monitoring</h1>
          <p className="hero-subtitle">Live air quality updates and insights from Kalady, Ernakulam</p>
          <p className="hero-note">
            This value reflects combined readings from monitoring stations in the area.
          </p>
          
          <div className="aqi-display">
            <div className="aqi-label">
              Current AQI  Last Updated: <span>
                {homeData?.latest_item?.date} at {homeData?.latest_item?.time}
              </span>
            </div>
            <div className="aqi-value">
              {homeData?.highest_sub_index || "N/A"}
            </div>
            <div className="aqi-status">AQI - {aqiStatus.status}</div>
            <div className="aqi-icon">
              <i className={aqiStatus.icon} style={{ color: aqiStatus.color }}></i>
            </div>
          </div>

          <a href="/map" className="cta-button">
            <i className="fas fa-map-marked-alt"></i>
            View Live AQI Map
          </a>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="stats-container">
          <div className="section-header">
            <h2 className="section-title">Kerala Air Quality Network</h2>
            <p className="section-subtitle">
              Comprehensive monitoring across multiple locations for accurate air quality assessment
            </p>
          </div>

          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">2</div>
              <div className="stat-label">Monitoring Stations</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">24/7</div>
              <div className="stat-label">Real-time Monitoring</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">7</div>
              <div className="stat-label">Key Pollutants Tracked</div>
            </div>
          </div>
        </div>
      </section>

      {/* AQI Scale Section */}
      <section className="section aqi-scale-section">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Understanding Air Quality Index</h2>
            <p className="section-subtitle">
              Know what the AQI numbers mean for your health and daily activities
            </p>
          </div>

          <div className="aqi-scale-grid">
            {[
              { range: 'Good (0-50)', class: 'aqi-good', description: 'Air quality is satisfactory. Air pollution poses little or no risk for everyone.' },
              { range: 'Moderate (51-100)', class: 'aqi-moderate', description: 'Air quality is acceptable. However, sensitive individuals may experience minor issues.' },
              { range: 'Unhealthy (101-200)', class: 'aqi-unhealthy', description: 'Members of sensitive groups may experience health effects. Limit outdoor activities.' },
              { range: 'Hazardous (201+)', class: 'aqi-hazardous', description: 'Health alert: The risk of health effects is increased for everyone. Avoid outdoor activities.' }
            ].map((item, index) => (
              <div key={index} className="aqi-scale-item">
                <div className="aqi-range">
                  <div className={`aqi-color-indicator ${item.class}`}></div>
                  <div className="aqi-range-text">{item.range}</div>
                </div>
                <div className="aqi-description">{item.description}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Cities Section */}
      <section className="section cities-section" id="cities">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Monitoring Locations</h2>
            <p className="section-subtitle">
              Air quality data from strategic locations across Ernakulam district
            </p>
          </div>

          <div className="cities-grid">
            {[
              { icon: 'fas fa-university', name: 'Adi Shankara Institute', type: 'Educational Institution' },
              { icon: 'fas fa-city', name: 'Mattoor Junction', type: 'Urban Area' }
            ].map((city, index) => (
              <div key={index} className="city-card">
                <div className="city-icon">
                  <i className={city.icon}></i>
                </div>
                <a href="/map" className="city-link">
                  <div className="city-name">{city.name}</div>
                  <div className="city-type">{city.type}</div>
                </a>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section className="section services-section" id="services">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Our Services</h2>
            <p className="section-subtitle">
              Advanced air quality solutions powered by cutting-edge technology
            </p>
          </div>

          <div className="services-grid">
            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <h3 className="service-title">Personalized AQI Forecast</h3>
              <p className="service-description">
                Get accurate air quality predictions with advanced meteorological models and machine learning algorithms for better planning.
              </p>
              <a href="/forecast" className="service-button">
                <i className="fas fa-arrow-right"></i>
                Get Forecast
              </a>
            </div>

            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-heartbeat"></i>
              </div>
              <h3 className="service-title">Health Risk Assessment</h3>
              <p className="service-description">
                Receive personalized health recommendations based on current air quality conditions and your individual health profile.
              </p>
              <a href={user ? "/health-assessment" : "/login"} className="service-button">
                <i className="fas fa-arrow-right"></i>
                Assess Risk
              </a>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer" id="contact">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirQuality Kerala</h3>
              <p className="footer-text">
                Leading provider of real-time air quality monitoring solutions in Kerala, committed to environmental sustainability and public health awareness.
              </p>
              <div className="social-links">
                <a href="#" className="social-link"><i className="fab fa-facebook-f"></i></a>
                <a href="#" className="social-link"><i className="fab fa-twitter"></i></a>
                <a href="#" className="social-link"><i className="fab fa-linkedin-in"></i></a>
                <a href="#" className="social-link"><i className="fab fa-instagram"></i></a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <a href="#" className="footer-link">Home</a>
              <a href="#cities" className="footer-link">Monitoring Stations</a>
              <a href="#services" className="footer-link">Services</a>
              <a href="/forecast" className="footer-link">Forecasting</a>
              {user ? (
                <a href="/dashboard" className="footer-link">Dashboard</a>
              ) : (
                <a href="/login" className="footer-link">Login</a>
              )}
            </div>

            <div className="footer-section">
              <h3>Services</h3>
              <a href="/forecast" className="footer-link">AQI Forecasting</a>
              <a href={user ? "/health-assessment" : "/login"} className="footer-link">Health Risk Assessment</a>
              <a href="/map" className="footer-link">Live AQI Map</a>
              <a href="#" className="footer-link">Data Analytics</a>
            </div>

            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Vidya Bharathi Nagar, Mattoor<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AirQuality Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>

      {/* CSS for animations */}
      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        .particle {
          position: absolute;
          background: linear-gradient(45deg, #3b82f6, #10b981);
          border-radius: 50%;
          opacity: 0.6;
          animation: float 8s ease-in-out infinite;
        }
        
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-15px) rotate(180deg); }
        }
        
        @media (prefers-reduced-motion: reduce) {
          .particle, .loading-spinner {
            animation: none;
          }
        }
      `}</style>
    </div>
  );
}

export default HomePage;
