import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../App'; // Assuming you have Auth context

function FamilyHealthReport() {
    const { memberId } = useParams();
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const { user } = useAuth();

    const API_BASE_URL = process.env.NODE_ENV === 'production' 
        ? 'https://airaware-app-gcw7.onrender.com' 
        : 'http://localhost:8000';

    useEffect(() => {
        const fetchReport = async () => {
            if (!user) {
                navigate('/login');
                return;
            }
            try {
                const response = await fetch(`${API_BASE_URL}/api/family-members/${memberId}/health-report/`, {
                    headers: { 'Content-Type': 'application/json' }
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch health report.');
                }
                const data = await response.json();
                setReportData(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchReport();
    }, [memberId, user, navigate, API_BASE_URL]);

    if (loading) return <div className="loading-container"><h2>Loading Health Report...</h2></div>;
    if (error) return <div className="error-container"><h2>Error</h2><p>{error}</p></div>;
    if (!reportData) return <div>No report data found.</div>;

    const { member_name, health_assessment, air_quality } = reportData;

    return (
        <div className="health-report-page">
            <header>
                <h1>Health Report for {member_name}</h1>
                <button onClick={() => navigate('/add-family')}>Back to Family Page</button>
            </header>
            <main>
                <section className="health-summary">
                    <h2>Health Summary</h2>
                    <p><strong>Health Score:</strong> {health_assessment.score}</p>
                    <p><strong>Risk Level:</strong> {health_assessment.risk_level}</p>
                </section>
                <section className="recommendations">
                    <h2>Recommendations</h2>
                    <ul>
                        {health_assessment.recommendations.map((rec, index) => (
                            <li key={index}>{rec}</li>
                        ))}
                    </ul>
                </section>
                <section className="air-quality">
                    <h2>Current Air Quality</h2>
                    <p><strong>AQI:</strong> {air_quality.highest_sub_index}</p>
                    <p><strong>Status:</strong> {air_quality.aqi_status}</p>
                    <p><strong>Location:</strong> {air_quality.station_name}</p>
                </section>
            </main>
        </div>
    );
}

export default FamilyHealthReport;
