import React, { useState } from 'react';
import './css/Login.css';

function Login() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [showOTP, setShowOTP] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Safe API URL - completely safe approach
  const API_BASE_URL = 'http://localhost:8000';

  console.log('Login component loaded');

  // Simple particles without complex animations
  const createParticles = () => {
    const particles = [];
    for (let i = 0; i < 20; i++) {
      particles.push(
        <div
          key={i}
          className="particle"
          style={{
            left: Math.random() * 100 + '%',
            top: Math.random() * 100 + '%',
            width: '4px',
            height: '4px',
            backgroundColor: 'rgba(255,255,255,0.3)',
            borderRadius: '50%',
            position: 'absolute',
            animationDelay: Math.random() * 5 + 's'
          }}
        />
      );
    }
    return particles;
  };

  // Clear messages
  const clearMessages = () => {
    setError('');
    setSuccess('');
  };

  // Validate phone number
  const validatePhoneNumber = (phone) => {
    const cleanPhone = phone.replace(/\D/g, '');
    
    if (cleanPhone.length === 10 && cleanPhone.match(/^[6-9]/)) {
      return cleanPhone;
    }
    return null;
  };

  // Handle phone number input
  const handlePhoneNumberChange = (e) => {
    const value = e.target.value;
    setPhoneNumber(value);
    clearMessages();
  };

  // Handle OTP input
  const handleOTPChange = (e) => {
    const value = e.target.value.replace(/\D/g, '').slice(0, 6);
    setOtp(value);
    clearMessages();
  };

  // Handle sending OTP
  const handleSendOTP = async (e) => {
    e.preventDefault();
    
    console.log('Send OTP clicked');
    
    if (loading) return;
    
    setLoading(true);
    clearMessages();

    try {
      const validatedPhone = validatePhoneNumber(phoneNumber);
      
      if (!validatedPhone) {
        setError('Please enter a valid 10-digit Indian mobile number');
        setLoading(false);
        return;
      }

      console.log('Sending OTP to:', validatedPhone);

      const response = await fetch(`${API_BASE_URL}/api/send-otp/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ phone_number: validatedPhone }),
      });

      const data = await response.json();
      console.log('Response:', data);

      if (response.ok) {
        setShowOTP(true);
        setSuccess('OTP sent successfully! Check your phone.');
        setPhoneNumber(validatedPhone);
      } else {
        setError(data.error || 'Failed to send OTP');
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle OTP verification
  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    
    console.log('Verify OTP clicked');
    
    if (loading) return;
    
    setLoading(true);
    clearMessages();

    try {
      if (!otp || otp.length !== 6) {
        setError('Please enter a valid 6-digit OTP');
        setLoading(false);
        return;
      }

      const response = await fetch(`${API_BASE_URL}/api/verify-otp/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          otp_code: otp, 
          phone_number: phoneNumber 
        }),
      });

      const data = await response.json();
      console.log('Verify response:', data);

      if (response.ok) {
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('isLoggedIn', 'true');
        
        setSuccess('Login successful! Redirecting...');
        
        setTimeout(() => {
          if (data.redirect_to === '/health-assessment') {
            window.location.href = '/health-assessment';
          } else {
            window.location.href = '/dashboard';
          }
        }, 1500);
      } else {
        setError(data.error || 'Invalid OTP');
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle back to phone
  const handleBackToPhone = () => {
    setShowOTP(false);
    setOtp('');
    clearMessages();
  };

  return (
    <div className="login-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" />
            AirAware
          </a>
        </div>
      </nav>

      {/* Login Section */}
      <section className="login-section">
        <div className="particles">
          {createParticles()}
        </div>
        
        <div className="login-container">
          <div className="login-header">
            <h2>
              👤 {showOTP ? 'Verify OTP' : 'Login'}
            </h2>
            <p>
              {showOTP 
                ? `Enter the 6-digit OTP sent to ${phoneNumber}` 
                : 'Welcome back! Please log in to your account.'
              }
            </p>
          </div>

          {/* Success message */}
          {success && (
            <div className="success-message" style={{
              backgroundColor: '#d4edda',
              color: '#155724',
              padding: '12px',
              borderRadius: '4px',
              marginBottom: '16px',
              border: '1px solid #c3e6cb'
            }}>
              ✅ {success}
            </div>
          )}

          {/* Error message */}
          {error && (
            <div className="error-message" style={{
              backgroundColor: '#f8d7da',
              color: '#721c24',
              padding: '12px',
              borderRadius: '4px',
              marginBottom: '16px',
              border: '1px solid #f5c6cb'
            }}>
              ⚠️ {error}
            </div>
          )}

          {!showOTP ? (
            // Phone Number Form
            <form onSubmit={handleSendOTP}>
              <div className="form-group">
                <label htmlFor="phone_number">Enter your phone number</label>
                <input
                  type="tel"
                  id="phone_number"
                  value={phoneNumber}
                  onChange={handlePhoneNumberChange}
                  placeholder="Enter 10-digit mobile number"
                  required
                  disabled={loading}
                  maxLength="10"
                  style={{
                    width: '100%',
                    padding: '12px',
                    fontSize: '16px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    marginTop: '8px'
                  }}
                />
                <small style={{ color: '#666', fontSize: '14px' }}>
                  Enter your 10-digit mobile number (e.g., 9876543210)
                </small>
              </div>
              <button 
                type="submit" 
                disabled={loading || !phoneNumber}
                style={{
                  width: '100%',
                  padding: '12px',
                  fontSize: '16px',
                  backgroundColor: loading ? '#ccc' : '#007bff',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: loading ? 'not-allowed' : 'pointer',
                  marginTop: '16px'
                }}
              >
                {loading ? '📤 Sending OTP...' : '📤 Send OTP'}
              </button>
            </form>
          ) : (
            // OTP Verification Form
            <form onSubmit={handleVerifyOTP}>
              <div className="form-group">
                <label htmlFor="otp">Enter OTP</label>
                <input
                  type="text"
                  id="otp"
                  value={otp}
                  onChange={handleOTPChange}
                  placeholder="Enter 6-digit OTP"
                  maxLength="6"
                  required
                  disabled={loading}
                  style={{
                    width: '100%',
                    padding: '12px',
                    fontSize: '16px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    marginTop: '8px',
                    textAlign: 'center',
                    letterSpacing: '2px'
                  }}
                />
                <small style={{ color: '#666', fontSize: '14px' }}>
                  Enter the 6-digit OTP sent to your phone
                </small>
              </div>
              
              <button 
                type="submit" 
                disabled={loading || otp.length !== 6}
                style={{
                  width: '100%',
                  padding: '12px',
                  fontSize: '16px',
                  backgroundColor: loading ? '#ccc' : '#28a745',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: loading ? 'not-allowed' : 'pointer',
                  marginTop: '16px'
                }}
              >
                {loading ? '🔄 Verifying...' : '✅ Verify OTP'}
              </button>
              
              <div style={{ marginTop: '16px', textAlign: 'center' }}>
                <button 
                  type="button" 
                  onClick={handleBackToPhone}
                  disabled={loading}
                  style={{
                    padding: '8px 16px',
                    fontSize: '14px',
                    backgroundColor: '#6c757d',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: loading ? 'not-allowed' : 'pointer',
                    marginRight: '8px'
                  }}
                >
                  ← Back to Phone Number
                </button>
              </div>
            </form>
          )}

          <div className="login-footer" style={{ marginTop: '24px', textAlign: 'center' }}>
            <p>
              <a href="#" style={{ color: '#007bff', textDecoration: 'none' }}>
                🔑 Forgot Password?
              </a>
            </p>
            <p>
              Don't have an account? 
              <a href="/signup" style={{ color: '#007bff', textDecoration: 'none', marginLeft: '4px' }}>
                👤 Sign Up
              </a>
            </p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Login;
