// ===== IDW INTERPOLATION CALCULATION (Same as Dashboard.js) =====
const calculateIDWInterpolation = (locationData, stations) => {
  const stationIds = Object.keys(stations);
  let totalWeight = 0;
  const weightedValues = {
    pm25: 0,
    pm10: 0,
    so2: 0,
    no2: 0,
    co: 0,
    o3: 0,
    nh3: 0,
    temp: 0,
    hum: 0,
    pre: 0
  };
  let weightedAqi = 0;
  stationIds.forEach(stationId => {
    const station = stations[stationId];
    const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);

    // Avoid division by zero
    const safeDistance = Math.max(distance, 0.001);
    const weight = 1.0 / safeDistance ** 2;
    totalWeight += weight;

    // Add weighted values
    const averages = station.averages || {};
    Object.keys(weightedValues).forEach(param => {
      if (averages[param] !== undefined) {
        weightedValues[param] += averages[param] * weight;
      }
    });

    // Add weighted AQI
    weightedAqi += (station.highest_sub_index || 0) * weight;
  });

  // Calculate final interpolated values
  const interpolated_values = {};
  Object.keys(weightedValues).forEach(param => {
    interpolated_values[param] = totalWeight > 0 ? Math.round(weightedValues[param] / totalWeight * 100) / 100 : 0;
  });
  const interpolated_aqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
  return {
    interpolated_values,
    interpolated_aqi,
    stations_used: stationIds.length,
    method: 'idw'
  };
};

