import React from 'react';
import { Line } from 'react-chartjs-2';
import './css/MapPage.css';

// Helper function to get AQI color
const getAQIColor = (aqi) => {
  if (aqi === null || aqi === undefined) return '#6b7280';
  if (aqi <= 50) return '#10b981';
  if (aqi <= 100) return '#f59e0b';
  if (aqi <= 150) return '#ef4444';
  if (aqi <= 200) return '#dc2626';
  if (aqi <= 300) return '#7c2d12';
  return '#4c1d95';
};

// Main Component
const MapPage = () => {
  const [map, setMap] = React.useState(null);
  const [stations, setStations] = React.useState({});
  const [selectedStationId, setSelectedStationId] = React.useState('lora-v1');
  const [isLoading, setIsLoading] = React.useState(true);
  const [error, setError] = React.useState(null);

  const mapRef = React.useRef(null);
  const markersRef = React.useRef({});
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Effect 1: Load Leaflet library and initialize the map. This runs only once.
  React.useEffect(() => {
    let isMounted = true;
    console.log("MapPage: Starting map initialization.");

    const loadLeaflet = () => {
      return new Promise((resolve, reject) => {
        if (window.L) {
          console.log("MapPage: Leaflet already available.");
          resolve(window.L);
          return;
        }
        console.log("MapPage: Loading Leaflet library...");
        const link = document.createElement('link');
        link.rel = "stylesheet";
        link.href = "https://unpkg.com/leaflet@1.7.1/dist/leaflet.css";
        document.head.appendChild(link);
        const script = document.createElement('script');
        script.src = "https://unpkg.com/leaflet@1.7.1/dist/leaflet.js";
        script.async = true;
        document.head.appendChild(script);
        script.onload = () => {
          if (isMounted) {
            console.log("MapPage: Leaflet library loaded.");
            resolve(window.L);
          }
        };
        script.onerror = () => {
          if (isMounted) reject(new Error("Failed to load map library."));
        };
      });
    };

    const initialize = async () => {
      try {
        const L = await loadLeaflet();
        if (isMounted && mapRef.current && !mapRef.current._leaflet_id) {
          console.log("MapPage: Initializing map instance...");
          const mapInstance = L.map(mapRef.current).setView([10.176, 76.430], 15);
          L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstance);
          setMap(mapInstance);
        }
      } catch (err) {
        if (isMounted) setError(err.message);
      }
    };

    initialize();
    return () => { isMounted = false; };
  }, []);

  // Effect 2: Fetch data once the map object is available
  React.useEffect(() => {
    if (!map) return;

    let isMounted = true;
    const fetchData = async () => {
      // Don't set loading to true here on subsequent fetches
      // setIsLoading(true); 
      setError(null);
      try {
        const response = await fetch(`${API_BASE_URL}/api/map/`);
        if (!response.ok) {
          throw new Error(`Server error: ${response.status}`);
        }
        const data = await response.json();
        if (data && data.stations) {
          if (isMounted) {
            setStations(data.stations);
            // --- BUG FIX ---
            // Set loading to false only after data is successfully fetched and set.
            setIsLoading(false); 
          }
        } else {
          throw new Error('Received invalid data from server.');
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setIsLoading(false); // Also stop loading on error
        }
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 300000);

    return () => {
      isMounted = false;
      clearInterval(interval);
    };
  }, [map, API_BASE_URL]);

  // Effect 3: Update map markers when station data is updated
  React.useEffect(() => {
    if (!map || Object.keys(stations).length === 0) return;

    Object.entries(stations).forEach(([id, station]) => {
      if (!station?.station_info?.lat || !station?.station_info?.lng) return;

      const { lat, lng } = station.station_info;
      const aqi = station.highest_sub_index;
      const popupContent = `<b>${station.station_info.name}</b><br>AQI: ${aqi ? Math.round(aqi) : 'N/A'}`;

      if (markersRef.current[id]) {
        markersRef.current[id].setLatLng([lat, lng]).setPopupContent(popupContent);
        markersRef.current[id].setStyle({ fillColor: getAQIColor(aqi) });
      } else {
        const marker = window.L.circleMarker([lat, lng], {
          radius: 15,
          fillColor: getAQIColor(aqi),
          color: '#ffffff',
          weight: 3,
          opacity: 1,
          fillOpacity: 0.8
        }).addTo(map);
        marker.bindPopup(popupContent);
        marker.on('click', () => setSelectedStationId(id));
        markersRef.current[id] = marker;
      }
    });
  }, [map, stations]);

  const selectedStationData = stations[selectedStationId];

  return (
    <div className="map-page">
      <div className="map-container">
        <div id="map" ref={mapRef} className="map-element"></div>
        {isLoading && (
            <div className="map-loading-overlay">
                <div className="loading-spinner"></div>
            </div>
        )}
      </div>
      <div className="details-panel open">
        {error && <div className="error-message">{error}</div>}
        {!error && (
            <>
                <div className="panel-header">
                  <div className="station-selector">
                    {/* Show loading text if stations haven't loaded yet */}
                    {isLoading ? (
                        <p>Loading stations...</p>
                    ) : (
                      Object.keys(stations).map(stationId => (
                        <button
                          key={stationId}
                          className={`station-btn ${selectedStationId === stationId ? 'active' : ''}`}
                          onClick={() => setSelectedStationId(stationId)}
                        >
                          {stations[stationId]?.station_info?.name || stationId}
                        </button>
                      ))
                    )}
                  </div>
                </div>
                <div className="panel-content">
                  {selectedStationData ? (
                    <>
                      <div className="aqi-overview">
                        <div className="aqi-value" style={{ color: getAQIColor(selectedStationData.highest_sub_index) }}>
                          {Math.round(selectedStationData.highest_sub_index) || 'N/A'}
                        </div>
                        <div className="aqi-status-text">Air Quality Index ({selectedStationData.aqi_status || 'Unknown'})</div>
                      </div>
                      <div className="metrics-grid">
                        {['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'].map(key => (
                          <div key={key} className="metric-card">
                            <div className="metric-label">{key.toUpperCase()}</div>
                            <div className="metric-value">{selectedStationData.averages?.[key] ? Math.round(selectedStationData.averages[key]) : '--'}</div>
                          </div>
                        ))}
                      </div>
                      <div className="forecast-section">
                        <div className="forecast-title">5-Day Forecast</div>
                        {selectedStationData.forecast_data && selectedStationData.forecast_data.length > 0 ? (
                          <div style={{ height: '200px' }}>
                            <Line
                              data={{
                                labels: selectedStationData.forecast_data.map(d => d.day),
                                datasets: [{
                                  label: 'PM2.5 Forecast',
                                  data: selectedStationData.forecast_data.map(d => d.pm25_max),
                                  borderColor: '#3b82f6',
                                  backgroundColor: 'rgba(59, 130, 246, 0.2)',
                                  tension: 0.1
                                }]
                              }}
                              options={{
                                responsive: true,
                                maintainAspectRatio: false,
                                legend: { display: false },
                                scales: {
                                  yAxes: [{ ticks: { fontColor: '#94a3b8', beginAtZero: true } }],
                                  xAxes: [{ ticks: { fontColor: '#94a3b8' } }]
                                }
                              }}
                            />
                          </div>
                        ) : (
                          <p>Forecast data is not available.</p>
                        )}
                      </div>
                    </>
                  ) : (
                    <div className="loading-spinner" style={{ margin: 'auto' }}></div>
                  )}
                </div>
            </>
        )}
      </div>
    </div>
  );
};

export default MapPage;
