import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

function Dashboard() {
  // ===== DJANGO TEMPLATE PATTERN: IMMEDIATE DATA AVAILABILITY =====
  // No loading states - data available instantly like server-side rendering
  const [username, setUsername] = useState(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    return user.name || 'User';
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleString());
  const [systemStatus, setSystemStatus] = useState('OPERATIONAL');

  // State for real-time updates (optional background enhancement)
  const [liveData, setLiveData] = useState(null);
  const [userLocation, setUserLocation] = useState('ASIET Campus Area');

  const mapRef = useRef(null);
  const chartRef = useRef(null);
  const navigate = useNavigate();

  // ===== IMMEDIATE DATA STRUCTURE (Like Django Context) =====
  const initialDashboardData = {
    // Current sensor readings (lora-v1 equivalent)
    currentReadings: {
      pm25: 25.5, pm10: 42.3, so2: 15.8, no2: 37.2,
      co: 1.2, o3: 45.6, nh3: 12.4, temp: 28, hum: 65, pre: 1013
    },
    
    // AQI and status
    currentAQI: 85,
    aqiStatus: 'MODERATE',
    stationName: 'ASIET Campus Station',
    
    // 4-day forecast data (from your S3 structure)
    forecastData: {
      'lora-v1': [
        { day: 'Today', pm25_max: 30.2, pm10_max: 37.0, so2_max: 2.4, no2_max: 40.1, co_max: 1.1, o3_max: 3.7, nh3_max: 92.7 },
        { day: 'Tomorrow', pm25_max: 24.1, pm10_max: 34.2, so2_max: 2.4, no2_max: 45.2, co_max: 1.2, o3_max: 3.2, nh3_max: 100.0 },
        { day: 'Day 3', pm25_max: 24.3, pm10_max: 30.3, so2_max: 2.2, no2_max: 42.1, co_max: 1.2, o3_max: 3.5, nh3_max: 100.0 },
        { day: 'Day 4', pm25_max: 19.7, pm10_max: 23.4, so2_max: 2.3, no2_max: 38.5, co_max: 1.2, o3_max: 3.7, nh3_max: 100.0 }
      ],
      'loradev2': [
        { day: 'Today', pm25_max: 30.2, pm10_max: 37.0, so2_max: 2.4, no2_max: 801.6, co_max: 1.1, o3_max: 3.7, nh3_max: 92.7 },
        { day: 'Tomorrow', pm25_max: 24.1, pm10_max: 34.2, so2_max: 2.4, no2_max: 1000.0, co_max: 1.2, o3_max: 3.2, nh3_max: 100.0 },
        { day: 'Day 3', pm25_max: 24.3, pm10_max: 30.3, so2_max: 2.2, no2_max: 1000.0, co_max: 1.2, o3_max: 3.5, nh3_max: 100.0 },
        { day: 'Day 4', pm25_max: 19.7, pm10_max: 23.4, so2_max: 2.3, no2_max: 1000.0, co_max: 1.2, o3_max: 3.7, nh3_max: 100.0 }
      ]
    },
    
    // Station locations
    sensorLocations: {
      'lora-v1': { lat: 10.178322301561957, lng: 76.43089144291672, name: 'Station 1 (ASIET Campus)' },
      'loradev2': { lat: 10.17095090340159, lng: 76.42962876824544, name: 'Station 2 (Mattoor Junction)' }
    },
    
    // Health data (placeholder)
    healthScore: 75,
    riskLevel: 'Moderate',
    
    // Timestamps
    lastUpdated: new Date().toISOString(),
    sensorTimestamp: new Date().toLocaleString()
  };

  // Use the initial data immediately
  const [dashboardData] = useState(initialDashboardData);

  // ===== UTILITY FUNCTIONS (Django Template Filters Equivalent) =====
  const getAQIStatus = (aqi) => {
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (aqi <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (aqi <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  };

  const getMetricIcon = (metric) => {
    const icons = {
      pm25: 'ðŸŒ±', pm10: 'ðŸƒ', so2: 'ðŸ­', no2: 'ðŸ’¨',
      co: 'â˜ï¸', o3: 'â˜€ï¸', nh3: 'âš—ï¸'
    };
    return icons[metric] || 'ðŸ“Š';
  };

  const formatValue = (value, metric) => {
    if (metric === 'co') return parseFloat(value).toFixed(1);
    return Math.round(parseFloat(value));
  };

  // ===== DJANGO-STYLE IMMEDIATE INITIALIZATION =====
  useEffect(() => {
    console.log('âœ… Dashboard mounted - instant loading like Django template');
    
    // Update current time
    setCurrentTime(new Date().toLocaleString());
    
    // Initialize libraries after DOM is ready (non-blocking)
    setTimeout(() => {
      initializeMap();
      initializeChart();
    }, 100);

    // Optional: Start background data updates (like Django's real-time updates)
    const updateInterval = setInterval(() => {
      setCurrentTime(new Date().toLocaleString());
      // Optionally fetch fresh data in background without affecting UI
      fetchLiveDataInBackground();
    }, 30000); // Every 30 seconds

    return () => {
      clearInterval(updateInterval);
      if (mapRef.current) {
        try { mapRef.current.remove(); } catch (e) {}
      }
      if (chartRef.current) {
        try { chartRef.current.destroy(); } catch (e) {}
      }
    };
  }, []);

  // ===== BACKGROUND DATA FETCHING (Non-blocking like Django AJAX) =====
  const fetchLiveDataInBackground = useCallback(async () => {
    try {
      // This runs in background without affecting UI
      const response = await fetch(`/api/dashboard/?username=${username}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      });
      
      if (response.ok) {
        const freshData = await response.json();
        setLiveData(freshData);
        console.log('ðŸ”„ Background update successful');
      }
    } catch (error) {
      console.log('âš ï¸ Background update failed, continuing with cached data');
      // Don't show errors to user - graceful degradation
    }
  }, [username]);

  // ===== MAP INITIALIZATION (Simplified, non-blocking) =====
  const initializeMap = useCallback(() => {
    if (!window.L) {
      console.log('ðŸ“ Leaflet not ready, skipping map');
      return;
    }

    const mapContainer = document.getElementById('dashboard-map');
    if (!mapContainer) return;

    try {
      const map = window.L.map('dashboard-map', {
        center: [10.178322301561957, 76.43089144291672],
        zoom: 12
      });

      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Â© OpenStreetMap'
      }).addTo(map);

      // Add markers for both stations
      Object.entries(dashboardData.sensorLocations).forEach(([id, location]) => {
        const marker = window.L.marker([location.lat, location.lng])
          .addTo(map)
          .bindPopup(`<b>${location.name}</b><br/>AQI: ${dashboardData.currentAQI}`);
      });

      mapRef.current = map;
      console.log('âœ… Map initialized');
    } catch (error) {
      console.log('âš ï¸ Map initialization failed, continuing without map');
    }
  }, [dashboardData]);

  // ===== CHART INITIALIZATION (Simplified, non-blocking) =====
  const initializeChart = useCallback(() => {
    if (!window.Chart) {
      console.log('ðŸ“Š Chart.js not ready, skipping chart');
      return;
    }

    const canvas = document.getElementById('forecast-chart');
    if (!canvas) return;

    try {
      if (chartRef.current) {
        chartRef.current.destroy();
      }

      const forecastData = dashboardData.forecastData['lora-v1'];
      const data = forecastData.map(item => item[`${selectedParameter}_max`] || 0);
      const labels = forecastData.map(item => item.day);

      const ctx = canvas.getContext('2d');
      chartRef.current = new window.Chart(ctx, {
        type: 'line',
        data: {
          labels: labels,
          datasets: [{
            label: selectedParameter.toUpperCase(),
            data: data,
            borderColor: '#3b82f6',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            tension: 0.4,
            fill: true
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: { legend: { display: true } },
          scales: { y: { beginAtZero: true } }
        }
      });

      console.log('âœ… Chart initialized');
    } catch (error) {
      console.log('âš ï¸ Chart initialization failed, continuing without chart');
    }
  }, [selectedParameter, dashboardData]);

  // ===== EVENT HANDLERS =====
  const handleParameterChange = (param) => {
    setSelectedParameter(param);
    setTimeout(initializeChart, 100);
  };

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const scrollToMap = () => {
    document.getElementById('map-section')?.scrollIntoView({ behavior: 'smooth' });
  };

  // ===== CURRENT VALUES (Use live data if available, fallback to initial) =====
  const currentValues = liveData?.nearest_sensor_info?.current_sensor_data || dashboardData.currentReadings;
  const currentAQI = liveData?.interpolated_aqi || dashboardData.currentAQI;
  const aqiStatus = getAQIStatus(currentAQI);

  // ===== RENDER (Immediate, no loading states) =====
  return (
    <div className="dashboard-page">
      {/* Real-time Status Indicator */}
      <div className="realtime-status">
        ðŸ”´ LIVE â€¢ Updates every 30s â€¢ Last updated: {currentTime}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" style={{ width: '40px', height: '40px', marginRight: '12px' }} />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>
            â˜°
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">ðŸ  Home</a></li>
            <li><a href="/forecast" className="nav-link">ðŸ“ˆ Forecasting</a></li>
            <li><a href="/health-assessment" className="nav-link">ðŸ“‹ Health Update</a></li>
            <li><a href="/health-report" className="nav-link">ðŸ“„ Health Report</a></li>
            <li><a href="/add-family" className="nav-link">ðŸ‘¥ Add Family</a></li>
            <li className="user-info">ðŸ‘¤ <span>{username}</span></li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">ðŸšª Logout</button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.class}`}>
        â„¹ï¸ <span>Your Location AQI: {currentAQI} - {aqiStatus.status}</span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div class="welcome-info">
              <div className="user-avatar">ðŸ‘¤</div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                <div className="location-info">
                  ðŸ“ <span>{userLocation}</span>
                </div>
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {currentAQI}
              </div>
              <div className="aqi-status">Air Quality Index</div>
              <div className="aqi-badge">{dashboardData.stationName}</div>
              <div className="last-updated">
                AQI calculated: {dashboardData.sensorTimestamp}
              </div>
            </div>
          </div>
        </div>

        {/* Sensor Notification */}
        <div className="sensor-notification">
          ðŸ“¡ <strong>Nearest Sensor: {dashboardData.stationName}</strong> - 
          You are viewing data from our closest monitoring station.
          <a href="#map-section" onClick={scrollToMap}> Click on the map section below</a>
          to check your exact current location values and explore all sensor stations in real-time.
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">ðŸŒ¬ï¸ Current Air Quality Conditions</h2>
          <div className="metrics-grid">
            {Object.entries(currentValues).slice(0, 7).map(([key, value]) => (
              <div key={key} className="metric-card">
                <div className="metric-icon">{getMetricIcon(key)}</div>
                <div className="metric-value">{formatValue(value, key)}</div>
                <div className="metric-label">{key.toUpperCase()}</div>
                <div className="metric-unit">{key === 'co' ? 'mg/mÂ³' : 'Âµg/mÂ³'}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div id="map-section" className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ—ºï¸ Interactive Sensor Map</h3>
            </div>
            <div className="map-container">
              <div id="dashboard-map" style={{ height: '300px', width: '100%' }}></div>
            </div>
            <div style={{ marginTop: '12px', textAlign: 'center', fontSize: '0.875rem', color: '#6b7280' }}>
              ðŸ“ Click map or markers for detailed view
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">ðŸ“Š AQI Forecast</h3>
            </div>

            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                <button 
                  key={param}
                  className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                  onClick={() => handleParameterChange(param)}
                >
                  {param.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="forecast-content">
              <div className="chart-container" style={{ height: '200px', marginTop: '15px' }}>
                <canvas id="forecast-chart"></canvas>
              </div>
              
              <div className="forecast-table-container">
                <table className="forecast-table">
                  <thead>
                    <tr>
                      <th>ðŸ“… Day</th>
                      <th>ðŸ“ˆ Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    {dashboardData.forecastData['lora-v1'].map((item, index) => (
                      <tr key={index}>
                        <td>{item.day}</td>
                        <td>{formatValue(item[`${selectedParameter}_max`] || 0, selectedParameter)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Health Status Section */}
        <div className="health-section">
          <div className="health-content">
            <div>
              <div className="card-header">
                <h3 className="card-title">â¤ï¸ Health Status Monitor</h3>
              </div>
              <div className="health-score-display">
                <div className="health-score-circle">
                  <div className="health-score-value">{dashboardData.healthScore}</div>
                  <div className="health-risk-level">{dashboardData.riskLevel}</div>
                </div>
              </div>
            </div>
            <div className="health-alert">
              <h3>ðŸ›¡ï¸ Health Advisory</h3>
              <p>
                People with asthma, heart disease, older adults, and young children are more susceptible to air pollution. 
                Please follow health advisories closely and seek medical attention if you experience any adverse symptoms.
              </p>
            </div>
          </div>
        </div>

        {/* Weather Information */}
        <div className="weather-section">
          <h2 className="section-title">ðŸŒ¤ï¸ Weather Conditions</h2>
          <div className="weather-grid">
            <div className="weather-card">
              <div className="weather-icon">ðŸŒ¡ï¸</div>
              <div className="weather-value">{currentValues.temp}Â°C</div>
              <div className="weather-label">Temperature</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ’§</div>
              <div className="weather-value">{currentValues.hum}%</div>
              <div className="weather-label">Humidity</div>
            </div>
            <div className="weather-card">
              <div className="weather-icon">ðŸ“</div>
              <div className="weather-value">{currentValues.pre} hPa</div>
              <div className="weather-label">Atmospheric Pressure</div>
            </div>
          </div>
        </div>

        {/* Data Disclaimer */}
        <div className="disclaimer">
          <h3>â„¹ï¸ ðŸ“‹ Data Methodology & Government Compliance</h3>
          <p>
            <strong>Advanced Air Quality Monitoring System:</strong> This government-approved platform monitors air quality 
            across Kerala and provides accurate updates for your location using data from nearby monitoring stations.
          </p>

          <div className="disclaimer-highlight">
            <p><strong>ðŸ›ï¸ Kerala Government Air Quality Network:</strong></p>
            <ul>
              <li><strong>2 monitoring stations:</strong> Government-approved, CPCB-certified equipment providing real-time calibrated readings</li>
              <li><strong>Real-time quality assurance:</strong> Automated data validation, outlier detection, and accuracy verification protocols</li>
              <li><strong>Government compliance:</strong> Full adherence to Central Pollution Control Board (CPCB) and Kerala State Pollution Control Board standards</li>
              <li><strong>AQI Persistence:</strong> AQI values remain constant for 12-hour periods to provide stability for health planning</li>
            </ul>
          </div>

          <p>
            <strong>âš ï¸ Information & Usage:</strong> Air quality readings are updated every few seconds to help you stay informed. 
            Please note that air conditions can change depending on traffic, weather, or nearby activities. For any serious health concerns, 
            always rely on official sources and medical advice.
          </p>

          <p>
            <strong>ðŸ”„ System Status:</strong> 
            <span style={{ color: '#10b981', fontWeight: 600 }}> {systemStatus}</span> | 
            Last update: {dashboardData.sensorTimestamp}
          </p>

          <p>
            <strong>ðŸ¥ Health & Safety Tips:</strong> Use this information to plan your day better. If you have breathing problems 
            or heart conditions, follow health guidelines and check with your doctor when needed. 
            Emergency contact: Kerala Pollution Control Board â€“ 0471-2418566
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirQuality Kerala</h3>
              <p className="footer-text">
                Leading provider of real-time air quality monitoring solutions in Kerala, 
                committed to environmental sustainability and public health awareness.
              </p>
              <div className="social-links">
                <a href="#" className="social-link">ðŸ“˜</a>
                <a href="#" className="social-link">ðŸ¦</a>
                <a href="#" className="social-link">ðŸ’¼</a>
                <a href="#" className="social-link">ðŸ“¸</a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <a href="/" className="footer-link">Home</a>
              <a href="/map" className="footer-link">Live Map</a>
              <a href="/forecast" className="footer-link">Forecasting</a>
              <a href="/health-report" className="footer-link">Health Report</a>
            </div>

            <div className="footer-section">
              <h3>Services</h3>
              <a href="/forecast" className="footer-link">AQI Forecasting</a>
              <a href="/health-assessment" className="footer-link">Health Assessment</a>
              <a href="/map" className="footer-link">Live AQI Map</a>
              <a href="#" className="footer-link">Data Analytics</a>
            </div>

            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Vidya Bharathi Nagar, Mattoor<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AirQuality Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
