import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logoImage from '../assets/aqi.webp'; 
import { useAuth } from '../App';
import './css/MapPage.css';

// --- Helper Functions ---
const getAQIColor = (aqi) => {
    if (aqi === null || aqi === undefined) return '#9ca3af'; // Gray for unknown/upcoming
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 200) return '#ef4444';
    return '#7c2d12';
};

const getAQIStatus = (aqi) => {
    if (aqi === null || aqi === undefined) return 'Upcoming';
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 200) return 'Unhealthy';
    return 'Hazardous';
};

const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) ** 2 + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const MapPage = () => {
    // --- STATE MANAGEMENT ---
    const [mapInstance, setMapInstance] = useState(null);
    const [stations, setStations] = useState({});
    const [selectedStation, setSelectedStation] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationData, setUserLocationData] = useState(null);
    const [isLocationLoading, setIsLocationLoading] = useState(false);
    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);
    const [activeView, setActiveView] = useState('user'); // 'user' or 'station'

    const mapRef = useRef(null);
    const markersRef = useRef({});
    const userLocationMarkerRef = useRef(null);

    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
        ? 'https://airaware-app-gcw7.onrender.com' 
        : 'http://localhost:8000';

    // --- HOOKS ---
    useEffect(() => {
        if (!user) navigate('/login');
    }, [user, navigate]);

    useEffect(() => {
        const handleResize = () => setIsMobileView(window.innerWidth <= 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    // CORRECTED: This effect now safely initializes the map once.
    useEffect(() => {
        if (mapRef.current && !mapRef.current._leaflet_id) {
            const map = window.L.map(mapRef.current, { zoomControl: false }).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', { attribution: '¬© OpenStreetMap' }).addTo(map);
            window.L.control.zoom({ position: 'topright' }).addTo(map);
            setMapInstance(map);
        }
    }, []); // Empty dependency array ensures this runs only on mount.

    const calculateSmartEstimation = useCallback((locationData, allStations) => {
        const realStationIds = ['lora-v1', 'loradev2'];
        const realStations = {};
        realStationIds.forEach(id => {
            if (allStations[id]) realStations[id] = allStations[id];
        });
        if (Object.keys(realStations).length === 0) return { aqi: 0, values: {} };
        
        let totalWeight = 0;
        const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0 };
        let weightedAqi = 0;

        Object.values(realStations).forEach(station => {
            const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
            const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
            totalWeight += weight;
            Object.keys(weightedValues).forEach(param => {
                if (station.averages?.[param] !== undefined) weightedValues[param] += station.averages[param] * weight;
            });
            weightedAqi += (station.highest_sub_index || 0) * weight;
        });

        const estimatedValues = {};
        Object.keys(weightedValues).forEach(param => {
            estimatedValues[param] = totalWeight > 0 ? parseFloat((weightedValues[param] / totalWeight).toFixed(2)) : 0;
        });
        const estimatedAqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
        return { values: estimatedValues, aqi: estimatedAqi };
    }, []);

    useEffect(() => {
        if (userLocation && Object.keys(stations).length > 0) {
            const estimation = calculateSmartEstimation(userLocation, stations);
            const { lat, lng } = stations['lora-v1'].station_info;
            const distanceToNearest = calculateDistance(userLocation.lat, userLocation.lng, lat, lng);
            const isHighConfidence = distanceToNearest <= 5.0;

            setUserLocationData({
                aqi: estimation.aqi,
                values: estimation.values,
                quality: isHighConfidence ? 'High-Accuracy' : 'Regional Estimation'
            });
        }
    }, [userLocation, stations, calculateSmartEstimation]);

    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
                if (!response.ok) throw new Error('Network response was not ok');
                const data = await response.json();
                setStations(data.stations);
            } catch (err) {
                console.error("Failed to fetch station data:", err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchData();
        const interval = setInterval(fetchData, 60000);
        return () => clearInterval(interval);
    }, [API_BASE_URL]);
    
    useEffect(() => {
        if (mapInstance && Object.keys(stations).length > 0) {
            Object.values(markersRef.current).forEach(marker => marker.remove());
            markersRef.current = {};
            
            Object.entries(stations).forEach(([id, station]) => {
                const { lat, lng } = station.station_info;
                const aqi = station.highest_sub_index;
                const icon = window.L.divIcon({
                    html: `<div class="station-marker-icon" style="background-color: ${getAQIColor(aqi)};"></div>`,
                    className: 'station-marker', iconSize: [24, 24], iconAnchor: [12, 12]
                });
                const marker = window.L.marker([lat, lng], { icon }).addTo(mapInstance);
                
                marker.on('click', () => {
                    if (station.averages) {
                        setSelectedStation(station);
                        setActiveView('station');
                    } else {
                        const popupContent = `
                            <div class="upcoming-popup">
                                <h4>${station.station_info.name}</h4>
                                <p>This station is coming soon.</p>
                                <span>Data Update Soon...</span>
                            </div>
                        `;
                        marker.bindPopup(popupContent).openPopup();
                    }
                });
                markersRef.current[id] = marker;
            });
        }
    }, [mapInstance, stations]);
    
    useEffect(() => {
        if (mapInstance && userLocation) {
            const userIcon = window.L.divIcon({
                html: `<div class="user-location-marker"></div>`,
                className: 'user-marker', iconSize: [24, 24], iconAnchor: [12, 12]
            });
            if (userLocationMarkerRef.current) {
                userLocationMarkerRef.current.setLatLng(userLocation);
            } else {
                userLocationMarkerRef.current = window.L.marker(userLocation, { icon: userIcon }).addTo(mapInstance);
            }
        }
    }, [mapInstance, userLocation]);

    const trackUserLocation = useCallback(() => {
        setIsLocationLoading(true);
        setActiveView('user');
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const loc = { lat: position.coords.latitude, lng: position.coords.longitude };
                setUserLocation(loc);
                setIsLocationLoading(false);
                if (mapInstance) mapInstance.setView(loc, 15);
            },
            () => {
                setIsLocationLoading(false);
            }
        );
    }, [mapInstance]);
    
    const renderPanelContent = () => {
        if (activeView === 'station' && selectedStation) {
            const data = selectedStation;
            return (
                <>
                    <div className="panel-header">
                        <h3>{data.station_info.name}</h3>
                        <button className="back-btn" onClick={() => setActiveView('user')}>‚Üê Back to Your Location</button>
                    </div>
                    <div className="aqi-display">
                        <div className="aqi-value" style={{ color: getAQIColor(data.highest_sub_index) }}>
                            {Math.round(data.highest_sub_index)}
                        </div>
                        <div className="aqi-status">{getAQIStatus(data.highest_sub_index)}</div>
                    </div>
                    <div className="readings-grid">
                        {['pm25', 'pm10', 'no2', 'so2', 'o3', 'co'].map(key => (
                             <div key={key} className="reading-item">
                                <span className="reading-label">{key.toUpperCase()}</span>
                                <span className="reading-value">{data.averages?.[key]?.toFixed(1) || 'N/A'}</span>
                             </div>
                        ))}
                    </div>
                </>
            );
        }

        return (
            <>
                <div className="panel-header">
                    <h3>Your Location's Air Quality</h3>
                </div>
                {userLocationData ? (
                    <>
                        <div className="aqi-display">
                            <div className="aqi-quality-badge">{userLocationData.quality}</div>
                            <div className="aqi-value" style={{ color: getAQIColor(userLocationData.aqi) }}>
                                {Math.round(userLocationData.aqi)}
                            </div>
                            <div className="aqi-status">{getAQIStatus(userLocationData.aqi)}</div>
                        </div>
                        <p className="panel-explanation">
                            This is an estimated air quality for your current location, calculated using our real sensor network.
                        </p>
                    </>
                ) : (
                    <div className="location-prompt">
                        <p>Click the location button on the map to see personalized air quality data for your area.</p>
                    </div>
                )}
            </>
        );
    };

    return (
        <div className="map-page">
             <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
                        AirAware
                    </Link>
                    <div className="nav-right">
                        {user ? (
                            <>
                                <Link to="/dashboard" className="nav-link">Dashboard</Link>
                                <button onClick={logout} className="logout-btn">Logout</button>
                            </>
                        ) : (
                            <Link to="/login" className="login-btn">Login</Link>
                        )}
                    </div>
                </div>
            </nav>
            <div className={`main-content ${isMobileView ? 'mobile-view' : ''}`}>
                <div className="map-wrapper">
                    <div id="map" ref={mapRef} className="map-element"></div>
                    <button onClick={trackUserLocation} className="my-location-btn" disabled={isLocationLoading}>
                        {isLocationLoading ? <div className="spinner"></div> : 'üìç'}
                    </button>
                </div>
                <div className="data-panel">
                    {isLoading ? <div className="panel-loader">Loading Stations...</div> : renderPanelContent()}
                </div>
            </div>
        </div>
    );
};

export default MapPage;
