import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/FamilyPage.css'; // Make sure you have created and styled this CSS file

function FamilyPage() {
    const { user } = useAuth();
    const [username] = useState(user?.name || null);
    const [familyMembers, setFamilyMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingMember, setEditingMember] = useState(null); // To track which member is being edited
    
    // Unified form state for both adding and editing
    const [formState, setFormState] = useState({ name: '', age: '', relationship: '' });
    const [formError, setFormError] = useState('');

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const fetchFamilyMembers = useCallback(async () => {
        if (!username) {
            navigate('/login');
            return;
        }
        setIsLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/api/family-members/?username=${username}`);
            if (!response.ok) throw new Error('Failed to fetch family members.');
            const data = await response.json();
            setFamilyMembers(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        fetchFamilyMembers();
    }, [fetchFamilyMembers]);

    // --- HANDLER FUNCTIONS for Modal and Actions ---

    const handleAddMemberClick = () => {
        setEditingMember(null); // Ensure we are in "add" mode
        setFormState({ name: '', age: '', relationship: '' }); // Reset form
        setFormError('');
        setIsModalOpen(true);
    };

    const handleEditMemberClick = (member) => {
        setEditingMember(member); // Set the member to edit
        setFormState({ name: member.name, age: member.age, relationship: member.relationship }); // Pre-fill form
        setFormError('');
        setIsModalOpen(true);
    };

    const handleDeleteMember = async (memberId) => {
        if (window.confirm("Are you sure you want to delete this family member?")) {
            try {
                const response = await fetch(`${API_BASE_URL}/api/family-members/${memberId}/`, { method: 'DELETE' });
                if (!response.ok) throw new Error('Failed to delete.');
                setFamilyMembers(prev => prev.filter(m => m.id !== memberId));
            } catch (err) {
                alert("Failed to delete member. Please try again.");
            }
        }
    };
    
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        setFormError('');

        const url = editingMember 
            ? `${API_BASE_URL}/api/family-members/update/${editingMember.id}/`
            : `${API_BASE_URL}/api/family-members/`;
        
        const method = editingMember ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ ...formState, username })
            });

            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.error || 'An unknown error occurred.');
            }

            const updatedMember = await response.json();

            if (editingMember) {
                // Update the existing member in the list
                setFamilyMembers(prev => prev.map(m => m.id === updatedMember.id ? updatedMember : m));
            } else {
                // Add the new member to the list
                setFamilyMembers(prev => [...prev, updatedMember]);
            }
            
            setIsModalOpen(false); // Close modal on success
        } catch (err) {
            setFormError(err.message);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormState(prev => ({ ...prev, [name]: value }));
    };

    if (isLoading) return <div className="panel-loader"><h2>Loading Family Members...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;

    return (
        <div className="family-page">
            <nav className="navbar">{/* --- Your Navbar JSX Here --- */}</nav>
            <div className="container">
                <div className="dashboard">
                    <section className="profile-card">
                        <div className="profile-avatar">{username ? username.charAt(0).toUpperCase() : 'U'}</div>
                        <h2 className="profile-name">{username}</h2>
                        <div className="profile-stats">
                            <div className="stat">
                                <div className="stat-number">{familyMembers.length}</div>
                                <div className="stat-label">Family Members</div>
                            </div>
                        </div>
                    </section>
                    <section className="members-section">
                        <div className="section-header">
                            <h2 className="section-title">Family Members</h2>
                            <button className="add-btn" onClick={handleAddMemberClick}>
                                <span>+</span> Add Member
                            </button>
                        </div>
                        {familyMembers.length > 0 ? (
                            <div className="members-grid">
                                {familyMembers.map(member => (
                                    <div className="member-card" key={member.id}>
                                        <div className="member-avatar">{member.name.charAt(0).toUpperCase()}</div>
                                        <div className="member-info">
                                            <div className="member-name">{member.name}</div>
                                            <div className="member-relation">{member.relationship}</div>
                                            <div className="member-age">{member.age} years old</div>
                                            <div className="member-actions">
                                                <Link to={`/health-assessment?username=${member.name}`} className="action-btn health-btn">Health Info</Link>
                                                <button className="action-btn edit-btn" onClick={() => handleEditMemberClick(member)}>Edit</button>
                                                <button className="action-btn delete-btn" onClick={() => handleDeleteMember(member.id)}>Delete</button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="no-members">
                                <p>No family members added yet. Click "Add Member" to get started.</p>
                            </div>
                        )}
                    </section>
                </div>
            </div>

            {isModalOpen && (
                <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
                    <div className="modal" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h3 className="modal-title">{editingMember ? 'Edit' : 'Add'} Family Member</h3>
                            <button className="close-btn" onClick={() => setIsModalOpen(false)}>&times;</button>
                        </div>
                        <form onSubmit={handleFormSubmit}>
                            {formError && <div className="form-error">{formError}</div>}
                            <div className="form-group">
                                <label htmlFor="name">Full Name</label>
                                <input type="text" name="name" value={formState.name} onChange={handleInputChange} placeholder="Enter full name" required />
                            </div>
                            <div className="form-group">
                                <label htmlFor="age">Age</label>
                                <input type="number" name="age" value={formState.age} onChange={handleInputChange} placeholder="Enter age" required />
                            </div>
                            <div className="form-group">
                                <label>Relationship</label>
                                <select name="relationship" value={formState.relationship} onChange={handleInputChange} required>
                                    <option value="">Select Relationship</option>
                                    <option value="Father">Father</option>
                                    <option value="Mother">Mother</option>
                                    <option value="Sibling">Sibling</option>
                                    <option value="Spouse">Spouse</option>
                                    <option value="Child">Child</option>
                                    <option value="Other">Other</option>
                                </select>
                            </div>
                            <button type="submit" className="btn">{editingMember ? 'Save Changes' : 'Add Member'}</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default FamilyPage;
