import React, { useState, useEffect } from 'react';
import './css/AdminDashboard.css'; // Create this new CSS file

function AdminDashboard() {
    const [dashboardData, setDashboardData] = useState(null);
    const [loading, setLoading] = useState(true);
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/api/admin/dashboard/`);
                const data = await response.json();
                setDashboardData(data);
            } catch (error) {
                console.error("Failed to fetch admin data:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, [API_BASE_URL]);

    if (loading) return <div className="admin-loader">Loading Admin Dashboard...</div>;
    if (!dashboardData) return <div>Failed to load data.</div>;

    const { station_data, registered_users, health_assessments } = dashboardData;

    return (
        <div className="main-wrapper">
            <aside className="sidebar">{/* --- Sidebar Navigation from admin_dashboard1.html --- */}</aside>
            <main className="content-area">
                <div className="topbar">{/* --- Topbar from admin_dashboard1.html --- */}</div>
                <div className="station-grid">
                    {Object.entries(station_data).map(([id, station]) => (
                        <div className="dashboard-card" key={id}>
                            <div className="card-header">
                                <h5 className="card-title">{station.latest_item.station_name || id}</h5>
                                <span className="status-badge status-normal">Active</span>
                            </div>
                            <div className="card-body">
                                <p>AQI: {station.aqi}</p>
                                {/* ... Display other station metrics ... */}
                            </div>
                        </div>
                    ))}
                </div>
                <div className="data-card">
                    <div className="card-header">
                        <h4 className="card-title">Registered Users</h4>
                    </div>
                    <div className="card-body p-0">
                        <table className="data-table">
                            <thead><tr><th>ID</th><th>Name</th><th>Phone</th></tr></thead>
                            <tbody>
                                {registered_users.map(user => (
                                    <tr key={user.id}><td>{user.id}</td><td>{user.name}</td><td>{user.phone_number}</td></tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
                {/* ... Health Assessments Table ... */}
            </main>
        </div>
    );
}
export default AdminDashboard;
