import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logoImage from '../assets/aqi.webp'; 
import { useAuth } from '../App';
import './css/MapPage.css';
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

// --- Helper Functions ---
const getAQIColor = (aqi) => {
    if (aqi === null || aqi === undefined) return '#9ca3af';
    if (aqi <= 50) return '#10b981';
    if (aqi <= 100) return '#f59e0b';
    if (aqi <= 200) return '#ef4444';
    return '#7c2d12';
};

const getAQIStatus = (aqi) => {
    if (aqi === null || aqi === undefined) return 'Unknown';
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 200) return 'Unhealthy';
    return 'Hazardous';
};

const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) ** 2 + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const MapPage = () => {
    const [mapInstance, setMapInstance] = useState(null);
    const [stations, setStations] = useState({});
    const [selectedStationId, setSelectedStationId] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationData, setUserLocationData] = useState(null);
    const [isLocationLoading, setIsLocationLoading] = useState(false);
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
    const [isPanelOpen, setIsPanelOpen] = useState(false);
    const [activeTab, setActiveTab] = useState('stations'); // Default to stations for logged-out users
    const [forecastData, setForecastData] = useState([]);
    const [isForecastLoading, setIsForecastLoading] = useState(false);

    const mapRef = useRef(null);
    const markersRef = useRef({});
    const userLocationMarkerRef = useRef(null);
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
        ? 'https://airaware-app-gcw7.onrender.com' 
        : 'http://localhost:8000';

    // **FIX 1: PAGE-LEVEL SECURITY CHECK REMOVED**
    // The useEffect that redirected if !user is now gone.

    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth <= 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {
        if (mapRef.current && !mapRef.current._leaflet_id) {
            const map = window.L.map(mapRef.current, { zoomControl: false }).setView([10.176, 76.430], 13);
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', { attribution: 'Â© OpenStreetMap' }).addTo(map);
            window.L.control.zoom({ position: 'topright' }).addTo(map);
            setMapInstance(map);
        }
    }, []);

    const calculateSmartEstimation = useCallback((locationData, allStations) => {
        const realStations = Object.values(allStations).filter(s => s.averages);
        if (realStations.length === 0) return { aqi: 0, values: {} };
        let totalWeight = 0, weightedAqi = 0;
        const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0 };
        realStations.forEach(station => {
            const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
            const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
            totalWeight += weight;
            Object.keys(weightedValues).forEach(param => {
                if (station.averages?.[param] !== undefined) weightedValues[param] += station.averages[param] * weight;
            });
            weightedAqi += (station.highest_sub_index || 0) * weight;
        });
        const estimatedValues = {};
        Object.keys(weightedValues).forEach(param => {
            estimatedValues[param] = totalWeight > 0 ? parseFloat((weightedValues[param] / totalWeight).toFixed(2)) : 0;
        });
        const estimatedAqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
        return { values: estimatedValues, aqi: estimatedAqi };
    }, []);

    useEffect(() => {
        if (userLocation && Object.keys(stations).length > 0) {
            const estimation = calculateSmartEstimation(userLocation, stations);
            const { lat, lng } = stations['lora-v1'].station_info;
            const distanceToNearest = calculateDistance(userLocation.lat, userLocation.lng, lat, lng);
            setUserLocationData({
                aqi: estimation.aqi,
                values: estimation.values,
                quality: distanceToNearest <= 5.0 ? 'High-Accuracy' : 'Regional Estimation'
            });
        }
    }, [userLocation, stations, calculateSmartEstimation]);

    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
                if (!response.ok) throw new Error('Network response was not ok');
                const data = await response.json();
                setStations(data.stations);
            } catch (err) {
                console.error("Failed to fetch station data:", err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchData();
        const interval = setInterval(fetchData, 60000);
        return () => clearInterval(interval);
    }, [API_BASE_URL]);

    useEffect(() => {
        if (!selectedStationId) return;
        const fetchForecast = async () => {
            setIsForecastLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/api/station/${selectedStationId}/forecast/`);
                const data = await response.json();
                setForecastData(data.forecast_data || []);
            } catch (err) {
                setForecastData([]);
            } finally {
                setIsForecastLoading(false);
            }
        };
        fetchForecast();
    }, [selectedStationId, API_BASE_URL]);

    useEffect(() => {
        if (mapInstance && Object.keys(stations).length > 0) {
            Object.values(markersRef.current).forEach(marker => marker.remove());
            markersRef.current = {};
            Object.entries(stations).forEach(([id, station]) => {
                const { lat, lng } = station.station_info;
                const icon = window.L.divIcon({
                    html: `<div class="station-marker-icon" style="background-color: ${getAQIColor(station.highest_sub_index)};"></div>`,
                    className: 'station-marker',
                });
                const marker = window.L.marker([lat, lng], { icon }).addTo(mapInstance);
                marker.on('click', () => {
                    setSelectedStationId(id);
                    if (isMobile) {
                        setActiveTab('forecast');
                        setIsPanelOpen(true);
                    }
                });
                markersRef.current[id] = marker;
            });
        }
    }, [mapInstance, stations, isMobile]);
    
    useEffect(() => {
        if (mapInstance && userLocation) {
            const userIcon = window.L.divIcon({
                html: `<div class="user-location-marker"></div>`,
                className: 'user-marker',
            });
            if (userLocationMarkerRef.current) {
                userLocationMarkerRef.current.setLatLng(userLocation);
            } else {
                userLocationMarkerRef.current = window.L.marker(userLocation, { icon: userIcon }).addTo(mapInstance);
            }
        }
    }, [mapInstance, userLocation]);

    // **FIX 2: SECURITY CHECK MOVED INSIDE THE BUTTON'S FUNCTION**
    const trackUserLocation = useCallback(() => {
        if (user) {
            // If user is logged in, track their location
            setIsLocationLoading(true);
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const loc = { lat: position.coords.latitude, lng: position.coords.longitude };
                    setUserLocation(loc);
                    setIsLocationLoading(false);
                    if (mapInstance) mapInstance.setView(loc, 15);
                    if (isMobile) {
                        setActiveTab('user');
                        setIsPanelOpen(true);
                    }
                },
                () => setIsLocationLoading(false),
                { enableHighAccuracy: true }
            );
        } else {
            // If user is NOT logged in, redirect to login page
            navigate('/login');
        }
    }, [mapInstance, isMobile, user, navigate]);
    
    const renderUserContent = () => {
        if (!user) return (
            <div className="mobile-login-prompt">
                <i className="fas fa-lock"></i>
                <h4>Personalized Data is Secure</h4>
                <p>Log in to track your location and get air quality data specific to you.</p>
                <Link to="/login" className="mobile-login-btn">
                    <i className="fas fa-sign-in-alt"></i> Login to View
                </Link>
            </div>
        );
        if (isLocationLoading) return <div className="mobile-loader"><div className="loading-spinner"></div><p>Finding Your Location...</p></div>;
        if (!userLocationData) return (
            <div className="mobile-location-prompt">
                <i className="fas fa-map-marker-alt"></i>
                <h4>Find Your Local Air Quality</h4>
                <p>Get personalized air quality data for your current location.</p>
                <button className="mobile-location-btn" onClick={trackUserLocation}>
                    <i className="fas fa-crosshairs"></i> Use My Location
                </button>
            </div>
        );

        return (
            <div className="mobile-user-aqi">
                <div className="user-aqi-circle" style={{ background: getAQIColor(userLocationData.aqi) }}>
                    <div className="aqi-number">{Math.round(userLocationData.aqi)}</div>
                    <div className="aqi-label">{getAQIStatus(userLocationData.aqi)}</div>
                </div>
                <div className="user-location-info">
                    <h4>{userLocationData.quality} Data</h4>
                    <p>This is an estimated air quality for your current location, calculated using our real sensor network.</p>
                </div>
            </div>
        );
    };

    const renderStationContent = () => {
        if (isLoading) return <div className="mobile-loader"><div className="loading-spinner"></div><p>Loading Stations...</p></div>;
        return (
            <div className="mobile-station-list">
                {Object.entries(stations).map(([id, station]) => (
                    <button key={id} className={`mobile-station-card ${selectedStationId === id ? 'active' : ''}`} onClick={() => { setSelectedStationId(id); setActiveTab('forecast'); }}>
                        <div className="station-info">
                            <h4>{station.station_info.name}</h4>
                            <span className="station-meta">Real-time Sensor</span>
                        </div>
                        <div className="station-aqi-badge" style={{ backgroundColor: getAQIColor(station.highest_sub_index) }}>
                            <span className="aqi-number">{Math.round(station.highest_sub_index)}</span>
                            <span className="aqi-status">{getAQIStatus(station.highest_sub_index)}</span>
                        </div>
                    </button>
                ))}
            </div>
        );
    };

    const renderForecastContent = () => {
        if (!selectedStationId) return (
             <div className="mobile-no-selection">
                <i className="fas fa-chart-line"></i>
                <h3>No Station Selected</h3>
                <p>Please select a station from the list to view its forecast.</p>
                 <button className="select-station-btn" onClick={() => setActiveTab('stations')}>Select a Station</button>
            </div>
        );
        if (isForecastLoading) return <div className="mobile-loader"><div className="loading-spinner"></div><p>Loading Forecast...</p></div>;
        
        const chartData = {
            labels: forecastData.map(d => new Date(d.day).toLocaleDateString('en-US', { weekday: 'short' })),
            datasets: [{
                label: 'AQI Forecast',
                data: forecastData.map(d => d.pm25_max),
                borderColor: '#3b82f6',
                backgroundColor: 'rgba(59, 130, 246, 0.1)',
                fill: true,
                tension: 0.4
            }]
        };
        
        return (
            <div className="mobile-forecast-section">
                <div className="mobile-section-header">
                    <h3><i className="fas fa-chart-line"></i> {stations[selectedStationId]?.station_info.name}</h3>
                </div>
                <div className="mobile-chart-container">
                    <Line data={chartData} options={{ responsive: true, maintainAspectRatio: false }} />
                </div>
            </div>
        );
    };

    const renderPanelForDesktop = () => (
         <div className="data-panel">
            <div className="data-panel-content">
                {renderUserContent()}
                <div className="station-list-section">
                    <h2 className="section-title"><i className="fas fa-broadcast-tower"></i> Monitoring Stations</h2>
                    <div className="station-selector">
                        {isLoading ? <div className="loading-spinner small"></div> : Object.entries(stations).map(([id, station]) => (
                            <button key={id} className={`station-btn ${selectedStationId === id ? 'active' : ''}`} onClick={() => setSelectedStationId(id)}>
                                <span className="station-name">{station.station_info.name}</span>
                                <span className="station-aqi" style={{ backgroundColor: getAQIColor(station.highest_sub_index) }}>
                                    {Math.round(station.highest_sub_index)}
                                </span>
                            </button>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );

    const renderDetailsPanelForDesktop = () => {
        if (!selectedStationId) return null;
        const station = stations[selectedStationId];
        if (!station) return null;

        return (
            <div className="station-details-panel">
                <div className="station-details-content">
                    <div className="station-header">
                        <h3>{station.station_info.name}</h3>
                    </div>
                    {/* Simplified for brevity. Add forecast chart and readings here */}
                </div>
            </div>
        );
    };

    return (
        <div className="map-page">
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src={logoImage} alt="AQM Logo" width={isMobile ? "28" : "32"} height={isMobile ? "28" : "32"} />
                        <span>AirAware</span>
                    </Link>
                    <div className="nav-right">
                        {user ? (
                            <div className="user-menu">
                                <Link to="/dashboard" className="nav-link">Dashboard</Link>
                                <button onClick={logout} className="logout-btn">Logout</button>
                            </div>
                        ) : (
                            <Link to="/login" className="login-btn">Login</Link>
                        )}
                    </div>
                </div>
            </nav>
            <div className="main-content">
                <div className="map-container">
                    <div id="map" ref={mapRef} className="map-element"></div>
                    <button onClick={trackUserLocation} className={`my-location-btn ${isLocationLoading ? 'loading' : ''}`} disabled={isLocationLoading}>
                        {isLocationLoading ? <div className="loading-spinner"></div> : <i className="fas fa-crosshairs"></i>}
                    </button>
                    {isMobile && (
                        <button className="mobile-menu-toggle" onClick={() => setIsPanelOpen(!isPanelOpen)}>
                            <i className={`fas ${isPanelOpen ? 'fa-times' : 'fa-bars'}`}></i>
                        </button>
                    )}
                </div>
                
                {!isMobile && renderPanelForDesktop()}
                {!isMobile && renderDetailsPanelForDesktop()}

                {isMobile && (
                    <div className={`mobile-bottom-sheet ${isPanelOpen ? 'open' : ''}`}>
                        <div className="mobile-tab-nav">
                            <button className={`mobile-tab-btn ${activeTab === 'user' ? 'active' : ''}`} onClick={() => setActiveTab('user')}>
                                <i className="fas fa-user-circle"></i> Your Location
                            </button>
                            <button className={`mobile-tab-btn ${activeTab === 'stations' ? 'active' : ''}`} onClick={() => setActiveTab('stations')}>
                                <i className="fas fa-broadcast-tower"></i> Stations
                            </button>
                            <button className={`mobile-tab-btn ${activeTab === 'forecast' ? 'active' : ''}`} onClick={() => setActiveTab('forecast')}>
                                <i className="fas fa-chart-line"></i> Forecast
                            </button>
                        </div>
                        <div className="mobile-tab-content-wrapper">
                            <div className="mobile-tab-content">
                                {activeTab === 'user' && renderUserContent()}
                                {activeTab === 'stations' && renderStationContent()}
                                {activeTab === 'forecast' && renderForecastContent()}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default MapPage;
