// App.js (Corrected and Final Version)

import React, { useState, useEffect, createContext, useContext, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from 'react-router-dom';

// Import your page components
import HomePage from './components/HomePage';
import Login from './components/Login';
import Signup from './components/Signup';
import HealthAssessment from './components/HealthAssessment';
import Dashboard from './components/Dashboard';
import MapPage from './components/MapPage';
import HealthReport from './components/HealthReport';
import FamilyPage from './components/FamilyPage';
import AdminDashboard from './components/AdminDashboard';
import AdminLogin from './components/AdminLogin';

// === 1. UNIFIED AUTHENTICATION CONTEXT ===
// We are combining the logic of SessionManager and AuthContext into one place.
const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

// A simple loading screen component
const LoadingScreen = () => (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f8fafc' }}>
        <p style={{ color: '#6b7280' }}>Loading Session...</p>
    </div>
);

// === 2. THE "SMART" PROTECTED ROUTE ===
// This component now uses the `useAuth` hook and understands the `loading` state.
const ProtectedRoute = () => {
    const { user, loading } = useAuth();

    if (loading) {
        // While we check for a user, show a loading screen.
        // THIS IS THE KEY FIX: It prevents the redirect from happening too early.
        return <LoadingScreen />;
    }

    if (!user) {
        // After loading is done, if there's still no user, NOW we redirect.
        return <Navigate to="/login" replace />;
    }

    // If loading is done and we have a user, show the requested page.
    return <Outlet />;
};

// Admin Protected Route - now uses localStorage correctly
const AdminProtectedRoute = ({ children }) => {
    const isAdminLoggedIn = !!localStorage.getItem('admin_user');
    return isAdminLoggedIn ? children : <Navigate to="/admin/login" replace />;
};

// === 3. THE MAIN APP COMPONENT WITH CENTRALIZED LOGIC ===
function App() {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true); // Start as true to check session on initial load

    const handleLogout = useCallback(() => {
        localStorage.removeItem('user');
        localStorage.removeItem('loginTime');
        setUser(null);
        // No need to force a page reload, React's state will handle UI updates.
    }, []);

    useEffect(() => {
        const SESSION_DURATION = 60 * 60 * 1000; // 1 hour

        try {
            const userInStorage = localStorage.getItem('user');
            const loginTime = localStorage.getItem('loginTime');

            if (userInStorage && loginTime) {
                const sessionAge = Date.now() - parseInt(loginTime, 10);
                if (sessionAge > SESSION_DURATION) {
                    // Session expired
                    handleLogout();
                } else {
                    // Session is valid
                    setUser(JSON.parse(userInStorage));
                }
            }
        } catch (error) {
            console.error("Failed to initialize auth state:", error);
            handleLogout(); // Clear potentially corrupted storage
        } finally {
            // IMPORTANT: We are done checking, so we set loading to false.
            setLoading(false);
        }
    }, [handleLogout]);

    const authValue = {
        user,
        loading,
        login: (userData) => {
            try {
                localStorage.setItem('user', JSON.stringify(userData));
                localStorage.setItem('loginTime', Date.now().toString());
                setUser(userData);
            } catch (error) {
                console.error("Login failed:", error);
            }
        },
        logout: handleLogout,
    };

    return (
        <AuthContext.Provider value={authValue}>
            <Router>
                <Routes>
                    {/* Public Routes */}
                    <Route path="/" element={<HomePage />} />
                    <Route path="/login" element={user ? <Navigate to="/dashboard" replace /> : <Login />} />
                    <Route path="/signup" element={user ? <Navigate to="/dashboard" replace /> : <Signup />} />
                    <Route path="/map" element={<MapPage />} />

                    {/* Admin Routes */}
                    <Route path="/admin/login" element={<AdminLogin />} />

                    {/* --- USE THE NEW PROTECTED ROUTE WRAPPER --- */}
                    <Route element={<ProtectedRoute />}>
                        <Route path="/dashboard" element={<Dashboard />} />
                        <Route path="/health-assessment" element={<HealthAssessment />} />
                        <Route path="/health-report" element={<HealthReport />} />
                        <Route path="/add-family" element={<FamilyPage />} />
                    </Route>

                    {/* Admin Protected Route */}
                    <Route path="/admin/dashboard" element={<AdminProtectedRoute><AdminDashboard /></AdminProtectedRoute>} />

                    {/* Legacy admin routes for backwards compatibility */}
                    <Route path="/admin-login" element={<Navigate to="/admin/login" replace />} />
                    <Route path="/admin-dashboard" element={<Navigate to="/admin/dashboard" replace />} />
                </Routes>
            </Router>
        </AuthContext.Provider>
    );
}

export default App;
